import React, { useState, useEffect, useMemo } from "react";
import { useSelector } from "react-redux";
import { useNavigate } from "react-router-dom";
import {
  Box,
  Typography,
  Grid,
  Card,
  CardContent,
  CardActions,
  Button,
  CircularProgress,
  Divider,
  Paper,
  List,
  ListItem,
  ListItemText,
  ListItemAvatar,
  Avatar,
  Chip,
  Tooltip,
  IconButton,
  useTheme,
} from "@mui/material";
import {
  School,
  Class,
  Person,
  People,
  Event,
  EventAvailable,
  CheckCircle,
  Face,
  Today,
  AccessTime,
  Timeline,
  Info,
  Error,
  Timelapse,
} from "@mui/icons-material";

// Import các services cần thiết
import {
  getTeacherTeachingClasses,
  getTeacherRecentAttendanceSessions,
} from "../services/teacherService";
// import { getStudentClasses, getStudentAttendanceScores, getStudentAttendanceLogs } from "../services/studentService"; // Sẽ thêm khi làm phần student
// import { getAdminStats, getAllClassesForAdmin, getUsersStatsForAdmin } from "../services/adminService"; // Sẽ thêm khi làm phần admin

const DashboardPage = () => {
  const navigate = useNavigate();
  const theme = useTheme();
  const { user, token } = useSelector((state) => state.auth);

  const [stats, setStats] = useState({
    classes: [],
    attendanceSessions: [],
    todayAttendance: [],
    totalAttendedSessions: 0,
    totalSessions: 0,
    attendancePercentage: 0,
    recentAttendance: [],
    upcomingClasses: [],
    attendanceScores: [],
  });
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchDashboardData = async () => {
      try {
        setIsLoading(true);
        setError(null);

        if (user.role === "student") {
          // Cải thiện xử lý lỗi bằng cách gọi API riêng lẻ thay vì Promise.all
          let classesData = [];
          let scoresData = [];
          let logsData = [];
          let upcomingData = [];

          try {
            // Sửa endpoint để sử dụng đúng API cho sinh viên
            // Thay vì /classes/teaching?student=${user._id} sai đường dẫn
            // Dùng /classes/teaching/student/${user._id} đúng định nghĩa route
            const classesResponse = await axios.get(
              `${API_URL}/classes/teaching/student/${user._id}`,
              { headers: { Authorization: `Bearer ${token}` } }
            );
            classesData = classesResponse.data.data || [];
          } catch (error) {
            console.error("Lỗi khi lấy dữ liệu lớp học:", error);
          }

          try {
            // Lấy thống kê điểm danh
            const scoresResponse = await axios.get(
              `${API_URL}/attendance/student/${user._id}/scores`,
              { headers: { Authorization: `Bearer ${token}` } }
            );
            scoresData = scoresResponse.data.data || [];
          } catch (error) {
            console.error("Lỗi khi lấy thống kê điểm danh:", error);
          }

          try {
            // Lấy lịch sử điểm danh gần đây
            const logsResponse = await axios.get(
              `${API_URL}/attendance/student/${user._id}/logs?limit=10`,
              { headers: { Authorization: `Bearer ${token}` } }
            );
            logsData = logsResponse.data.data || [];
          } catch (error) {
            console.error("Lỗi khi lấy lịch sử điểm danh:", error);
          }

          // Không sử dụng endpoint upcoming nếu không tồn tại
          // try {
          //   // Lấy các lớp sắp tới
          //   const upcomingResponse = await axios.get(
          //     `${API_URL}/classes/upcoming?student=${user._id}&limit=3`,
          //     { headers: { Authorization: `Bearer ${token}` } }
          //   );
          //   upcomingData = upcomingResponse.data.data || [];
          // } catch (error) {
          //   console.error("Lỗi khi lấy lớp học sắp tới:", error);
          // }

          // Tính toán tổng số buổi đã tham gia
          const totalAttended = scoresData.reduce(
            (total, score) =>
              total + (score.total_sessions - score.absent_sessions),
            0
          );

          const totalSessions = scoresData.reduce(
            (total, score) => total + score.total_sessions,
            0
          );

          // Lọc phiên điểm danh hôm nay
          const today = new Date();
          today.setHours(0, 0, 0, 0);

          const todayAttendance = logsData.filter((log) => {
            if (!log.timestamp) return false;

            const logDate = new Date(log.timestamp);
            logDate.setHours(0, 0, 0, 0);
            return logDate.getTime() === today.getTime();
          });

          // Xử lý dữ liệu phần trăm cho từng lớp học
          const classesWithPercentage = classesData.map((classItem) => {
            const classScore = scoresData.find(
              (score) =>
                (score.teaching_class_id?._id || score.teaching_class_id) ===
                classItem._id
            );
            return {
              ...classItem,
              // Lấy các trường cần thiết từ classScore hoặc classItem làm fallback
              attendanceStats: classScore
                ? {
                    attended: classScore.attended_sessions || 0,
                    total: classScore.total_sessions || 0, // Số buổi đã hoàn thành
                    percentage: classScore.attendance_percentage || 0,
                    completed_sessions: classScore.completed_sessions || 0,
                    total_planned_sessions:
                      classScore.total_planned_sessions ||
                      classItem.total_sessions ||
                      0, // Lấy total_sessions từ classItem nếu score chưa có
                    absent: classScore.absent_sessions || 0, // Thêm số buổi vắng
                    is_failed: classScore.is_failed_due_to_absent || false, // Thêm trạng thái cấm thi
                  }
                : {
                    attended: 0,
                    total: 0,
                    percentage: 0,
                    completed_sessions: 0,
                    total_planned_sessions: classItem.total_sessions || 0,
                    absent: 0,
                    is_failed: false,
                  },
            };
          });

          setStats({
            classes: classesWithPercentage || [],
            attendanceSessions: logsData || [],
            todayAttendance,
            totalAttendedSessions: totalAttended,
            totalSessions,
            attendancePercentage:
              totalSessions > 0
                ? Math.round((totalAttended / totalSessions) * 100)
                : 0,
            recentAttendance: logsData.slice(0, 5),
            upcomingClasses: upcomingData,
            attendanceScores: scoresData,
          });
        } else if (user.role === "teacher") {
          // Dữ liệu cho giáo viên
          let teachingClassesData = [];
          let recentSessionsData = [];

          try {
            const classesResponse = await getTeacherTeachingClasses(user._id);
            // Giả sử service trả về { data: [...] } hoặc trực tiếp [...]
            // Nếu service trả về response đầy đủ, bạn cần classesResponse.data.data
            // Dựa trên teacherService, nó trả về response.data, nên ở đây chỉ cần classesResponse.data
            teachingClassesData = classesResponse.data || [];
          } catch (err) {
            console.error("Lỗi khi lấy lớp học của giáo viên từ service:", err);
            // setError("Không thể tải dữ liệu lớp học của giáo viên."); // Bạn có thể set lỗi cụ thể
          }

          try {
            const sessionsResponse = await getTeacherRecentAttendanceSessions(
              user._id,
              5
            );
            recentSessionsData = sessionsResponse.data || [];
          } catch (err) {
            console.error(
              "Lỗi khi lấy phiên điểm danh gần đây của giáo viên từ service:",
              err
            );
          }

          // Lọc phiên điểm danh hôm nay
          const today = new Date();
          today.setHours(0, 0, 0, 0);

          const todayAttendance = recentSessionsData.filter((session) => {
            if (!session.date) return false; // Thêm kiểm tra nếu session.date không tồn tại
            const sessionDate = new Date(session.date);
            sessionDate.setHours(0, 0, 0, 0);
            return sessionDate.getTime() === today.getTime();
          });

          setStats({
            classes: teachingClassesData,
            attendanceSessions: recentSessionsData,
            todayAttendance,
            totalClasses: teachingClassesData.length,
            // ... các stats khác của teacher nếu có ...
          });
        } else if (user.role === "admin") {
          // Dữ liệu cho admin

          // Lấy tổng số lớp học
          const classesResponse = await axios.get(
            `${API_URL}/classes/teaching?limit=5`,
            {
              headers: { Authorization: `Bearer ${token}` },
            }
          );

          // Lấy tổng số người dùng
          const usersResponse = await axios.get(`${API_URL}/users/stats`, {
            headers: { Authorization: `Bearer ${token}` },
          });

          // Lấy các phiên điểm danh gần đây
          const sessionsResponse = await axios.get(
            `${API_URL}/attendance/sessions?limit=5`,
            {
              headers: { Authorization: `Bearer ${token}` },
            }
          );

          setStats({
            classes: classesResponse.data.data || [],
            attendanceSessions: sessionsResponse.data.data || [],
            totalClasses: classesResponse.data.totalCount || 0,
            totalUsers: usersResponse.data.totalUsers || 0,
            totalStudents: usersResponse.data.students || 0,
            totalTeachers: usersResponse.data.teachers || 0,
          });
        }

        setIsLoading(false);
      } catch (error) {
        console.error("Lỗi khi tải dữ liệu dashboard:", error);
        setError("Không thể tải dữ liệu. Vui lòng thử lại sau.");
        setIsLoading(false);
