                  <Typography color="textSecondary" gutterBottom>
                    Đang diễn ra hôm nay
                  </Typography>
                  <Box display="flex" alignItems="center">
                    <Today
                      sx={{ fontSize: 32, mr: 2, color: "success.main" }}
                    />
                    <Typography variant="h4">
                      {stats.todayAttendance.length}
                    </Typography>
                  </Box>
                </CardContent>
              </Card>
            </Grid>

            <Grid item xs={12} sm={4}>
              <Card>
                <CardContent>
                  <Typography color="textSecondary" gutterBottom>
                    Phiên gần đây
                  </Typography>
                  <Box display="flex" alignItems="center">
                    <AccessTime
                      sx={{ fontSize: 32, mr: 2, color: "info.main" }}
                    />
                    <Typography variant="h4">
                      {stats.attendanceSessions.length}
                    </Typography>
                  </Box>
                </CardContent>
              </Card>
            </Grid>
          </Grid>
        </Paper>

        <Card>
          <CardContent>
            <Typography variant="h6" gutterBottom>
              Các lớp bạn đang dạy
            </Typography>

            {stats.classes.length === 0 ? (
              <Typography variant="body2" color="textSecondary">
                Bạn chưa được phân công lớp học nào
              </Typography>
            ) : (
              <Grid container spacing={2}>
                {stats.classes.map((classItem) => (
                  <Grid item xs={12} sm={6} key={classItem._id}>
                    <Card variant="outlined">
                      <CardContent>
                        <Typography variant="h6" noWrap>
                          {classItem.class_name}
                        </Typography>
                        <Typography
                          variant="body2"
                          color="textSecondary"
                          noWrap
                        >
                          <strong>Môn học:</strong>{" "}
                          {classItem.subject_id?.name ||
                            "Chưa có thông tin môn học"}{" "}
                          ({classItem.subject_id?.code || "N/A"})
                        </Typography>
                        {classItem.main_class_id && (
                          <Typography variant="body2" color="textSecondary">
                            <strong>Lớp chính:</strong>{" "}
                            {classItem.main_class_id?.name}{" "}
                            {classItem.main_class_id?.class_code
                              ? `(${classItem.main_class_id.class_code})`
                              : ""}
                          </Typography>
                        )}
                        <Typography variant="body2" color="textSecondary">
                          <strong>Học kỳ:</strong>{" "}
                          {classItem.semester_id?.name || "Chưa có thông tin"}{" "}
                          {classItem.semester_id?.year || ""}
                        </Typography>
                        <Box mt={1} display="flex" alignItems="center">
                          <People sx={{ mr: 1, fontSize: 18 }} />
                          <Typography variant="body2">
                            {classItem.students?.length || 0} sinh viên
                          </Typography>
                        </Box>
                      </CardContent>
                      <CardActions>
                        <Button
                          size="small"
                          onClick={() =>
                            navigate(`/teacher/classes/${classItem._id}`)
                          }
                        >
                          Chi tiết
                        </Button>
                      </CardActions>
                    </Card>
                  </Grid>
                ))}
              </Grid>
            )}
          </CardContent>

          {stats.classes.length > 0 && (
            <CardActions>
              <Button size="small" onClick={() => navigate("/teacher/classes")}>
                Xem tất cả lớp học
              </Button>
            </CardActions>
          )}
        </Card>
      </Grid>

      <Grid item xs={12} md={4}>
        <Card sx={{ mb: 3 }}>
          <CardContent>
            <Typography variant="h6" gutterBottom>
              Hôm nay ({new Date().toLocaleDateString("vi-VN")})
            </Typography>

            {stats.todayAttendance.length === 0 ? (
              <Typography variant="body2" color="textSecondary">
                Không có phiên điểm danh nào hôm nay
              </Typography>
            ) : (
              <List dense>
                {stats.todayAttendance.map((session) => (
                  <ListItem key={session._id}>
                    <ListItemAvatar>
                      <Avatar>
                        <Event />
                      </Avatar>
                    </ListItemAvatar>
                    <ListItemText
                      primary={session.teaching_class_id?.class_name}
                      secondary={`Buổi ${session.session_number} - ${
                        session.status === "active"
                          ? "Đang diễn ra"
                          : "Đã kết thúc"
                      }`}
                    />
                    <Chip
                      size="small"
                      color={
                        session.status === "active" ? "success" : "default"
                      }
                      label={
                        session.status === "active"
                          ? "Đang diễn ra"
                          : "Đã kết thúc"
                      }
                    />
                  </ListItem>
                ))}
              </List>
            )}
          </CardContent>

          <CardActions>
            <Button size="small" onClick={() => navigate("/teacher/classes")}>
              Tạo phiên điểm danh mới
            </Button>
          </CardActions>
        </Card>

        <Card>
          <CardContent>
            <Typography variant="h6" gutterBottom>
              Phiên điểm danh gần đây
            </Typography>

            {stats.attendanceSessions.length === 0 ? (
              <Typography variant="body2" color="textSecondary">
                Chưa có phiên điểm danh nào
              </Typography>
            ) : (
              <List dense>
                {stats.attendanceSessions.slice(0, 5).map((session) => (
                  <ListItem key={session._id}>
                    <ListItemAvatar>
                      <Avatar>
                        <Event />
                      </Avatar>
                    </ListItemAvatar>
                    <ListItemText
                      primary={session.teaching_class_id?.class_name}
                      secondary={`Buổi ${session.session_number} - ${new Date(
                        session.date
                      ).toLocaleDateString("vi-VN")}`}
                    />
                    <Chip
                      size="small"
                      color={
                        session.status === "active"
                          ? "success"
                          : session.status === "pending"
                          ? "warning"
                          : "default"
                      }
                      label={
                        session.status === "active"
                          ? "Đang diễn ra"
                          : session.status === "pending"
                          ? "Sắp diễn ra"
                          : "Đã kết thúc"
                      }
                    />
                  </ListItem>
                ))}
              </List>
            )}
          </CardContent>
        </Card>
      </Grid>
    </Grid>
  );

  // Dashboard cho admin
  const renderAdminDashboard = () => (
    <Grid container spacing={3}>
      <Grid item xs={12}>
        <Paper sx={{ p: 2, mb: 3 }}>
          <Box display="flex" alignItems="center" mb={2}>
            <Person sx={{ fontSize: 40, mr: 2, color: "primary.main" }} />
            <Box>
              <Typography variant="h5">Xin chào, {user.full_name}!</Typography>
              <Typography variant="body2" color="textSecondary">
                Quản trị viên hệ thống
              </Typography>
            </Box>
          </Box>

          <Grid container spacing={2}>
            <Grid item xs={12} sm={6} md={3}>
              <Card>
                <CardContent>
                  <Typography color="textSecondary" gutterBottom>
                    Tổng số lớp học
                  </Typography>
                  <Box display="flex" alignItems="center">
                    <School
                      sx={{ fontSize: 32, mr: 2, color: "primary.main" }}
                    />
                    <Typography variant="h4">
                      {stats.totalClasses || 0}
                    </Typography>
                  </Box>
                </CardContent>
              </Card>
            </Grid>

            <Grid item xs={12} sm={6} md={3}>
              <Card>
                <CardContent>
                  <Typography color="textSecondary" gutterBottom>
                    Tổng số người dùng
                  </Typography>
                  <Box display="flex" alignItems="center">
                    <People
                      sx={{ fontSize: 32, mr: 2, color: "secondary.main" }}
                    />
                    <Typography variant="h4">
                      {stats.totalUsers || 0}
                    </Typography>
                  </Box>
                </CardContent>
              </Card>
            </Grid>

            <Grid item xs={12} sm={6} md={3}>
              <Card>
                <CardContent>
                  <Typography color="textSecondary" gutterBottom>
                    Sinh viên
                  </Typography>
                  <Box display="flex" alignItems="center">
                    <Person
                      sx={{ fontSize: 32, mr: 2, color: "success.main" }}
                    />
                    <Typography variant="h4">
                      {stats.totalStudents || 0}
                    </Typography>
                  </Box>
                </CardContent>
              </Card>
            </Grid>

            <Grid item xs={12} sm={6} md={3}>
              <Card>
                <CardContent>
                  <Typography color="textSecondary" gutterBottom>
                    Giáo viên
                  </Typography>
                  <Box display="flex" alignItems="center">
                    <Person sx={{ fontSize: 32, mr: 2, color: "info.main" }} />
                    <Typography variant="h4">
                      {stats.totalTeachers || 0}
                    </Typography>
                  </Box>
                </CardContent>
              </Card>
            </Grid>
          </Grid>
        </Paper>
      </Grid>

      <Grid item xs={12} md={6}>
        <Card>
          <CardContent>
            <Typography variant="h6" gutterBottom>
              Lớp học gần đây
            </Typography>

            {stats.classes.length === 0 ? (
              <Typography variant="body2" color="textSecondary">
                Chưa có lớp học nào
              </Typography>
            ) : (
              <List dense>
                {stats.classes.map((classItem) => (
                  <ListItem key={classItem._id}>
                    <ListItemAvatar>
                      <Avatar>
                        <Class />
                      </Avatar>
                    </ListItemAvatar>
                    <ListItemText
                      primary={classItem.class_name}
                      secondary={`${
                        classItem.subject_id?.name ||
                        "Chưa có thông tin môn học"
                      } (${classItem.subject_id?.code || "N/A"}) - GV: ${
                        classItem.teacher_id?.full_name || "Chưa phân công"
                      }`}
                    />
                  </ListItem>
                ))}
              </List>
            )}
          </CardContent>

          <CardActions>
            <Button size="small" onClick={() => navigate("/admin/classes")}>
              Quản lý lớp học
            </Button>
          </CardActions>
        </Card>
      </Grid>

      <Grid item xs={12} md={6}>
        <Card>
          <CardContent>
            <Typography variant="h6" gutterBottom>
              Phiên điểm danh gần đây
            </Typography>

            {stats.attendanceSessions.length === 0 ? (
              <Typography variant="body2" color="textSecondary">
                Chưa có phiên điểm danh nào
              </Typography>
            ) : (
              <List dense>
                {stats.attendanceSessions.slice(0, 5).map((session) => (
                  <ListItem key={session._id}>
                    <ListItemAvatar>
                      <Avatar>
                        <Event />
                      </Avatar>
                    </ListItemAvatar>
                    <ListItemText
                      primary={session.teaching_class_id?.class_name}
                      secondary={`Buổi ${session.session_number} - ${new Date(
                        session.date
                      ).toLocaleDateString("vi-VN")}`}
                    />
                    <Chip
                      size="small"
                      color={
                        session.status === "active"
                          ? "success"
                          : session.status === "pending"
                          ? "warning"
                          : "default"
                      }
                      label={
                        session.status === "active"
                          ? "Đang diễn ra"
                          : session.status === "pending"
                          ? "Sắp diễn ra"
                          : "Đã kết thúc"
                      }
                    />
                  </ListItem>
                ))}
              </List>
            )}
          </CardContent>

          <CardActions>
            <Button size="small" onClick={() => navigate("/admin/users")}>
              Quản lý người dùng
            </Button>
          </CardActions>
        </Card>
      </Grid>
    </Grid>
  );

  return (
    <Box>
      <Typography variant="h4" gutterBottom>
        Trang chủ
      </Typography>
      <Divider sx={{ mb: 3 }} />

      {renderContent()}
    </Box>
  );
};

export default DashboardPage;
