import React, { useState, useEffect } from "react";
import { Helmet } from "react-helmet-async";
import {
  Box,
  Card,
  Container,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TablePagination,
  TableRow,
  Paper,
  Typography,
  Button,
  IconButton,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  Grid,
  CircularProgress,
  Tooltip,
} from "@mui/material";
import { useSnackbar } from "notistack";
import axios from "axios";
import AddIcon from "@mui/icons-material/Add";
import EditIcon from "@mui/icons-material/Edit";
import DeleteIcon from "@mui/icons-material/Delete";
import MainLayout from "../../layouts/MainLayout";
import { API_URL } from "../../config";
import ConfirmDialog from "../../components/ConfirmDialog";

const SubjectsPage = () => {
  // State
  const [subjects, setSubjects] = useState([]);
  const [page, setPage] = useState(0);
  const [rowsPerPage, setRowsPerPage] = useState(10);
  const [dialogOpen, setDialogOpen] = useState(false);
  const [deleteDialogOpen, setDeleteDialogOpen] = useState(false);
  const [selectedSubject, setSelectedSubject] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [subjectForm, setSubjectForm] = useState({
    name: "",
    code: "",
    credits: 1,
  });
  const [dialogMode, setDialogMode] = useState("create");
  const [total, setTotal] = useState(0);

  const { enqueueSnackbar } = useSnackbar();
  const token = localStorage.getItem("token");

  // Load subjects on page load
  useEffect(() => {
    loadSubjects();
  }, [page, rowsPerPage]);

  // Load subjects
  const loadSubjects = async () => {
    try {
      setIsLoading(true);
      const response = await axios.get(
        `${API_URL}/subjects?page=${page + 1}&limit=${rowsPerPage}`,
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      setSubjects(response.data.data || []);
      setTotal(response.data.total || 0);
    } catch (error) {
      console.error("Lỗi khi tải danh sách môn học:", error);
      enqueueSnackbar("Lỗi khi tải danh sách môn học", { variant: "error" });
    } finally {
      setIsLoading(false);
    }
  };

  // Handle page change
  const handlePageChange = (event, newPage) => {
    setPage(newPage);
  };

  // Handle rows per page change
  const handleRowsPerPageChange = (event) => {
    setRowsPerPage(parseInt(event.target.value, 10));
    setPage(0);
  };

  // Handle open dialog to create
  const handleOpenCreateDialog = () => {
    setSubjectForm({
      name: "",
      code: "",
      credits: 1,
    });
    setDialogMode("create");
    setDialogOpen(true);
  };

  // Handle open dialog to edit
  const handleOpenEditDialog = (subject) => {
    setSelectedSubject(subject);
    setSubjectForm({
      name: subject.name,
      code: subject.code,
      credits: subject.credits,
    });
    setDialogMode("edit");
    setDialogOpen(true);
  };

  // Handle open dialog to delete
  const handleOpenDeleteDialog = (subject) => {
    setSelectedSubject(subject);
    setDeleteDialogOpen(true);
  };

  // Handle form change
  const handleFormChange = (event) => {
    const { name, value } = event.target;
    setSubjectForm({
      ...subjectForm,
      [name]: name === "credits" ? parseInt(value, 10) || 0 : value,
    });
  };

  // Validate form
  const validateForm = () => {
    if (!subjectForm.name.trim()) {
      enqueueSnackbar("Vui lòng nhập tên môn học", { variant: "error" });
      return false;
    }

    if (!subjectForm.code.trim()) {
      enqueueSnackbar("Vui lòng nhập mã môn học", { variant: "error" });
      return false;
    }

    if (!subjectForm.credits || subjectForm.credits < 1) {
      enqueueSnackbar("Số tín chỉ phải lớn hơn 0", { variant: "error" });
      return false;
    }

    return true;
  };

  // Handle form submit
  const handleFormSubmit = async () => {
    try {
      if (!validateForm()) {
        return;
      }

      if (dialogMode === "create") {
        await axios.post(`${API_URL}/subjects`, subjectForm, {
          headers: { Authorization: `Bearer ${token}` },
        });
        enqueueSnackbar("Tạo môn học mới thành công", { variant: "success" });
      } else {
        await axios.put(
          `${API_URL}/subjects/${selectedSubject._id}`,
          subjectForm,
          {
            headers: { Authorization: `Bearer ${token}` },
          }
        );
        enqueueSnackbar("Cập nhật môn học thành công", { variant: "success" });
      }

      setDialogOpen(false);
      loadSubjects();
    } catch (error) {
      console.error("Lỗi khi lưu môn học:", error);
      enqueueSnackbar(
        `Lỗi khi ${dialogMode === "create" ? "tạo" : "cập nhật"} môn học: ${
          error.response?.data?.message || error.message
        }`,
        { variant: "error" }
      );
    }
  };

  // Handle delete subject
  const handleDeleteSubject = async () => {
    try {
      await axios.delete(`${API_URL}/subjects/${selectedSubject._id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      enqueueSnackbar("Xóa môn học thành công", { variant: "success" });
      setDeleteDialogOpen(false);
      loadSubjects();
    } catch (error) {
      console.error("Lỗi khi xóa môn học:", error);
      enqueueSnackbar(
        `Lỗi khi xóa môn học: ${
          error.response?.data?.message || error.message
        }`,
        { variant: "error" }
      );
    }
  };

  return (
    <MainLayout>
      <Helmet>
        <title>Quản lý môn học | Face Recognition System</title>
      </Helmet>
      <Container maxWidth="xl">
        <Box sx={{ mb: 4 }}>
          <Typography variant="h4" gutterBottom>
            Quản lý môn học
          </Typography>
          <Button
            variant="contained"
            startIcon={<AddIcon />}
            onClick={handleOpenCreateDialog}
          >
            Thêm môn học
          </Button>
        </Box>

        <Card>
          <TableContainer component={Paper}>
            <Table sx={{ minWidth: 650 }}>
              <TableHead>
                <TableRow>
                  <TableCell>#</TableCell>
                  <TableCell>Tên môn học</TableCell>
                  <TableCell>Mã môn học</TableCell>
                  <TableCell>Số tín chỉ</TableCell>
                  <TableCell>Thao tác</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {isLoading ? (
                  <TableRow>
                    <TableCell colSpan={5} align="center">
                      <CircularProgress />
                    </TableCell>
                  </TableRow>
                ) : subjects.length === 0 ? (
                  <TableRow>
                    <TableCell colSpan={5} align="center">
                      Không có dữ liệu
                    </TableCell>
                  </TableRow>
                ) : (
                  subjects.map((subject, index) => (
                    <TableRow key={subject._id}>
                      <TableCell>{page * rowsPerPage + index + 1}</TableCell>
                      <TableCell>{subject.name}</TableCell>
                      <TableCell>{subject.code}</TableCell>
                      <TableCell>{subject.credits}</TableCell>
                      <TableCell>
                        <Tooltip title="Chỉnh sửa">
                          <IconButton
                            color="primary"
                            onClick={() => handleOpenEditDialog(subject)}
                          >
                            <EditIcon />
                          </IconButton>
                        </Tooltip>
                        <Tooltip title="Xóa">
                          <IconButton
                            color="error"
                            onClick={() => handleOpenDeleteDialog(subject)}
                          >
                            <DeleteIcon />
                          </IconButton>
                        </Tooltip>
                      </TableCell>
                    </TableRow>
                  ))
                )}
              </TableBody>
            </Table>
          </TableContainer>
          <TablePagination
            rowsPerPageOptions={[5, 10, 25]}
            component="div"
            count={total}
            rowsPerPage={rowsPerPage}
            page={page}
            onPageChange={handlePageChange}
            onRowsPerPageChange={handleRowsPerPageChange}
            labelRowsPerPage="Số hàng mỗi trang:"
            labelDisplayedRows={({ from, to, count }) =>
              `${from}-${to} của ${count}`
            }
          />
        </Card>

        {/* Dialog for creating/editing subject */}
        <Dialog
          open={dialogOpen}
          onClose={() => setDialogOpen(false)}
          maxWidth="md"
          fullWidth
        >
          <DialogTitle>
            {dialogMode === "create" ? "Thêm môn học mới" : "Chỉnh sửa môn học"}
          </DialogTitle>
          <DialogContent>
            <Grid container spacing={2} sx={{ mt: 1 }}>
              <Grid item xs={12}>
                <TextField
                  name="name"
                  label="Tên môn học"
                  value={subjectForm.name}
                  onChange={handleFormChange}
                  fullWidth
                  required
                />
              </Grid>
              <Grid item xs={12} sm={6}>
                <TextField
                  name="code"
                  label="Mã môn học"
                  value={subjectForm.code}
                  onChange={handleFormChange}
                  fullWidth
                  required
                />
              </Grid>
              <Grid item xs={12} sm={6}>
                <TextField
                  name="credits"
                  label="Số tín chỉ"
                  type="number"
                  value={subjectForm.credits}
                  onChange={handleFormChange}
                  fullWidth
                  required
                  inputProps={{ min: 1 }}
                />
              </Grid>
            </Grid>
          </DialogContent>
          <DialogActions>
            <Button onClick={() => setDialogOpen(false)}>Hủy</Button>
            <Button variant="contained" onClick={handleFormSubmit}>
              {dialogMode === "create" ? "Thêm" : "Lưu"}
            </Button>
          </DialogActions>
        </Dialog>

        {/* Confirmation dialog for deleting subject */}
        <ConfirmDialog
          open={deleteDialogOpen}
          title="Xác nhận xóa môn học"
          content={`Bạn có chắc chắn muốn xóa môn học "${
            selectedSubject?.name || ""
          }"? Hành động này không thể hoàn tác.`}
          onCancel={() => setDeleteDialogOpen(false)}
          onConfirm={handleDeleteSubject}
        />
      </Container>
    </MainLayout>
  );
};

export default SubjectsPage;
