import React, { useState, useEffect, useRef } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { useSnackbar } from "notistack";
import {
  Box,
  Typography,
  Card,
  CardContent,
  Grid,
  Paper,
  Button,
  Divider,
  CircularProgress,
  List,
  ListItem,
  ListItemAvatar,
  ListItemText,
  Avatar,
  Chip,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  IconButton,
  Alert,
  AlertTitle,
  Tooltip,
} from "@mui/material";
import {
  CameraAlt,
  Check,
  Close,
  Edit,
  Refresh,
  Save,
  VerifiedUser,
  HourglassEmpty,
  ArrowBack,
  Info,
} from "@mui/icons-material";
import Webcam from "react-webcam";
import * as faceapi from "face-api.js";
import {
  getClassFaceFeatures,
  verifyAttendance,
  manualAttendance,
  setModelLoaded,
  setCameraReady,
  setDetectedFaces,
  setRecognizedStudents,
  clearRecognitionState,
} from "../../redux/slices/faceRecognitionSlice";
import axios from "axios";

const API_URL = process.env.REACT_APP_API_URL || "http://localhost:5000/api";

const AttendancePage = () => {
  const { classId, sessionId } = useParams();
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const { enqueueSnackbar } = useSnackbar();

  const webcamRef = useRef(null);
  const canvasRef = useRef(null);
  const videoRef = useRef(null);
  const [intervalId, setIntervalId] = useState(null);
  const [showLandmark, setShowLandmark] = useState(true);
  const [landmarkIntervalId, setLandmarkIntervalId] = useState(null);
  const [cameraMode, setCameraMode] = useState("face_recognition");

  const { token } = useSelector((state) => state.auth);
  const {
    isModelLoaded,
    isCameraReady,
    detectedFaces,
    recognizedStudents,
    classStudents,
    isProcessing,
    error,
  } = useSelector((state) => state.faceRecognition);

  const [classInfo, setClassInfo] = useState(null);
  const [sessionInfo, setSessionInfo] = useState(null);
  const [attendanceLogs, setAttendanceLogs] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [isRecognizing, setIsRecognizing] = useState(false);
  const [isAutoMode, setIsAutoMode] = useState(false);
  const [selectedStudent, setSelectedStudent] = useState(null);
  const [manualDialogOpen, setManualDialogOpen] = useState(false);
  const [manualAttendanceData, setManualAttendanceData] = useState({
    status: "present",
    note: "",
  });

  // Các hằng số cấu hình được tối ưu
  const RECOGNITION_THRESHOLD = 0.45; // Ngưỡng nhận diện được tối ưu
  const DETECTION_THRESHOLD = 0.5; // Ngưỡng phát hiện khuôn mặt
  const CONFIDENCE_THRESHOLD = 0.6; // Ngưỡng độ tin cậy để điểm danh
  const AUTO_DETECT_INTERVAL = 1000; // Thời gian giữa các lần phát hiện (ms)
  const ENABLE_DEBUG_LOGS = false; // Tắt log gỡ lỗi trong môi trường sản phẩm

  // Thêm state mới để theo dõi số lần thử lại camera
  const [cameraRetryCount, setCameraRetryCount] = useState(0);
  const [lastDetectionTime, setLastDetectionTime] = useState(null);

  // Thêm state theo dõi interval phát hiện landmarks
  const [landmarkDetectionInterval, setLandmarkDetectionInterval] =
    useState(null);

  // Load class info, session info và models
  useEffect(() => {
    const loadInitialData = async () => {
      try {
        setIsLoading(true);

        // Lấy thông tin lớp học
        const classPromise = axios.get(
          `${API_URL}/classes/teaching/${classId}`,
          {
            headers: { Authorization: `Bearer ${token}` },
          }
        );

        // Lấy thông tin phiên điểm danh
        const sessionPromise = axios.get(
          `${API_URL}/attendance/sessions/${sessionId}`,
          {
            headers: { Authorization: `Bearer ${token}` },
          }
        );

        // Lấy danh sách điểm danh
        const logsPromise = axios.get(
          `${API_URL}/attendance/logs/${sessionId}`,
          {
            headers: { Authorization: `Bearer ${token}` },
          }
        );

        // Chờ tất cả API calls hoàn thành
        const [classResponse, sessionResponse, logsResponse] =
          await Promise.all([classPromise, sessionPromise, logsPromise]);

        setClassInfo(classResponse.data.data);
        setSessionInfo(sessionResponse.data.data);
        setAttendanceLogs(logsResponse.data.data);

        // Đã tải xong dữ liệu cơ bản
        setIsLoading(false);

        // Tải model nhận diện nếu chưa tải
        if (!isModelLoaded) {
          const modelLoaded = await loadFaceRecognitionModels();
          if (modelLoaded) {
            dispatch(setModelLoaded(true));
          }
        }

        if (sessionResponse.data.data.status !== "completed") {
          // Lấy đặc trưng khuôn mặt của sinh viên trong lớp
          try {
            await dispatch(getClassFaceFeatures(classId)).unwrap();
          } catch (apiError) {
            console.error("Lỗi khi lấy dữ liệu khuôn mặt:", apiError);
            enqueueSnackbar(
              "Lỗi khi lấy dữ liệu khuôn mặt. Vui lòng kiểm tra lại.",
              { variant: "error" }
            );
          }
        }

        // Khởi tạo thời gian phát hiện ban đầu
        setLastDetectionTime(Date.now());
      } catch (error) {
        console.error("Lỗi khi tải dữ liệu:", error);
        enqueueSnackbar("Lỗi khi tải dữ liệu", { variant: "error" });
        setIsLoading(false);
      }
    };

    loadInitialData();

    // Clean up
    return () => {
      if (intervalId) {
        clearInterval(intervalId);
      }

      // Dừng landmark interval nếu đang chạy
      if (landmarkIntervalId) {
        clearInterval(landmarkIntervalId);
      }

      // Dừng camera stream nếu đang chạy
      if (
        webcamRef.current &&
        webcamRef.current.video &&
        webcamRef.current.video.srcObject
      ) {
        const tracks = webcamRef.current.video.srcObject.getTracks();
        tracks.forEach((track) => track.stop());
      }

      if (videoRef.current && videoRef.current.srcObject) {
        const tracks = videoRef.current.srcObject.getTracks();
        tracks.forEach((track) => track.stop());
      }

      dispatch(clearRecognitionState());

      // Reset các state theo dõi camera
      setCameraRetryCount(0);
      setLastDetectionTime(null);
    };
  }, [
    classId,
    sessionId,
    token,
    dispatch,
    enqueueSnackbar,
    isModelLoaded,
    intervalId,
    landmarkIntervalId,
  ]);

  // Hàm tối ưu để tải model nhận diện khuôn mặt
  const loadFaceRecognitionModels = async () => {
    try {
      const MODEL_URL = "/models";

      // Kiểm tra nhanh xem các models đã được tải chưa
      if (
        faceapi.nets.tinyFaceDetector.isLoaded &&
        faceapi.nets.faceLandmark68Net.isLoaded &&
        faceapi.nets.faceRecognitionNet.isLoaded &&
        faceapi.nets.ssdMobilenetv1.isLoaded
      ) {
        console.log("Các models đã được tải từ trước");
        dispatch(setModelLoaded(true));
        return true;
      }

      // Hiển thị thông báo đang tải
      enqueueSnackbar("Đang tải mô hình nhận diện...", { variant: "info" });

      // Tải song song tất cả model
      await Promise.all([
        faceapi.nets.tinyFaceDetector.loadFromUri(MODEL_URL),
        faceapi.nets.faceLandmark68Net.loadFromUri(MODEL_URL),
        faceapi.nets.faceRecognitionNet.loadFromUri(MODEL_URL),
        faceapi.nets.ssdMobilenetv1.loadFromUri(MODEL_URL),
      ]);

      dispatch(setModelLoaded(true));
      enqueueSnackbar("Tải mô hình nhận diện thành công", {
        variant: "success",
      });
      return true;
    } catch (error) {
      console.error("Lỗi khi tải mô hình:", error);
      enqueueSnackbar("Không thể tải mô hình nhận diện", { variant: "error" });
      return false;
    }
  };

  // Hàm khởi tạo camera đã tối ưu
  const initDirectCamera = async () => {
    try {
      // Hiển thị thông báo đang khởi tạo
      enqueueSnackbar("Đang khởi tạo camera...", { variant: "info" });

      // Tải model nếu chưa tải
      if (!isModelLoaded) {
        const modelLoaded = await loadFaceRecognitionModels();
        if (!modelLoaded) {
          enqueueSnackbar(
            "Không thể tải mô hình nhận diện. Vui lòng thử lại.",
            { variant: "error" }
          );
          return false;
        }
      }

      // Dừng stream cũ nếu có
      const stopExistingStream = (videoElement) => {
        if (videoElement?.srcObject) {
          const tracks = videoElement.srcObject.getTracks();
          tracks.forEach((track) => track.stop());
        }
      };

      stopExistingStream(webcamRef.current?.video);
      stopExistingStream(videoRef.current);

      // Cấu hình camera tối ưu
      const constraints = {
        video: {
          width: { ideal: 640, min: 320 },
          height: { ideal: 480, min: 240 },
          facingMode: "user",
          frameRate: { ideal: 24, min: 15 },
        },
        audio: false,
      };

      // Truy cập camera
      try {
        const stream = await navigator.mediaDevices.getUserMedia(constraints);

        // Xử lý stream video
        const setupVideoStream = (videoElement) => {
          if (!videoElement) return false;

          videoElement.srcObject = stream;
          videoElement.onloadedmetadata = () => {
            videoElement
              .play()
              .catch((err) => console.error("Lỗi khi play video:", err));
          };
          return true;
        };

        // Thiết lập stream cho webcam hoặc video element
        let streamSetupSuccess = false;
        if (webcamRef.current && webcamRef.current.video) {
          streamSetupSuccess = setupVideoStream(webcamRef.current.video);
        } else if (videoRef.current) {
          streamSetupSuccess = setupVideoStream(videoRef.current);
        }

        if (!streamSetupSuccess) {
          throw new Error("Không thể thiết lập video stream");
        }

        // Đánh dấu camera đã sẵn sàng
        dispatch(setCameraReady(true));
        setLastDetectionTime(Date.now());
        setCameraRetryCount(0);

        // Bật hiển thị landmark
        setTimeout(() => {
          setShowLandmark(true);
          startLandmarkDetection();
        }, 1000);

        enqueueSnackbar("Đã kết nối camera thành công", { variant: "success" });
        return true;
      } catch (err) {
        console.error("Lỗi truy cập camera:", err);

        // Thử phương án thay thế nếu lỗi
        if (cameraRetryCount < 2) {
          try {
            enqueueSnackbar("Đang thử kết nối với camera thay thế...", {
              variant: "info",
            });
            const altStream = await navigator.mediaDevices.getUserMedia({
              video: true,
              audio: false,
            });

            if (webcamRef.current?.video) {
              webcamRef.current.video.srcObject = altStream;
            } else if (videoRef.current) {
              videoRef.current.srcObject = altStream;
              videoRef.current.onloadedmetadata = () => videoRef.current.play();
            }

            dispatch(setCameraReady(true));
            setLastDetectionTime(Date.now());
            setCameraRetryCount((prev) => prev + 1);
            enqueueSnackbar("Đã kết nối camera thay thế", {
              variant: "success",
            });
            return true;
          } catch (altErr) {
            console.error("Không thể sử dụng camera thay thế:", altErr);
          }
        }

        enqueueSnackbar(`Không thể truy cập camera: ${err.message}`, {
          variant: "error",
        });
        return false;
      }
    } catch (err) {
      console.error("Lỗi khi khởi tạo camera:", err);
      enqueueSnackbar(`Lỗi: ${err.message}`, { variant: "error" });
      return false;
    }
  };

  // Hàm bắt đầu phát hiện landmarks theo thời gian thực
  const startLandmarkDetection = () => {
    // Xóa interval cũ nếu có
    if (landmarkDetectionInterval) {
      clearInterval(landmarkDetectionInterval);
    }

    // Tạo interval mới để cập nhật landmarks mỗi 50ms
    const intervalId = setInterval(() => {
      if (cameraMode === "face_recognition" && showLandmark) {
        handleDetectFaces(true);
      }
    }, 50);

    setLandmarkDetectionInterval(intervalId);
  };

  // Hàm dừng phát hiện landmarks theo thời gian thực
  const stopLandmarkDetection = () => {
    if (landmarkDetectionInterval) {
      clearInterval(landmarkDetectionInterval);
      setLandmarkDetectionInterval(null);
    }
  };

  // Cập nhật useEffect để bắt đầu và dừng phát hiện landmarks khi camera hoặc chế độ thay đổi
  useEffect(() => {
    if (cameraMode === "face_recognition" && showLandmark) {
      startLandmarkDetection();
    } else {
      stopLandmarkDetection();
    }

    return () => {
      stopLandmarkDetection();
    };
  }, [cameraMode, showLandmark]);

  // Cập nhật useEffect cho việc toggle showLandmark
  useEffect(() => {
    if (showLandmark && cameraMode === "face_recognition") {
      startLandmarkDetection();
    } else {
      stopLandmarkDetection();
    }
  }, [showLandmark]);

  // Hàm kiểm tra và đảm bảo camera hoạt động
  const ensureCameraIsWorking = () => {
    // Kiểm tra nếu camera chưa hoạt động và đã thử khởi động
    if (!isCameraReady && cameraRetryCount === 0) {
      console.log("[DEBUG] Camera chưa sẵn sàng, thử khởi động");
      setCameraRetryCount(1);
      initDirectCamera();
      return false;
    }

    // Kiểm tra stream của camera
    const videoElement = isAutoMode
      ? videoRef.current
      : webcamRef.current?.video;

    if (
      !videoElement ||
      !videoElement.srcObject ||
      videoElement.readyState !== 4
    ) {
      console.log(
        "[DEBUG] Video stream không hoạt động, thử khởi động lại camera"
      );
      if (cameraRetryCount < 3) {
        setCameraRetryCount((prev) => prev + 1);
        initDirectCamera();
        return false;
      }
    }

    return true;
  };

  // Hàm vẽ landmarks đã được tối ưu với hiển thị thông tin sinh viên
  const drawFaceLandmarks = (detections, canvas, recognizedStudents = []) => {
    if (!canvas) return;

    const ctx = canvas.getContext("2d");
    ctx.clearRect(0, 0, canvas.width, canvas.height);

    // Lọc các phát hiện không hợp lệ
    const validDetections = detections.filter((detection) => {
      const box = detection.detection?._box;
      return (
        box &&
        typeof box._x === "number" &&
        !isNaN(box._x) &&
        typeof box._y === "number" &&
        !isNaN(box._y) &&
        typeof box._width === "number" &&
        !isNaN(box._width) &&
        typeof box._height === "number" &&
        !isNaN(box._height)
      );
    });

    validDetections.forEach((detection, index) => {
      const { _box: box } = detection.detection || {};
      const landmarks = detection.landmarks;

      if (!box || !landmarks?.positions || box._x == null || box._y == null)
        return;

      // Vẽ hộp giới hạn khuôn mặt với viền gradient
      const boxGradient = ctx.createLinearGradient(
        box._x,
        box._y,
        box._x + box._width,
        box._y + box._height
      );
      boxGradient.addColorStop(0, "#4CAF50");
      boxGradient.addColorStop(1, "#2196F3");

      ctx.strokeStyle = boxGradient;
      ctx.lineWidth = 3;
      ctx.strokeRect(box._x, box._y, box._width, box._height);

      // Tìm sinh viên đã được nhận diện tương ứng với detection này
      const matchingStudent = recognizedStudents.find(
        (student, idx) => idx === index
      );

      // Tạo background cho text
      const labelY = box._y - 10;
      const labelText = matchingStudent
        ? `${matchingStudent.name} (${Math.round(
            matchingStudent.confidence * 100
          )}%)`
        : "Đã phát hiện khuôn mặt";

      const textMetrics = ctx.measureText(labelText);
      const textWidth = textMetrics.width + 10; // Thêm padding

      // Vẽ background cho label
      ctx.fillStyle = "rgba(0, 0, 0, 0.7)";
      ctx.fillRect(box._x, labelY - 20, textWidth, 24);

      // Vẽ text tên và % nhận diện
      ctx.fillStyle = "#FFFFFF";
      ctx.font = "14px Arial";
      ctx.fillText(labelText, box._x + 5, labelY - 4);

      const points = landmarks.positions;
      if (!points || points.length < 68) return;

      // Vẽ các điểm landmarks với alpha thấp hơn để không quá nổi bật
      ctx.globalAlpha = 0.6;

      // Hàm vẽ nhóm điểm
      const drawPoints = (startIdx, endIdx, color) => {
        ctx.fillStyle = color;
        for (let i = startIdx; i <= endIdx; i++) {
          if (points[i]?.x != null && points[i]?.y != null) {
            ctx.beginPath();
            ctx.arc(points[i].x, points[i].y, 2, 0, 2 * Math.PI);
            ctx.fill();
          }
        }
      };

      // Vẽ các điểm landmarks
      drawPoints(36, 41, "#FF5722"); // Mắt trái (cam)
      drawPoints(42, 47, "#2196F3"); // Mắt phải (xanh dương)
      drawPoints(27, 35, "#FFEB3B"); // Mũi (vàng)
      drawPoints(48, 67, "#E91E63"); // Miệng (hồng)
      drawPoints(0, 26, "#4CAF50"); // Viền khuôn mặt (xanh lá)

      // Kết nối các điểm để tạo đường viền rõ ràng hơn cho các bộ phận quan trọng
      ctx.globalAlpha = 0.4;
      ctx.lineWidth = 1;

      // Vẽ viền mắt trái
      ctx.strokeStyle = "#FF5722";
      ctx.beginPath();
      points[36] && ctx.moveTo(points[36].x, points[36].y);
      for (let i = 37; i <= 41; i++) {
        if (points[i]) ctx.lineTo(points[i].x, points[i].y);
      }
      if (points[36]) ctx.lineTo(points[36].x, points[36].y);
      ctx.stroke();

      // Vẽ viền mắt phải
      ctx.strokeStyle = "#2196F3";
      ctx.beginPath();
      points[42] && ctx.moveTo(points[42].x, points[42].y);
      for (let i = 43; i <= 47; i++) {
        if (points[i]) ctx.lineTo(points[i].x, points[i].y);
      }
      if (points[42]) ctx.lineTo(points[42].x, points[42].y);
      ctx.stroke();

      // Vẽ viền miệng ngoài
      ctx.strokeStyle = "#E91E63";
      ctx.beginPath();
      points[48] && ctx.moveTo(points[48].x, points[48].y);
      for (let i = 49; i <= 59; i++) {
        if (points[i]) ctx.lineTo(points[i].x, points[i].y);
      }
      if (points[48]) ctx.lineTo(points[48].x, points[48].y);
      ctx.stroke();

      // Vẽ viền miệng trong
      ctx.beginPath();
      points[60] && ctx.moveTo(points[60].x, points[60].y);
      for (let i = 61; i <= 67; i++) {
        if (points[i]) ctx.lineTo(points[i].x, points[i].y);
      }
      if (points[60]) ctx.lineTo(points[60].x, points[60].y);
      ctx.stroke();

      // Reset alpha
      ctx.globalAlpha = 1.0;
    });
  };

  // Tối ưu hàm phát hiện khuôn mặt với tích hợp hiển thị thông tin sinh viên
  const handleDetectFaces = async (landmarkOnly = false) => {
    if (cameraMode !== "face_recognition" || !faceapi || !canvasRef.current)
      return [];

    // Xác định video element hiện tại
    const videoElement = isAutoMode
      ? videoRef.current
      : webcamRef.current?.video;

    // Kiểm tra video element kỹ hơn
    if (
      !videoElement ||
      videoElement.readyState !== 4 ||
      videoElement.videoWidth === 0 ||
      videoElement.videoHeight === 0 ||
      !videoElement.srcObject ||
      videoElement.srcObject.active === false
    ) {
      console.warn("Video chưa sẵn sàng hoặc không có kích thước hợp lệ");
      return [];
    }

    const canvasElement = canvasRef.current;

    try {
      // Đặt kích thước canvas để khớp với video thực tế
      const displaySize = {
        width: videoElement.videoWidth,
        height: videoElement.videoHeight,
      };

      if (displaySize.width === 0 || displaySize.height === 0) {
        console.warn("Kích thước video không hợp lệ:", displaySize);
        return [];
      }

      // Đảm bảo kích thước canvas khớp với video
      faceapi.matchDimensions(canvasElement, displaySize);

      // Sử dụng TinyFaceDetector nhanh hơn để phát hiện khuôn mặt với landmarks
      let detections = [];
      try {
        detections = await faceapi
          .detectAllFaces(
            videoElement,
            new faceapi.TinyFaceDetectorOptions({
              scoreThreshold: DETECTION_THRESHOLD,
              inputSize: 320,
            })
          )
          .withFaceLandmarks();
      } catch (detectionError) {
        console.error("Lỗi khi phát hiện khuôn mặt:", detectionError);
        return [];
      }

      // Lọc bỏ các phát hiện không hợp lệ
      detections = detections.filter((detection) => {
        const box = detection.detection?._box;
        return (
          box &&
          typeof box._x === "number" &&
          !isNaN(box._x) &&
          typeof box._y === "number" &&
          !isNaN(box._y) &&
          typeof box._width === "number" &&
          !isNaN(box._width) &&
          typeof box._height === "number" &&
          !isNaN(box._height)
        );
      });

      // Nếu chỉ cần vẽ landmarks, không cần trích xuất descriptor
      if (landmarkOnly) {
        if (detections.length > 0) {
          try {
            // Điều chỉnh kết quả theo kích thước canvas
            const resizedDetections = faceapi.resizeResults(detections, {
              width: canvasElement.width,
              height: canvasElement.height,
            });

            // Vẽ landmarks với thông tin nhận diện nếu có
            drawFaceLandmarks(
              resizedDetections,
              canvasElement,
              recognizedStudents
            );
          } catch (resizeError) {
            console.error(
              "Lỗi khi điều chỉnh kích thước kết quả:",
              resizeError
            );
            canvasElement
              .getContext("2d")
              .clearRect(0, 0, canvasElement.width, canvasElement.height);
          }
        } else {
          // Xóa canvas nếu không phát hiện khuôn mặt
          canvasElement
            .getContext("2d")
            .clearRect(0, 0, canvasElement.width, canvasElement.height);
        }
        return detections;
      }

      // Nếu cần nhận diện đầy đủ, trích xuất thêm descriptors
      if (detections.length > 0) {
        // Thêm descriptors
        let finalDetections = [];
        try {
          finalDetections = await faceapi
            .detectAllFaces(
              videoElement,
              new faceapi.TinyFaceDetectorOptions({
                scoreThreshold: DETECTION_THRESHOLD,
                inputSize: 320,
              })
            )
            .withFaceLandmarks()
            .withFaceDescriptors();

          // Lọc các kết quả không hợp lệ
          finalDetections = finalDetections.filter((detection) => {
            const box = detection.detection?._box;
            return (
              box &&
              typeof box._x === "number" &&
              !isNaN(box._x) &&
              typeof box._y === "number" &&
              !isNaN(box._y) &&
              typeof box._width === "number" &&
              !isNaN(box._width) &&
              typeof box._height === "number" &&
              !isNaN(box._height) &&
              detection.descriptor &&
              detection.descriptor.length === 128
            );
          });

          // Nếu không có kết quả hoặc descriptor không hợp lệ, thử dùng SSD MobileNet
          if (
            (finalDetections.length === 0 || !finalDetections[0]?.descriptor) &&
            faceapi.nets.ssdMobilenetv1.isLoaded
          ) {
            console.log("Thử dùng SSD MobileNet để trích xuất descriptors");
            finalDetections = await faceapi
              .detectAllFaces(
                videoElement,
                new faceapi.SsdMobilenetv1Options({
                  minConfidence: DETECTION_THRESHOLD,
                })
              )
              .withFaceLandmarks()
              .withFaceDescriptors();

            // Lọc lại kết quả
            finalDetections = finalDetections.filter((detection) => {
              const box = detection.detection?._box;
              return (
                box &&
                typeof box._x === "number" &&
                !isNaN(box._x) &&
                typeof box._y === "number" &&
                !isNaN(box._y) &&
                typeof box._width === "number" &&
                !isNaN(box._width) &&
                typeof box._height === "number" &&
                !isNaN(box._height) &&
                detection.descriptor &&
                detection.descriptor.length === 128
              );
            });
          }
        } catch (error) {
          console.error("Lỗi khi lấy mô tả khuôn mặt:", error);
          finalDetections = detections.filter((det) => {
            const box = det.detection?._box;
            return (
              box &&
              box._x != null &&
              box._y != null &&
              box._width != null &&
              box._height != null
            );
          });
        }

        if (finalDetections.length > 0) {
          try {
            // Điều chỉnh kích thước và vẽ landmarks
            const resizedDetections = faceapi.resizeResults(finalDetections, {
              width: canvasElement.width,
              height: canvasElement.height,
            });

            // Vẽ landmarks nếu được bật
            if (showLandmark) {
              drawFaceLandmarks(
                resizedDetections,
                canvasElement,
                recognizedStudents
              );
            }

            // Xử lý nhận diện
            handleFaceDetection(finalDetections);
            return finalDetections;
          } catch (resizeError) {
            console.error(
              "Lỗi khi điều chỉnh kích thước kết quả:",
              resizeError
            );
            canvasElement
              .getContext("2d")
              .clearRect(0, 0, canvasElement.width, canvasElement.height);
            return [];
          }
        }
      }

      // Xóa canvas nếu không có kết quả
      canvasElement
        .getContext("2d")
        .clearRect(0, 0, canvasElement.width, canvasElement.height);
      return [];
    } catch (error) {
      console.error("Lỗi khi phát hiện khuôn mặt:", error);
      if (canvasElement) {
        canvasElement
          .getContext("2d")
          .clearRect(0, 0, canvasElement.width, canvasElement.height);
      }
      return [];
    }
  };

  // Hàm trích xuất descriptor từ sinh viên, tối ưu hoá và chỉ phải làm một lần
  const extractStudentDescriptors = (student) => {
    if (!student) return [];

    const descriptors = [];

    // Kiểm tra cấu trúc mới
    if (student.faceFeatures?.descriptors) {
      const featuresData = student.faceFeatures.descriptors;

      // Duyệt qua tất cả cấu trúc lồng nhau để tìm mảng 128 phần tử
      const findDescriptorsInArray = (arr, maxDepth = 3, depth = 0) => {
        if (!Array.isArray(arr) || depth > maxDepth) return;

        if (arr.length === 128 && arr.every((val) => typeof val === "number")) {
          descriptors.push(arr);
          return;
        }

        for (const item of arr) {
          if (Array.isArray(item)) {
            findDescriptorsInArray(item, maxDepth, depth + 1);
          }
        }
      };

      findDescriptorsInArray(featuresData);
    }

    // Kiểm tra cấu trúc cũ
    if (descriptors.length === 0 && student.faceDescriptors) {
      const oldFeatures = student.faceDescriptors;

      if (Array.isArray(oldFeatures)) {
        // Trường hợp 1: mảng các descriptor
        for (const item of oldFeatures) {
          if (Array.isArray(item) && item.length === 128) {
            descriptors.push(item);
          }
          // Trường hợp 2: mảng các object chứa descriptor
          else if (typeof item === "object" && item !== null) {
            // Tìm trong các thuộc tính của object
            for (const key in item) {
              const value = item[key];

              if (
                Array.isArray(value) &&
                value.length === 128 &&
                value.every((v) => typeof v === "number")
              ) {
                descriptors.push(value);
              }
              // Tìm trong thuộc tính descriptors
              else if (key === "descriptors" && Array.isArray(value)) {
                value.forEach((desc) => {
                  if (Array.isArray(desc) && desc.length === 128) {
                    descriptors.push(desc);
                  }
                });
              }
            }
          }
        }
      }
    }

    return descriptors;
  };

  // Xử lý phát hiện khuôn mặt
  const handleFaceDetection = async (detections) => {
    // Cập nhật state
    dispatch(setDetectedFaces(detections));

    try {
      // Cập nhật thời gian phát hiện
      setLastDetectionTime(Date.now());

      // Nếu không có khuôn mặt nào được phát hiện, dừng lại
      if (!detections || detections.length === 0) {
        if (ENABLE_DEBUG_LOGS) {
          console.log("[DEBUG] Không phát hiện khuôn mặt");
        }
        return;
      }

      if (ENABLE_DEBUG_LOGS) {
        console.log(`[DEBUG] Đã phát hiện ${detections.length} khuôn mặt`);
      }

      // Xử lý nhận diện khuôn mặt nếu có sinh viên
      if (classStudents && classStudents.length > 0) {
        // Xử lý nhận diện khuôn mặt sẽ được thêm sau
      }
    } catch (error) {
      console.error("Lỗi khi xử lý khuôn mặt:", error);
    }
  };

  // Điểm danh thủ công
  const openManualAttendanceDialog = (student) => {
    if (!student) {
      enqueueSnackbar("Không tìm thấy thông tin sinh viên", {
        variant: "error",
      });
      return;
    }

    setSelectedStudent(student);
    setManualAttendanceData({
      status: "present", // Mặc định là có mặt
      note: "",
    });
    setManualDialogOpen(true);
  };

  const handleManualDialogClose = () => {
    setManualDialogOpen(false);
  };

  const handleManualAttendanceChange = (e) => {
    setManualAttendanceData({
      ...manualAttendanceData,
      [e.target.name]: e.target.value,
    });
  };

  const handleManualAttendanceSubmit = async () => {
    if (!selectedStudent) return;

    try {
      // Hiển thị thông báo đang xử lý
      enqueueSnackbar("Đang xử lý điểm danh thủ công...", { variant: "info" });

      // Gọi API điểm danh thủ công
      const result = await dispatch(
        manualAttendance({
          sessionId,
          studentId: selectedStudent._id,
          status: manualAttendanceData.status,
          note: manualAttendanceData.note,
        })
      ).unwrap();

      // Nếu điểm danh thành công
      if (result?.data) {
        // Cập nhật logs điểm danh hiệu quả hơn
        setAttendanceLogs((prevLogs) => {
          // Lọc bỏ log cũ của sinh viên nếu có
          const filteredLogs = prevLogs.filter(
            (log) => log.student_id._id !== selectedStudent._id
          );
          // Thêm log mới
          return [
            ...filteredLogs,
            {
              ...result.data,
              student_id: selectedStudent,
            },
          ];
        });

        enqueueSnackbar(
          `Đã điểm danh ${
            manualAttendanceData.status === "present"
              ? "có mặt"
              : manualAttendanceData.status === "absent"
              ? "vắng mặt"
              : manualAttendanceData.status === "late"
              ? "đi trễ"
              : "về sớm"
          } cho ${selectedStudent.full_name}`,
          { variant: "success" }
        );

        // Đóng dialog
        handleManualDialogClose();

        // Cập nhật thông tin phiên
        fetchSessionInfo();
      }
    } catch (error) {
      console.error("Lỗi khi điểm danh thủ công:", error);
      enqueueSnackbar("Lỗi khi điểm danh thủ công", { variant: "error" });
    }
  };

  // Đánh dấu điểm danh từ nhận diện khuôn mặt
  const markAttendance = async (
    studentId,
    faceDescriptor,
    confidence,
    imageBase64
  ) => {
    try {
      // Hiển thị thông báo đang xử lý
      enqueueSnackbar("Đang xử lý điểm danh...", { variant: "info" });

      // Gọi API điểm danh qua Redux
      const result = await dispatch(
        verifyAttendance({
          sessionId,
          studentId,
          faceDescriptor,
          confidence,
          imageBase64,
        })
      ).unwrap();

      // Nếu điểm danh thành công
      if (result?.data) {
        // Tìm thông tin sinh viên
        const student = classStudents.find((s) => s._id === studentId);

        if (student) {
          // Cập nhật logs điểm danh hiệu quả hơn
          setAttendanceLogs((prevLogs) => {
            // Lọc bỏ log cũ của sinh viên nếu có
            const filteredLogs = prevLogs.filter(
              (log) => log.student_id._id !== studentId
            );
            // Thêm log mới
            return [
              ...filteredLogs,
              {
                ...result.data,
                student_id: student,
              },
            ];
          });

          enqueueSnackbar(`Đã điểm danh cho ${student.full_name}`, {
            variant: "success",
          });
        }

        // Cập nhật thông tin phiên một cách bất đồng bộ
        fetchSessionInfo();
        return true;
      }
      return false;
    } catch (error) {
      console.error("Lỗi khi xác nhận điểm danh:", error);
      enqueueSnackbar("Lỗi khi xác nhận điểm danh", { variant: "error" });
      return false;
    }
  };

  // Kết thúc phiên điểm danh
  const completeSession = async () => {
    try {
      // Hiển thị thông báo đang xử lý
      enqueueSnackbar("Đang kết thúc phiên điểm danh...", { variant: "info" });

      // Gọi API kết thúc phiên
      await axios.put(
        `${API_URL}/attendance/sessions/${sessionId}`,
        { status: "completed" },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      enqueueSnackbar("Phiên điểm danh đã kết thúc thành công", {
        variant: "success",
      });

      // Chuyển hướng về trang lớp học
      setTimeout(() => {
        navigate(`/teacher/classes/${classId}`);
      }, 1000);
    } catch (error) {
      console.error("Lỗi khi kết thúc phiên điểm danh:", error);
      enqueueSnackbar("Lỗi khi kết thúc phiên điểm danh", { variant: "error" });
    }
  };

  // Làm mới danh sách điểm danh
  const refreshAttendanceLogs = async () => {
    try {
      enqueueSnackbar("Đang làm mới dữ liệu điểm danh...", { variant: "info" });

      // Tải song song dữ liệu logs và thông tin phiên
      const [logsResponse, sessionResponse] = await Promise.all([
        axios.get(`${API_URL}/attendance/logs/${sessionId}`, {
          headers: { Authorization: `Bearer ${token}` },
        }),
        axios.get(`${API_URL}/attendance/sessions/${sessionId}`, {
          headers: { Authorization: `Bearer ${token}` },
        }),
      ]);

      // Cập nhật state
      setAttendanceLogs(logsResponse.data.data);
      setSessionInfo(sessionResponse.data.data);

      enqueueSnackbar("Đã làm mới dữ liệu điểm danh", { variant: "success" });
    } catch (error) {
      console.error("Lỗi khi làm mới dữ liệu:", error);
      enqueueSnackbar("Lỗi khi làm mới dữ liệu điểm danh", {
        variant: "error",
      });
    }
  };

  // Tối ưu hàm lấy thông tin phiên
  const fetchSessionInfo = async () => {
    try {
      const sessionResponse = await axios.get(
        `${API_URL}/attendance/sessions/${sessionId}`,
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      setSessionInfo(sessionResponse.data.data);
      return sessionResponse.data.data;
    } catch (error) {
      console.error("Lỗi khi lấy thông tin phiên:", error);
      return null;
    }
  };

  // Tối ưu hàm xử lý nhận diện khuôn mặt
  const captureAndRecognize = async () => {
    try {
      setIsRecognizing(true);
      enqueueSnackbar("Đang nhận diện khuôn mặt...", { variant: "info" });

      // Kiểm tra camera
      if (!ensureCameraIsWorking()) {
        enqueueSnackbar("Camera không hoạt động, vui lòng khởi động lại", {
          variant: "error",
        });
        setIsRecognizing(false);
        return;
      }

      // Phát hiện khuôn mặt
      const detections = await handleDetectFaces(false);

      // Kiểm tra phát hiện khuôn mặt
      if (!detections || detections.length === 0) {
        enqueueSnackbar("Không phát hiện khuôn mặt nào trong hình", {
          variant: "warning",
        });
        setIsRecognizing(false);
        return;
      }

      // Kiểm tra descriptor
      if (!detections[0].descriptor) {
        enqueueSnackbar(
          "Không thể trích xuất đặc trưng khuôn mặt để nhận diện",
          { variant: "error" }
        );
        setIsRecognizing(false);
        return;
      }

      // Xử lý nhận diện với dữ liệu sinh viên
      if (!classStudents || classStudents.length === 0) {
        enqueueSnackbar("Không có dữ liệu sinh viên để so sánh", {
          variant: "error",
        });
        setIsRecognizing(false);
        return;
      }

      // Chụp ảnh từ webcam
      const imageBase64 = webcamRef.current?.getScreenshot();

      // Lấy descriptor của khuôn mặt phát hiện được
      const faceDescriptor = detections[0].descriptor;

      // So sánh với dữ liệu sinh viên
      const matchResults = [];

      // Tạo một mảng promises để xử lý song song các tính toán
      const comparePromises = classStudents.map(async (student) => {
        // Trích xuất descriptors từ sinh viên
        const studentDescriptors = extractStudentDescriptors(student);

        if (studentDescriptors.length === 0) return null;

        // Tính độ tương đồng tốt nhất
        let bestMatch = 0;
        for (const descriptor of studentDescriptors) {
          try {
            const distance = faceapi.euclideanDistance(
              faceDescriptor,
              descriptor
            );
            const similarity = Math.max(0, 1 - distance);
            bestMatch = Math.max(bestMatch, similarity);
          } catch (error) {
            // Bỏ qua lỗi và tiếp tục với descriptor tiếp theo
          }
        }

        return {
          studentId: student._id,
          name: student.full_name,
          confidence: bestMatch,
          studentCode: student.student_code,
        };
      });

      // Đợi tất cả tính toán hoàn thành và lọc kết quả null
      const results = (await Promise.all(comparePromises)).filter(
        (result) => result !== null
      );

      // Sắp xếp kết quả theo độ tương đồng
      results.sort((a, b) => b.confidence - a.confidence);
      matchResults.push(...results);

      // Lọc kết quả theo ngưỡng
      const filteredResults = matchResults.filter(
        (r) => r.confidence > RECOGNITION_THRESHOLD
      );

      // Cập nhật danh sách sinh viên được nhận diện
      dispatch(setRecognizedStudents(filteredResults));

      // Hiển thị kết quả
      if (matchResults.length > 0) {
        const topMatches = matchResults.slice(0, 3);

        if (filteredResults.length > 0) {
          const topMatch = filteredResults[0];
          enqueueSnackbar(
            `Đã nhận diện: ${topMatch.name} (${Math.round(
              topMatch.confidence * 100
            )}%)`,
            { variant: "success" }
          );

          // Tự động điểm danh nếu độ tin cậy cao
          if (topMatch.confidence > CONFIDENCE_THRESHOLD) {
            handleMarkAttendance(topMatch.studentId, topMatch.confidence);
          }
        } else {
          const matchDetails = topMatches
            .map((m) => `${m.name}: ${(m.confidence * 100).toFixed(1)}%`)
            .join(", ");

          enqueueSnackbar(
            `Không nhận diện được sinh viên nào vượt ngưỡng. Gần nhất: ${matchDetails}`,
            { variant: "warning" }
          );
        }
      } else {
        enqueueSnackbar("Không nhận diện được sinh viên nào", {
          variant: "warning",
        });
      }
    } catch (error) {
      console.error("Lỗi khi nhận diện khuôn mặt:", error);
      enqueueSnackbar(`Lỗi khi nhận diện: ${error.message}`, {
        variant: "error",
      });
    } finally {
      setIsRecognizing(false);
    }
  };

  // Tối ưu hàm đánh dấu điểm danh
  const handleMarkAttendance = async (studentId, confidence) => {
    // Kiểm tra xem sinh viên đã được điểm danh chưa
    const isAlreadyAttended = attendanceLogs.some(
      (log) => log.student_id._id === studentId && log.status === "present"
    );

    if (isAlreadyAttended) return;

    try {
      // Chụp ảnh từ video element
      const videoElement = isAutoMode
        ? videoRef.current
        : webcamRef.current?.video;
      if (!videoElement) return;

      let imageBase64 = null;

      // Tạo ảnh chụp
      if (!isAutoMode && webcamRef.current) {
        imageBase64 = webcamRef.current.getScreenshot();
      } else if (isAutoMode && videoElement) {
        // Tạo canvas để chụp từ video
        const canvas = document.createElement("canvas");
        canvas.width = videoElement.videoWidth;
        canvas.height = videoElement.videoHeight;
        const ctx = canvas.getContext("2d");
        ctx.drawImage(videoElement, 0, 0);
        imageBase64 = canvas.toDataURL("image/jpeg");
      }

      // Tìm sinh viên trong danh sách
      const student = classStudents.find((s) => s._id === studentId);
      if (!student) return;

      // Trích xuất descriptor
      const descriptors = extractStudentDescriptors(student);
      const descriptor = descriptors.length > 0 ? descriptors[0] : null;

      // Gọi API điểm danh
      await markAttendance(studentId, descriptor, confidence, imageBase64);
    } catch (error) {
      console.error("Lỗi khi đánh dấu điểm danh:", error);
      enqueueSnackbar("Lỗi khi đánh dấu điểm danh", { variant: "error" });
    }
  };

  // Bật/tắt hiển thị landmark trên khuôn mặt
  const toggleLandmarkDetection = () => {
    const newState = !showLandmark;
    setShowLandmark(newState);

    // Bắt đầu hoặc dừng interval landmark detection
    if (newState) {
      startLandmarkDetection();
    } else if (landmarkIntervalId) {
      clearInterval(landmarkIntervalId);
      setLandmarkIntervalId(null);

      // Xóa canvas khi tắt landmarks
      const canvasElement = canvasRef.current;
      if (canvasElement) {
        const ctx = canvasElement.getContext("2d");
        ctx.clearRect(0, 0, canvasElement.width, canvasElement.height);
      }
    }
  };

  // Thêm hàm debug để kiểm tra cấu trúc dữ liệu khuôn mặt
  const debugFaceFeatures = () => {
    if (!classStudents || classStudents.length === 0) {
      console.log(
        "[DEBUG] Không có sinh viên nào để kiểm tra dữ liệu khuôn mặt"
      );
      return;
    }

    classStudents.forEach((student, index) => {
      console.log(`[DEBUG] Sinh viên #${index + 1}: ${student.full_name}`);

      // Kiểm tra dữ liệu faceFeatures
      if (student.faceFeatures) {
        console.log(`  - Có dữ liệu faceFeatures`);

        // Kiểm tra descriptors
        if (student.faceFeatures.descriptors) {
          console.log(
            `  - Có descriptors: ${student.faceFeatures.descriptors.length} nhóm`
          );

          student.faceFeatures.descriptors.forEach((group, groupIndex) => {
            if (Array.isArray(group)) {
              console.log(
                `    - Nhóm #${groupIndex + 1}: ${group.length} descriptors`
              );

              group.forEach((descriptor, descIndex) => {
                if (Array.isArray(descriptor)) {
                  console.log(
                    `      - Descriptor #${descIndex + 1}: ${
                      descriptor.length
                    } phần tử`
                  );
                } else {
                  console.log(
                    `      - Descriptor #${descIndex + 1}: Không phải mảng`
                  );
                }
              });
            } else {
              console.log(`    - Nhóm #${groupIndex + 1}: Không phải mảng`);
            }
          });
        } else {
          console.log(`  - Không có dữ liệu descriptors`);
        }
      } else {
        console.log(`  - Không có dữ liệu faceFeatures`);
      }

      // Kiểm tra dữ liệu cũ (faceDescriptors)
      if (student.faceDescriptors) {
        console.log(
          `  - Có dữ liệu faceDescriptors (cấu trúc cũ): ${student.faceDescriptors.length} descriptors`
        );
      }
    });
  };

  // Tối ưu chế độ tự động điểm danh
  const startAutoAttendance = () => {
    // Dừng interval cũ nếu có
    if (intervalId) {
      clearInterval(intervalId);
    }

    // Hiển thị thông báo bắt đầu
    enqueueSnackbar("Đã bật chế độ tự động điểm danh", {
      variant: "success",
    });

    // Thiết lập thời gian phát hiện ban đầu
    setLastDetectionTime(Date.now());

    // Khởi tạo interval mới để điểm danh tự động
    const id = setInterval(async () => {
      // Chỉ xử lý nếu camera đã sẵn sàng
      if (isCameraReady) {
        try {
          // Phát hiện khuôn mặt
          const detections = await handleDetectFaces(false);

          // Nếu đã phát hiện khuôn mặt và có descriptor
          if (detections && detections.length > 0 && detections[0].descriptor) {
            // Nhận diện và điểm danh tự động
            await processAutoRecognition(detections);
          }
        } catch (error) {
          console.error("Lỗi trong quá trình điểm danh tự động:", error);
        }
      }
    }, AUTO_DETECT_INTERVAL);

    // Lưu interval ID để có thể dừng sau này
    setIntervalId(id);
    setIsAutoMode(true);
  };

  // Hàm dừng chế độ tự động điểm danh
  const stopAutoAttendance = () => {
    if (intervalId) {
      clearInterval(intervalId);
      setIntervalId(null);
    }
    setIsAutoMode(false);
    enqueueSnackbar("Đã tắt chế độ tự động điểm danh", {
      variant: "info",
    });
  };

  // Hàm xử lý nhận diện tự động
  const processAutoRecognition = async (detections) => {
    try {
      // Lấy descriptor khuôn mặt đầu tiên được phát hiện
      const faceDescriptor = detections[0].descriptor;

      // Kiểm tra có lớp học và sinh viên không
      if (!classStudents || classStudents.length === 0) {
        return;
      }

      // Lấy ảnh từ camera
      const videoElement = isAutoMode
        ? videoRef.current
        : webcamRef.current?.video;
      if (!videoElement) return;

      let imageBase64 = null;
      // Chụp ảnh từ video element
      const canvas = document.createElement("canvas");
      canvas.width = videoElement.videoWidth;
      canvas.height = videoElement.videoHeight;
      const ctx = canvas.getContext("2d");
      ctx.drawImage(videoElement, 0, 0);
      imageBase64 = canvas.toDataURL("image/jpeg");

      // Tạo một mảng promises để xử lý song song các tính toán
      const comparePromises = classStudents.map(async (student) => {
        // Trích xuất descriptors từ sinh viên
        const studentDescriptors = extractStudentDescriptors(student);

        if (studentDescriptors.length === 0) return null;

        // Tính độ tương đồng tốt nhất
        let bestMatch = 0;
        for (const descriptor of studentDescriptors) {
          try {
            const distance = faceapi.euclideanDistance(
              faceDescriptor,
              descriptor
            );
            const similarity = Math.max(0, 1 - distance);
            bestMatch = Math.max(bestMatch, similarity);
          } catch (error) {
            // Bỏ qua lỗi và tiếp tục với descriptor tiếp theo
          }
        }

        return {
          studentId: student._id,
          name: student.full_name,
          confidence: bestMatch,
          studentCode: student.student_code,
        };
      });

      // Đợi tất cả tính toán hoàn thành và lọc kết quả null
      const results = (await Promise.all(comparePromises)).filter(
        (result) => result !== null
      );

      // Sắp xếp kết quả theo độ tương đồng
      results.sort((a, b) => b.confidence - a.confidence);

      // Lọc kết quả theo ngưỡng
      const filteredResults = results.filter(
        (r) => r.confidence > RECOGNITION_THRESHOLD
      );

      // Cập nhật danh sách sinh viên được nhận diện
      dispatch(setRecognizedStudents(filteredResults));

      // Nếu có kết quả vượt ngưỡng tin cậy, tự động điểm danh
      if (filteredResults.length > 0) {
        const topMatch = filteredResults[0];

        if (topMatch.confidence > CONFIDENCE_THRESHOLD) {
          // Kiểm tra sinh viên đã được điểm danh chưa
          const isAlreadyAttended = attendanceLogs.some(
            (log) =>
              log.student_id._id === topMatch.studentId &&
              log.status === "present"
          );

          if (!isAlreadyAttended) {
            // Đánh dấu điểm danh cho sinh viên
            await handleMarkAttendance(topMatch.studentId, topMatch.confidence);

            // Hiển thị thông báo thành công
            enqueueSnackbar(
              `Đã tự động điểm danh cho ${topMatch.name} (${Math.round(
                topMatch.confidence * 100
              )}%)`,
              { variant: "success" }
            );
          }
        }
      }
    } catch (error) {
      console.error("Lỗi khi xử lý nhận diện tự động:", error);
    }
  };

  if (isLoading) {
    return (
      <Box
        display="flex"
        justifyContent="center"
        alignItems="center"
        minHeight="60vh"
      >
        <CircularProgress />
      </Box>
    );
  }

  // Lấy danh sách sinh viên vắng mặt
  const getAbsentStudents = () => {
    if (!classInfo || !classInfo.students || !attendanceLogs) return [];

    // Tạo Set các ID sinh viên đã điểm danh để tăng tốc độ tìm kiếm
    const presentStudentIds = new Set(
      attendanceLogs
        .filter((log) => log.status === "present")
        .map((log) => log.student_id._id)
    );

    // Lọc ra sinh viên chưa điểm danh
    return classInfo.students.filter(
      (student) => !presentStudentIds.has(student._id)
    );
  };

  const absentStudents = getAbsentStudents();

  // Điểm danh thủ công trong chế độ đơn giản
  const showStudentList = () => {
    if (!classInfo) {
      enqueueSnackbar("Không có thông tin lớp học", { variant: "error" });
      return;
    }

    // Lấy danh sách sinh viên vắng mặt
    const absentStudents = getAbsentStudents();

    if (absentStudents.length === 0) {
      enqueueSnackbar("Tất cả sinh viên đã được điểm danh", {
        variant: "success",
      });
      return;
    }

    // Mở dialog điểm danh thủ công với sinh viên đầu tiên trong danh sách
    if (absentStudents.length > 0 && absentStudents[0]) {
      openManualAttendanceDialog(absentStudents[0]);
    }
  };

  // Tối ưu phần Webcam và Camera
  const VideoDisplay = ({
    isCameraReady,
    isAutoMode,
    webcamRef,
    videoRef,
    canvasRef,
    onUserMedia,
    onUserMediaError,
  }) => {
    // Component con để xử lý hiển thị video
    return (
      <Paper
        elevation={3}
        sx={{
          position: "relative",
          width: "100%",
          height: "400px",
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          overflow: "hidden",
          mb: 2,
          borderRadius: "8px",
          border: "1px solid rgba(0, 0, 0, 0.12)",
        }}
      >
        {isCameraReady ? (
          <>
            {isAutoMode ? (
              // Video element cho chế độ tự động
              <video
                ref={videoRef}
                style={{
                  position: "absolute",
                  width: "100%",
                  height: "100%",
                  objectFit: "cover",
                }}
                autoPlay
                playsInline
                muted
              />
            ) : (
              // Webcam component cho chế độ thủ công
              <Webcam
                audio={false}
                ref={webcamRef}
                screenshotFormat="image/jpeg"
                width={640}
                height={480}
                videoConstraints={{
                  width: 640,
                  height: 480,
                  facingMode: "user",
                }}
                style={{
                  position: "absolute",
                  width: "100%",
                  height: "100%",
                  objectFit: "cover",
                }}
                onUserMedia={onUserMedia}
                onUserMediaError={onUserMediaError}
              />
            )}

            {/* Canvas cho hiển thị landmarks */}
            <canvas
              ref={canvasRef}
              style={{
                position: "absolute",
                top: 0,
                left: 0,
                width: "100%",
                height: "100%",
                objectFit: "cover",
                zIndex: 10,
              }}
            />

            {/* Chỉ báo chế độ */}
            <Box
              sx={{
                position: "absolute",
                bottom: 10,
                right: 10,
                backgroundColor: "rgba(0,0,0,0.6)",
                color: "white",
                padding: "4px 8px",
                borderRadius: "4px",
                fontSize: "12px",
                display: "flex",
                alignItems: "center",
                zIndex: 20,
              }}
            >
              {isAutoMode ? (
                <>
                  <span style={{ color: "#4CAF50", marginRight: "5px" }}>
                    ●
                  </span>
                  Đang tự động điểm danh
                </>
              ) : (
                <>
                  <span style={{ color: "#FFC107", marginRight: "5px" }}>
                    ●
                  </span>
                  Chế độ thủ công
                </>
              )}
            </Box>
          </>
        ) : (
          <CameraLoadingPlaceholder />
        )}
      </Paper>
    );
  };

  // Tối ưu giao diện hiển thị danh sách sinh viên đã điểm danh
  const PresentStudentsList = ({
    attendanceLogs,
    refreshAttendanceLogs,
    totalStudents,
  }) => (
    <Card
      sx={{
        mb: 3,
        borderRadius: "10px",
        boxShadow: "0 2px 8px rgba(0,0,0,0.08)",
      }}
    >
      <CardContent>
        <Box
          display="flex"
          justifyContent="space-between"
          alignItems="center"
          mb={2}
        >
          <Box display="flex" alignItems="center">
            <Box
              sx={{
                width: 10,
                height: 10,
                borderRadius: "50%",
                backgroundColor: "#4CAF50",
                mr: 1,
              }}
            />
            <Typography variant="h6">
              Có mặt (
              {attendanceLogs.filter((log) => log.status === "present").length}/
              {totalStudents})
            </Typography>
          </Box>
          <Tooltip title="Làm mới danh sách điểm danh">
            <IconButton
              size="small"
              onClick={refreshAttendanceLogs}
              sx={{
                backgroundColor: "#f4f4f4",
                "&:hover": { backgroundColor: "#e0e0e0" },
              }}
            >
              <Refresh />
            </IconButton>
          </Tooltip>
        </Box>

        <Box sx={{ maxHeight: "350px", overflow: "auto", pr: 1 }}>
          {attendanceLogs.filter((log) => log.status === "present").length ===
          0 ? (
            <Box
