            >
              <HourglassEmpty sx={{ fontSize: 40, mb: 1, opacity: 0.5 }} />
              <Typography variant="body2">
                Chưa có sinh viên nào điểm danh
              </Typography>
            </Box>
          ) : (
            <List dense>
              {attendanceLogs
                .filter((log) => log.status === "present")
                .sort((a, b) => new Date(b.timestamp) - new Date(a.timestamp))
                .map((log) => (
                  <Paper
                    key={log._id}
                    elevation={0}
                    sx={{
