                            ) : null
                          }
                          label={log.recognized ? "Tự động" : "Thủ công"}
                          color={log.recognized ? "success" : "primary"}
                          sx={{
                            borderRadius: "4px",
                            "& .MuiChip-label": { px: 1 },
                          }}
                        />
                      }
                    >
                      <ListItemAvatar>
                        <Avatar
                          alt={log.student_id.full_name}
                          src={log.student_id.avatar_url}
                          sx={{
                            border: "2px solid #f0f0f0",
                            bgcolor: log.recognized ? "#e8f5e9" : "#e3f2fd",
                          }}
                        />
                      </ListItemAvatar>
                      <ListItemText
                        primary={
                          <Typography variant="body2" fontWeight={500}>
                            {log.student_id.full_name}
                          </Typography>
                        }
                        secondary={
                          <Typography variant="caption" color="text.secondary">
                            {new Date(log.timestamp).toLocaleTimeString(
                              "vi-VN"
                            )}
                            {log.note && ` • ${log.note}`}
                          </Typography>
                        }
                      />
                    </ListItem>
                  </Paper>
                ))}
            </List>
          )}
        </Box>
      </CardContent>
    </Card>
  );

  // Tối ưu giao diện hiển thị danh sách sinh viên vắng mặt
  const AbsentStudentsList = ({
    absentStudents,
    onOpenManualAttendance,
    totalStudents,
  }) => (
    <Card
      sx={{ borderRadius: "10px", boxShadow: "0 2px 8px rgba(0,0,0,0.08)" }}
    >
      <CardContent>
        <Box display="flex" alignItems="center" mb={2}>
          <Box
            sx={{
              width: 10,
              height: 10,
              borderRadius: "50%",
              backgroundColor: "#f44336",
              mr: 1,
            }}
          />
          <Typography variant="h6">
            Vắng mặt ({absentStudents.length}/{totalStudents})
          </Typography>
        </Box>

        <Box sx={{ maxHeight: "350px", overflow: "auto", pr: 1 }}>
          {absentStudents.length === 0 ? (
            <Box
              sx={{
                display: "flex",
                flexDirection: "column",
                alignItems: "center",
                justifyContent: "center",
                py: 4,
                color: "text.secondary",
              }}
            >
              <Check sx={{ fontSize: 40, mb: 1, opacity: 0.5 }} />
              <Typography variant="body2">
                Tất cả sinh viên đã được điểm danh
              </Typography>
            </Box>
          ) : (
            <List dense>
              {absentStudents.map((student) => (
                <Paper
                  key={student._id}
                  elevation={0}
                  sx={{
                    mb: 1,
                    borderRadius: "8px",
