import React, { useState, useEffect, useCallback } from "react";
import {
  Box,
  Card,
  Container,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TablePagination,
  TableRow,
  Paper,
  Typography,
  Button,
  IconButton,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  Grid,
  CircularProgress,
  Tooltip,
  Alert,
  InputAdornment,
  CardContent,
  Collapse,
  Chip,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
} from "@mui/material";
import { useSnackbar } from "notistack";
import axios from "axios";
import AddIcon from "@mui/icons-material/Add";
import EditIcon from "@mui/icons-material/Edit";
import DeleteIcon from "@mui/icons-material/Delete";
import SearchIcon from "@mui/icons-material/Search";
import RefreshIcon from "@mui/icons-material/Refresh";
import SchoolIcon from "@mui/icons-material/School";
import MenuBookIcon from "@mui/icons-material/MenuBook";
import AssignmentIcon from "@mui/icons-material/Assignment";
import MainLayout from "../../layouts/MainLayout";
import { API_URL } from "../../config";
import ConfirmDialog from "../../components/ConfirmDialog";

const SubjectsPage = () => {
  // State
  const [subjects, setSubjects] = useState([]);
  const [page, setPage] = useState(0);
  const [rowsPerPage, setRowsPerPage] = useState(10);
  const [dialogOpen, setDialogOpen] = useState(false);
  const [deleteDialogOpen, setDeleteDialogOpen] = useState(false);
  const [selectedSubject, setSelectedSubject] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const [subjectForm, setSubjectForm] = useState({
    name: "",
    code: "",
    credit: 1,
  });
  const [dialogMode, setDialogMode] = useState("create");
  const [total, setTotal] = useState(0);
  const [searchTerm, setSearchTerm] = useState("");
  const [advancedFilters, setAdvancedFilters] = useState({
    creditMin: "",
    creditMax: "",
    sortBy: "name", // name, code, credit
  });
  const [stats, setStats] = useState({
    total: 0,
    averageCredit: 0,
    maxCredit: 0,
    minCredit: 0,
  });

  const { enqueueSnackbar } = useSnackbar();
  const token = localStorage.getItem("token");

  // Load subjects - sử dụng useCallback để tránh warning về dependency
  const loadSubjects = useCallback(async () => {
    try {
      setIsLoading(true);
      setError(null);

      let url = `${API_URL}/courses?page=${page + 1}&limit=${rowsPerPage}`;

      // Thêm tham số tìm kiếm nếu có
      if (searchTerm) {
        url += `&search=${encodeURIComponent(searchTerm)}`;
      }

      // Thêm bộ lọc nâng cao nếu có
      if (advancedFilters.creditMin) {
        url += `&creditMin=${advancedFilters.creditMin}`;
      }

      if (advancedFilters.creditMax) {
        url += `&creditMax=${advancedFilters.creditMax}`;
      }

      if (advancedFilters.sortBy) {
        url += `&sort=${advancedFilters.sortBy}`;
      }

      const response = await axios.get(url, {
        headers: { Authorization: `Bearer ${token}` },
      });

      if (response.data && response.data.data) {
        setSubjects(response.data.data);
        setTotal(response.data.total || 0);
      } else {
        setSubjects([]);
        setTotal(0);
        console.warn("Dữ liệu API không đúng định dạng:", response.data);
      }
    } catch (error) {
      console.error("Lỗi khi tải danh sách môn học:", error);
      setError("Không thể tải danh sách môn học. Vui lòng thử lại sau.");
      enqueueSnackbar("Lỗi khi tải danh sách môn học", { variant: "error" });
    } finally {
      setIsLoading(false);
    }
  }, [page, rowsPerPage, token, enqueueSnackbar, searchTerm, advancedFilters]);

  // Load subjects on page load và khi page/rowsPerPage thay đổi
  useEffect(() => {
    loadSubjects();
  }, [loadSubjects]);

  // Load thống kê
  const loadStats = useCallback(async () => {
    try {
      const response = await axios.get(`${API_URL}/courses/statistics`, {
        headers: { Authorization: `Bearer ${token}` },
      });

      if (response.data && response.data.data) {
        setStats({
          total: response.data.data.total || 0,
          averageCredit: response.data.data.averageCredit || 0,
          maxCredit: response.data.data.maxCredit || 0,
          minCredit: response.data.data.minCredit || 0,
        });
      } else {
        // Nếu API không có, tạo dữ liệu thống kê từ danh sách môn học đã tải
        if (subjects.length > 0) {
          const credits = subjects.map((subject) => subject.credit);
          setStats({
            total: subjects.length,
            averageCredit: (
              credits.reduce((a, b) => a + b, 0) / credits.length
            ).toFixed(1),
            maxCredit: Math.max(...credits),
            minCredit: Math.min(...credits),
          });
        }
      }
    } catch (error) {
      console.error("Lỗi khi tải thống kê môn học:", error);
    }
  }, [token, subjects]);

  // Load thống kê khi trang được tải
  useEffect(() => {
    loadStats();
  }, [loadStats]);

  // Handle page change
  const handlePageChange = (event, newPage) => {
    setPage(newPage);
  };

  // Handle rows per page change
  const handleRowsPerPageChange = (event) => {
    setRowsPerPage(parseInt(event.target.value, 10));
    setPage(0);
  };

  // Handle search
  const handleSearch = () => {
    setPage(0);
    loadSubjects();
  };

  // Handle filter change
  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setAdvancedFilters({
      ...advancedFilters,
      [name]: value,
    });
  };

  // Handle apply filters
  const applyFilters = () => {
    setPage(0);
    loadSubjects();
  };

  // Handle clear filters
  const clearFilters = () => {
    setSearchTerm("");
    setAdvancedFilters({
      creditMin: "",
      creditMax: "",
      sortBy: "name",
    });
    setPage(0);
  };

  // Handle open dialog to create
  const handleOpenCreateDialog = () => {
    setSubjectForm({
      name: "",
      code: "",
      credit: 1,
    });
    setDialogMode("create");
    setDialogOpen(true);
  };

  // Handle open dialog to edit
  const handleOpenEditDialog = (subject) => {
    setSelectedSubject(subject);
    setSubjectForm({
      name: subject.name,
      code: subject.code,
      credit: subject.credit,
    });
    setDialogMode("edit");
    setDialogOpen(true);
  };

  // Handle open dialog to delete
  const handleOpenDeleteDialog = (subject) => {
    setSelectedSubject(subject);
    setDeleteDialogOpen(true);
  };

  // Handle form change
  const handleFormChange = (event) => {
    const { name, value } = event.target;
    setSubjectForm({
      ...subjectForm,
      [name]: name === "credit" ? parseInt(value, 10) || 0 : value,
    });
  };

  // Validate form
  const validateForm = () => {
    if (!subjectForm.name.trim()) {
      enqueueSnackbar("Vui lòng nhập tên môn học", { variant: "error" });
      return false;
    }

    if (!subjectForm.code.trim()) {
      enqueueSnackbar("Vui lòng nhập mã môn học", { variant: "error" });
      return false;
    }

    if (!subjectForm.credit || subjectForm.credit < 1) {
      enqueueSnackbar("Số tín chỉ phải lớn hơn 0", { variant: "error" });
      return false;
    }

    return true;
  };

  // Handle form submit
  const handleFormSubmit = async () => {
    try {
      if (!validateForm()) {
        return;
      }

      setIsLoading(true);

      if (dialogMode === "create") {
        await axios.post(`${API_URL}/courses`, subjectForm, {
          headers: { Authorization: `Bearer ${token}` },
        });
        enqueueSnackbar("Tạo môn học mới thành công", { variant: "success" });
      } else {
        await axios.put(
          `${API_URL}/courses/${selectedSubject._id}`,
          subjectForm,
          {
            headers: { Authorization: `Bearer ${token}` },
          }
        );
        enqueueSnackbar("Cập nhật môn học thành công", { variant: "success" });
      }

      setDialogOpen(false);
      loadSubjects();
      loadStats();
    } catch (error) {
      console.error("Lỗi khi lưu môn học:", error);
      enqueueSnackbar(
        `Lỗi khi ${dialogMode === "create" ? "tạo" : "cập nhật"} môn học: ${
          error.response?.data?.message || error.message
        }`,
        { variant: "error" }
      );
    } finally {
      setIsLoading(false);
    }
  };

  // Handle delete subject
  const handleDeleteSubject = async () => {
    try {
      setIsLoading(true);
      await axios.delete(`${API_URL}/courses/${selectedSubject._id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      enqueueSnackbar("Xóa môn học thành công", { variant: "success" });
      setDeleteDialogOpen(false);
      loadSubjects();
      loadStats();
    } catch (error) {
      console.error("Lỗi khi xóa môn học:", error);
      enqueueSnackbar(
        `Lỗi khi xóa môn học: ${
          error.response?.data?.message || error.message
        }`,
        { variant: "error" }
      );
    } finally {
      setIsLoading(false);
    }
  };

  // Render statistics cards
  const renderStatCards = () => (
    <Grid container spacing={2} sx={{ mb: 3 }}>
      <Grid item xs={12} sm={6} md={3}>
        <Card
          sx={{
            borderLeft: "4px solid #3f51b5",
            boxShadow: 2,
            height: "100%",
          }}
        >
          <CardContent>
            <Typography variant="h6" color="textSecondary" gutterBottom>
              Tổng số môn học
            </Typography>
            <Typography variant="h4">{stats.total}</Typography>
            <Box sx={{ mt: 1, display: "flex", alignItems: "center" }}>
              <MenuBookIcon
                fontSize="small"
                sx={{ mr: 1, color: "text.secondary" }}
              />
              <Typography variant="body2" color="textSecondary">
                Tất cả các môn học trong hệ thống
              </Typography>
            </Box>
          </CardContent>
        </Card>
      </Grid>

      <Grid item xs={12} sm={6} md={3}>
        <Card
          sx={{
            borderLeft: "4px solid #4caf50",
            boxShadow: 2,
            height: "100%",
          }}
        >
          <CardContent>
            <Typography variant="h6" color="textSecondary" gutterBottom>
              Trung bình tín chỉ
            </Typography>
            <Typography variant="h4">{stats.averageCredit}</Typography>
            <Box sx={{ mt: 1, display: "flex", alignItems: "center" }}>
              <AssignmentIcon
                fontSize="small"
                sx={{ mr: 1, color: "text.secondary" }}
              />
              <Typography variant="body2" color="textSecondary">
                Trung bình tín chỉ mỗi môn học
              </Typography>
            </Box>
          </CardContent>
        </Card>
      </Grid>

      <Grid item xs={12} sm={6} md={3}>
        <Card
          sx={{
            borderLeft: "4px solid #2196f3",
            boxShadow: 2,
            height: "100%",
          }}
        >
          <CardContent>
            <Typography variant="h6" color="textSecondary" gutterBottom>
              Tín chỉ lớn nhất
            </Typography>
            <Typography variant="h4">{stats.maxCredit}</Typography>
            <Box sx={{ mt: 1, display: "flex", alignItems: "center" }}>
              <SchoolIcon
                fontSize="small"
                sx={{ mr: 1, color: "text.secondary" }}
              />
              <Typography variant="body2" color="textSecondary">
                Môn học có nhiều tín chỉ nhất
              </Typography>
            </Box>
          </CardContent>
        </Card>
      </Grid>

      <Grid item xs={12} sm={6} md={3}>
        <Card
          sx={{
            borderLeft: "4px solid #ff9800",
            boxShadow: 2,
            height: "100%",
          }}
        >
          <CardContent>
            <Typography variant="h6" color="textSecondary" gutterBottom>
              Tín chỉ nhỏ nhất
            </Typography>
            <Typography variant="h4">{stats.minCredit}</Typography>
            <Box sx={{ mt: 1, display: "flex", alignItems: "center" }}>
              <SchoolIcon
                fontSize="small"
                sx={{ mr: 1, color: "text.secondary" }}
              />
              <Typography variant="body2" color="textSecondary">
                Môn học có ít tín chỉ nhất
              </Typography>
            </Box>
          </CardContent>
        </Card>
      </Grid>
    </Grid>
  );

  // Empty state
  const renderEmptyState = () => (
    <Box
      sx={{
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
        py: 5,
        px: 2,
        bgcolor: "#f5f5f5",
        borderRadius: 2,
      }}
    >
      <Box sx={{ mb: 2 }}>
        <MenuBookIcon sx={{ fontSize: 60, color: "#3f51b5" }} />
      </Box>
      <Typography variant="h6" gutterBottom>
        Chưa có môn học nào
      </Typography>
      <Typography
        variant="body2"
        color="textSecondary"
        align="center"
        sx={{ mb: 3 }}
      >
        Môn học là đơn vị cơ bản của chương trình đào tạo. Mỗi môn học có mã
        riêng và số tín chỉ tương ứng.
      </Typography>
      <Button
        variant="contained"
        color="primary"
        startIcon={<AddIcon />}
        onClick={handleOpenCreateDialog}
      >
        Tạo môn học mới
      </Button>
    </Box>
  );

  return (
    <MainLayout>
      <Container maxWidth="xl">
        <Box sx={{ mb: 4 }}>
          <Typography variant="h4" gutterBottom>
            Quản lý môn học
          </Typography>
          <Typography variant="body1" color="textSecondary" paragraph>
            Quản lý các môn học trong hệ thống điểm danh
          </Typography>
        </Box>

        {renderStatCards()}

        {error && (
          <Alert severity="error" sx={{ mb: 3 }}>
            {error}
          </Alert>
        )}

        <Paper sx={{ p: 2, mb: 3 }}>
          <Box
            sx={{
              display: "flex",
              justifyContent: "space-between",
              alignItems: "center",
              flexWrap: "wrap",
              gap: 2,
              mb: 2,
            }}
          >
            <Box sx={{ flexGrow: 1, maxWidth: "500px" }}>
              <TextField
                fullWidth
                placeholder="Tìm kiếm môn học theo tên hoặc mã..."
                size="small"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                onKeyPress={(e) => e.key === "Enter" && handleSearch()}
                InputProps={{
                  startAdornment: (
                    <InputAdornment position="start">
                      <SearchIcon />
                    </InputAdornment>
                  ),
                }}
              />
            </Box>

            <Box display="flex" gap={2}>
              <Button
                variant="outlined"
                startIcon={<RefreshIcon />}
                onClick={() => {
                  clearFilters();
                  loadSubjects();
                }}
              >
                Làm mới
              </Button>
              <Button
                variant="contained"
                startIcon={<AddIcon />}
                onClick={handleOpenCreateDialog}
              >
                Thêm môn học
              </Button>
            </Box>
          </Box>

          <Collapse in={true}>
            <Box sx={{ mb: 2 }}>
              <Typography variant="subtitle2" gutterBottom>
                Bộ lọc nâng cao
              </Typography>
              <Grid container spacing={2} alignItems="center">
                <Grid item xs={12} sm={3}>
                  <TextField
                    name="creditMin"
                    label="Tín chỉ từ"
                    type="number"
                    size="small"
                    fullWidth
                    value={advancedFilters.creditMin}
                    onChange={handleFilterChange}
                    InputProps={{ inputProps: { min: 1 } }}
                  />
                </Grid>
                <Grid item xs={12} sm={3}>
                  <TextField
                    name="creditMax"
                    label="Tín chỉ đến"
                    type="number"
                    size="small"
                    fullWidth
                    value={advancedFilters.creditMax}
                    onChange={handleFilterChange}
                    InputProps={{ inputProps: { min: 1 } }}
                  />
                </Grid>
                <Grid item xs={12} sm={3}>
                  <FormControl fullWidth size="small">
                    <InputLabel>Sắp xếp theo</InputLabel>
                    <Select
                      name="sortBy"
                      value={advancedFilters.sortBy}
                      onChange={handleFilterChange}
                      label="Sắp xếp theo"
                    >
                      <MenuItem value="name">Tên môn học</MenuItem>
                      <MenuItem value="code">Mã môn học</MenuItem>
                      <MenuItem value="credit">Số tín chỉ</MenuItem>
                    </Select>
                  </FormControl>
                </Grid>
                <Grid item xs={12} sm={3}>
                  <Box display="flex" gap={1}>
                    <Button
                      variant="outlined"
                      color="primary"
                      fullWidth
                      onClick={applyFilters}
                    >
                      Áp dụng
                    </Button>
                    <Button
                      variant="outlined"
                      color="inherit"
                      onClick={clearFilters}
                    >
                      Xóa lọc
                    </Button>
                  </Box>
                </Grid>
              </Grid>
            </Box>
          </Collapse>
        </Paper>

        {/* Hiển thị kết quả hoặc trạng thái trống */}
        {isLoading ? (
          <Box sx={{ display: "flex", justifyContent: "center", py: 8 }}>
            <CircularProgress />
          </Box>
        ) : subjects.length === 0 &&
          searchTerm === "" &&
          !advancedFilters.creditMin &&
          !advancedFilters.creditMax ? (
          renderEmptyState()
        ) : (
          <Card>
            <TableContainer component={Paper}>
              <Table sx={{ minWidth: 650 }}>
                <TableHead>
                  <TableRow sx={{ backgroundColor: "#f5f5f5" }}>
                    <TableCell>#</TableCell>
                    <TableCell>Tên môn học</TableCell>
                    <TableCell>Mã môn học</TableCell>
                    <TableCell>Số tín chỉ</TableCell>
                    <TableCell align="right">Thao tác</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {subjects.length === 0 ? (
                    <TableRow>
                      <TableCell colSpan={5} align="center">
                        Không tìm thấy môn học nào
                      </TableCell>
                    </TableRow>
                  ) : (
                    subjects.map((subject, index) => (
                      <TableRow key={subject._id} hover>
                        <TableCell>{page * rowsPerPage + index + 1}</TableCell>
                        <TableCell>
                          <Typography variant="body1" fontWeight="500">
                            {subject.name}
                          </Typography>
                        </TableCell>
                        <TableCell>
                          <Chip
                            label={subject.code}
                            size="small"
                            variant="outlined"
                            color="primary"
                          />
                        </TableCell>
                        <TableCell>
                          <Chip
                            label={`${subject.credit} tín chỉ`}
                            size="small"
                            color={
                              subject.credit > 3
                                ? "error"
                                : subject.credit > 2
                                ? "warning"
                                : "success"
                            }
                            variant="outlined"
                          />
                        </TableCell>
                        <TableCell align="right">
                          <Tooltip title="Chỉnh sửa">
                            <IconButton
                              color="primary"
                              onClick={() => handleOpenEditDialog(subject)}
                              size="small"
                              sx={{ mr: 1 }}
                            >
                              <EditIcon fontSize="small" />
                            </IconButton>
                          </Tooltip>
                          <Tooltip title="Xóa">
                            <IconButton
                              color="error"
                              onClick={() => handleOpenDeleteDialog(subject)}
                              size="small"
                            >
                              <DeleteIcon fontSize="small" />
                            </IconButton>
                          </Tooltip>
                        </TableCell>
                      </TableRow>
                    ))
                  )}
                </TableBody>
              </Table>
            </TableContainer>
            <TablePagination
              rowsPerPageOptions={[5, 10, 25, 50]}
              component="div"
              count={total}
              rowsPerPage={rowsPerPage}
              page={page}
              onPageChange={handlePageChange}
              onRowsPerPageChange={handleRowsPerPageChange}
              labelRowsPerPage="Số hàng mỗi trang:"
              labelDisplayedRows={({ from, to, count }) =>
                `${from}-${to} của ${count !== -1 ? count : `hơn ${to}`}`
              }
            />
          </Card>
        )}

        {/* Dialog for creating/editing subject */}
        <Dialog
          open={dialogOpen}
          onClose={() => setDialogOpen(false)}
          maxWidth="md"
          fullWidth
        >
          <DialogTitle>
            {dialogMode === "create" ? "Thêm môn học mới" : "Chỉnh sửa môn học"}
          </DialogTitle>
          <DialogContent>
            <Grid container spacing={2} sx={{ mt: 1 }}>
              <Grid item xs={12}>
                <TextField
                  name="name"
                  label="Tên môn học"
                  value={subjectForm.name}
                  onChange={handleFormChange}
                  fullWidth
                  required
                  helperText="Ví dụ: Lập trình hướng đối tượng"
                />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  name="code"
                  label="Mã môn học"
                  value={subjectForm.code}
                  onChange={handleFormChange}
                  fullWidth
                  required
                  helperText="Ví dụ: IT001"
                />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  name="credit"
                  label="Số tín chỉ"
                  value={subjectForm.credit}
                  onChange={handleFormChange}
                  fullWidth
                  required
                  type="number"
                  inputProps={{ min: 1 }}
                  helperText="Nhập số tín chỉ môn học (lớn hơn 0)"
                />
              </Grid>
            </Grid>
          </DialogContent>
          <DialogActions sx={{ px: 3, pb: 2 }}>
            <Button onClick={() => setDialogOpen(false)}>Hủy</Button>
            <Button
              variant="contained"
              onClick={handleFormSubmit}
              disabled={isLoading}
            >
              {isLoading ? (
                <CircularProgress size={24} />
              ) : dialogMode === "create" ? (
                "Thêm môn học"
              ) : (
                "Lưu thay đổi"
              )}
            </Button>
          </DialogActions>
        </Dialog>

        {/* Confirmation dialog for deleting subject */}
        <ConfirmDialog
          open={deleteDialogOpen}
          onClose={() => setDeleteDialogOpen(false)}
          onConfirm={handleDeleteSubject}
          title="Xác nhận xóa môn học"
          content={`Bạn có chắc chắn muốn xóa môn học "${
            selectedSubject?.name || ""
          }" (${
            selectedSubject?.code || ""
          })? Hành động này không thể hoàn tác và có thể ảnh hưởng đến các lớp học liên quan.`}
          isLoading={isLoading}
          danger={true}
        />
      </Container>
    </MainLayout>
  );
};

export default SubjectsPage;
