import React, { useState, useEffect, useMemo } from "react";
import { useNavigate } from "react-router-dom";
import { useSelector } from "react-redux";
import { useSnackbar } from "notistack";
import axios from "axios";
import {
  Box,
  Typography,
  Card,
  CardContent,
  CardHeader,
  Avatar,
  Grid,
  Paper,
  Button,
  Divider,
  LinearProgress,
  List,
  ListItem,
  ListItemText,
  ListItemAvatar,
  ListItemButton,
  IconButton,
  Chip,
  Stack,
  CircularProgress,
  Badge,
  useTheme,
} from "@mui/material";
import {
  Person,
  School,
  Class,
  CalendarToday,
  AccessTime,
  Visibility,
  HowToReg,
  Warning,
  Check,
  Schedule,
  KeyboardArrowRight,
  LocalLibrary,
  NotificationsActive,
  AccessTimeFilled,
  ViewList,
  Dashboard,
  Event,
  Today,
  ArrowForward,
  Book,
  Groups,
} from "@mui/icons-material";

const API_URL = process.env.REACT_APP_API_URL || "http://localhost:5000/api";

const StudentHomePage = () => {
  const navigate = useNavigate();
  const { enqueueSnackbar } = useSnackbar();
  const { user, token } = useSelector((state) => state.auth);
  const theme = useTheme();

  // Các state
  const [isLoading, setIsLoading] = useState(true);
  const [userDetails, setUserDetails] = useState(null);
  const [classes, setClasses] = useState([]);
  const [todayClasses, setTodayClasses] = useState([]);
  const [attendanceStats, setAttendanceStats] = useState({
    total: 0,
    present: 0,
    absent: 0,
    late: 0,
    rate: 0,
  });
  const [faceRegistrationStatus, setFaceRegistrationStatus] = useState(false);

  // Load dữ liệu ban đầu
  useEffect(() => {
    const fetchData = async () => {
      try {
        setIsLoading(true);

        // Lấy thông tin chi tiết của người dùng
        const userResponse = await axios.get(`${API_URL}/auth/me`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setUserDetails(userResponse.data.data);
        setFaceRegistrationStatus(
          userResponse.data.data.face_registered || false
        );

        // Lấy danh sách lớp học
        const classesResponse = await axios.get(
          `${API_URL}/classes/teaching/student/${user._id}`,
          { headers: { Authorization: `Bearer ${token}` } }
        );
        const classesData = classesResponse.data.data || [];
        setClasses(classesData);

        // Lấy thống kê điểm danh tổng quan cho tất cả các lớp học
        let totalSessions = 0;
        let presentCount = 0;
        let absentCount = 0;
        let lateCount = 0;

        // Lấy thống kê điểm danh cho từng lớp
        for (const classItem of classesData) {
          try {
            const attendanceResponse = await axios.get(
              `${API_URL}/attendance/student/${user._id}/logs?teaching_class=${classItem._id}`,
              { headers: { Authorization: `Bearer ${token}` } }
            );

            const logs = attendanceResponse.data.data || [];
            totalSessions += logs.length;

            logs.forEach((log) => {
              if (log.status === "present") presentCount++;
              else if (log.status === "late") lateCount++;
              else if (log.status === "absent") absentCount++;
            });
          } catch (error) {
            console.error(
              `Lỗi khi lấy thống kê điểm danh cho lớp ${classItem._id}:`,
              error
            );
          }
        }

        setAttendanceStats({
          total: totalSessions,
          present: presentCount,
          absent: absentCount,
          late: lateCount,
          rate:
            totalSessions > 0
              ? ((presentCount + lateCount) / totalSessions) * 100
              : 0,
        });

        // Lọc lớp học cho hôm nay
        const today = new Date();
        const dayOfWeek = today.getDay(); // 0: Chủ nhật, 1-6: Thứ 2 - Thứ 7

        const todayClassesList = classesData.filter((classItem) => {
          if (!classItem.schedule || !classItem.schedule.length) return false;
          return classItem.schedule.some(
            (schedule) => schedule.day_of_week === dayOfWeek
          );
        });

        setTodayClasses(todayClassesList);
      } catch (error) {
        console.error("Lỗi khi tải dữ liệu trang chủ:", error);
        enqueueSnackbar("Lỗi khi tải dữ liệu trang chủ", { variant: "error" });
      } finally {
        setIsLoading(false);
      }
    };

    fetchData();
  }, [user._id, token, enqueueSnackbar]);

  // Chuyển đổi ngày thành chuỗi thứ trong tuần
  const getDayOfWeekString = (dayNumber) => {
    const days = [
      "Chủ nhật",
      "Thứ hai",
      "Thứ ba",
      "Thứ tư",
      "Thứ năm",
      "Thứ sáu",
      "Thứ bảy",
    ];
    return days[dayNumber];
  };

  // Lấy ngày hiện tại dưới dạng chuỗi
  const todayString = useMemo(() => {
    const today = new Date();
    return `${getDayOfWeekString(today.getDay())}, ${today.getDate()}/${
      today.getMonth() + 1
    }/${today.getFullYear()}`;
  }, []);

  // Xử lý chuyển hướng đến trang đăng ký khuôn mặt
  const handleRegisterFace = () => {
    navigate("/register-face");
  };

  // Xử lý chuyển hướng đến trang danh sách lớp học
  const handleViewAllClasses = () => {
    navigate("/student/classes");
  };

  // Xử lý chuyển hướng đến trang chi tiết điểm danh của lớp học
  const handleViewAttendance = (classId) => {
    navigate(`/student/attendance/${classId}`);
  };

  if (isLoading) {
    return (
      <Box
        sx={{
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          height: "80vh",
        }}
      >
        <CircularProgress />
      </Box>
    );
  }

  return (
    <Box sx={{ padding: 3 }}>
      {/* Header */}
      <Box
        sx={{
          mb: 4,
          display: "flex",
          justifyContent: "space-between",
          alignItems: "center",
        }}
      >
        <Typography variant="h4" gutterBottom>
          <Dashboard sx={{ mr: 1, verticalAlign: "middle" }} />
          Trang chủ
        </Typography>
        <Chip
          label={todayString}
          color="primary"
          icon={<Today />}
          variant="outlined"
        />
      </Box>

      {/* Thông báo khuôn mặt nếu chưa đăng ký */}
      {!faceRegistrationStatus && (
        <Paper
          sx={{
            p: 2,
            mb: 3,
            bgcolor: "warning.light",
            border: "1px solid",
            borderColor: "warning.main",
          }}
        >
          <Box sx={{ display: "flex", alignItems: "center" }}>
            <Warning color="warning" sx={{ mr: 2, fontSize: 28 }} />
            <Box sx={{ flex: 1 }}>
              <Typography variant="subtitle1" fontWeight="bold">
                Bạn chưa đăng ký khuôn mặt cho hệ thống điểm danh!
              </Typography>
              <Typography variant="body2">
                Để có thể điểm danh bằng khuôn mặt, bạn cần đăng ký khuôn mặt
                trước.
              </Typography>
            </Box>
            <Button
              variant="contained"
              color="warning"
              onClick={handleRegisterFace}
              startIcon={<HowToReg />}
            >
              Đăng ký khuôn mặt
            </Button>
          </Box>
        </Paper>
      )}

      <Grid container spacing={3}>
        {/* Thông tin cá nhân */}
        <Grid item xs={12} md={4}>
          <Card sx={{ height: "100%" }}>
            <CardContent>
              <Box
                sx={{
                  display: "flex",
                  flexDirection: "column",
                  alignItems: "center",
                  mb: 2,
                }}
              >
                <Avatar
                  src={userDetails?.avatar_url}
                  sx={{ width: 96, height: 96, mb: 2 }}
                >
                  {userDetails?.full_name?.charAt(0) || "S"}
                </Avatar>
                <Typography variant="h5" fontWeight="bold" align="center">
                  {userDetails?.full_name}
                </Typography>
                <Chip
                  label={userDetails?.school_info?.student_code || "N/A"}
                  color="primary"
                  sx={{ mt: 1 }}
                  icon={<Person />}
                />
              </Box>
              <Divider sx={{ my: 2 }} />
              <List>
                <ListItem>
                  <ListItemAvatar>
                    <Avatar sx={{ bgcolor: "primary.light" }}>
                      <School />
                    </Avatar>
                  </ListItemAvatar>
                  <ListItemText
                    primary="Ngành học"
                    secondary={userDetails?.school_info?.major || "N/A"}
                  />
                </ListItem>
                <ListItem>
                  <ListItemAvatar>
                    <Avatar sx={{ bgcolor: "primary.light" }}>
                      <Groups />
                    </Avatar>
                  </ListItemAvatar>
                  <ListItemText
                    primary="Lớp"
                    secondary={
                      userDetails?.school_info?.class_id?.name ||
                      userDetails?.school_info?.class ||
                      "N/A"
                    }
                  />
                </ListItem>
                <ListItem>
                  <ListItemAvatar>
                    <Avatar sx={{ bgcolor: "primary.light" }}>
                      <Book />
                    </Avatar>
                  </ListItemAvatar>
                  <ListItemText
                    primary="Khoa"
                    secondary={userDetails?.school_info?.department || "N/A"}
                  />
                </ListItem>
              </List>
            </CardContent>
          </Card>
        </Grid>

        {/* Thống kê điểm danh */}
        <Grid item xs={12} md={8}>
          <Grid container spacing={3}>
            {/* Tổng quan điểm danh */}
            <Grid item xs={12}>
              <Card>
                <CardHeader
                  title={
                    <Box sx={{ display: "flex", alignItems: "center" }}>
                      <HowToReg sx={{ mr: 1 }} />
                      <Typography variant="h6">Thống kê điểm danh</Typography>
                    </Box>
                  }
                />
                <Divider />
                <CardContent>
                  <Box sx={{ mb: 2 }}>
                    <Typography variant="body1" gutterBottom>
                      Tỷ lệ tham gia:{" "}
                      <strong>{Math.round(attendanceStats.rate)}%</strong>
                    </Typography>
                    <LinearProgress
                      variant="determinate"
                      value={attendanceStats.rate}
                      color={
                        attendanceStats.rate >= 80
                          ? "success"
                          : attendanceStats.rate >= 50
                          ? "warning"
                          : "error"
                      }
                      sx={{ height: 12, borderRadius: 2, mb: 1 }}
                    />
                    <Typography
                      variant="subtitle1"
                      fontWeight="bold"
                      align="center"
                      sx={{ mt: 2 }}
                    >
                      Đã tham gia{" "}
                      <strong>
                        {attendanceStats.present + attendanceStats.late}
                      </strong>{" "}
                      / <strong>{attendanceStats.total}</strong> buổi
                    </Typography>
                  </Box>
                  <Grid container spacing={2} sx={{ mt: 1 }}>
                    <Grid item xs={4}>
                      <Paper
                        sx={{
                          p: 1.5,
                          textAlign: "center",
                          bgcolor: "success.light",
                          borderRadius: 2,
                        }}
                      >
                        <Typography
                          variant="h4"
                          color="success.dark"
                          fontWeight="bold"
                        >
                          {attendanceStats.present}
                        </Typography>
                        <Typography variant="body2">Có mặt</Typography>
                      </Paper>
                    </Grid>
                    <Grid item xs={4}>
                      <Paper
                        sx={{
                          p: 1.5,
                          textAlign: "center",
                          bgcolor: "warning.light",
                          borderRadius: 2,
                        }}
                      >
                        <Typography
                          variant="h4"
                          color="warning.dark"
                          fontWeight="bold"
                        >
                          {attendanceStats.late}
                        </Typography>
                        <Typography variant="body2">Đi muộn</Typography>
                      </Paper>
                    </Grid>
                    <Grid item xs={4}>
                      <Paper
                        sx={{
                          p: 1.5,
                          textAlign: "center",
                          bgcolor: "error.light",
                          borderRadius: 2,
                        }}
                      >
                        <Typography
                          variant="h4"
                          color="error.dark"
                          fontWeight="bold"
                        >
                          {attendanceStats.absent}
                        </Typography>
                        <Typography variant="body2">Vắng mặt</Typography>
                      </Paper>
                    </Grid>
                  </Grid>
                </CardContent>
              </Card>
            </Grid>

            {/* Lớp học hôm nay */}
            <Grid item xs={12}>
              <Card>
                <CardHeader
                  title={
                    <Box sx={{ display: "flex", alignItems: "center" }}>
                      <CalendarToday sx={{ mr: 1 }} />
                      <Typography variant="h6">Lớp học hôm nay</Typography>
                    </Box>
                  }
                  action={
                    <Button
                      endIcon={<ArrowForward />}
                      onClick={handleViewAllClasses}
                    >
                      Xem tất cả
                    </Button>
                  }
                />
                <Divider />
                <CardContent>
                  {todayClasses.length > 0 ? (
                    <List>
                      {todayClasses.map((classItem) => {
                        // Lấy thông tin lịch học hôm nay
                        const today = new Date();
                        const dayOfWeek = today.getDay();
                        const scheduleToday = classItem.schedule.find(
                          (s) => s.day_of_week === dayOfWeek
                        );

                        return (
                          <Paper
                            key={classItem._id}
                            elevation={1}
                            sx={{ mb: 2, borderRadius: 2, overflow: "hidden" }}
                          >
                            <ListItem
                              secondaryAction={
                                <IconButton
                                  edge="end"
                                  onClick={() =>
                                    handleViewAttendance(classItem._id)
                                  }
                                >
                                  <KeyboardArrowRight />
                                </IconButton>
                              }
                              sx={{
                                bgcolor: classItem.is_active
                                  ? "background.paper"
                                  : "action.disabledBackground",
                              }}
                            >
                              <ListItemAvatar>
                                <Avatar
                                  sx={{
                                    bgcolor: classItem.is_active
                                      ? "primary.main"
                                      : "text.disabled",
                                  }}
                                >
                                  <LocalLibrary />
                                </Avatar>
                              </ListItemAvatar>
                              <ListItemText
                                primary={
                                  <Typography
                                    variant="subtitle1"
                                    fontWeight="bold"
                                  >
                                    {classItem.class_name}
                                  </Typography>
                                }
                                secondary={
                                  <>
                                    <Typography
                                      variant="body2"
                                      color="text.primary"
                                    >
                                      {classItem.subject_id?.name || "N/A"} (
                                      {classItem.subject_id?.code || "N/A"})
                                    </Typography>
                                    <Box
                                      sx={{
                                        display: "flex",
                                        alignItems: "center",
                                        mt: 0.5,
                                      }}
                                    >
                                      <AccessTimeFilled
                                        fontSize="small"
                                        sx={{
                                          mr: 0.5,
                                          color: "text.secondary",
                                        }}
                                      />
                                      <Typography
                                        variant="body2"
                                        color="text.secondary"
                                      >
                                        {scheduleToday?.start_time} -{" "}
                                        {scheduleToday?.end_time}
                                      </Typography>
                                      <Chip
                                        label={
                                          classItem.is_active
                                            ? "Đang diễn ra"
                                            : "Đã kết thúc"
                                        }
                                        size="small"
                                        color={
                                          classItem.is_active
                                            ? "success"
                                            : "default"
                                        }
                                        sx={{ ml: 1 }}
                                      />
                                    </Box>
                                  </>
                                }
                              />
                            </ListItem>
                          </Paper>
                        );
                      })}
                    </List>
                  ) : (
                    <Box
                      sx={{
                        p: 3,
                        textAlign: "center",
                        color: "text.secondary",
                      }}
                    >
                      <Schedule
                        sx={{ fontSize: 50, mb: 1, color: "action.disabled" }}
                      />
                      <Typography variant="h6">
                        Không có lớp học hôm nay
                      </Typography>
                      <Typography variant="body2">
                        Bạn có thể xem tất cả lớp học đã đăng ký bằng cách nhấn
                        vào "Xem tất cả"
                      </Typography>
                    </Box>
                  )}
                </CardContent>
              </Card>
            </Grid>
          </Grid>
        </Grid>

        {/* Danh sách lớp học */}
        <Grid item xs={12}>
          <Card>
            <CardHeader
              title={
                <Box sx={{ display: "flex", alignItems: "center" }}>
                  <Class sx={{ mr: 1 }} />
                  <Typography variant="h6">Lớp học của tôi</Typography>
                </Box>
              }
              action={
                <Button
                  color="primary"
                  endIcon={<ViewList />}
                  onClick={handleViewAllClasses}
                >
                  Xem tất cả
                </Button>
              }
            />
            <Divider />
            <CardContent>
              <Grid container spacing={2}>
                {classes.slice(0, 4).map((classItem) => (
                  <Grid item xs={12} sm={6} md={3} key={classItem._id}>
                    <Card
                      variant="outlined"
                      sx={{
                        height: "100%",
                        display: "flex",
                        flexDirection: "column",
                        position: "relative",
                        transition: "transform 0.2s",
                        "&:hover": {
                          transform: "translateY(-4px)",
                          boxShadow: 3,
                        },
                      }}
                    >
                      {classItem.is_active && (
                        <Chip
                          label="Đang hoạt động"
                          color="success"
                          size="small"
                          sx={{
                            position: "absolute",
                            top: 8,
                            right: 8,
                            zIndex: 1,
                          }}
                        />
                      )}
                      <CardContent sx={{ flexGrow: 1 }}>
                        <Typography
                          variant="h6"
                          component="div"
                          noWrap
                          sx={{
                            mb: 1,
                            fontWeight: "bold",
                            overflow: "hidden",
                            textOverflow: "ellipsis",
                          }}
                        >
                          {classItem.class_name}
                        </Typography>
                        <Typography
                          variant="body2"
                          color="text.secondary"
                          gutterBottom
                        >
                          {classItem.subject_id?.name || "N/A"}
                        </Typography>
                        <Typography variant="body2" color="text.secondary">
                          <Person
                            fontSize="small"
                            sx={{ verticalAlign: "middle", mr: 0.5 }}
                          />
                          {classItem.teacher_id?.full_name || "N/A"}
                        </Typography>
                        <Typography variant="body2" color="text.secondary">
                          <School
                            fontSize="small"
                            sx={{ verticalAlign: "middle", mr: 0.5 }}
                          />
                          {classItem.semester_id?.name || "N/A"}
                        </Typography>
                      </CardContent>
                      <Divider />
                      <Box sx={{ p: 1 }}>
                        <Button
                          fullWidth
                          variant="text"
                          size="small"
                          onClick={() => handleViewAttendance(classItem._id)}
                          startIcon={<Visibility />}
                        >
                          Xem điểm danh
                        </Button>
                      </Box>
                    </Card>
                  </Grid>
                ))}
                {classes.length === 0 && (
                  <Grid item xs={12}>
                    <Box
                      sx={{
                        p: 4,
                        textAlign: "center",
                        color: "text.secondary",
                      }}
                    >
                      <Event
                        sx={{ fontSize: 60, mb: 1, color: "action.disabled" }}
                      />
                      <Typography variant="h6">Chưa có lớp học nào</Typography>
                      <Typography variant="body2">
                        Bạn chưa đăng ký lớp học nào cho học kỳ này
                      </Typography>
                    </Box>
                  </Grid>
                )}
              </Grid>
              {classes.length > 4 && (
                <Box sx={{ textAlign: "center", mt: 2 }}>
                  <Button
                    variant="outlined"
                    onClick={handleViewAllClasses}
                    endIcon={<ArrowForward />}
                  >
                    Xem thêm {classes.length - 4} lớp học khác
                  </Button>
                </Box>
              )}
            </CardContent>
          </Card>
        </Grid>
      </Grid>
    </Box>
  );
};

export default StudentHomePage;
