import React, { useState, useEffect } from "react";
import {
  Box,
  Container,
  Typography,
  Paper,
  CircularProgress,
  Button,
  Alert,
} from "@mui/material";
import MainLayout from "../../layouts/MainLayout";
import { API_URL } from "../../config";
import axios from "axios";

const SubjectsPage = () => {
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const [subjects, setSubjects] = useState([]);
  const token = localStorage.getItem("token");

  useEffect(() => {
    const fetchData = async () => {
      try {
        setIsLoading(true);
        console.log("Đang tải dữ liệu môn học...");

        // Kiểm tra token
        if (!token) {
          console.error("Không tìm thấy token");
          setError("Vui lòng đăng nhập lại để tiếp tục");
          setIsLoading(false);
          return;
        }

        const response = await axios.get(`${API_URL}/courses`, {
          headers: { Authorization: `Bearer ${token}` },
        });

        console.log("Dữ liệu nhận được:", response.data);

        if (response.data && response.data.data) {
          setSubjects(response.data.data);
        } else {
          setSubjects([]);
        }
      } catch (err) {
        console.error("Lỗi khi tải dữ liệu:", err);
        setError(
          "Không thể tải dữ liệu môn học. " +
            (err.response?.data?.message || err.message)
        );
      } finally {
        setIsLoading(false);
      }
    };

    fetchData();
  }, [token]);

  const handleRefresh = () => {
    setIsLoading(true);
    setError(null);
    window.location.reload();
  };

  return (
    <MainLayout>
      <Container maxWidth="xl">
        <Box sx={{ mb: 4 }}>
          <Typography variant="h4" gutterBottom>
            Quản lý môn học
          </Typography>
          <Typography variant="body2" color="text.secondary">
            Trang quản lý danh sách các môn học trong hệ thống
          </Typography>
        </Box>

        {error && (
          <Alert severity="error" sx={{ mb: 2 }}>
            {error}
          </Alert>
        )}

        <Paper elevation={3} sx={{ p: 3, mb: 3 }}>
          {isLoading ? (
            <Box sx={{ display: "flex", justifyContent: "center", py: 5 }}>
              <CircularProgress />
            </Box>
          ) : (
            <Box sx={{ mb: 2 }}>
              <Typography variant="h6" gutterBottom>
                {subjects.length > 0
                  ? `Đã tìm thấy ${subjects.length} môn học`
                  : "Không tìm thấy môn học nào"}
              </Typography>

              <Typography variant="body2" paragraph>
                {subjects.length > 0
                  ? "Dưới đây là danh sách các môn học trong hệ thống"
                  : "Chưa có môn học nào được tạo trong hệ thống"}
              </Typography>

              {subjects.length > 0 && (
                <Box
                  component="pre"
                  sx={{
                    p: 2,
                    backgroundColor: "#f5f5f5",
                    borderRadius: 1,
                    overflow: "auto",
                    maxHeight: "300px",
                  }}
                >
                  {JSON.stringify(subjects, null, 2)}
                </Box>
              )}

              <Button
                variant="contained"
                onClick={handleRefresh}
                sx={{ mt: 2 }}
              >
                Làm mới dữ liệu
              </Button>
            </Box>
          )}
        </Paper>
      </Container>
    </MainLayout>
  );
};

export default SubjectsPage;
