          <Button
            onClick={handleDeleteStudentFromMainClass}
            color="error"
            autoFocus
          >
            Xóa
          </Button>
        </DialogActions>
      </Dialog>

      {/* Confirmation Dialog for deleting main class */}
      {renderConfirmDeleteMainClassDialog()}
    </Box>
  );
};

export default TeacherMainClassPage;
