            sx={{
              display: "flex",
              alignItems: "center",
              justifyContent: "space-between",
              mb: 2,
            }}
          >
            <Typography
              variant="h6"
              sx={{
                color: "primary.main",
                fontWeight: "bold",
                display: "flex",
                alignItems: "center",
              }}
            >
              <Class sx={{ mr: 1 }} />
              Thông tin lớp {mainClass.name}
            </Typography>
            <Box sx={{ display: "flex", gap: 1 }}>
