                  max={99}
                  sx={{ "& .MuiBadge-badge": { fontWeight: "bold" } }}
                >
                  <School sx={{ mr: 1 }} />
                </Badge>
                <Box sx={{ ml: approvedStudents.length > 0 ? 1 : 0 }}>
                  Sinh viên của lớp
                </Box>
              </Box>
