                      </TableCell>
                      <TableCell>{student.email}</TableCell>
                      <TableCell>
                        {student.school_info?.student_id ||
                          student.school_info?.student_code || (
                            <Typography
                              variant="body2"
                              color="text.secondary"
                              fontStyle="italic"
