import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import axios from "axios";

const API_URL = process.env.REACT_APP_API_URL || "http://localhost:5000/api";

// Thunks cho quản lý cơ sở
export const fetchAllCampuses = createAsyncThunk(
  "facility/fetchAllCampuses",
  async (
    { page = 1, limit = 10, search = "" },
    { getState, rejectWithValue }
  ) => {
    try {
      const { token } = getState().auth;
      const response = await axios.get(
        `${API_URL}/facilities/campuses?page=${page}&limit=${limit}&search=${search}`,
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      return response.data;
    } catch (error) {
      return rejectWithValue(
        error.response?.data?.message || "Không thể lấy danh sách cơ sở"
      );
    }
  }
);

export const createCampus = createAsyncThunk(
  "facility/createCampus",
  async (campusData, { getState, rejectWithValue }) => {
    try {
      const { token } = getState().auth;
      const response = await axios.post(
        `${API_URL}/facilities/campuses`,
        campusData,
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      return response.data;
    } catch (error) {
      return rejectWithValue(
        error.response?.data?.message || "Không thể tạo cơ sở mới"
      );
    }
  }
);

export const updateCampus = createAsyncThunk(
  "facility/updateCampus",
  async ({ campusId, campusData }, { getState, rejectWithValue }) => {
    try {
      const { token } = getState().auth;
      const response = await axios.put(
        `${API_URL}/facilities/campuses/${campusId}`,
        campusData,
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      return response.data;
    } catch (error) {
      return rejectWithValue(
        error.response?.data?.message || "Không thể cập nhật cơ sở"
      );
    }
  }
);

export const deleteCampus = createAsyncThunk(
  "facility/deleteCampus",
  async (campusId, { getState, rejectWithValue }) => {
    try {
      const { token } = getState().auth;
      await axios.delete(`${API_URL}/facilities/campuses/${campusId}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      return campusId;
    } catch (error) {
      return rejectWithValue(
        error.response?.data?.message || "Không thể xóa cơ sở"
      );
    }
  }
);

// Thunks cho quản lý tòa nhà
export const fetchAllBuildings = createAsyncThunk(
  "facility/fetchAllBuildings",
  async (
    { page = 1, limit = 10, search = "" },
    { getState, rejectWithValue }
  ) => {
    try {
      const { token } = getState().auth;
      const response = await axios.get(
        `${API_URL}/facilities/buildings?page=${page}&limit=${limit}&search=${search}`,
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      return response.data;
    } catch (error) {
      return rejectWithValue(
        error.response?.data?.message || "Không thể lấy danh sách tòa nhà"
      );
    }
  }
);

export const createBuilding = createAsyncThunk(
  "facility/createBuilding",
  async (buildingData, { rejectWithValue, getState }) => {
    try {
      // Lấy token từ state của auth
      const { auth } = getState();
      if (!auth?.user?.token) {
        return rejectWithValue("Không có token xác thực");
      }

      // Xử lý dữ liệu trước khi gửi
      const dataToSubmit = { ...buildingData };

      // Kiểm tra và làm sạch campus_id
      if (buildingData.campus_id) {
        // Kiểm tra nếu campus_id là một MongoDB ObjectId hợp lệ (24 ký tự hex)
        if (!/^[0-9a-fA-F]{24}$/.test(buildingData.campus_id)) {
          delete dataToSubmit.campus_id; // Xóa campus_id nếu không hợp lệ
        }
      }

      // Chuyển đổi các giá trị số
      if (buildingData.floors_count) {
        dataToSubmit.floors_count = Number(buildingData.floors_count);
      }

      if (buildingData.year_built) {
        dataToSubmit.year_built = Number(buildingData.year_built);
      }

      const response = await axios.post(
        `${API_URL}/facilities/buildings`,
        dataToSubmit,
        {
          headers: {
            Authorization: `Bearer ${auth.user.token}`,
          },
        }
      );

      return response.data;
    } catch (error) {
      if (error.response?.data?.message) {
        return rejectWithValue(error.response.data.message);
      }

      if (error.message === "jwt expired" || error.message.includes("jwt")) {
        return rejectWithValue(
          "Token đã hết hạn hoặc không hợp lệ. Vui lòng đăng nhập lại"
        );
      }

      return rejectWithValue(error.message || "Có lỗi xảy ra khi tạo tòa nhà");
    }
  }
);

export const updateBuilding = createAsyncThunk(
  "facility/updateBuilding",
  async ({ buildingId, buildingData }, { getState, rejectWithValue }) => {
    try {
      const { token } = getState().auth;
      const response = await axios.put(
        `${API_URL}/facilities/buildings/${buildingId}`,
        buildingData,
        {
          headers: {
