import React, { useState, useEffect } from "react";
import { useDispatch } from "react-redux";
import { useNavigate, useLocation } from "react-router-dom";
import { useSnackbar } from "notistack";
import axios from "../utils/axios";
import {
  Container,
  Box,
  Typography,
  Button,
  Paper,
  TextField,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  CircularProgress,
  Avatar,
  Grid,
  FormHelperText,
  Autocomplete,
  Card,
  CardContent,
  CardActionArea,
  Alert,
  Divider,
  Chip,
} from "@mui/material";
import { School, Work, Person, Check, Info } from "@mui/icons-material";
import { setCredentials } from "../redux/slices/authSlice";

const API_URL = process.env.REACT_APP_API_URL || "http://localhost:5000/api";

const CompleteRegistrationPage = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const location = useLocation();
  const { enqueueSnackbar } = useSnackbar();
  const [isLoading, setIsLoading] = useState(false);
  const [advisors, setAdvisors] = useState([]);
  const [loadingAdvisors, setLoadingAdvisors] = useState(false);
  const [showRoleSelection, setShowRoleSelection] = useState(true);

  // Lấy thông tin từ URL params
  const queryParams = new URLSearchParams(location.search);
  const email = queryParams.get("email");
  const googleId = queryParams.get("googleId");
  const name = queryParams.get("name");
  const avatar = queryParams.get("avatar");
  const needsRegistration = queryParams.get("needsRegistration") === "true";

  const [formData, setFormData] = useState({
    role: "",
    fullName: name || "",
    phone: "",
    department: "",
    studentCode: "",
    teacherCode: "",
    major: "",
    class: "",
    year: new Date().getFullYear(),
    advisorId: "",
  });

  const [formErrors, setFormErrors] = useState({
    role: "",
    fullName: "",
    advisorId: "",
  });

  useEffect(() => {
    // Kiểm tra xem có đủ thông tin không
    if (!email || !googleId) {
      enqueueSnackbar("Thiếu thông tin cần thiết để hoàn tất đăng ký", {
        variant: "error",
      });
      navigate("/login");
    }

    // Hiển thị thông báo cho người dùng mới
    if (needsRegistration) {
      enqueueSnackbar("Vui lòng hoàn tất thông tin đăng ký của bạn", {
        variant: "info",
        autoHideDuration: 5000,
      });
    }
  }, [email, googleId, navigate, enqueueSnackbar, needsRegistration]);

  // Lấy danh sách giáo viên cố vấn khi người dùng chọn vai trò sinh viên
  useEffect(() => {
    if (formData.role === "student") {
      fetchAdvisors();
    }
  }, [formData.role]);

  const fetchAdvisors = async () => {
    setLoadingAdvisors(true);
    try {
      const response = await axios.get(`${API_URL}/users/teachers`);
      if (response.data.success) {
        setAdvisors(response.data.data);
      }
    } catch (error) {
      console.error("Error fetching advisors:", error);
      setAdvisors([]);
      enqueueSnackbar("Không thể tải danh sách giáo viên cố vấn", {
        variant: "error",
      });
    } finally {
      setLoadingAdvisors(false);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });

    // Xóa lỗi khi người dùng nhập
    if (formErrors[name]) {
      setFormErrors({ ...formErrors, [name]: "" });
    }
  };

  const handleAdvisorChange = (event, newValue) => {
    setFormData({ ...formData, advisorId: newValue?._id || "" });
    if (formErrors.advisorId) {
      setFormErrors({ ...formErrors, advisorId: "" });
    }
  };

  const handleRoleSelect = (role) => {
    setFormData({ ...formData, role });
    setFormErrors({ ...formErrors, role: "" });
    setShowRoleSelection(false);
  };

  const validateForm = () => {
    let valid = true;
    const errors = { ...formErrors };

    // Kiểm tra vai trò
    if (!formData.role) {
      errors.role = "Vui lòng chọn vai trò";
      valid = false;
    }

    // Kiểm tra họ tên
    if (!formData.fullName) {
      errors.fullName = "Họ tên là bắt buộc";
      valid = false;
    }

    // Kiểm tra giáo viên cố vấn cho sinh viên
    if (formData.role === "student" && !formData.advisorId) {
      errors.advisorId = "Vui lòng chọn giáo viên cố vấn";
      valid = false;
    }

    setFormErrors(errors);
    return valid;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateForm()) {
      return;
    }

    setIsLoading(true);
    try {
      // Chuẩn bị dữ liệu
      const registrationData = {
        email,
        googleId,
        fullName: formData.fullName,
        role: formData.role,
        avatarUrl: avatar,
      };

      // Thêm thông tin cố vấn cho sinh viên
      if (formData.role === "student" && formData.advisorId) {
        registrationData.advisor_id = formData.advisorId;
      }

      // Thêm thông tin trường học
      registrationData.school_info = {
        department: formData.department,
        major: formData.major,
        class: formData.class,
        year: formData.year,
      };

      // Thêm mã sinh viên/giáo viên
      if (formData.role === "student" && formData.studentCode) {
        registrationData.school_info.student_code = formData.studentCode;
      } else if (formData.role === "teacher" && formData.teacherCode) {
        registrationData.school_info.teacher_code = formData.teacherCode;
      }

      // Thêm số điện thoại
      if (formData.phone) {
        registrationData.contact = {
          phone: formData.phone,
        };
      }

      const response = await axios.post(
        `${API_URL}/auth/google-complete`,
        registrationData
      );

      if (response.data.success) {
        dispatch(
          setCredentials({
            token: response.data.token,
            user: response.data.user,
          })
        );

        enqueueSnackbar(response.data.message || "Đăng ký thành công!", {
          variant: "success",
        });

        // Chuyển hướng đến trang chờ phê duyệt
        navigate(`/pending-approval?role=${formData.role}`);
      }
    } catch (error) {
      const errorMessage = error.response?.data?.message || "Đăng ký thất bại";
      enqueueSnackbar(errorMessage, { variant: "error" });
    } finally {
      setIsLoading(false);
    }
  };

  // Hiển thị trang chọn vai trò
  const renderRoleSelection = () => {
    return (
      <Box>
        <Alert severity="info" sx={{ mb: 3 }}>
          <Typography variant="body1">
            <Info fontSize="small" sx={{ verticalAlign: "middle", mr: 1 }} />
            Bạn cần chọn vai trò của mình để tiếp tục đăng ký.
          </Typography>
        </Alert>

        <Typography variant="h6" gutterBottom>
          Bạn là ai trong hệ thống?
        </Typography>

        <Grid container spacing={3} sx={{ mt: 1 }}>
          <Grid item xs={12} md={6}>
            <Card
              variant="outlined"
              sx={{
                height: "100%",
                borderColor:
                  formData.role === "student" ? "primary.main" : "grey.300",
                boxShadow: formData.role === "student" ? 3 : 1,
              }}
            >
              <CardActionArea
                onClick={() => handleRoleSelect("student")}
                sx={{ height: "100%", p: 2 }}
              >
                <CardContent sx={{ textAlign: "center" }}>
                  <School color="primary" sx={{ fontSize: 60, mb: 2 }} />
                  <Typography variant="h5" component="div" gutterBottom>
                    Sinh Viên
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    Đăng ký với tư cách sinh viên và chọn giáo viên cố vấn của
                    bạn.
                  </Typography>
                  {formData.role === "student" && (
                    <Check
                      sx={{
                        position: "absolute",
                        right: 16,
                        top: 16,
                        color: "primary.main",
                        backgroundColor: "white",
                        borderRadius: "50%",
                      }}
                    />
                  )}
                </CardContent>
              </CardActionArea>
            </Card>
          </Grid>

          <Grid item xs={12} md={6}>
            <Card
              variant="outlined"
              sx={{
                height: "100%",
                borderColor:
                  formData.role === "teacher" ? "primary.main" : "grey.300",
                boxShadow: formData.role === "teacher" ? 3 : 1,
              }}
            >
              <CardActionArea
                onClick={() => handleRoleSelect("teacher")}
                sx={{ height: "100%", p: 2 }}
              >
                <CardContent sx={{ textAlign: "center" }}>
                  <Work color="secondary" sx={{ fontSize: 60, mb: 2 }} />
                  <Typography variant="h5" component="div" gutterBottom>
                    Giảng Viên
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    Đăng ký với tư cách giảng viên để quản lý lớp học và sinh
                    viên.
                  </Typography>
                  {formData.role === "teacher" && (
                    <Check
                      sx={{
                        position: "absolute",
                        right: 16,
                        top: 16,
                        color: "primary.main",
                        backgroundColor: "white",
                        borderRadius: "50%",
                      }}
                    />
                  )}
                </CardContent>
              </CardActionArea>
            </Card>
          </Grid>
        </Grid>

        <Box sx={{ mt: 3, textAlign: "right" }}>
          <Button
            variant="contained"
            onClick={() => setShowRoleSelection(false)}
            disabled={!formData.role}
            size="large"
          >
            Tiếp tục
          </Button>
        </Box>
      </Box>
    );
  };

  // Hiển thị form nhập thông tin
  const renderRegistrationForm = () => {
    return (
      <Box component="form" onSubmit={handleSubmit} noValidate>
        <Box sx={{ mb: 3 }}>
          <Divider>
            <Chip
              icon={formData.role === "student" ? <School /> : <Work />}
              label={formData.role === "student" ? "Sinh viên" : "Giảng viên"}
              color="primary"
            />
          </Divider>
        </Box>

        <Grid container spacing={2}>
          <Grid item xs={12}>
            <TextField
              required
              fullWidth
              id="fullName"
              label="Họ và tên"
              name="fullName"
              value={formData.fullName}
              onChange={handleChange}
              error={!!formErrors.fullName}
              helperText={formErrors.fullName}
            />
          </Grid>

          <Grid item xs={12} md={6}>
            <TextField
              fullWidth
              id="phone"
              label="Số điện thoại"
              name="phone"
              value={formData.phone}
              onChange={handleChange}
            />
          </Grid>

          <Grid item xs={12} md={6}>
            <TextField
              fullWidth
              id="department"
              label="Khoa"
              name="department"
              value={formData.department}
              onChange={handleChange}
            />
          </Grid>

          {formData.role === "student" && (
            <>
              <Grid item xs={12} md={6}>
                <TextField
                  fullWidth
                  id="studentCode"
                  label="Mã sinh viên"
                  name="studentCode"
                  value={formData.studentCode}
                  onChange={handleChange}
                />
              </Grid>

              <Grid item xs={12} md={6}>
                <TextField
                  fullWidth
                  id="major"
                  label="Ngành học"
                  name="major"
                  value={formData.major}
                  onChange={handleChange}
                />
              </Grid>

              <Grid item xs={12} md={6}>
                <TextField
                  fullWidth
                  id="class"
                  label="Lớp"
                  name="class"
                  value={formData.class}
                  onChange={handleChange}
                />
              </Grid>

              <Grid item xs={12} md={6}>
                <TextField
                  fullWidth
                  id="year"
                  label="Khóa học"
                  name="year"
                  type="number"
                  value={formData.year}
                  onChange={handleChange}
                />
              </Grid>

              <Grid item xs={12}>
                <Autocomplete
                  id="advisorId"
                  options={advisors}
                  getOptionLabel={(option) =>
                    `${option.full_name} (${option.email})`
                  }
                  loading={loadingAdvisors}
                  onChange={handleAdvisorChange}
                  renderInput={(params) => (
                    <TextField
                      {...params}
                      required
                      label="Giáo viên cố vấn"
                      error={!!formErrors.advisorId}
                      helperText={
                        formErrors.advisorId ||
                        "Vui lòng chọn giáo viên cố vấn của bạn"
                      }
                      InputProps={{
                        ...params.InputProps,
                        endAdornment: (
                          <>
                            {loadingAdvisors ? (
                              <CircularProgress color="inherit" size={20} />
                            ) : null}
                            {params.InputProps.endAdornment}
                          </>
                        ),
                      }}
                    />
                  )}
                />
                {advisors.length === 0 && !loadingAdvisors && (
                  <Alert severity="warning" sx={{ mt: 1 }}>
                    Không có giáo viên nào được tìm thấy. Vui lòng liên hệ với
                    quản trị viên.
                  </Alert>
                )}
              </Grid>
            </>
          )}

          {formData.role === "teacher" && (
            <Grid item xs={12} md={6}>
              <TextField
                fullWidth
                id="teacherCode"
                label="Mã giảng viên"
                name="teacherCode"
                value={formData.teacherCode}
                onChange={handleChange}
              />
            </Grid>
          )}
        </Grid>

        <Box sx={{ mt: 3, display: "flex", justifyContent: "space-between" }}>
          <Button variant="outlined" onClick={() => setShowRoleSelection(true)}>
            Quay lại
          </Button>

          <Button type="submit" variant="contained" disabled={isLoading}>
            {isLoading ? (
              <CircularProgress size={24} color="inherit" />
            ) : (
              "Hoàn tất đăng ký"
            )}
          </Button>
        </Box>
      </Box>
    );
  };

  return (
    <Container component="main" maxWidth="md">
      <Box
        sx={{
          marginTop: 4,
          marginBottom: 4,
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
        }}
      >
        <Paper
          elevation={3}
          sx={{
            padding: 4,
            width: "100%",
            borderRadius: 3,
          }}
        >
          <Box
            sx={{
              display: "flex",
              alignItems: "center",
              mb: 4,
              flexDirection: "column",
            }}
          >
            <Typography component="h1" variant="h4" gutterBottom>
              {showRoleSelection ? "Chọn vai trò của bạn" : "Hoàn tất đăng ký"}
            </Typography>

            <Typography variant="body1" color="text.secondary" mb={2}>
              {showRoleSelection
                ? "Vui lòng chọn vai trò để tiếp tục quá trình đăng ký"
                : "Vui lòng cung cấp thêm thông tin để hoàn tất đăng ký"}
            </Typography>

            {avatar && (
              <Avatar
                src={avatar}
                alt={name || email}
                sx={{ width: 80, height: 80, mb: 2 }}
              />
            )}

            <Typography variant="subtitle1" gutterBottom>
              {email}
            </Typography>
          </Box>

          {showRoleSelection ? renderRoleSelection() : renderRegistrationForm()}
        </Paper>
      </Box>
    </Container>
  );
};

export default CompleteRegistrationPage;
