import React, { useState, useEffect, useCallback } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { useSelector } from "react-redux";
import { useSnackbar } from "notistack";
import axios from "axios";
import {
  Box,
  Typography,
  Card,
  CardContent,
  Grid,
  Paper,
  Button,
  Tabs,
  Tab,
  CircularProgress,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Chip,
  IconButton,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  List,
  ListItem,
  ListItemText,
  ListItemSecondaryAction,
  Divider,
  Alert,
  Tooltip,
  Checkbox,
} from "@mui/material";
import {
  PersonAdd,
  Delete,
  Add,
  Edit,
  PlayArrow,
  Stop,
  CheckCircle,
  Warning,
  Info,
  CalendarToday,
  AccessTime,
  Room,
  Download,
  School,
} from "@mui/icons-material";

const API_URL = process.env.REACT_APP_API_URL || "http://localhost:5000/api";

function a11yProps(index) {
  return {
    id: `class-tab-${index}`,
    "aria-controls": `class-tabpanel-${index}`,
  };
}

function TabPanel(props) {
  const { children, value, index, ...other } = props;

  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      id={`class-tabpanel-${index}`}
      aria-labelledby={`class-tab-${index}`}
      {...other}
    >
      {value === index && <Box sx={{ p: 3 }}>{children}</Box>}
    </div>
  );
}

const TeacherClassDetailPage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { enqueueSnackbar } = useSnackbar();
  const { token } = useSelector((state) => state.auth);

  // State cho dữ liệu
  const [classInfo, setClassInfo] = useState(null);
  const [students, setStudents] = useState([]);
  const [attendanceSessions, setAttendanceSessions] = useState([]);
  const [attendanceStats, setAttendanceStats] = useState({
    total: 0,
    pending: 0,
    completed: 0,
  });
  const [isLoading, setIsLoading] = useState(true);

  // State cho UI
  const [tabValue, setTabValue] = useState(0);
  const [openSessionDialog, setOpenSessionDialog] = useState(false);
  const [sessionFormData, setSessionFormData] = useState({
    title: "",
    date: new Date().toISOString().split("T")[0],
    start_time: "08:00",
    end_time: "09:30",
    location: "",
  });

  // State cho thêm sinh viên
  const [openAddStudentDialog, setOpenAddStudentDialog] = useState(false);
  const [availableStudents, setAvailableStudents] = useState([]);
  const [selectedStudents, setSelectedStudents] = useState([]);
  const [searchStudent, setSearchStudent] = useState("");

  // Load dữ liệu ban đầu
  const fetchClassData = useCallback(async () => {
    try {
      setIsLoading(true);

      // Kiểm tra id có tồn tại không
      if (!id) {
        enqueueSnackbar("ID lớp học không hợp lệ", { variant: "error" });
        navigate("/teacher/classes");
        return;
      }

      // Kiểm tra id có đúng định dạng MongoDB ObjectId không (24 ký tự hex)
      const objectIdPattern = /^[0-9a-fA-F]{24}$/;
      if (!objectIdPattern.test(id)) {
        enqueueSnackbar("ID lớp học không hợp lệ", { variant: "error" });
        navigate("/teacher/classes");
        return;
      }

      // Lấy thông tin lớp học
      const classResponse = await axios.get(
        `${API_URL}/classes/teaching/${id}`,
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );

      // Kiểm tra nếu không có dữ liệu trả về
      if (!classResponse.data.success || !classResponse.data.data) {
        enqueueSnackbar("Không tìm thấy thông tin lớp học", {
          variant: "error",
        });
        navigate("/teacher/classes");
        return;
      }

      setClassInfo(classResponse.data.data);

      // Lấy danh sách sinh viên đã có trong lớp
      if (classResponse.data.data.students) {
        setStudents(classResponse.data.data.students);
      }

      // Lấy danh sách phiên điểm danh
      const sessionsResponse = await axios.get(
        `${API_URL}/attendance/teaching-class/${id}/sessions`,
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      setAttendanceSessions(sessionsResponse.data.data || []);

      // Tính toán thống kê điểm danh
      const stats = {
        total: sessionsResponse.data.data?.length || 0,
        pending: 0,
        completed: 0,
      };

      sessionsResponse.data.data?.forEach((session) => {
        if (session.status === "completed") {
          stats.completed++;
        } else {
          stats.pending++;
        }
      });

      setAttendanceStats(stats);
    } catch (error) {
      console.error("Lỗi khi tải dữ liệu lớp học:", error);
      // Xử lý các loại lỗi từ API
      if (error.response) {
        // Nếu máy chủ phản hồi với mã lỗi
        if (error.response.status === 404) {
          enqueueSnackbar("Không tìm thấy lớp học với ID đã cho", {
            variant: "error",
          });
        } else if (error.response.status === 400) {
          enqueueSnackbar("ID lớp học không hợp lệ", { variant: "error" });
        } else {
          enqueueSnackbar(
            error.response.data?.message || "Lỗi khi tải dữ liệu lớp học",
            { variant: "error" }
          );
        }
      } else {
        enqueueSnackbar("Không thể kết nối đến máy chủ", { variant: "error" });
      }
      navigate("/teacher/classes");
    } finally {
      setIsLoading(false);
    }
  }, [id, token, enqueueSnackbar, navigate]);

  useEffect(() => {
    fetchClassData();
  }, [fetchClassData]);

  // Thay đổi tab
  const handleChangeTab = (event, newValue) => {
    setTabValue(newValue);
  };

  // Mở dialog tạo phiên điểm danh
  const handleOpenSessionDialog = () => {
    setSessionFormData({
      title: `Buổi học ${attendanceSessions.length + 1}`,
      date: new Date().toISOString().split("T")[0],
      start_time: "08:00",
      end_time: "09:30",
      location: "",
    });
    setOpenSessionDialog(true);
  };

  // Đóng dialog tạo phiên điểm danh
  const handleCloseSessionDialog = () => {
    setOpenSessionDialog(false);
  };

  // Cập nhật form dữ liệu phiên điểm danh
  const handleSessionFormChange = (e) => {
    const { name, value } = e.target;
    setSessionFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  // Tạo phiên điểm danh mới
  const handleCreateSession = async () => {
    try {
      const { title, date, start_time, end_time, location } = sessionFormData;

      // Kiểm tra dữ liệu
      if (!title || !date || !start_time || !end_time) {
        enqueueSnackbar("Vui lòng điền đầy đủ thông tin", { variant: "error" });
        return;
      }

      const response = await axios.post(
        `${API_URL}/attendance/sessions`,
        {
          title,
          date,
          start_time,
          end_time,
          location,
          teaching_class_id: id,
        },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );

      enqueueSnackbar("Tạo phiên điểm danh thành công", {
        variant: "success",
      });
      setOpenSessionDialog(false);

      // Cập nhật danh sách phiên
      setAttendanceSessions((prev) => [...prev, response.data.data]);
      setAttendanceStats((prev) => ({
        ...prev,
        total: prev.total + 1,
        pending: prev.pending + 1,
      }));
    } catch (error) {
      console.error("Lỗi khi tạo phiên điểm danh:", error);
      enqueueSnackbar(
        error.response?.data?.message || "Lỗi khi tạo phiên điểm danh",
        { variant: "error" }
      );
    }
  };

  // Bắt đầu phiên điểm danh
  const handleStartSession = (sessionId) => {
    navigate(`/teacher/attendance/${id}/${sessionId}`);
  };

  // Mở dialog thêm sinh viên
  const handleOpenAddStudentDialog = async () => {
    try {
      // Lấy danh sách sinh viên chưa có trong lớp
      const response = await axios.get(
        `${API_URL}/users?role=student&status=approved&limit=100`,
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );

      // Lọc sinh viên chưa có trong lớp
      const currentStudentIds = students.map((s) => s._id);
      const filteredStudents = response.data.data.filter(
        (s) => !currentStudentIds.includes(s._id)
      );

      setAvailableStudents(filteredStudents);
      setSelectedStudents([]);
      setOpenAddStudentDialog(true);
    } catch (error) {
      console.error("Lỗi khi tải danh sách sinh viên:", error);
      enqueueSnackbar("Lỗi khi tải danh sách sinh viên", { variant: "error" });
    }
  };

  // Đóng dialog thêm sinh viên
  const handleCloseAddStudentDialog = () => {
    setOpenAddStudentDialog(false);
  };

  // Xử lý chọn sinh viên để thêm
  const handleSelectStudent = (studentId) => {
    if (selectedStudents.includes(studentId)) {
      setSelectedStudents(selectedStudents.filter((id) => id !== studentId));
    } else {
      setSelectedStudents([...selectedStudents, studentId]);
    }
  };

  // Thêm sinh viên vào lớp
  const handleAddStudents = async () => {
    try {
      if (selectedStudents.length === 0) {
        enqueueSnackbar("Vui lòng chọn ít nhất một sinh viên", {
          variant: "warning",
        });
        return;
      }

      await axios.post(
        `${API_URL}/classes/teaching/${id}/students/batch`,
        {
          student_ids: selectedStudents,
        },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );

      enqueueSnackbar(`Đã thêm ${selectedStudents.length} sinh viên vào lớp`, {
        variant: "success",
      });
      setOpenAddStudentDialog(false);

      // Cập nhật lại danh sách sinh viên
      fetchClassData();
    } catch (error) {
      console.error("Lỗi khi thêm sinh viên:", error);
      enqueueSnackbar("Lỗi khi thêm sinh viên vào lớp", { variant: "error" });
    }
  };

  // Xóa sinh viên khỏi lớp
  const handleRemoveStudent = async (studentId) => {
    if (
      !window.confirm("Bạn có chắc chắn muốn xóa sinh viên này khỏi lớp không?")
    ) {
      return;
    }

    try {
      await axios.delete(
        `${API_URL}/classes/teaching/${id}/students/${studentId}`,
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );

      enqueueSnackbar("Đã xóa sinh viên khỏi lớp", { variant: "success" });

      // Cập nhật lại danh sách sinh viên
      setStudents(students.filter((s) => s._id !== studentId));
    } catch (error) {
      console.error("Lỗi khi xóa sinh viên:", error);
      enqueueSnackbar("Lỗi khi xóa sinh viên khỏi lớp", { variant: "error" });
    }
  };

  // Xuất danh sách sinh viên
  const handleExportStudentList = () => {
    try {
      const studentsData = students.map((student, index) => ({
        STT: index + 1,
        "Mã SV": student.student_id,
        "Họ và tên": student.full_name,
        Email: student.email,
      }));

      const header = ["STT", "Mã SV", "Họ và tên", "Email"];
      const csvContent =
        "data:text/csv;charset=utf-8," +
        [
          header.join(","),
          ...studentsData.map((row) =>
            [row.STT, row["Mã SV"], row["Họ và tên"], row.Email].join(",")
          ),
        ].join("\n");

      const encodedUri = encodeURI(csvContent);
      const link = document.createElement("a");
      link.setAttribute("href", encodedUri);
      link.setAttribute(
        "download",
        `danh_sach_sinh_vien_${classInfo?.class_name}.csv`
      );
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);

      enqueueSnackbar("Xuất danh sách sinh viên thành công", {
        variant: "success",
      });
    } catch (error) {
      console.error("Lỗi khi xuất danh sách:", error);
      enqueueSnackbar("Lỗi khi xuất danh sách sinh viên", { variant: "error" });
    }
  };

  // Lọc sinh viên theo từ khóa tìm kiếm
  const filteredAvailableStudents = availableStudents.filter(
    (student) =>
      student.full_name.toLowerCase().includes(searchStudent.toLowerCase()) ||
      student.student_id?.toLowerCase().includes(searchStudent.toLowerCase()) ||
      student.email.toLowerCase().includes(searchStudent.toLowerCase())
  );

  if (isLoading) {
    return (
      <Box
        sx={{
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          height: "80vh",
        }}
      >
        <CircularProgress />
      </Box>
    );
  }

  if (!classInfo) {
    return (
      <Box sx={{ py: 3 }}>
        <Card sx={{ textAlign: "center", p: 4 }}>
          <Typography variant="h5" color="error" gutterBottom>
            Không tìm thấy lớp học
          </Typography>
          <Typography variant="body1" sx={{ mb: 3 }}>
            Lớp học bạn đang tìm kiếm không tồn tại hoặc đã bị xóa.
          </Typography>
          <Button
            variant="contained"
            color="primary"
            onClick={() => navigate("/teacher/classes")}
          >
            Quay lại danh sách lớp
          </Button>
        </Card>
      </Box>
    );
  }

  return (
    <Box sx={{ padding: 3 }}>
      <Box sx={{ mb: 3 }}>
        <Button
          variant="outlined"
          onClick={() => navigate("/teacher/classes")}
          sx={{ mb: 2 }}
        >
          Quay lại danh sách lớp
        </Button>
        <Typography variant="h4" gutterBottom>
          {classInfo.class_name}
        </Typography>
        <Typography variant="subtitle1" color="text.secondary" gutterBottom>
          {classInfo.course_id?.name} ({classInfo.course_id?.code})
        </Typography>
      </Box>

      <Grid container spacing={3} sx={{ mb: 3 }}>
        <Grid item xs={12} md={4}>
          <Card>
            <CardContent>
              <Typography variant="h6" gutterBottom>
                Thông tin lớp học
              </Typography>
              <Box sx={{ mt: 2 }}>
                <Typography
                  variant="body2"
                  sx={{ mb: 1, display: "flex", alignItems: "center" }}
                >
                  <CalendarToday sx={{ mr: 1, fontSize: "small" }} />
                  Học kỳ: {classInfo.semester?.name || "N/A"}
                </Typography>
                <Typography
                  variant="body2"
                  sx={{ mb: 1, display: "flex", alignItems: "center" }}
                >
                  <School sx={{ mr: 1, fontSize: "small" }} />
                  Năm học: {classInfo.academic_year || "N/A"}
                </Typography>
                <Typography
                  variant="body2"
                  sx={{ mb: 1, display: "flex", alignItems: "center" }}
                >
                  <Info sx={{ mr: 1, fontSize: "small" }} />
                  Số buổi học: {classInfo.total_sessions || 15}
                </Typography>
              </Box>
            </CardContent>
          </Card>
        </Grid>
        <Grid item xs={12} md={4}>
          <Card>
            <CardContent>
              <Typography variant="h6" gutterBottom>
                Điểm danh
              </Typography>
              <Box sx={{ mt: 2 }}>
                <Typography variant="body2" sx={{ mb: 1 }}>
                  Tổng số buổi: {attendanceStats.total}
                </Typography>
                <Typography variant="body2" sx={{ mb: 1 }}>
                  Đã hoàn thành: {attendanceStats.completed}
                </Typography>
                <Typography variant="body2" sx={{ mb: 1 }}>
                  Chưa hoàn thành: {attendanceStats.pending}
                </Typography>
              </Box>
            </CardContent>
          </Card>
        </Grid>
        <Grid item xs={12} md={4}>
          <Card>
            <CardContent>
              <Typography variant="h6" gutterBottom>
                Sinh viên
              </Typography>
              <Box sx={{ mt: 2 }}>
                <Typography variant="body2" sx={{ mb: 1 }}>
                  Tổng số sinh viên: {students.length}
                </Typography>
              </Box>
              <Button
                variant="outlined"
                size="small"
                startIcon={<Download />}
                onClick={handleExportStudentList}
                sx={{ mt: 2 }}
              >
                Xuất danh sách
              </Button>
            </CardContent>
          </Card>
        </Grid>
      </Grid>

      <Box sx={{ width: "100%" }}>
        <Box sx={{ borderBottom: 1, borderColor: "divider" }}>
          <Tabs
            value={tabValue}
            onChange={handleChangeTab}
            aria-label="lớp học tabs"
          >
            <Tab label="Sinh viên" {...a11yProps(0)} />
            <Tab label="Phiên điểm danh" {...a11yProps(1)} />
          </Tabs>
        </Box>
        <TabPanel value={tabValue} index={0}>
          <Box sx={{ display: "flex", justifyContent: "flex-end", mb: 2 }}>
            <Button
              variant="contained"
              startIcon={<PersonAdd />}
              onClick={handleOpenAddStudentDialog}
            >
              Thêm sinh viên
            </Button>
          </Box>
          <TableContainer component={Paper}>
            <Table>
              <TableHead>
                <TableRow>
                  <TableCell>STT</TableCell>
                  <TableCell>Mã sinh viên</TableCell>
                  <TableCell>Họ và tên</TableCell>
                  <TableCell>Email</TableCell>
                  <TableCell>Trạng thái</TableCell>
                  <TableCell>Thao tác</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {students.length > 0 ? (
                  students.map((student, index) => (
                    <TableRow key={student._id}>
                      <TableCell>{index + 1}</TableCell>
                      <TableCell>{student.student_id || "N/A"}</TableCell>
                      <TableCell>{student.full_name}</TableCell>
                      <TableCell>{student.email}</TableCell>
                      <TableCell>
                        {student.face_registered ? (
                          <Chip
                            label="Đã đăng ký khuôn mặt"
                            color="success"
                            size="small"
                          />
                        ) : (
                          <Chip
                            label="Chưa đăng ký khuôn mặt"
                            color="warning"
                            size="small"
                          />
                        )}
                      </TableCell>
                      <TableCell>
                        <Tooltip title="Xóa khỏi lớp">
                          <IconButton
                            size="small"
                            color="error"
                            onClick={() => handleRemoveStudent(student._id)}
                          >
                            <Delete />
                          </IconButton>
                        </Tooltip>
                      </TableCell>
                    </TableRow>
                  ))
                ) : (
                  <TableRow>
                    <TableCell colSpan={6} align="center">
                      Chưa có sinh viên nào trong lớp
                    </TableCell>
                  </TableRow>
                )}
              </TableBody>
            </Table>
          </TableContainer>
        </TabPanel>
        <TabPanel value={tabValue} index={1}>
          <Box sx={{ display: "flex", justifyContent: "flex-end", mb: 2 }}>
            <Button
              variant="contained"
              startIcon={<Add />}
              onClick={handleOpenSessionDialog}
            >
              Tạo phiên điểm danh
            </Button>
          </Box>
          <TableContainer component={Paper}>
            <Table>
              <TableHead>
                <TableRow>
                  <TableCell>STT</TableCell>
                  <TableCell>Tiêu đề</TableCell>
                  <TableCell>Ngày</TableCell>
                  <TableCell>Thời gian</TableCell>
                  <TableCell>Vị trí</TableCell>
                  <TableCell>Trạng thái</TableCell>
                  <TableCell>Thao tác</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {attendanceSessions.length > 0 ? (
                  attendanceSessions.map((session, index) => (
                    <TableRow key={session._id}>
                      <TableCell>{index + 1}</TableCell>
                      <TableCell>{session.title}</TableCell>
                      <TableCell>
                        {new Date(session.date).toLocaleDateString("vi-VN")}
                      </TableCell>
                      <TableCell>
                        {session.start_time} - {session.end_time}
                      </TableCell>
                      <TableCell>{session.location || "N/A"}</TableCell>
                      <TableCell>
                        {session.status === "completed" ? (
                          <Chip
                            label="Hoàn thành"
                            color="success"
                            size="small"
                          />
                        ) : session.status === "in_progress" ? (
                          <Chip
                            label="Đang diễn ra"
                            color="primary"
                            size="small"
                          />
                        ) : (
                          <Chip
                            label="Chưa bắt đầu"
                            color="default"
                            size="small"
                          />
                        )}
                      </TableCell>
                      <TableCell>
                        <Button
                          variant="contained"
                          size="small"
                          color="primary"
                          onClick={() => handleStartSession(session._id)}
                          startIcon={
                            session.status === "completed" ? (
                              <CheckCircle />
                            ) : (
                              <PlayArrow />
                            )
                          }
                        >
                          {session.status === "completed" ? "Xem" : "Bắt đầu"}
                        </Button>
                      </TableCell>
                    </TableRow>
                  ))
                ) : (
                  <TableRow>
                    <TableCell colSpan={7} align="center">
                      Chưa có phiên điểm danh nào
                    </TableCell>
                  </TableRow>
                )}
              </TableBody>
            </Table>
          </TableContainer>
        </TabPanel>
      </Box>

      {/* Dialog tạo phiên điểm danh */}
      <Dialog
        open={openSessionDialog}
        onClose={handleCloseSessionDialog}
        maxWidth="sm"
        fullWidth
      >
        <DialogTitle>Tạo phiên điểm danh mới</DialogTitle>
        <DialogContent>
          <Box sx={{ mt: 2 }}>
            <TextField
              fullWidth
              label="Tiêu đề"
              name="title"
              value={sessionFormData.title}
              onChange={handleSessionFormChange}
              sx={{ mb: 2 }}
            />
            <TextField
              fullWidth
              label="Ngày"
              name="date"
              type="date"
              value={sessionFormData.date}
              onChange={handleSessionFormChange}
              sx={{ mb: 2 }}
            />
            <Grid container spacing={2}>
              <Grid item xs={6}>
                <TextField
                  fullWidth
                  label="Thời gian bắt đầu"
                  name="start_time"
                  type="time"
                  value={sessionFormData.start_time}
                  onChange={handleSessionFormChange}
                  sx={{ mb: 2 }}
                />
              </Grid>
              <Grid item xs={6}>
                <TextField
                  fullWidth
                  label="Thời gian kết thúc"
                  name="end_time"
                  type="time"
                  value={sessionFormData.end_time}
                  onChange={handleSessionFormChange}
                  sx={{ mb: 2 }}
                />
              </Grid>
            </Grid>
            <TextField
              fullWidth
              label="Địa điểm"
              name="location"
              value={sessionFormData.location}
              onChange={handleSessionFormChange}
              sx={{ mb: 2 }}
            />
          </Box>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleCloseSessionDialog}>Hủy</Button>
          <Button
            variant="contained"
            color="primary"
            onClick={handleCreateSession}
          >
            Tạo
          </Button>
        </DialogActions>
      </Dialog>

      {/* Dialog thêm sinh viên */}
      <Dialog
        open={openAddStudentDialog}
        onClose={handleCloseAddStudentDialog}
        maxWidth="md"
        fullWidth
      >
        <DialogTitle>Thêm sinh viên vào lớp</DialogTitle>
        <DialogContent>
          <TextField
            fullWidth
            label="Tìm kiếm sinh viên"
            value={searchStudent}
            onChange={(e) => setSearchStudent(e.target.value)}
            sx={{ mb: 2, mt: 1 }}
          />
          <Box sx={{ maxHeight: 400, overflow: "auto", mb: 2 }}>
            <List>
              {filteredAvailableStudents.length > 0 ? (
                filteredAvailableStudents.map((student) => (
                  <React.Fragment key={student._id}>
                    <ListItem>
                      <ListItemText
                        primary={student.full_name}
                        secondary={
                          <>
                            {student.student_id || "Chưa có mã SV"} -{" "}
                            {student.email}
                          </>
                        }
                      />
                      <ListItemSecondaryAction>
                        <Checkbox
                          edge="end"
                          checked={selectedStudents.includes(student._id)}
                          onChange={() => handleSelectStudent(student._id)}
                        />
                      </ListItemSecondaryAction>
                    </ListItem>
                    <Divider />
                  </React.Fragment>
                ))
              ) : (
                <ListItem>
                  <ListItemText primary="Không tìm thấy sinh viên nào" />
                </ListItem>
              )}
            </List>
          </Box>
          <Box sx={{ textAlign: "right" }}>
            <Typography variant="body2">
              Đã chọn: {selectedStudents.length} sinh viên
            </Typography>
          </Box>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleCloseAddStudentDialog}>Hủy</Button>
          <Button
            variant="contained"
            color="primary"
            onClick={handleAddStudents}
            disabled={selectedStudents.length === 0}
          >
            Thêm vào lớp
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default TeacherClassDetailPage;
