import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { useSelector } from "react-redux";
import { useSnackbar } from "notistack";
import axios from "axios";
import {
  Box,
  Typography,
  Card,
  CardContent,
  Grid,
  Paper,
  Button,
  TextField,
  CircularProgress,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  TablePagination,
  InputAdornment,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Chip,
  Divider,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  IconButton,
  FormHelperText,
} from "@mui/material";
import {
  Search,
  School,
  CalendarToday,
  Class,
  Add,
  BarChart,
  Room as RoomIcon,
  Home as HomeIcon,
  Business as BuildingIcon,
  Event,
} from "@mui/icons-material";

const API_URL = process.env.REACT_APP_API_URL || "http://localhost:5000/api";

// --- Định nghĩa thời gian tiết học ---
const periodTimings = {
  1: { start: "07:00", end: "07:45" },
  2: { start: "07:50", end: "08:35" },
  3: { start: "08:40", end: "09:25" },
  4: { start: "09:35", end: "10:20" },
  5: { start: "10:25", end: "11:10" },
  6: { start: "11:15", end: "12:00" },
  // Giả sử có nghỉ trưa
  7: { start: "13:00", end: "13:45" },
  8: { start: "13:50", end: "14:35" },
  9: { start: "14:40", end: "15:25" },
  10: { start: "15:35", end: "16:20" },
  11: { start: "16:25", end: "17:10" },
  12: { start: "17:15", end: "18:00" },
  // Giả sử có tiết tối
  13: { start: "18:05", end: "18:50" },
  14: { start: "18:55", end: "19:40" },
  15: { start: "19:45", end: "20:30" },
  // Thêm các tiết khác nếu cần
};
// --- Kết thúc định nghĩa ---

const TeacherClassesPage = () => {
  const navigate = useNavigate();
  const { enqueueSnackbar } = useSnackbar();
  const { user, token } = useSelector((state) => state.auth);

  // State cho dữ liệu
  const [classes, setClasses] = useState([]);
  const [semesters, setSemesters] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [totalClasses, setTotalClasses] = useState(0);

  // State cho tìm kiếm và phân trang
  const [search, setSearch] = useState("");
  const [semester, setSemester] = useState("");
  const [page, setPage] = useState(0);
  const [rowsPerPage, setRowsPerPage] = useState(10);

  // State cho dialog thêm lớp học
  const [openAddClassDialog, setOpenAddClassDialog] = useState(false);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [newClass, setNewClass] = useState({
    class_name: "",
    class_code: "",
    subject_id: "",
    teacher_id: user?._id || "",
    main_class_id: "",
    semester_id: "",
    total_sessions: 15,
    course_start_date: "",
    course_end_date: "",
    auto_generate_sessions: true,
    schedule: [],
  });

  // State cho danh sách dữ liệu tham chiếu
  const [subjects, setSubjects] = useState([]);
  const [mainClasses, setMainClasses] = useState([]);
  const [campuses, setCampuses] = useState([]);
  const [buildings, setBuildings] = useState([]);
  const [rooms, setRooms] = useState([]);
  const [selectedCampus, setSelectedCampus] = useState("");
  const [selectedBuilding, setSelectedBuilding] = useState("");
  const [scheduleDays, setScheduleDays] = useState([
    {
      day_of_week: 1,
      start_period: 1,
      end_period: 2,
      start_time: periodTimings[1]?.start || "",
      end_time: periodTimings[2]?.end || "",
      room_id: "",
      is_recurring: true,
      specific_dates: [],
      excluded_dates: [],
      campusId: "",
      buildingId: "",
      availableBuildings: [],
      availableRooms: [],
    },
  ]);

  // Load dữ liệu ban đầu
  useEffect(() => {
    const fetchInitialData = async () => {
      try {
        setIsLoading(true);

        // Load các học kỳ
        const semestersResponse = await axios.get(`${API_URL}/semesters`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setSemesters(semestersResponse.data.data || []);

        // Load lớp học theo mặc định
        await fetchClasses();
      } catch (error) {
        console.error("Lỗi khi tải dữ liệu:", error);
        enqueueSnackbar("Lỗi khi tải dữ liệu", { variant: "error" });
      } finally {
        setIsLoading(false);
      }
    };

    fetchInitialData();
  }, [token, enqueueSnackbar]);

  // Tải danh sách lớp học
  const fetchClasses = async () => {
    try {
      setIsLoading(true);

      let url = `${API_URL}/classes/teaching/teacher/${user._id}?page=${
        page + 1
      }&limit=${rowsPerPage}`;

      if (search) {
        url += `&search=${encodeURIComponent(search)}`;
      }

      if (semester) {
        url += `&semester_id=${semester}`;
      }

      const response = await axios.get(url, {
        headers: { Authorization: `Bearer ${token}` },
      });

      // Lưu nhật ký để gỡ lỗi
      // console.log("Danh sách lớp học đã tải:", response.data.data);

      setClasses(response.data.data || []);
      setTotalClasses(response.data.count || 0);
    } catch (error) {
      console.error("Lỗi khi tải danh sách lớp học:", error);
      enqueueSnackbar("Lỗi khi tải danh sách lớp học", {
        variant: "error",
      });
    } finally {
      setIsLoading(false);
    }
  };

  // Xử lý thay đổi trang hoặc số dòng mỗi trang
  useEffect(() => {
    fetchClasses();
  }, [page, rowsPerPage, token]);

  // Tải dữ liệu tham chiếu cho dialog thêm lớp
  const fetchReferenceData = async () => {
    try {
      const [subjectsRes, mainClassesRes, campusesRes] = await Promise.all([
        axios.get(`${API_URL}/subjects`, {
          headers: { Authorization: `Bearer ${token}` },
        }),
        axios.get(`${API_URL}/classes/main?all=true`, {
          headers: { Authorization: `Bearer ${token}` },
        }),
        axios.get(`${API_URL}/facilities/campuses`, {
          headers: { Authorization: `Bearer ${token}` },
        }),
      ]);

      setSubjects(subjectsRes.data.data || []);
      setMainClasses(mainClassesRes.data.data || []);
      setCampuses(campusesRes.data.data || []);
    } catch (error) {
      console.error("Lỗi khi tải dữ liệu tham chiếu:", error);
      enqueueSnackbar("Lỗi khi tải dữ liệu tham chiếu", { variant: "error" });
    }
  };

  // Tải danh sách tòa nhà khi chọn campus
  const fetchBuildings = async (campusId) => {
    if (!campusId) {
      setBuildings([]);
      setSelectedBuilding("");
      return;
    }

    try {
      const response = await axios.get(
        `${API_URL}/facilities/buildings/campuses/${campusId}`,
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      setBuildings(response.data.data || []);
    } catch (error) {
      console.error("Lỗi khi tải danh sách tòa nhà:", error);
      enqueueSnackbar("Lỗi khi tải danh sách tòa nhà", { variant: "error" });
    }
  };

  // Tải danh sách phòng học khi chọn tòa nhà
  const fetchRooms = async (buildingId) => {
    if (!buildingId) {
      setRooms([]);
      return;
    }

    try {
      const response = await axios.get(
        `${API_URL}/facilities/rooms/building/${buildingId}`,
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      setRooms(response.data.data || []);
    } catch (error) {
      console.error("Lỗi khi tải danh sách phòng học:", error);
      enqueueSnackbar("Lỗi khi tải danh sách phòng học", { variant: "error" });
    }
  };

  // Xử lý tìm kiếm
  const handleSearch = () => {
    setPage(0); // Reset về trang đầu tiên
    fetchClasses();
  };

  // Xử lý thay đổi trang
  const handleChangePage = (event, newPage) => {
    setPage(newPage);
  };

  // Xử lý thay đổi số dòng mỗi trang
  const handleChangeRowsPerPage = (event) => {
    setRowsPerPage(parseInt(event.target.value, 10));
    setPage(0);
  };

  // Xử lý khi nhấn Enter trong ô tìm kiếm
  const handleSearchKeyDown = (e) => {
    if (e.key === "Enter") {
      handleSearch();
    }
  };

  // Lọc theo học kỳ hoặc năm học
  const handleFilterChange = () => {
    setPage(0);
    fetchClasses();
  };

  // Mở trang chi tiết lớp học
  const handleViewClass = (classId) => {
    // Kiểm tra classId có tồn tại không
    if (!classId) {
      enqueueSnackbar("ID lớp học không hợp lệ", { variant: "error" });
      return;
    }

    // Không kiểm tra định dạng ObjectId nữa, chấp nhận mọi định dạng ID từ BE
    navigate(`/teacher/classes/${classId}`);
  };

  // Mở dialog thêm lớp học
  const handleOpenAddClassDialog = () => {
    fetchReferenceData();
    setOpenAddClassDialog(true);
  };

  // Đóng dialog thêm lớp học
  const handleCloseAddClassDialog = () => {
    setOpenAddClassDialog(false);
    resetNewClassForm();
  };

  // Reset form thêm lớp
  const resetNewClassForm = () => {
    setNewClass({
      class_name: "",
      class_code: "",
      subject_id: "",
      teacher_id: user?._id || "",
      main_class_id: "",
      semester_id: "",
      total_sessions: 15,
      course_start_date: "",
      course_end_date: "",
      auto_generate_sessions: true,
      schedule: [],
    });
    setScheduleDays([
      {
        day_of_week: 1,
        start_period: 1,
        end_period: 2,
        start_time: periodTimings[1]?.start || "",
        end_time: periodTimings[2]?.end || "",
        room_id: "",
        is_recurring: true,
        specific_dates: [],
        excluded_dates: [],
        campusId: "",
        buildingId: "",
        availableBuildings: [],
        availableRooms: [],
      },
    ]);
  };

  // Xử lý thay đổi các trường thông tin lớp học
  const handleNewClassChange = (e) => {
    const { name, value } = e.target;
    setNewClass((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  // Xử lý thay đổi thông tin lịch học
  const handleScheduleChange = (index, field, value) => {
    const updatedSchedule = [...scheduleDays];
    const currentDay = { ...updatedSchedule[index] };

    // Chuyển đổi value sang number nếu là period
    const processedValue =
      field === "start_period" || field === "end_period"
        ? parseInt(value) || ""
        : value;

    currentDay[field] = processedValue;

    // Tính toán lại start_time và end_time nếu period thay đổi
    if (field === "start_period" || field === "end_period") {
      const startP =
        field === "start_period" ? processedValue : currentDay.start_period;
      const endP =
        field === "end_period" ? processedValue : currentDay.end_period;

      currentDay.start_time = periodTimings[startP]?.start || "";
      currentDay.end_time = periodTimings[endP]?.end || "";

      // Đảm bảo end_period >= start_period
      if (field === "start_period" && currentDay.end_period < startP) {
        currentDay.end_period = startP;
        currentDay.end_time = periodTimings[startP]?.end || "";
      }
      if (field === "end_period" && endP < currentDay.start_period) {
        // Không cho phép chọn end < start, có thể reset hoặc giữ nguyên
        // Ở đây ta sẽ giữ nguyên giá trị cũ nếu chọn sai
        currentDay[field] = updatedSchedule[index][field]; // Lấy lại giá trị cũ
        currentDay.end_time = updatedSchedule[index].end_time;
        enqueueSnackbar("Tiết kết thúc phải lớn hơn hoặc bằng tiết bắt đầu", {
          variant: "warning",
        });
      }
    }

    // Xử lý khi thay đổi Campus hoặc Building cho ngày cụ thể
    if (field === "campusId") {
      fetchBuildingsForDay(index, processedValue);
      currentDay.buildingId = ""; // Reset building và room
      currentDay.room_id = "";
      currentDay.availableBuildings = [];
      currentDay.availableRooms = [];
    } else if (field === "buildingId") {
      fetchRoomsForDay(index, processedValue);
      currentDay.room_id = ""; // Reset room
      currentDay.availableRooms = [];
    }

    updatedSchedule[index] = currentDay;
    setScheduleDays(updatedSchedule);

    // Luôn tính lại ngày kết thúc khi lịch trình thay đổi
    setNewClass((prev) => ({
      ...prev,
      course_end_date: calculateEndDate(
        prev.course_start_date,
        updatedSchedule,
        prev.total_sessions
      ),
    }));
  };

  // Thêm hàm lấy thứ trong tuần từ ngày
  const getDayOfWeekFromDate = (dateString) => {
    if (!dateString) return 1; // Mặc định là thứ 2 nếu không có ngày

    const date = new Date(dateString);
    // getDay() trả về 0 cho Chủ Nhật, 1-6 cho Thứ 2 đến Thứ 7
    const day = date.getDay();

    // Chuyển đổi để 0 = Chủ Nhật, 1 = Thứ Hai, ...
    return day;
  };

  // Thêm hàm để lấy tên của thứ
  const getDayOfWeekName = (dayOfWeek) => {
    const days = [
      "Chủ Nhật",
      "Thứ Hai",
      "Thứ Ba",
      "Thứ Tư",
      "Thứ Năm",
      "Thứ Sáu",
      "Thứ Bảy",
    ];
    return days[dayOfWeek];
  };

  // Thêm hàm để lấy tên của thứ
  const addScheduleDay = () => {
    // Lấy ngày bắt đầu
    const startDate = newClass.course_start_date;
    if (!startDate) {
      enqueueSnackbar("Vui lòng chọn ngày bắt đầu trước", {
        variant: "warning",
      });
      return;
    }

    // Lấy thứ từ ngày bắt đầu
    const baseDayOfWeek = getDayOfWeekFromDate(startDate);

    // Tìm thứ tiếp theo chưa có trong lịch
    const existingDays = scheduleDays.map((day) => day.day_of_week);
    let nextDay = (baseDayOfWeek + 1) % 7; // Thứ tiếp theo

    // Tìm một thứ chưa có trong lịch hiện tại
    for (let i = 0; i < 7; i++) {
      if (!existingDays.includes(nextDay)) {
        break;
      }
      nextDay = (nextDay + 1) % 7;
    }

    // Nếu đã có đủ 7 ngày trong tuần, thì dùng thứ 2
    if (existingDays.length >= 7) {
      nextDay = 1;
    }

    const lastDay = scheduleDays[scheduleDays.length - 1] || {}; // Lấy thông tin từ ngày cuối cùng

    const newDayData = {
      day_of_week: nextDay,
      start_period: 1,
      end_period: 2,
      start_time: periodTimings[1]?.start || "",
      end_time: periodTimings[2]?.end || "",
      room_id: "", // Bắt đầu rỗng
      is_recurring: true,
      specific_dates: [],
      excluded_dates: [],
      campusId: lastDay.campusId || "",
      buildingId: lastDay.buildingId || "",
      availableBuildings: lastDay.availableBuildings || [],
      availableRooms: lastDay.availableRooms || [],
    };
    const updatedSchedule = [...scheduleDays, newDayData];
    setScheduleDays(updatedSchedule);

    // Tính lại ngày kết thúc sau khi thêm buổi học
    setNewClass((prev) => ({
      ...prev,
      course_end_date: calculateEndDate(
        prev.course_start_date,
        updatedSchedule,
        prev.total_sessions
      ),
    }));
  };

  // Thêm một hàm để tính ngày kết thúc dựa trên ngày bắt đầu và lịch học
  const calculateEndDate = (startDate, schedule, totalSessions) => {
    if (!startDate || !schedule || schedule.length === 0 || !totalSessions) {
      return "";
    }

    try {
      // Chuyển ngày bắt đầu thành đối tượng Date
      const start = new Date(startDate);
      if (isNaN(start.getTime())) {
        return "";
      }

      // Tổng số tuần cần thiết = tổng số buổi học / số buổi học mỗi tuần
      const sessionsPerWeek = schedule.length;
      const weeksNeeded = Math.ceil(totalSessions / sessionsPerWeek);

      // Tạo mảng chứa các ngày trong tuần từ lịch học (0-6, với 0 là Chủ Nhật)
      const daysOfWeek = schedule.map((day) => day.day_of_week);

      // Sắp xếp các ngày trong tuần
      daysOfWeek.sort((a, b) => a - b);

      // Tìm ngày đầu tiên trong lịch học sau ngày bắt đầu
      let currentDate = new Date(start);

      // Đếm số buổi học đã xếp lịch
      let sessionCount = 0;

      // Lặp qua từng tuần
      for (let week = 0; week < weeksNeeded * 2; week++) {
        // Nhân 2 để đảm bảo đủ số buổi
        // Lặp qua các ngày trong tuần từ lịch học
        for (let dayIndex = 0; dayIndex < daysOfWeek.length; dayIndex++) {
          const targetDayOfWeek = daysOfWeek[dayIndex];

          // Tìm ngày tiếp theo có thứ phù hợp
          while (currentDate.getDay() !== targetDayOfWeek) {
            currentDate.setDate(currentDate.getDate() + 1);
          }

          sessionCount++;

          // Nếu đã đủ số buổi học, trả về ngày hiện tại
          if (sessionCount >= totalSessions) {
            return currentDate.toISOString().split("T")[0];
          }

          // Di chuyển đến ngày tiếp theo
          currentDate.setDate(currentDate.getDate() + 1);
        }
      }

      // Nếu không tính được, trả về chuỗi rỗng
      return "";
    } catch (error) {
      console.error("Lỗi khi tính ngày kết thúc:", error);
      return "";
    }
  };

  // Cập nhật hàm xử lý thay đổi ngày bắt đầu
  const handleStartDateChange = (e) => {
    const startDate = e.target.value;
    const dayOfWeek = getDayOfWeekFromDate(startDate);

    const updatedSchedule = [
      {
        day_of_week: dayOfWeek,
        start_period: 1,
        end_period: 2,
        start_time: periodTimings[1]?.start || "",
        end_time: periodTimings[2]?.end || "",
        room_id: "",
        is_recurring: true,
        specific_dates: [],
        excluded_dates: [],
        campusId: "",
        buildingId: "",
        availableBuildings: [],
        availableRooms: [],
      },
    ];

    setScheduleDays(updatedSchedule);

    setNewClass((prev) => ({
      ...prev,
      course_start_date: startDate,
      course_end_date: calculateEndDate(
        startDate,
        updatedSchedule,
        prev.total_sessions
      ),
    }));
  };

  // Cập nhật hàm xử lý thay đổi số buổi học
  const handleTotalSessionsChange = (e) => {
    const totalSessions = e.target.value;

    setNewClass((prev) => ({
      ...prev,
      total_sessions: totalSessions,
      // Tính lại ngày kết thúc khi thay đổi số buổi học
      course_end_date: calculateEndDate(
        prev.course_start_date,
        scheduleDays,
        totalSessions
      ),
    }));
  };

  // Tạo lớp học mới
  const handleCreateClass = async () => {
    // Kiểm tra dữ liệu nhập
    if (
      !newClass.class_name ||
      !newClass.class_code ||
      !newClass.subject_id ||
      !newClass.semester_id ||
      !newClass.total_sessions ||
      !newClass.course_start_date
    ) {
      enqueueSnackbar("Vui lòng điền đầy đủ thông tin bắt buộc", {
        variant: "warning",
      });
      return;
    }

    // Kiểm tra room_id trong từng schedule item
    for (const day of scheduleDays) {
      if (!day.room_id) {
        enqueueSnackbar(
          `Vui lòng chọn phòng học cho buổi học vào ${getDayOfWeekName(
            day.day_of_week
          )}`,
          { variant: "warning" }
        );
        return;
      }
    }

    try {
      setIsSubmitting(true);

      const classData = {
        ...newClass,
        schedule: scheduleDays,
        teacher_id: user._id,
      };

      // Xóa room_id khỏi classData nếu nó vô tình còn sót lại
      delete classData.room_id;

      const response = await axios.post(
        `${API_URL}/classes/teaching`,
        classData,
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );

      enqueueSnackbar("Tạo lớp học thành công", { variant: "success" });
      handleCloseAddClassDialog();
      fetchClasses(); // Tải lại danh sách lớp
    } catch (error) {
      console.error("Lỗi khi tạo lớp học:", error);
      enqueueSnackbar(error.response?.data?.message || "Lỗi khi tạo lớp học", {
        variant: "error",
      });
    } finally {
      setIsSubmitting(false);
    }
  };

  // Thêm lại hàm removeScheduleDay
  const removeScheduleDay = (index) => {
    if (scheduleDays.length > 1) {
      const updatedSchedule = scheduleDays.filter((_, i) => i !== index);
      setScheduleDays(updatedSchedule);

      // Tính lại ngày kết thúc sau khi xóa buổi học
      setNewClass((prev) => ({
        ...prev,
        course_end_date: calculateEndDate(
          prev.course_start_date,
          updatedSchedule,
          prev.total_sessions
        ),
      }));
    }
  };

  // --- Hàm fetch dữ liệu riêng cho từng ngày trong lịch ---
  const fetchBuildingsForDay = async (index, campusId) => {
    if (!campusId) {
      const updatedSchedule = [...scheduleDays];
      updatedSchedule[index] = {
        ...updatedSchedule[index],
        buildingId: "",
        roomId: "",
        availableBuildings: [],
        availableRooms: [],
      };
      setScheduleDays(updatedSchedule);
      return;
    }
    try {
      const response = await axios.get(
        `${API_URL}/facilities/buildings/campuses/${campusId}`,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      const updatedSchedule = [...scheduleDays];
      updatedSchedule[index] = {
        ...updatedSchedule[index],
        buildingId: "", // Reset building khi campus thay đổi
        roomId: "", // Reset room khi campus thay đổi
        availableBuildings: response.data.data || [],
        availableRooms: [], // Reset rooms khi campus thay đổi
      };
      setScheduleDays(updatedSchedule);
    } catch (error) {
      console.error("Error fetching buildings for schedule day:", error);
      // Có thể thêm enqueueSnackbar ở đây
    }
  };

  const fetchRoomsForDay = async (index, buildingId) => {
    if (!buildingId) {
      const updatedSchedule = [...scheduleDays];
      updatedSchedule[index] = {
        ...updatedSchedule[index],
        roomId: "",
        availableRooms: [],
      };
      setScheduleDays(updatedSchedule);
      return;
    }
    try {
      const response = await axios.get(
        `${API_URL}/facilities/rooms/building/${buildingId}`,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      const updatedSchedule = [...scheduleDays];
      updatedSchedule[index] = {
        ...updatedSchedule[index],
        roomId: "", // Reset room khi building thay đổi
        availableRooms: response.data.data || [],
      };
      setScheduleDays(updatedSchedule);
    } catch (error) {
      console.error("Error fetching rooms for schedule day:", error);
      // Có thể thêm enqueueSnackbar ở đây
    }
  };
  // --- Kết thúc hàm fetch riêng ---

  return (
    <Box sx={{ padding: 3 }}>
      <Box sx={{ display: "flex", justifyContent: "space-between", mb: 3 }}>
        <Typography variant="h4" gutterBottom sx={{ fontWeight: "bold" }}>
          <Class sx={{ mr: 1, verticalAlign: "middle" }} />
          Quản lý lớp học
        </Typography>
        <Button
          variant="contained"
          color="primary"
          startIcon={<Add />}
          onClick={handleOpenAddClassDialog}
        >
          Thêm lớp học
        </Button>
      </Box>

      {/* Bộ lọc và tìm kiếm */}
      <Paper sx={{ p: 2, mb: 3 }}>
        <Grid container spacing={2} alignItems="center">
          <Grid item xs={12} sm={5}>
            <TextField
              fullWidth
              label="Tìm kiếm lớp học"
              variant="outlined"
              value={search}
              onChange={(e) => setSearch(e.target.value)}
              onKeyDown={handleSearchKeyDown}
              InputProps={{
                startAdornment: (
                  <InputAdornment position="start">
                    <Search />
                  </InputAdornment>
                ),
              }}
            />
          </Grid>
          <Grid item xs={12} sm={5}>
            <FormControl fullWidth variant="outlined">
              <InputLabel>Học kỳ</InputLabel>
              <Select
                value={semester}
                onChange={(e) => {
                  setSemester(e.target.value);
                  handleFilterChange();
                }}
                label="Học kỳ"
              >
                <MenuItem value="">Tất cả học kỳ</MenuItem>
                {semesters.map((sem) => (
                  <MenuItem key={sem._id} value={sem._id}>
                    {sem.name} (
                    {sem.academic_year || `${sem.year}-${sem.year + 1}`})
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
          </Grid>
          <Grid item xs={12} sm={2}>
            <Button
              variant="contained"
              color="primary"
              fullWidth
              onClick={handleSearch}
            >
              Tìm kiếm
            </Button>
          </Grid>
        </Grid>
      </Paper>

      {/* Danh sách lớp học */}
      <Paper>
        {isLoading ? (
          <Box sx={{ display: "flex", justifyContent: "center", p: 3 }}>
            <CircularProgress />
          </Box>
        ) : (
          <>
            <TableContainer>
              <Table>
                <TableHead>
                  <TableRow>
                    <TableCell>Tên lớp</TableCell>
                    <TableCell>Môn học</TableCell>
                    <TableCell>Kỳ học</TableCell>
                    <TableCell>Lớp chính</TableCell>
                    <TableCell align="center">Số lượng SV</TableCell>
                    <TableCell>Trạng thái</TableCell>
                    <TableCell>Thao tác</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {classes.length > 0 ? (
                    classes.map((classItem) => (
                      <TableRow key={classItem._id}>
                        <TableCell>
                          <Typography variant="body2">
                            {classItem.class_name}
                          </Typography>
                        </TableCell>
                        <TableCell>
                          {classItem.subject_id ? (
                            <Box>
                              <Typography variant="body2">
                                {classItem.subject_id.name}
                              </Typography>
                              <Typography
                                variant="caption"
                                color="textSecondary"
                              >
                                {classItem.subject_id.code}
                              </Typography>
                            </Box>
                          ) : (
                            <Chip label="N/A" size="small" variant="outlined" />
                          )}
                        </TableCell>
                        <TableCell>
                          {classItem.semester_id ? (
                            <Chip
                              label={classItem.semester_id.name}
                              size="small"
                              color="info"
                              variant="outlined"
                            />
                          ) : (
                            <Chip
                              label="Không xác định"
                              size="small"
                              variant="outlined"
                              color="default"
                            />
                          )}
                        </TableCell>
                        <TableCell>
                          {classItem.main_class_id ? (
                            <Chip
                              label={classItem.main_class_id.name}
                              size="small"
                              sx={{ bgcolor: "#e3f2fd" }}
                            />
                          ) : (
                            <Chip
                              label="Không có"
                              size="small"
                              variant="outlined"
                              color="default"
                            />
                          )}
                        </TableCell>
                        <TableCell align="center">
                          <Chip
                            label={classItem.students?.length || 0}
                            color={
                              classItem.students?.length > 0
                                ? "success"
                                : "default"
                            }
                            size="small"
                          />
                        </TableCell>
                        <TableCell>
                          {classItem.status === "đang học" ? (
                            <Chip
                              label="Đang học"
                              color="success"
                              size="small"
                            />
                          ) : classItem.status === "đã kết thúc" ? (
                            <Chip
                              label="Đã kết thúc"
                              color="default"
                              size="small"
                            />
                          ) : (
                            <Chip
                              label="Chưa bắt đầu"
                              color="primary"
                              size="small"
                            />
                          )}
                        </TableCell>
                        <TableCell>
                          <Button
                            variant="outlined"
                            size="small"
                            onClick={() => handleViewClass(classItem._id)}
                          >
                            Chi tiết
                          </Button>
                        </TableCell>
                      </TableRow>
                    ))
                  ) : (
                    <TableRow>
                      <TableCell colSpan={7} align="center">
                        Không có lớp học nào.
                      </TableCell>
                    </TableRow>
                  )}
                </TableBody>
              </Table>
            </TableContainer>
            <TablePagination
              rowsPerPageOptions={[5, 10, 25]}
              component="div"
              count={totalClasses}
              rowsPerPage={rowsPerPage}
              page={page}
              onPageChange={handleChangePage}
              onRowsPerPageChange={handleChangeRowsPerPage}
              labelRowsPerPage="Dòng mỗi trang:"
              labelDisplayedRows={({ from, to, count }) =>
                `${from}-${to} trong số ${count !== -1 ? count : `hơn ${to}`}`
              }
            />
          </>
        )}
      </Paper>

      {/* Dialog thêm lớp học */}
      <Dialog
        open={openAddClassDialog}
        onClose={handleCloseAddClassDialog}
        maxWidth="md"
        fullWidth
      >
        <DialogTitle
          sx={{ bgcolor: "primary.main", color: "white", fontWeight: "bold" }}
        >
          <Add sx={{ verticalAlign: "middle", mr: 1 }} />
          Thêm lớp học mới
        </DialogTitle>
        <DialogContent dividers>
          <Grid container spacing={2} sx={{ mt: 1 }}>
            <Grid item xs={12} sm={6}>
              <TextField
                fullWidth
                label="Tên lớp"
                name="class_name"
                value={newClass.class_name}
                onChange={handleNewClassChange}
                required
                variant="outlined"
                placeholder="VD: Lập trình web - Nhóm 1"
              />
            </Grid>
            <Grid item xs={12} sm={6}>
              <TextField
                fullWidth
                label="Mã lớp"
                name="class_code"
                value={newClass.class_code}
                onChange={handleNewClassChange}
                required
                variant="outlined"
                placeholder="VD: WEB101-G1"
              />
            </Grid>
            <Grid item xs={12} sm={6}>
              <FormControl fullWidth required>
                <InputLabel>Môn học</InputLabel>
                <Select
                  label="Môn học"
                  name="subject_id"
                  value={newClass.subject_id}
                  onChange={handleNewClassChange}
                >
                  {subjects.map((subject) => (
                    <MenuItem key={subject._id} value={subject._id}>
                      {subject.name} ({subject.code})
                    </MenuItem>
                  ))}
                </Select>
              </FormControl>
            </Grid>
            <Grid item xs={12} sm={6}>
              <FormControl fullWidth required>
                <InputLabel>Học kỳ</InputLabel>
                <Select
                  label="Học kỳ"
                  name="semester_id"
                  value={newClass.semester_id}
                  onChange={handleNewClassChange}
                >
                  {semesters.map((sem) => (
                    <MenuItem key={sem._id} value={sem._id}>
                      {sem.name} ({sem.academic_year})
                    </MenuItem>
                  ))}
                </Select>
              </FormControl>
            </Grid>
            <Grid item xs={12} sm={6}>
              <FormControl fullWidth>
                <InputLabel>Lớp chính</InputLabel>
                <Select
                  label="Lớp chính"
                  name="main_class_id"
                  value={newClass.main_class_id}
                  onChange={handleNewClassChange}
                >
                  <MenuItem value="">Không chọn lớp chính</MenuItem>
                  {mainClasses.map((mainClass) => (
                    <MenuItem key={mainClass._id} value={mainClass._id}>
                      {mainClass.name} ({mainClass.class_code})
                    </MenuItem>
                  ))}
                </Select>
                <FormHelperText>
                  Có thể để trống nếu lớp dành cho nhiều lớp chính
                </FormHelperText>
              </FormControl>
            </Grid>
            <Grid item xs={12} sm={6}>
              <TextField
                fullWidth
                type="number"
                label="Tổng số buổi học"
                name="total_sessions"
                value={newClass.total_sessions}
                onChange={handleTotalSessionsChange}
                required
                variant="outlined"
                InputProps={{ inputProps: { min: 1 } }}
              />
            </Grid>
            <Grid item xs={12} sm={6}>
              <TextField
                fullWidth
                type="date"
                label="Ngày bắt đầu khóa học"
                name="course_start_date"
                value={newClass.course_start_date}
                onChange={handleStartDateChange}
                required
                variant="outlined"
                InputLabelProps={{ shrink: true }}
              />
            </Grid>
            <Grid item xs={12} sm={6}>
              <TextField
                fullWidth
                type="date"
                label="Ngày kết thúc khóa học (tự động tính)"
                name="course_end_date"
                value={newClass.course_end_date}
                disabled
                variant="outlined"
                InputLabelProps={{ shrink: true }}
                helperText="Được tính tự động từ ngày bắt đầu và lịch học"
              />
            </Grid>
            <Grid item xs={12} sm={6}>
              <FormControl fullWidth>
                <InputLabel>Tự động tạo buổi học</InputLabel>
                <Select
                  label="Tự động tạo buổi học"
                  name="auto_generate_sessions"
                  value={newClass.auto_generate_sessions}
                  onChange={handleNewClassChange}
                >
                  <MenuItem value={true}>Có</MenuItem>
                  <MenuItem value={false}>Không</MenuItem>
                </Select>
                <FormHelperText>
                  Khi bật, hệ thống sẽ tự động tạo các buổi học dựa trên lịch
                </FormHelperText>
              </FormControl>
            </Grid>

            {/* Lịch học */}
            <Grid item xs={12}>
              <Typography
                variant="subtitle1"
                gutterBottom
                sx={{ fontWeight: "bold", mt: 2 }}
              >
                <Event sx={{ verticalAlign: "middle", mr: 1 }} />
                Lịch học
              </Typography>
              <Divider sx={{ mb: 2 }} />
            </Grid>

            {scheduleDays.map((day, index) => (
              <React.Fragment key={index}>
                <Grid item xs={12} sx={{ pt: index > 0 ? 4 : 1, pb: 1 }}>
                  <Typography variant="subtitle2" sx={{ fontWeight: "bold" }}>
                    Buổi học {index + 1}: {getDayOfWeekName(day.day_of_week)}
                  </Typography>
                  <Divider />
                </Grid>

                <Grid container item spacing={2} xs={12}>
                  <Grid item xs={6} sm={3}>
                    <TextField
                      label="Thứ"
                      value={getDayOfWeekName(day.day_of_week)}
                      InputProps={{ readOnly: true }}
                      variant="filled"
                      size="small"
                    />
                  </Grid>
                  <Grid item xs={6} sm={2}>
                    <TextField
                      label="Tiết BĐ"
                      type="number"
                      value={day.start_period}
                      onChange={(e) =>
                        handleScheduleChange(
                          index,
                          "start_period",
                          e.target.value
                        )
                      }
                      InputProps={{ inputProps: { min: 1, max: 15 } }}
                      size="small"
                    />
                  </Grid>
                  <Grid item xs={6} sm={2}>
                    <TextField
                      label="Tiết KT"
                      type="number"
                      value={day.end_period}
                      onChange={(e) =>
                        handleScheduleChange(
                          index,
                          "end_period",
                          e.target.value
                        )
                      }
                      InputProps={{
                        inputProps: { min: day.start_period || 1, max: 15 },
                      }}
                      size="small"
                    />
                  </Grid>
                  <Grid item xs={6} sm={2}>
                    <TextField
                      label="Giờ BĐ"
                      type="time"
                      value={day.start_time}
                      InputLabelProps={{ shrink: true }}
                      InputProps={{ readOnly: true }}
                      variant="filled"
                      helperText="Tự động"
                      size="small"
                    />
                  </Grid>
                  <Grid item xs={6} sm={2}>
                    <TextField
                      label="Giờ KT"
                      type="time"
                      value={day.end_time}
                      InputLabelProps={{ shrink: true }}
                      InputProps={{ readOnly: true }}
                      variant="filled"
                      helperText="Tự động"
                      size="small"
                    />
                  </Grid>
                  <Grid
                    item
                    xs={12}
                    sm={1}
                    sx={{
                      display: "flex",
                      alignItems: "center",
                      justifyContent: "center",
                    }}
                  >
                    <IconButton
                      color="error"
                      onClick={() => removeScheduleDay(index)}
                      disabled={scheduleDays.length <= 1}
                    >
                      {" "}
                      <Chip
                        label="Xóa"
                        color="error"
                        size="small"
                        variant="outlined"
                        disabled={scheduleDays.length <= 1}
                      />{" "}
                    </IconButton>
                  </Grid>

                  <Grid item xs={12} sm={4}>
                    <FormControl fullWidth size="small">
                      <InputLabel>Cơ sở</InputLabel>
                      <Select
                        label="Cơ sở"
                        value={day.campusId || ""}
                        onChange={(e) =>
                          handleScheduleChange(
                            index,
                            "campusId",
                            e.target.value
                          )
                        }
                      >
                        <MenuItem value="">
                          <em>Chọn cơ sở</em>
                        </MenuItem>
                        {campuses.map((campus) => (
                          <MenuItem key={campus._id} value={campus._id}>
                            {campus.name}
                          </MenuItem>
                        ))}
                      </Select>
                    </FormControl>
                  </Grid>
                  <Grid item xs={12} sm={4}>
                    <FormControl
                      fullWidth
                      size="small"
                      disabled={!day.campusId}
                    >
                      <InputLabel>Tòa nhà</InputLabel>
                      <Select
                        label="Tòa nhà"
                        value={day.buildingId || ""}
                        onChange={(e) =>
                          handleScheduleChange(
                            index,
                            "buildingId",
                            e.target.value
                          )
                        }
                      >
                        <MenuItem value="">
                          <em>Chọn tòa nhà</em>
                        </MenuItem>
                        {day.availableBuildings.map((building) => (
                          <MenuItem key={building._id} value={building._id}>
                            {building.name}
                          </MenuItem>
                        ))}
                      </Select>
                    </FormControl>
                  </Grid>
                  <Grid item xs={12} sm={4}>
                    <FormControl
                      fullWidth
                      required
                      error={!day.room_id && isSubmitting}
                      size="small"
                    >
                      <InputLabel>Phòng học</InputLabel>
                      <Select
                        label="Phòng học"
                        value={day.room_id || ""}
                        onChange={(e) =>
                          handleScheduleChange(index, "room_id", e.target.value)
                        }
                        disabled={!day.buildingId}
                      >
                        <MenuItem value="">
                          <em>Vui lòng chọn phòng</em>
                        </MenuItem>
                        {day.availableRooms.map((room) => (
                          <MenuItem key={room._id} value={room._id}>
                            {room.room_number} ({room.capacity})
                          </MenuItem>
                        ))}
                      </Select>
                      {!day.room_id && isSubmitting && (
                        <FormHelperText error>Bắt buộc</FormHelperText>
                      )}
                    </FormControl>
                  </Grid>

                  <Grid item xs={12} sm={4}>
                    <FormControl fullWidth size="small">
                      <InputLabel>Loại lịch</InputLabel>
                      <Select
                        label="Loại lịch"
                        value={day.is_recurring}
                        onChange={(e) =>
                          handleScheduleChange(
                            index,
                            "is_recurring",
                            e.target.value
                          )
                        }
                      >
                        <MenuItem value={true}>Định kỳ hàng tuần</MenuItem>
                        <MenuItem value={false}>Các ngày cụ thể</MenuItem>
                      </Select>
                    </FormControl>
                  </Grid>
                  <Grid item xs={12} sm={4}>
                    <FormControl fullWidth size="small">
                      <InputLabel>Ngày nghỉ/Ngày học</InputLabel>
                      <Select
                        label="Ngày nghỉ/Ngày học"
                        value={day.is_recurring ? "Định kỳ" : "Cụ thể"}
                        onChange={(e) =>
                          handleScheduleChange(
                            index,
                            "is_recurring",
                            e.target.value === "Định kỳ"
                          )
                        }
                      >
                        <MenuItem value="Định kỳ">Định kỳ</MenuItem>
                        <MenuItem value={false}>Cụ thể</MenuItem>
                      </Select>
                    </FormControl>
                  </Grid>
                </Grid>
              </React.Fragment>
            ))}

            <Grid item xs={12}>
              <Button
                variant="outlined"
                color="primary"
                startIcon={<Add />}
                onClick={addScheduleDay}
                sx={{ mt: 1 }}
              >
                Thêm lịch học
              </Button>
            </Grid>
          </Grid>
        </DialogContent>
        <DialogActions sx={{ px: 3, pb: 2 }}>
          <Button onClick={handleCloseAddClassDialog} disabled={isSubmitting}>
            Hủy
          </Button>
          <Button
            onClick={handleCreateClass}
            variant="contained"
            color="primary"
            disabled={isSubmitting}
            startIcon={isSubmitting ? <CircularProgress size={20} /> : <Add />}
          >
            {isSubmitting ? "Đang tạo..." : "Tạo lớp học"}
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default TeacherClassesPage;
