import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import axios from "axios";

const API_URL = process.env.REACT_APP_API_URL || "http://localhost:5000/api";

// Thunks cho quản lý cơ sở
export const fetchAllCampuses = createAsyncThunk(
  "facility/fetchAllCampuses",
  async (
    { page = 1, limit = 10, search = "" },
    { getState, rejectWithValue }
  ) => {
    try {
      const { token } = getState().auth;
      const response = await axios.get(
        `${API_URL}/facilities/campuses?page=${page}&limit=${limit}&search=${search}`,
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      return response.data;
    } catch (error) {
      return rejectWithValue(
        error.response?.data?.message || "Không thể lấy danh sách cơ sở"
      );
    }
  }
);

export const createCampus = createAsyncThunk(
  "facility/createCampus",
  async (campusData, { getState, rejectWithValue }) => {
    try {
      const { token } = getState().auth;
      const response = await axios.post(
        `${API_URL}/facilities/campuses`,
        campusData,
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      return response.data;
    } catch (error) {
      return rejectWithValue(
        error.response?.data?.message || "Không thể tạo cơ sở mới"
      );
    }
  }
);

export const updateCampus = createAsyncThunk(
  "facility/updateCampus",
  async ({ campusId, campusData }, { getState, rejectWithValue }) => {
    try {
      const { token } = getState().auth;
      const response = await axios.put(
        `${API_URL}/facilities/campuses/${campusId}`,
        campusData,
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      return response.data;
    } catch (error) {
      return rejectWithValue(
        error.response?.data?.message || "Không thể cập nhật cơ sở"
      );
    }
  }
);

export const deleteCampus = createAsyncThunk(
  "facility/deleteCampus",
  async (campusId, { getState, rejectWithValue }) => {
    try {
      const { token } = getState().auth;
      await axios.delete(`${API_URL}/facilities/campuses/${campusId}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      return campusId;
    } catch (error) {
      return rejectWithValue(
        error.response?.data?.message || "Không thể xóa cơ sở"
      );
    }
  }
);

// Thunks cho quản lý tòa nhà
export const fetchAllBuildings = createAsyncThunk(
  "facility/fetchAllBuildings",
  async (
    { page = 1, limit = 10, search = "" },
    { getState, rejectWithValue }
  ) => {
    try {
      const { token } = getState().auth;
      const response = await axios.get(
        `${API_URL}/facilities/buildings?page=${page}&limit=${limit}&search=${search}`,
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      return response.data;
    } catch (error) {
      return rejectWithValue(
        error.response?.data?.message || "Không thể lấy danh sách tòa nhà"
      );
    }
  }
);

export const createBuilding = createAsyncThunk(
  "facility/createBuilding",
  async (buildingData, { getState, rejectWithValue }) => {
    try {
      // Xác thực dữ liệu đầu vào
      if (!buildingData.name || !buildingData.name.trim()) {
        return rejectWithValue("Tên tòa nhà không được để trống");
      }
      if (!buildingData.code || !buildingData.code.trim()) {
        return rejectWithValue("Mã tòa nhà không được để trống");
      }

      // Lấy token từ state hoặc localStorage
      const token =
        getState().auth?.userToken || localStorage.getItem("userToken");
      if (!token) {
        return rejectWithValue("Vui lòng đăng nhập để thực hiện chức năng này");
      }

      // Xác thực campus_id nếu có
      if (
        buildingData.campus_id &&
        !/^[0-9a-fA-F]{24}$/.test(buildingData.campus_id)
      ) {
        return rejectWithValue("ID cơ sở không hợp lệ");
      }

      const response = await axios.post("/facilities/buildings", buildingData);
      return response.data;
    } catch (error) {
      // Xử lý lỗi từ server
      if (error.response) {
        // Server trả về phản hồi với mã lỗi
        switch (error.response.status) {
          case 401:
            return rejectWithValue(
              "Không được phép truy cập, token không hợp lệ"
            );
          case 404:
            return rejectWithValue(
              "Không tìm thấy API endpoint, kiểm tra lại URL"
            );
          case 500:
            return rejectWithValue(
              "Lỗi server: " +
                (error.response.data?.message || "Không xác định")
            );
          default:
            return rejectWithValue(
              error.response.data?.message || "Có lỗi xảy ra khi tạo tòa nhà"
            );
        }
      } else if (error.request) {
        // Yêu cầu được gửi nhưng không nhận được phản hồi
        return rejectWithValue("Không nhận được phản hồi từ server");
      } else {
        // Lỗi khi thiết lập yêu cầu
        return rejectWithValue("Lỗi khi gửi yêu cầu: " + error.message);
      }
    }
  }
);

