  campuses: [],
  buildings: [],
  rooms: [],
  selectedCampus: null,
  selectedBuilding: null,
  selectedRoom: null,
  isLoading: false,
  error: null,
  campusPagination: {
    page: 1,
    limit: 10,
    totalPages: 1,
    totalCount: 0,
  },
  buildingPagination: {
