import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { useSelector } from "react-redux";
import { useSnackbar } from "notistack";
import axios from "axios";
import {
  Box,
  Typography,
  Paper,
  Tabs,
  Tab,
  Button,
  TextField,
  CircularProgress,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  TablePagination,
  Chip,
  IconButton,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Grid,
  Card,
  CardContent,
  Alert,
  Divider,
  Avatar,
  List,
  ListItem,
  ListItemAvatar,
  ListItemText,
  ListItemSecondaryAction,
  Tooltip,
  Badge,
  Skeleton,
  MenuItem,
  Select,
  FormControl,
  InputLabel,
  OutlinedInput,
} from "@mui/material";
import {
  Check,
  Close,
  PersonAdd,
  PersonRemove,
  School,
  Info,
  Warning,
  AccessTime,
  Error,
  CheckCircle,
  HourglassEmpty,
  SwapHoriz,
  Class,
} from "@mui/icons-material";

const API_URL = process.env.REACT_APP_API_URL || "http://localhost:5000/api";

function TabPanel(props) {
  const { children, value, index, ...other } = props;

  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      id={`tabpanel-${index}`}
      aria-labelledby={`tab-${index}`}
      {...other}
    >
      {value === index && <Box sx={{ p: 3 }}>{children}</Box>}
    </div>
  );
}

const TeacherMainClassPage = () => {
  const navigate = useNavigate();
  const { enqueueSnackbar } = useSnackbar();
  const { user, token } = useSelector((state) => state.auth);

  // State cho dữ liệu
  const [mainClass, setMainClass] = useState(null);
  const [mainClasses, setMainClasses] = useState([]);
  const [pendingStudents, setPendingStudents] = useState([]);
  const [approvedStudents, setApprovedStudents] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [isLoadingPending, setIsLoadingPending] = useState(false);
  const [isLoadingApproved, setIsLoadingApproved] = useState(false);
  const [error, setError] = useState(null);
  const [tabValue, setTabValue] = useState(0);
  const [rejectDialog, setRejectDialog] = useState({
    open: false,
    studentId: null,
    reason: "",
  });

  // Phân trang
  const [page, setPage] = useState(0);
  const [rowsPerPage, setRowsPerPage] = useState(10);

  useEffect(() => {
    fetchMainClasses();
  }, [token]);

  // Lấy danh sách các lớp chính mà giảng viên được phân công làm cố vấn
  const fetchMainClasses = async () => {
    try {
      setIsLoading(true);
      setError(null);

      // Lấy danh sách lớp được phân công cố vấn
      const response = await axios.get(
        `${API_URL}/classes/main?advisor_id=${user._id}&all=true`,
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );

      if (response.data.data && response.data.data.length > 0) {
        const classes = response.data.data;
        setMainClasses(classes);

        // Lấy lớp đầu tiên làm mặc định
        const classData = classes[0];
        setMainClass(classData);

        // Lấy danh sách sinh viên chờ duyệt và đã duyệt
        await fetchPendingStudents(classData._id);
        await fetchApprovedStudents(classData._id);
      } else {
        enqueueSnackbar("Bạn chưa được phân công làm cố vấn cho lớp nào", {
          variant: "info",
        });
      }
    } catch (error) {
      console.error("Lỗi khi tải dữ liệu lớp chính:", error);
      setError("Không thể tải thông tin lớp chính. Vui lòng thử lại sau.");
      enqueueSnackbar("Lỗi khi tải dữ liệu lớp chính", { variant: "error" });
    } finally {
      setIsLoading(false);
    }
  };

  // Lấy sinh viên đang chờ duyệt
  const fetchPendingStudents = async (classId) => {
    try {
      setIsLoadingPending(true);
      setError(null);

      const response = await axios.get(
        `${API_URL}/classes/main/${classId}/pending-students`,
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      setPendingStudents(response.data.data || []);
    } catch (error) {
      console.error("Lỗi khi tải danh sách sinh viên chờ duyệt:", error);

      let errorMessage = "Lỗi khi tải danh sách sinh viên chờ duyệt";

      if (error.response) {
        if (error.response.status === 403) {
          errorMessage =
            "Bạn không có quyền xem danh sách sinh viên chờ duyệt của lớp này";
        } else if (error.response.status === 404) {
          errorMessage = "Không tìm thấy thông tin lớp";
        } else if (error.response.data && error.response.data.message) {
          errorMessage = error.response.data.message;
        }
      }

      setError(errorMessage);
      enqueueSnackbar(errorMessage, { variant: "error" });
    } finally {
      setIsLoadingPending(false);
    }
  };

  // Lấy sinh viên đã được duyệt
  const fetchApprovedStudents = async (classId) => {
    try {
      setIsLoadingApproved(true);

      const response = await axios.get(
        `${API_URL}/classes/main/${classId}/approved-students`,
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      setApprovedStudents(response.data.data || []);
    } catch (error) {
      console.error("Lỗi khi tải danh sách sinh viên đã duyệt:", error);

      let errorMessage = "Lỗi khi tải danh sách sinh viên đã duyệt";

      if (
        error.response &&
        error.response.data &&
        error.response.data.message
      ) {
        errorMessage = error.response.data.message;
      }

      enqueueSnackbar(errorMessage, { variant: "error" });
    } finally {
      setIsLoadingApproved(false);
    }
  };

  // Xử lý thay đổi tab
  const handleTabChange = (event, newValue) => {
    setTabValue(newValue);
  };

  // Xử lý thay đổi trang
  const handleChangePage = (event, newPage) => {
    setPage(newPage);
  };

  // Xử lý thay đổi số dòng mỗi trang
  const handleChangeRowsPerPage = (event) => {
    setRowsPerPage(parseInt(event.target.value, 10));
    setPage(0);
  };

  // Phê duyệt sinh viên
  const handleApproveStudent = async (studentId) => {
    try {
      await axios.put(
        `${API_URL}/classes/main/${mainClass._id}/approve-student/${studentId}`,
        {},
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );

      enqueueSnackbar("Phê duyệt sinh viên thành công", { variant: "success" });

      // Cập nhật lại danh sách
      await fetchPendingStudents(mainClass._id);
      await fetchApprovedStudents(mainClass._id);
    } catch (error) {
      console.error("Lỗi khi phê duyệt sinh viên:", error);
      enqueueSnackbar(
        error.response?.data?.message || "Lỗi khi phê duyệt sinh viên",
        { variant: "error" }
      );
    }
  };

  // Mở dialog từ chối sinh viên
  const openRejectDialog = (studentId) => {
    setRejectDialog({
      open: true,
      studentId,
      reason: "",
    });
  };

  // Đóng dialog từ chối sinh viên
  const closeRejectDialog = () => {
    setRejectDialog({
      open: false,
      studentId: null,
      reason: "",
    });
  };

  // Từ chối sinh viên
  const handleRejectStudent = async () => {
    try {
      await axios.put(
        `${API_URL}/classes/main/${mainClass._id}/reject-student/${rejectDialog.studentId}`,
        { reason: rejectDialog.reason },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );

      enqueueSnackbar("Đã từ chối sinh viên", { variant: "success" });
      closeRejectDialog();

      // Cập nhật lại danh sách
      await fetchPendingStudents(mainClass._id);
    } catch (error) {
      console.error("Lỗi khi từ chối sinh viên:", error);
      enqueueSnackbar(
        error.response?.data?.message || "Lỗi khi từ chối sinh viên",
        { variant: "error" }
      );
    }
  };

  // Xử lý khi thay đổi lớp
  const handleChangeClass = (event) => {
    const selectedClassId = event.target.value;
    const selectedClass = mainClasses.find(
      (cls) => cls._id === selectedClassId
    );

    if (selectedClass) {
      setMainClass(selectedClass);
      setPendingStudents([]);
      setApprovedStudents([]);
      setPage(0);
      fetchPendingStudents(selectedClassId);
      fetchApprovedStudents(selectedClassId);
    }
  };

  // Render thông tin lớp
  const renderClassInfo = () => {
    if (!mainClass) return null;

    return (
      <Card sx={{ mb: 3, boxShadow: 3 }}>
        <CardContent>
          <Grid container spacing={2}>
            <Grid item xs={12} md={6}>
              <Typography
                variant="h6"
                gutterBottom
                sx={{ color: "primary.main", fontWeight: "bold" }}
              >
                Thông tin lớp chính
              </Typography>
              <Box
                sx={{
                  pl: 2,
                  borderLeft: "4px solid",
                  borderColor: "primary.main",
                }}
              >
                <Typography variant="body1" sx={{ mb: 1 }}>
                  <strong>Tên lớp:</strong> {mainClass.name}
                </Typography>
                <Typography variant="body1" sx={{ mb: 1 }}>
                  <strong>Mã lớp:</strong> {mainClass.class_code}
                </Typography>
                {mainClass.department_id && (
                  <Typography variant="body1" sx={{ mb: 1 }}>
                    <strong>Khoa:</strong> {mainClass.department_id.name}
                  </Typography>
                )}
                <Typography variant="body1" sx={{ mb: 1 }}>
                  <strong>Tổng số sinh viên:</strong>{" "}
                  <Chip
                    label={mainClass.students?.length || 0}
                    color="primary"
                    size="small"
                    sx={{ fontWeight: "bold" }}
                  />
                </Typography>
              </Box>
            </Grid>
            <Grid item xs={12} md={6}>
              <Typography
                variant="h6"
                gutterBottom
                sx={{ color: "primary.main", fontWeight: "bold" }}
              >
                Giáo viên cố vấn
              </Typography>
              <Box
                sx={{
                  pl: 2,
                  borderLeft: "4px solid",
                  borderColor: "primary.main",
                }}
              >
                {mainClass.advisor_id ? (
                  <>
                    <Box sx={{ display: "flex", alignItems: "center", mb: 1 }}>
                      <Avatar sx={{ mr: 2, bgcolor: "primary.main" }}>
                        {mainClass.advisor_id.full_name?.charAt(0) || "A"}
                      </Avatar>
                      <Box>
                        <Typography variant="body1">
                          <strong>Họ tên:</strong>{" "}
                          {mainClass.advisor_id.full_name}
                        </Typography>
                        <Typography variant="body2">
                          <strong>Email:</strong> {mainClass.advisor_id.email}
                        </Typography>
                      </Box>
                    </Box>
                    <Chip
                      icon={<CheckCircle />}
                      label="Giáo viên cố vấn hiện tại"
                      color="success"
                      variant="outlined"
                      size="small"
                    />
                  </>
                ) : (
                  <Typography color="text.secondary">
                    <Warning
                      sx={{
                        verticalAlign: "middle",
                        mr: 1,
                        color: "warning.main",
                      }}
                    />
                    Chưa có giáo viên cố vấn
                  </Typography>
                )}
              </Box>
            </Grid>
          </Grid>
        </CardContent>
      </Card>
    );
  };

  // Xem chi tiết sinh viên
  const handleViewStudent = (studentId) => {
    navigate(`/profile/${studentId}`);
  };

  // Render khi đang tải dữ liệu
  const renderLoadingState = () => (
    <Box sx={{ p: 3 }}>
      <Skeleton
        variant="rectangular"
        width="100%"
        height={100}
        sx={{ mb: 2 }}
      />
      <Skeleton variant="rectangular" width="100%" height={400} />
    </Box>
  );

  // Render khi có lỗi
  const renderErrorState = () => (
    <Box sx={{ p: 3 }}>
      <Alert
        severity="error"
        sx={{ mb: 2 }}
        action={
          <Button color="inherit" size="small" onClick={fetchMainClasses}>
            Thử lại
          </Button>
        }
      >
        <Typography variant="subtitle1" sx={{ fontWeight: "bold" }}>
          Lỗi khi tải dữ liệu
        </Typography>
        <Typography variant="body2">{error}</Typography>
      </Alert>
      <Button
        variant="contained"
        color="primary"
        onClick={() => navigate("/teacher/dashboard")}
        startIcon={<School />}
      >
        Quay lại trang chủ
      </Button>
    </Box>
  );

  if (isLoading) {
    return renderLoadingState();
  }

  if (error && !mainClass) {
    return renderErrorState();
  }

  if (!mainClass) {
    return (
      <Box sx={{ p: 3 }}>
        <Alert
          severity="info"
          icon={<Info />}
          sx={{ mb: 2, display: "flex", alignItems: "center" }}
        >
          <Box>
            <Typography variant="subtitle1" sx={{ fontWeight: "bold" }}>
              Bạn chưa được phân công làm cố vấn cho lớp nào
            </Typography>
            <Typography variant="body2">
              Vui lòng liên hệ với quản trị viên để được phân công lớp cố vấn
            </Typography>
          </Box>
        </Alert>
        <Button
          variant="contained"
          color="primary"
          onClick={() => navigate("/teacher/dashboard")}
          startIcon={<School />}
        >
          Quay lại trang chủ
        </Button>
      </Box>
    );
  }

  return (
    <Box sx={{ padding: 3 }}>
      <Typography
        variant="h4"
        gutterBottom
        sx={{
          fontWeight: "bold",
          color: "primary.main",
          display: "flex",
          alignItems: "center",
        }}
      >
        <School sx={{ mr: 1, fontSize: 32 }} />
        Quản lý lớp chính
      </Typography>

      {renderClassInfo()}

      <Paper sx={{ width: "100%", mb: 2, overflow: "hidden", boxShadow: 3 }}>
        <Tabs
          value={tabValue}
          onChange={handleTabChange}
          indicatorColor="primary"
          textColor="primary"
          variant="fullWidth"
          sx={{
            borderBottom: 1,
            borderColor: "divider",
            "& .MuiTab-root": { fontWeight: "bold" },
          }}
        >
          <Tab
            label={
              <Box sx={{ display: "flex", alignItems: "center" }}>
                <Badge
                  badgeContent={pendingStudents.length}
                  color="error"
                  max={99}
                  sx={{ "& .MuiBadge-badge": { fontWeight: "bold" } }}
                >
                  <HourglassEmpty sx={{ mr: 1 }} />
                </Badge>
                <Box sx={{ ml: pendingStudents.length > 0 ? 1 : 0 }}>
                  Sinh viên chờ duyệt
                </Box>
              </Box>
            }
          />
          <Tab
            label={
              <Box sx={{ display: "flex", alignItems: "center" }}>
                <Badge
                  badgeContent={approvedStudents.length}
                  color="success"
                  max={99}
                  sx={{ "& .MuiBadge-badge": { fontWeight: "bold" } }}
                >
                  <School sx={{ mr: 1 }} />
                </Badge>
                <Box sx={{ ml: approvedStudents.length > 0 ? 1 : 0 }}>
                  Sinh viên của lớp
                </Box>
              </Box>
            }
          />
        </Tabs>

        <TabPanel value={tabValue} index={0}>
          {isLoadingPending ? (
            <Box sx={{ p: 2 }}>
              <Skeleton variant="rectangular" height={50} sx={{ mb: 1 }} />
              <Skeleton variant="rectangular" height={80} sx={{ mb: 1 }} />
              <Skeleton variant="rectangular" height={80} sx={{ mb: 1 }} />
              <Skeleton variant="rectangular" height={80} />
            </Box>
          ) : error ? (
            <Alert
              severity="error"
              sx={{ m: 2 }}
              action={
                <Button
                  color="inherit"
                  size="small"
                  onClick={() => fetchPendingStudents(mainClass._id)}
                >
                  Thử lại
                </Button>
              }
            >
              <Typography variant="subtitle1" sx={{ fontWeight: "bold" }}>
                Không thể tải danh sách sinh viên chờ duyệt
              </Typography>
              <Typography variant="body2">{error}</Typography>
            </Alert>
          ) : pendingStudents.length === 0 ? (
            <Box sx={{ p: 4, textAlign: "center" }}>
              <CheckCircle color="success" sx={{ fontSize: 60, mb: 2 }} />
              <Typography variant="h6" gutterBottom>
                Tuyệt vời! Không có sinh viên nào đang chờ phê duyệt
              </Typography>
              <Typography variant="body1" color="textSecondary">
                Khi có sinh viên đăng ký vào lớp, họ sẽ xuất hiện ở đây
              </Typography>
            </Box>
          ) : (
            <Box sx={{ p: 3 }}>
              <Box
                sx={{
                  display: "flex",
                  justifyContent: "space-between",
                  alignItems: "center",
                  mb: 2,
                }}
              >
                <Typography
                  variant="h6"
                  sx={{ fontWeight: "bold", color: "primary.main" }}
                >
                  Danh sách sinh viên đang chờ phê duyệt (
                  {pendingStudents.length})
                </Typography>
                <Button
                  variant="contained"
                  color="primary"
                  size="small"
                  onClick={() => fetchPendingStudents(mainClass._id)}
                  startIcon={<HourglassEmpty />}
                >
                  Làm mới danh sách
                </Button>
              </Box>
              <List>
                {pendingStudents.map((student) => (
                  <ListItem
                    key={student._id}
                    sx={{
                      mb: 1,
                      border: "1px solid #e0e0e0",
                      borderRadius: 2,
                      boxShadow: 1,
                      bgcolor: "background.paper",
                      transition: "all 0.2s",
                      "&:hover": {
                        boxShadow: 3,
                        borderColor: "primary.light",
                      },
                    }}
                  >
                    <ListItemAvatar>
                      <Avatar sx={{ bgcolor: "warning.main" }}>
                        {student.full_name.charAt(0).toUpperCase()}
                      </Avatar>
                    </ListItemAvatar>
                    <ListItemText
                      primary={
                        <Box sx={{ display: "flex", alignItems: "center" }}>
                          <Typography
                            variant="subtitle1"
                            sx={{ fontWeight: "bold" }}
                          >
                            {student.full_name}
                          </Typography>
                          <Chip
                            label="Chờ duyệt"
                            color="warning"
                            size="small"
                            icon={<HourglassEmpty />}
                            sx={{ ml: 1 }}
                          />
                        </Box>
                      }
                      secondary={
                        <Box sx={{ mt: 1 }}>
                          <Grid container spacing={1}>
                            <Grid item xs={12} sm={6}>
                              <Typography variant="body2" component="span">
                                <strong>Email:</strong> {student.email}
                              </Typography>
                            </Grid>
                            <Grid item xs={12} sm={6}>
                              <Typography variant="body2" component="span">
                                <strong>MSSV:</strong>{" "}
                                {student.school_info?.student_code || "Chưa có"}
                              </Typography>
                            </Grid>
                            <Grid item xs={12}>
                              <Typography variant="body2" component="span">
                                <strong>Ngày đăng ký:</strong>{" "}
                                {new Date(
                                  student.created_at
                                ).toLocaleDateString("vi-VN", {
                                  day: "2-digit",
                                  month: "2-digit",
                                  year: "numeric",
                                  hour: "2-digit",
                                  minute: "2-digit",
                                })}
                              </Typography>
                            </Grid>
                          </Grid>
                        </Box>
                      }
                    />
                    <ListItemSecondaryAction>
                      <Tooltip title="Phê duyệt">
                        <IconButton
                          edge="end"
                          color="success"
                          onClick={() => handleApproveStudent(student._id)}
                          sx={{
                            mr: 1,
                            bgcolor: "success.light",
                            color: "white",
                            "&:hover": { bgcolor: "success.main" },
                          }}
                        >
                          <Check />
                        </IconButton>
                      </Tooltip>
                      <Tooltip title="Từ chối">
                        <IconButton
                          edge="end"
                          color="error"
                          onClick={() => openRejectDialog(student._id)}
                          sx={{
                            mr: 1,
                            bgcolor: "error.light",
                            color: "white",
                            "&:hover": { bgcolor: "error.main" },
                          }}
                        >
                          <Close />
                        </IconButton>
                      </Tooltip>
                      <Tooltip title="Xem chi tiết">
                        <IconButton
                          edge="end"
                          color="info"
                          onClick={() => handleViewStudent(student._id)}
                          sx={{
                            bgcolor: "info.light",
                            color: "white",
                            "&:hover": { bgcolor: "info.main" },
                          }}
                        >
                          <Info />
                        </IconButton>
                      </Tooltip>
                    </ListItemSecondaryAction>
                  </ListItem>
                ))}
              </List>
            </Box>
          )}
        </TabPanel>

        <TabPanel value={tabValue} index={1}>
          {isLoadingApproved ? (
            <Box sx={{ p: 2 }}>
              <Skeleton variant="rectangular" height={50} sx={{ mb: 1 }} />
              <Skeleton variant="rectangular" height={300} />
            </Box>
          ) : approvedStudents.length === 0 ? (
            <Box sx={{ p: 4, textAlign: "center" }}>
              <School
                color="primary"
                sx={{ fontSize: 60, mb: 2, opacity: 0.6 }}
              />
              <Typography variant="h6" gutterBottom>
                Chưa có sinh viên nào trong lớp
              </Typography>
              <Typography variant="body1" color="textSecondary">
                Khi bạn phê duyệt sinh viên, họ sẽ xuất hiện ở đây
              </Typography>
            </Box>
          ) : (
            <Box sx={{ p: 3 }}>
              <Box
                sx={{
                  display: "flex",
                  justifyContent: "space-between",
                  alignItems: "center",
                  mb: 2,
                }}
              >
                <Typography
                  variant="h6"
                  sx={{ fontWeight: "bold", color: "primary.main" }}
                >
                  Danh sách sinh viên của lớp ({approvedStudents.length})
                </Typography>
                <Button
                  variant="contained"
                  color="primary"
                  size="small"
                  onClick={() => fetchApprovedStudents(mainClass._id)}
                  startIcon={<School />}
                >
                  Làm mới danh sách
                </Button>
              </Box>
              <TableContainer
                sx={{
                  border: "1px solid #e0e0e0",
                  borderRadius: 2,
                  boxShadow: 1,
                  mb: 2,
                }}
              >
                <Table>
                  <TableHead sx={{ bgcolor: "primary.light" }}>
                    <TableRow>
                      <TableCell sx={{ fontWeight: "bold", color: "white" }}>
                        Họ tên
                      </TableCell>
                      <TableCell sx={{ fontWeight: "bold", color: "white" }}>
                        MSSV
                      </TableCell>
                      <TableCell sx={{ fontWeight: "bold", color: "white" }}>
                        Email
                      </TableCell>
                      <TableCell sx={{ fontWeight: "bold", color: "white" }}>
                        Trạng thái
                      </TableCell>
                      <TableCell
                        align="center"
                        sx={{ fontWeight: "bold", color: "white" }}
                      >
                        Thao tác
                      </TableCell>
                    </TableRow>
                  </TableHead>
                  <TableBody>
                    {approvedStudents
                      .slice(
                        page * rowsPerPage,
                        page * rowsPerPage + rowsPerPage
                      )
                      .map((student) => (
                        <TableRow key={student._id} hover>
                          <TableCell>
                            <Box sx={{ display: "flex", alignItems: "center" }}>
                              <Avatar
                                sx={{
                                  mr: 1,
                                  bgcolor: "primary.main",
                                  width: 30,
                                  height: 30,
                                }}
                              >
                                {student.full_name.charAt(0).toUpperCase()}
                              </Avatar>
                              <Typography variant="body2">
                                {student.full_name}
                              </Typography>
                            </Box>
                          </TableCell>
                          <TableCell>
                            {student.school_info?.student_code || (
                              <Typography
                                variant="body2"
                                color="text.secondary"
                              >
                                Chưa có
                              </Typography>
                            )}
                          </TableCell>
                          <TableCell>{student.email}</TableCell>
                          <TableCell>
                            <Chip
                              label="Đã phê duyệt"
                              color="success"
                              size="small"
                              icon={<CheckCircle />}
                              variant="outlined"
                            />
                          </TableCell>
                          <TableCell align="center">
                            <Tooltip title="Xem chi tiết">
                              <IconButton
                                color="info"
                                onClick={() => handleViewStudent(student._id)}
                                size="small"
                                sx={{
                                  bgcolor: "info.light",
                                  color: "white",
                                  "&:hover": { bgcolor: "info.main" },
                                  width: 32,
                                  height: 32,
                                }}
                              >
                                <Info fontSize="small" />
                              </IconButton>
                            </Tooltip>
                          </TableCell>
                        </TableRow>
                      ))}
                  </TableBody>
                </Table>
              </TableContainer>
              <TablePagination
                component="div"
                count={approvedStudents.length}
                rowsPerPage={rowsPerPage}
                page={page}
                onPageChange={handleChangePage}
                onRowsPerPageChange={handleChangeRowsPerPage}
                rowsPerPageOptions={[5, 10, 25, 50]}
                labelRowsPerPage="Dòng mỗi trang:"
                labelDisplayedRows={({ from, to, count }) =>
                  `${from}-${to} trên ${count} sinh viên`
                }
                sx={{
                  ".MuiTablePagination-selectLabel, .MuiTablePagination-displayedRows":
                    {
                      margin: 0,
                    },
                }}
              />
            </Box>
          )}
        </TabPanel>
      </Paper>

      {/* Dialog từ chối sinh viên */}
      <Dialog
        open={rejectDialog.open}
        onClose={closeRejectDialog}
        maxWidth="sm"
        fullWidth
        PaperProps={{
          sx: { borderRadius: 2 },
        }}
      >
        <DialogTitle
          sx={{ bgcolor: "error.light", color: "white", fontWeight: "bold" }}
        >
          <Close sx={{ verticalAlign: "middle", mr: 1 }} />
          Từ chối sinh viên
        </DialogTitle>
        <DialogContent sx={{ py: 2 }}>
          <Alert severity="warning" sx={{ mt: 2, mb: 2 }}>
            <Typography variant="body2">
              Sinh viên sẽ nhận được thông báo về việc bị từ chối và lý do.
              Trạng thái tài khoản của sinh viên sẽ chuyển sang "Từ chối".
            </Typography>
          </Alert>
          <TextField
            fullWidth
            multiline
            rows={3}
            label="Lý do từ chối"
            placeholder="Nhập lý do từ chối để thông báo cho sinh viên"
            value={rejectDialog.reason}
            onChange={(e) =>
              setRejectDialog({ ...rejectDialog, reason: e.target.value })
            }
            variant="outlined"
            required
            error={rejectDialog.reason.trim() === ""}
            helperText={
              rejectDialog.reason.trim() === ""
                ? "Vui lòng nhập lý do từ chối"
                : ""
            }
            sx={{ mt: 1 }}
          />
        </DialogContent>
        <DialogActions sx={{ px: 3, pb: 2 }}>
          <Button
            onClick={closeRejectDialog}
            variant="outlined"
            startIcon={<Close />}
          >
            Hủy
          </Button>
          <Button
            onClick={handleRejectStudent}
            color="error"
            variant="contained"
            startIcon={<Close />}
            disabled={rejectDialog.reason.trim() === ""}
          >
            Từ chối sinh viên
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default TeacherMainClassPage;
