        <Collapse in={true}>
          <Box
            sx={{ p: 1, border: "1px solid #e0e0e0", borderRadius: 1, mt: 1 }}
          >
            <Typography variant="subtitle2" gutterBottom>
              Bộ lọc nâng cao
            </Typography>
            <Grid container spacing={2}>
              {tabValue === 0 ? (
                // Bộ lọc cho lớp chính
                <>
                  <Grid item xs={12} sm={6} md={3}>
                    <FormControl fullWidth size="small">
                      <InputLabel>Khoa/Bộ môn</InputLabel>
                      <Select
                        name="department"
                        value={filterOptions.department}
                        onChange={handleFilterChange}
                        label="Khoa/Bộ môn"
                      >
                        <MenuItem value="">Tất cả</MenuItem>
                        {departments.map((dept) => (
                          <MenuItem key={dept._id} value={dept._id}>
                            {dept.name}
                          </MenuItem>
                        ))}
                      </Select>
                    </FormControl>
                  </Grid>
                  <Grid item xs={12} sm={6} md={3}>
                    <FormControl fullWidth size="small">
                      <InputLabel>Giáo viên chủ nhiệm</InputLabel>
                      <Select
                        name="teacher"
                        value={filterOptions.teacher}
                        onChange={handleFilterChange}
                        label="Giáo viên chủ nhiệm"
                      >
                        <MenuItem value="">Tất cả</MenuItem>
                        {teachers.map((teacher) => (
                          <MenuItem key={teacher._id} value={teacher._id}>
                            {teacher.full_name}
                          </MenuItem>
                        ))}
                      </Select>
                    </FormControl>
                  </Grid>
                </>
              ) : (
                // Bộ lọc cho lớp giảng dạy
                <>
                  <Grid item xs={12} sm={6} md={3}>
                    <FormControl fullWidth size="small">
                      <InputLabel>Môn học</InputLabel>
                      <Select
                        name="course"
                        value={filterOptions.course}
                        onChange={handleFilterChange}
                        label="Môn học"
                      >
                        <MenuItem value="">Tất cả</MenuItem>
                        {courses.map((course) => (
                          <MenuItem key={course._id} value={course._id}>
                            {course.name}
                          </MenuItem>
                        ))}
                      </Select>
                    </FormControl>
                  </Grid>
                  <Grid item xs={12} sm={6} md={3}>
                    <FormControl fullWidth size="small">
                      <InputLabel>Giáo viên</InputLabel>
                      <Select
                        name="teacher"
                        value={filterOptions.teacher}
                        onChange={handleFilterChange}
                        label="Giáo viên"
                      >
                        <MenuItem value="">Tất cả</MenuItem>
                        {teachers.map((teacher) => (
                          <MenuItem key={teacher._id} value={teacher._id}>
                            {teacher.full_name}
                          </MenuItem>
                        ))}
                      </Select>
                    </FormControl>
                  </Grid>
                  <Grid item xs={12} sm={6} md={3}>
                    <TextField
                      fullWidth
                      size="small"
                      label="Học kỳ"
                      name="semester"
                      value={filterOptions.semester}
                      onChange={handleFilterChange}
                    />
                  </Grid>
                </>
              )}
              <Grid item xs={12} sm={6} md={3}>
                <Box display="flex" gap={1}>
                  <Button
                    variant="outlined"
                    color="primary"
                    fullWidth
                    onClick={applyFilters}
                  >
                    Áp dụng
                  </Button>
                  <Button
                    variant="outlined"
                    color="inherit"
                    onClick={clearFilters}
                  >
                    Xóa lọc
                  </Button>
                </Box>
              </Grid>
            </Grid>
          </Box>
        </Collapse>
      </Paper>

      {/* Hiển thị kết quả hoặc trạng thái trống */}
      {isLoading ? (
        <Box sx={{ display: "flex", justifyContent: "center", py: 8 }}>
          <CircularProgress />
        </Box>
      ) : classes.length === 0 &&
        searchTerm === "" &&
        !filterOptions.department &&
        !filterOptions.teacher &&
        !filterOptions.course &&
        !filterOptions.semester ? (
        renderEmptyState()
      ) : tabValue === 0 ? (
        renderMainClassesTable()
      ) : (
        renderTeachingClassesTable()
      )}

      {/* Dialog forms */}
      {tabValue === 0
        ? renderMainClassFormDialog()
        : renderTeachingClassFormDialog()}

      {/* Delete confirmation dialog */}
      <Dialog
        open={deleteDialogOpen}
        onClose={() => setDeleteDialogOpen(false)}
      >
        <DialogTitle>
          Xác nhận xóa {tabValue === 0 ? "lớp chính" : "lớp giảng dạy"}
        </DialogTitle>
        <DialogContent>
          <DialogContentText>
            Bạn có chắc chắn muốn xóa{" "}
            {tabValue === 0
              ? `lớp chính "${selectedClass?.name}"`
              : `lớp giảng dạy "${selectedClass?.class_name}"`}
            ? Hành động này không thể hoàn tác.
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setDeleteDialogOpen(false)}>Hủy</Button>
          <Button onClick={handleDeleteClass} variant="contained" color="error">
            Xóa
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default ClassesPage;
