import React, { useState, useEffect, useCallback } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { useSelector } from "react-redux";
import { useSnackbar } from "notistack";
import axios from "axios";
import {
  Box,
  Typography,
  Card,
  CardContent,
  Grid,
  Paper,
  Button,
  Tabs,
  Tab,
  CircularProgress,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Chip,
  IconButton,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  List,
  ListItem,
  ListItemText,
  ListItemSecondaryAction,
  Divider,
  Alert,
  Tooltip,
  Checkbox,
  FormHelperText,
} from "@mui/material";
import {
  PersonAdd,
  Delete,
  Add,
  Edit,
  PlayArrow,
  Stop,
  CheckCircle,
  Warning,
  Info,
  CalendarToday,
  AccessTime,
  Room,
  Download,
  School,
} from "@mui/icons-material";

const API_URL = process.env.REACT_APP_API_URL || "http://localhost:5000/api";

function a11yProps(index) {
  return {
    id: `class-tab-${index}`,
    "aria-controls": `class-tabpanel-${index}`,
  };
}

function TabPanel(props) {
  const { children, value, index, ...other } = props;

  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      id={`class-tabpanel-${index}`}
      aria-labelledby={`class-tab-${index}`}
      {...other}
    >
      {value === index && <Box sx={{ p: 3 }}>{children}</Box>}
    </div>
  );
}

const TeacherClassDetailPage = () => {
  const { classId } = useParams();
  const id = classId; // Đảm bảo sử dụng đúng tên param
  const navigate = useNavigate();
  const { enqueueSnackbar } = useSnackbar();
  const { token } = useSelector((state) => state.auth);

  // State cho dữ liệu
  const [classInfo, setClassInfo] = useState(null);
  const [students, setStudents] = useState([]);
  const [attendanceSessions, setAttendanceSessions] = useState([]);
  const [attendanceStats, setAttendanceStats] = useState({
    total: 0,
    pending: 0,
    completed: 0,
  });
  const [isLoading, setIsLoading] = useState(true);

  // State cho UI
  const [tabValue, setTabValue] = useState(0);
  const [openSessionDialog, setOpenSessionDialog] = useState(false);
  const [sessionFormData, setSessionFormData] = useState({
    title: "",
    date: new Date().toISOString().split("T")[0],
    start_time: "07:00",
    end_time: "08:40",
    room: "",
    start_period: 1,
    end_period: 2,
    notes: "",
  });

  // State cho thêm sinh viên
  const [openAddStudentDialog, setOpenAddStudentDialog] = useState(false);
  const [availableStudents, setAvailableStudents] = useState([]);
  const [selectedStudents, setSelectedStudents] = useState([]);
  const [searchStudent, setSearchStudent] = useState("");
  const [mainClasses, setMainClasses] = useState([]);
  const [selectedMainClass, setSelectedMainClass] = useState("");
  const [isLoadingStudents, setIsLoadingStudents] = useState(false);

  // Thêm state để lưu danh sách phòng học
  const [rooms, setRooms] = useState([]);

  // Thêm hàm fetch danh sách phòng học
  const fetchRooms = async () => {
    try {
      const response = await axios.get(
        `${API_URL}/facilities/rooms?limit=1000`,
        {
          // Fetch all rooms
          headers: { Authorization: `Bearer ${token}` },
        }
      );

      if (response.data.success && Array.isArray(response.data.data)) {
        // Revert to fetching building and campus details manually for each room
        const roomsWithDetails = await Promise.all(
          response.data.data.map(async (room) => {
            let buildingData = null;
            let campusData = null;
            let buildingIdToFetch = null;
            let campusIdToFetch = null;

            // Determine building ID to fetch or use existing object
            if (room.building_id) {
              if (
                typeof room.building_id === "object" &&
                room.building_id !== null &&
                room.building_id._id
              ) {
                // Use the potentially partially populated object, but still need to fetch for full data / campus_id
                buildingIdToFetch = room.building_id._id;
                // Try to get campusId from the partial object first
                campusIdToFetch =
                  typeof room.building_id.campus_id === "object"
                    ? room.building_id.campus_id._id
                    : room.building_id.campus_id;
              } else if (typeof room.building_id === "string") {
                buildingIdToFetch = room.building_id;
              }
            }

            // Fetch full building details if we have an ID
            if (buildingIdToFetch) {
              try {
                const buildingResponse = await axios.get(
                  `${API_URL}/facilities/buildings/${buildingIdToFetch}`,
                  { headers: { Authorization: `Bearer ${token}` } }
                );
                if (
                  buildingResponse.data.success &&
                  buildingResponse.data.data
                ) {
                  buildingData = buildingResponse.data.data; // This should contain the populated campus_id object
                  // Extract campus_id string from the fetched building data
                  campusIdToFetch =
                    typeof buildingData.campus_id === "object" &&
                    buildingData.campus_id !== null
                      ? buildingData.campus_id._id
                      : buildingData.campus_id;
                }
              } catch (error) {
                console.error(
                  `Lỗi khi lấy thông tin tòa nhà ${buildingIdToFetch}:`,
                  error
                );
                // Keep original room.building_id if fetch fails but it was an object initially
                if (
                  typeof room.building_id === "object" &&
                  room.building_id !== null
                ) {
                  buildingData = room.building_id;
                } else {
                  buildingData = { _id: buildingIdToFetch }; // Fallback with ID only
                }
              }
            } else if (
              typeof room.building_id === "object" &&
              room.building_id !== null
            ) {
              // Case where initial room.building_id was object but invalid for fetch (e.g., missing _id)
              buildingData = room.building_id;
              campusIdToFetch =
                typeof room.building_id.campus_id === "object"
                  ? room.building_id.campus_id._id
                  : room.building_id.campus_id;
            }

            // Fetch campus details if we have a campus ID string
            if (campusIdToFetch && typeof campusIdToFetch === "string") {
              try {
                const campusResponse = await axios.get(
                  `${API_URL}/facilities/campus/${campusIdToFetch}`,
                  { headers: { Authorization: `Bearer ${token}` } }
                );
                if (campusResponse.data.success && campusResponse.data.data) {
                  campusData = campusResponse.data.data;
                }
              } catch (error) {
                console.error(
                  `Lỗi khi lấy thông tin cơ sở ${campusIdToFetch}:`,
                  error
                );
              }
            }

            // Construct the final room object with nested data
            const finalRoom = { ...room };
            // Ensure building_id is always the string ID
            finalRoom.building_id =
              typeof room.building_id === "object" && room.building_id !== null
                ? room.building_id._id
                : room.building_id;

            if (buildingData) {
              finalRoom.building = { ...buildingData }; // Store fetched/existing building data
              // Ensure nested campus_id is also the string ID
              if (
                finalRoom.building.campus_id &&
                typeof finalRoom.building.campus_id === "object"
              ) {
                finalRoom.building.campus_id = finalRoom.building.campus_id._id;
              }
              if (campusData) {
                finalRoom.building.campus = campusData; // Store fetched campus data nested
              }
            }

            return finalRoom;
          })
        );
        setRooms(roomsWithDetails);
      } else {
        setRooms([]);
        console.error(
          "API không trả về dữ liệu phòng học hợp lệ:",
          response.data
        );
        enqueueSnackbar(
          "Không thể tải danh sách phòng học (dữ liệu không hợp lệ)",
          { variant: "warning" }
        );
      }
    } catch (error) {
      console.error("Lỗi khi tải danh sách phòng học:", error);
      enqueueSnackbar("Không thể tải danh sách phòng học", {
        variant: "error",
      });
      setRooms([]);
    }
  };

  // Load dữ liệu ban đầu
  const fetchClassData = useCallback(async () => {
    try {
      setIsLoading(true);

      // Kiểm tra id có tồn tại không
      if (!id) {
        enqueueSnackbar("ID lớp học không hợp lệ", { variant: "error" });
        navigate("/teacher/classes");
        return;
      }

      // Lấy thông tin lớp học
      const classResponse = await axios.get(
        `${API_URL}/classes/teaching/${id}`,
        {
          headers: {
            Authorization: `Bearer ${token}`,
            Accept: "application/json",
          },
        }
      );

      // Log dữ liệu để xem cấu trúc
      console.log("Dữ liệu lớp học nhận về:", classResponse.data);

      // Kiểm tra nếu không có dữ liệu trả về
      if (!classResponse.data.success || !classResponse.data.data) {
        enqueueSnackbar("Không tìm thấy thông tin lớp học", {
          variant: "error",
        });
        navigate("/teacher/classes");
        return;
      }

      setClassInfo(classResponse.data.data);

      // Vì API getClassStudents không trả về đầy đủ thông tin, chúng ta sẽ lấy chi tiết từng sinh viên
      if (
        classResponse.data.data.students &&
        classResponse.data.data.students.length > 0
      ) {
        try {
          // Lấy danh sách sinh viên cơ bản
          const studentsResponse = await axios.get(
            `${API_URL}/classes/teaching/${id}/students`,
            {
              headers: {
                Authorization: `Bearer ${token}`,
                Accept: "application/json",
              },
            }
          );

          console.log("Dữ liệu sinh viên cơ bản:", studentsResponse.data);

          // Lưu danh sách sinh viên cơ bản trước
          let studentsWithBasicInfo = [];
          if (studentsResponse.data.success && studentsResponse.data.data) {
            studentsWithBasicInfo = studentsResponse.data.data;
          } else {
            studentsWithBasicInfo = classResponse.data.data.students || [];
          }

          // Lấy chi tiết cho từng sinh viên (bao gồm faceFeatures)
          const detailedStudents = await Promise.all(
            studentsWithBasicInfo.map(async (student) => {
              try {
                // Gọi API để lấy thông tin chi tiết của sinh viên
                const studentDetailResponse = await axios.get(
                  `${API_URL}/users/${student._id}`,
                  {
                    headers: {
                      Authorization: `Bearer ${token}`,
                      Accept: "application/json",
                    },
                  }
                );

                console.log(
                  `Thông tin chi tiết của sinh viên ${student.full_name}:`,
                  studentDetailResponse.data
                );

                if (
                  studentDetailResponse.data.success &&
                  studentDetailResponse.data.data
                ) {
                  // Kết hợp dữ liệu từ API chi tiết sinh viên với dữ liệu cơ bản
                  const detailedInfo = studentDetailResponse.data.data;

                  // Kiểm tra dữ liệu khuôn mặt
                  const hasFaceData = !!(
                    detailedInfo.faceFeatures &&
                    detailedInfo.faceFeatures.descriptors &&
                    detailedInfo.faceFeatures.descriptors.length > 0
                  );

                  console.log(
                    `Sinh viên ${detailedInfo.full_name} có dữ liệu khuôn mặt: ${hasFaceData}`
                  );

                  return {
                    ...student,
                    ...detailedInfo,
                    has_face_data: hasFaceData,
                  };
                } else {
                  // Nếu không lấy được chi tiết, sử dụng thông tin cơ bản
                  return {
                    ...student,
                    has_face_data: false,
                  };
                }
              } catch (error) {
                console.error(
                  `Lỗi khi lấy thông tin chi tiết của sinh viên ${student._id}:`,
                  error
                );
                return {
                  ...student,
                  has_face_data: false,
                };
              }
            })
          );

          setStudents(detailedStudents);
        } catch (error) {
          console.error("Lỗi khi lấy danh sách sinh viên:", error);
          // Sử dụng danh sách cơ bản nếu có lỗi
          setStudents(classResponse.data.data.students || []);
        }
      } else {
        setStudents([]);
      }

      // Lấy danh sách phiên điểm danh
      const sessionsResponse = await axios.get(
        `${API_URL}/attendance/teaching-class/${id}/sessions`,
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      setAttendanceSessions(sessionsResponse.data.data || []);

      // Tính toán thống kê điểm danh
      const stats = {
        total: sessionsResponse.data.data?.length || 0,
        pending: 0,
        completed: 0,
      };

      sessionsResponse.data.data?.forEach((session) => {
        if (session.status === "completed") {
          stats.completed++;
        } else {
          stats.pending++;
        }
      });

      setAttendanceStats(stats);
    } catch (error) {
      console.error("Lỗi khi tải dữ liệu lớp học:", error);
      // Xử lý các loại lỗi từ API
      if (error.response) {
        // Nếu máy chủ phản hồi với mã lỗi
        if (error.response.status === 404) {
          enqueueSnackbar("Không tìm thấy lớp học với ID đã cho", {
            variant: "error",
          });
        } else if (error.response.status === 400) {
          enqueueSnackbar("ID lớp học không hợp lệ", { variant: "error" });
        } else {
          enqueueSnackbar(
            error.response.data?.message || "Lỗi khi tải dữ liệu lớp học",
            { variant: "error" }
          );
        }
      } else {
        enqueueSnackbar("Không thể kết nối đến máy chủ", { variant: "error" });
      }
      setIsLoading(false);
    } finally {
      setIsLoading(false);
    }
  }, [id, token, enqueueSnackbar, navigate]);

  useEffect(() => {
    fetchClassData();
