      } else {
        enqueueSnackbar("Lỗi kết nối khi thêm sinh viên vào lớp", {
          variant: "error",
        });
      }
    }
  };

  // Xóa sinh viên khỏi lớp
  const handleRemoveStudent = async (studentId) => {
    if (
      !window.confirm("Bạn có chắc chắn muốn xóa sinh viên này khỏi lớp không?")
