                          {hasFaceData ? (
                            <Chip
                              label="Đã đăng ký khuôn mặt"
                              color="success"
                              size="small"
                            />
                          ) : (
                            <Chip
                              label="Chưa đăng ký khuôn mặt"
                              color="warning"
                              size="small"
                            />
                          )}
                        </TableCell>
                        <TableCell>
                          <Tooltip title="Xóa khỏi lớp">
                            <IconButton
                              size="small"
                              color="error"
                              onClick={() => handleRemoveStudent(student._id)}
                            >
                              <Delete />
                            </IconButton>
                          </Tooltip>
                        </TableCell>
                      </TableRow>
                    );
                  })
                ) : (
                  <TableRow>
                    <TableCell colSpan={6} align="center">
                      Chưa có sinh viên nào trong lớp
                    </TableCell>
                  </TableRow>
                )}
              </TableBody>
            </Table>
          </TableContainer>
        </TabPanel>
        <TabPanel value={tabValue} index={1}>
          <Box sx={{ display: "flex", justifyContent: "flex-end", mb: 2 }}>
            <Button
              variant="contained"
              startIcon={<Add />}
              onClick={handleOpenSessionDialog}
            >
              Tạo phiên điểm danh
            </Button>
          </Box>
          <TableContainer component={Paper}>
            <Table>
              <TableHead>
                <TableRow>
                  <TableCell>STT</TableCell>
                  <TableCell>Ngày</TableCell>
                  <TableCell>Tiết/Thời gian</TableCell>
                  <TableCell>Phòng</TableCell>
                  <TableCell>Ghi chú</TableCell>
                  <TableCell>Trạng thái</TableCell>
                  <TableCell>Thao tác</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {attendanceSessions.length > 0 ? (
                  attendanceSessions.map((session, index) => (
                    <TableRow key={session._id}>
                      <TableCell>{index + 1}</TableCell>
                      <TableCell>
                        {new Date(session.date).toLocaleString("vi-VN", {
                          weekday: "long",
                          year: "numeric",
                          month: "numeric",
                          day: "numeric",
                        })}
                      </TableCell>
                      <TableCell>
                        {session.start_period && session.end_period ? (
                          <Box>
                            <Typography variant="body2">
                              Tiết {session.start_period}-{session.end_period}
                            </Typography>
                            <Typography
                              variant="caption"
                              color="text.secondary"
                            >
                              {session.start_time && session.end_time
                                ? `${new Date(
                                    session.start_time
                                  ).toLocaleTimeString("vi-VN", {
                                    hour: "2-digit",
                                    minute: "2-digit",
                                  })} - ${new Date(
                                    session.end_time
                                  ).toLocaleTimeString("vi-VN", {
                                    hour: "2-digit",
                                    minute: "2-digit",
                                  })}`
                                : ""}
                            </Typography>
                          </Box>
                        ) : session.start_time && session.end_time ? (
                          `${new Date(session.start_time).toLocaleTimeString(
                            "vi-VN",
                            { hour: "2-digit", minute: "2-digit" }
                          )} - ${new Date(session.end_time).toLocaleTimeString(
                            "vi-VN",
                            { hour: "2-digit", minute: "2-digit" }
                          )}`
                        ) : (
                          "N/A"
                        )}
                      </TableCell>
                      <TableCell>
                        {session.room ? (
                          typeof session.room === "object" ? (
                            <Tooltip title="Thông tin đầy đủ phòng học">
                              <Typography variant="body2">
                                {session.room.room_number || "Không xác định"} -{" "}
                                {session.room.building?.name ||
                                  "Không xác định"}
                                {session.room.building?.campus?.name
                                  ? ` - ${session.room.building.campus.name}`
                                  : ""}
                              </Typography>
                            </Tooltip>
                          ) : (
                            <Tooltip title="Thông tin đầy đủ phòng học">
                              <Typography variant="body2">
                                {getRoomFullInfo(session.room)}
                              </Typography>
                            </Tooltip>
                          )
                        ) : (
                          <Typography variant="body2" color="text.secondary">
                            Không có phòng
                          </Typography>
                        )}
                      </TableCell>
                      <TableCell>
                        {session.notes ? (
                          <Tooltip title={session.notes}>
                            <Typography
                              variant="body2"
                              sx={{
                                maxWidth: "150px",
                                whiteSpace: "nowrap",
                                overflow: "hidden",
                                textOverflow: "ellipsis",
                              }}
                            >
                              {session.notes}
                            </Typography>
                          </Tooltip>
                        ) : (
                          <Typography variant="body2" color="text.secondary">
                            -
                          </Typography>
                        )}
                      </TableCell>
                      <TableCell>
                        {session.status === "completed" ? (
                          <Chip
                            label="Hoàn thành"
                            color="success"
                            size="small"
                          />
                        ) : session.status === "in_progress" ? (
                          <Chip
                            label="Đang diễn ra"
                            color="primary"
                            size="small"
                          />
                        ) : (
                          <Chip
                            label="Chưa bắt đầu"
                            color="default"
                            size="small"
                          />
                        )}
                      </TableCell>
                      <TableCell>
                        <Box sx={{ display: "flex", gap: 1 }}>
                          <Button
                            variant="contained"
                            size="small"
                            color="primary"
                            onClick={() => handleStartSession(session._id)}
                            startIcon={
                              session.status === "completed" ? (
                                <CheckCircle />
                              ) : (
                                <PlayArrow />
                              )
                            }
                          >
                            {session.status === "completed" ? "Xem" : "Bắt đầu"}
                          </Button>
                          <Tooltip title="Chỉnh sửa thông tin">
                            <IconButton
                              size="small"
                              color="default"
                              onClick={() => {
                                setSessionFormData({
                                  ...session,
                                  date: session.date.split("T")[0],
                                });
                                // Tải danh sách phòng học
                                fetchRooms();
                                setOpenSessionDialog(true);
                              }}
                            >
                              <Edit />
                            </IconButton>
                          </Tooltip>
                        </Box>
                      </TableCell>
                    </TableRow>
                  ))
                ) : (
                  <TableRow>
                    <TableCell colSpan={8} align="center">
                      Chưa có phiên điểm danh nào
                    </TableCell>
                  </TableRow>
                )}
              </TableBody>
            </Table>
          </TableContainer>
        </TabPanel>
      </Box>

      {/* Dialog tạo/chỉnh sửa phiên điểm danh */}
      <Dialog
        open={openSessionDialog}
        onClose={handleCloseSessionDialog}
        maxWidth="md"
        fullWidth
      >
        <DialogTitle>
          {sessionFormData._id
            ? "Chỉnh sửa phiên điểm danh"
            : "Tạo phiên điểm danh mới"}
        </DialogTitle>
        <DialogContent>
          <Box sx={{ mt: 2 }}>
            <TextField
              fullWidth
              label="Nội dung buổi học"
              name="title"
              placeholder="Ví dụ: Kiểm tra giữa kỳ, Học bài mới..."
              value={sessionFormData.title}
              onChange={handleSessionFormChange}
              sx={{ mb: 2 }}
            />
            <TextField
              fullWidth
              label="Ngày"
              name="date"
              type="date"
              value={sessionFormData.date}
              onChange={handleSessionFormChange}
              sx={{ mb: 2 }}
            />

            <Grid container spacing={2} sx={{ mb: 2 }}>
              <Grid item xs={6} md={3}>
                <TextField
                  fullWidth
                  label="Tiết bắt đầu"
                  name="start_period"
                  type="number"
                  value={sessionFormData.start_period}
                  onChange={handleSessionFormChange}
                  InputProps={{ inputProps: { min: 1, max: 15 } }}
                />
              </Grid>
              <Grid item xs={6} md={3}>
                <TextField
                  fullWidth
                  label="Tiết kết thúc"
                  name="end_period"
                  type="number"
                  value={sessionFormData.end_period}
                  onChange={handleSessionFormChange}
                  InputProps={{
                    inputProps: {
                      min: sessionFormData.start_period || 1,
                      max: 15,
                    },
                  }}
                />
              </Grid>
              <Grid item xs={6} md={3}>
                <TextField
                  fullWidth
                  label="Thời gian bắt đầu"
                  name="start_time"
                  type="time"
                  value={sessionFormData.start_time}
                  onChange={handleSessionFormChange}
                  InputLabelProps={{ shrink: true }}
                />
              </Grid>
              <Grid item xs={6} md={3}>
                <TextField
                  fullWidth
                  label="Thời gian kết thúc"
                  name="end_time"
                  type="time"
                  value={sessionFormData.end_time}
                  onChange={handleSessionFormChange}
                  InputLabelProps={{ shrink: true }}
                />
              </Grid>
            </Grid>

            <Grid container spacing={2} sx={{ mb: 2 }}>
              <Grid item xs={12}>
                <FormControl fullWidth>
                  <InputLabel>Phòng học</InputLabel>
                  <Select
                    name="room"
                    value={sessionFormData.room || ""}
                    onChange={handleSessionFormChange}
                    label="Phòng học"
                  >
                    <MenuItem value="">-- Chọn phòng học --</MenuItem>
                    {rooms &&
                      rooms.map((room) => (
                        <MenuItem key={room._id} value={room._id}>
                          {room.room_number || "Không xác định"} -{" "}
                          {room.building?.name || "Không xác định"} -{" "}
                          {room.building?.campus?.name || "Không xác định"}
                        </MenuItem>
                      ))}
                  </Select>
                  <FormHelperText>
                    Chọn phòng học với thông tin đầy đủ
                  </FormHelperText>
                </FormControl>
              </Grid>
            </Grid>

            <TextField
              fullWidth
              label="Ghi chú"
              name="notes"
              multiline
              rows={3}
              placeholder="Nội dung buổi học, yêu cầu đặc biệt, v.v..."
              value={sessionFormData.notes}
              onChange={handleSessionFormChange}
              sx={{ mb: 2 }}
            />
          </Box>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleCloseSessionDialog}>Hủy</Button>
          <Button
            variant="contained"
            color="primary"
            onClick={
              sessionFormData._id ? handleUpdateSession : handleCreateSession
            }
          >
            {sessionFormData._id ? "Lưu thay đổi" : "Tạo"}
          </Button>
        </DialogActions>
      </Dialog>

      {/* Dialog thêm sinh viên */}
      <Dialog
        open={openAddStudentDialog}
        onClose={handleCloseAddStudentDialog}
        maxWidth="md"
        fullWidth
      >
        <DialogTitle>Thêm sinh viên vào lớp</DialogTitle>
        <DialogContent>
          <Box sx={{ mb: 3, mt: 1 }}>
            <FormControl fullWidth sx={{ mb: 2 }}>
              <InputLabel>Chọn lớp chính</InputLabel>
              <Select
                value={selectedMainClass}
                onChange={handleMainClassChange}
                label="Chọn lớp chính"
                disabled={isLoadingStudents}
              >
                <MenuItem value="">-- Chọn lớp chính --</MenuItem>
                {mainClasses.map((mainClass) => (
                  <MenuItem key={mainClass._id} value={mainClass._id}>
                    {mainClass.name} ({mainClass.class_code})
                  </MenuItem>
                ))}
              </Select>
            </FormControl>

            {selectedMainClass && (
              <TextField
                fullWidth
                label="Tìm kiếm sinh viên"
                value={searchStudent}
                onChange={(e) => setSearchStudent(e.target.value)}
                disabled={isLoadingStudents}
              />
            )}
          </Box>

          {isLoadingStudents ? (
            <Box sx={{ display: "flex", justifyContent: "center", my: 3 }}>
              <CircularProgress />
            </Box>
          ) : (
            <Box sx={{ maxHeight: 400, overflow: "auto", mb: 2 }}>
              {!selectedMainClass ? (
                <Alert severity="info">
                  Vui lòng chọn lớp chính để xem danh sách sinh viên
                </Alert>
              ) : availableStudents.length === 0 ? (
                <Alert severity="warning">
                  Không tìm thấy sinh viên có thể thêm vào lớp
                </Alert>
              ) : (
                <List>
                  {availableStudents
                    .filter(
                      (student) =>
                        student.full_name
                          ?.toLowerCase()
                          .includes(searchStudent.toLowerCase()) ||
                        student.school_info?.student_code
                          ?.toLowerCase()
                          .includes(searchStudent.toLowerCase()) ||
                        student.email
                          ?.toLowerCase()
                          .includes(searchStudent.toLowerCase())
                    )
                    .map((student) => (
                      <React.Fragment key={student._id}>
                        <ListItem>
                          <ListItemText
                            primary={student.full_name || student.name}
                            secondary={
                              <>
                                {student.school_info?.student_code ||
                                  "Chưa có mã SV"}{" "}
                                - {student.email}
                              </>
                            }
                          />
                          <ListItemSecondaryAction>
                            <Checkbox
                              edge="end"
                              checked={selectedStudents.includes(student._id)}
                              onChange={() => handleSelectStudent(student._id)}
                            />
                          </ListItemSecondaryAction>
                        </ListItem>
                        <Divider />
                      </React.Fragment>
                    ))}
                </List>
              )}
            </Box>
          )}

          <Box sx={{ textAlign: "right" }}>
            <Typography variant="body2">
              Đã chọn: {selectedStudents.length} sinh viên
            </Typography>
          </Box>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleCloseAddStudentDialog}>Hủy</Button>
          <Button
            variant="contained"
            color="primary"
            onClick={handleAddStudents}
            disabled={selectedStudents.length === 0 || isLoadingStudents}
          >
            Thêm vào lớp
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default TeacherClassDetailPage;
