                  disabled={isSubmitting}
                  startIcon={
                    isSubmitting ? <CircularProgress size={20} /> : <Add />
                  }
                >
                  {isSubmitting ? "Đang tạo..." : "Tạo lớp"}
                </Button>
              </DialogActions>
            </Dialog>

            {/* Dialog chỉnh sửa lớp chính */}
            <Dialog
              open={openEditClassDialog}
              onClose={handleCloseEditClassDialog}
              maxWidth="md"
              fullWidth
            >
              <DialogTitle
                sx={{
                  bgcolor: "primary.main",
                  color: "white",
                  fontWeight: "bold",
                }}
              >
                <Edit sx={{ verticalAlign: "middle", mr: 1 }} />
                Chỉnh sửa thông tin lớp chính
              </DialogTitle>
              <DialogContent dividers>
                {editClassData && (
                  <Grid container spacing={2} sx={{ mt: 1 }}>
                    <Grid item xs={12} sm={6}>
                      <TextField
                        fullWidth
                        label="Tên lớp"
                        name="name"
                        value={editClassData.name}
                        onChange={handleEditClassChange}
                        required
                        variant="outlined"
                        placeholder="VD: Công nghệ thông tin 1"
                      />
                    </Grid>
                    <Grid item xs={12} sm={6}>
                      <TextField
                        fullWidth
                        label="Mã lớp"
                        name="code"
                        value={editClassData.code}
                        onChange={handleEditClassChange}
                        required
                        variant="outlined"
                        placeholder="VD: CNTT2021-1"
                      />
                    </Grid>
                    <Grid item xs={12}>
                      <FormControl fullWidth required>
                        <InputLabel>Khoa</InputLabel>
                        <Select
                          label="Khoa"
                          name="department_id"
                          value={editClassData.department_id}
                          onChange={handleEditClassChange}
                        >
                          {departments.map((dept) => (
                            <MenuItem key={dept._id} value={dept._id}>
                              {dept.name}
                            </MenuItem>
                          ))}
                        </Select>
                      </FormControl>
                    </Grid>
                  </Grid>
                )}
              </DialogContent>
              <DialogActions sx={{ px: 3, pb: 2 }}>
                <Button
                  onClick={handleCloseEditClassDialog}
                  disabled={isSubmittingEdit}
                >
                  Hủy
                </Button>
                <Button
                  onClick={handleUpdateMainClass}
                  variant="contained"
                  color="primary"
                  disabled={isSubmittingEdit}
                  startIcon={
                    isSubmittingEdit ? <CircularProgress size={20} /> : <Edit />
                  }
                >
                  {isSubmittingEdit ? "Đang cập nhật..." : "Cập nhật lớp"}
                </Button>
              </DialogActions>
            </Dialog>

            {renderStudentDetailDialog()}

            {/* Confirmation Dialog for deleting student from main class */}
            <Dialog
              open={confirmDeleteStudentDialog.open}
              onClose={handleCloseConfirmDeleteStudentDialog}
              maxWidth="xs"
            >
              <DialogTitle>Xác nhận xóa sinh viên</DialogTitle>
              <DialogContent>
                <DialogContentText>
                  {`Bạn có chắc chắn muốn xóa sinh viên `}
                  <strong>
                    {confirmDeleteStudentDialog.student?.full_name}
                  </strong>
                  {` (MSSV: `}
                  <strong>
                    {confirmDeleteStudentDialog.student?.school_info
                      ?.student_id || "N/A"}
                  </strong>
                  {`) khỏi lớp chủ nhiệm này không? Hành động này sẽ gỡ sinh viên ra khỏi lớp.`}
                </DialogContentText>
              </DialogContent>
              <DialogActions>
                <Button
                  onClick={handleCloseConfirmDeleteStudentDialog}
                  color="inherit"
                >
                  Hủy
                </Button>
                <Button
                  onClick={handleDeleteStudentFromMainClass}
                  color="error"
                  autoFocus
                >
                  Xóa
                </Button>
              </DialogActions>
            </Dialog>

            {/* Confirmation Dialog for deleting main class */}
            {renderConfirmDeleteMainClassDialog()}
          </>
        );
      })()}
    </Box>
  );
};

export default TeacherMainClassPage;
