import React, { useState, useEffect, useRef } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { useSnackbar } from "notistack";
import {
  Box,
  Typography,
  Card,
  CardContent,
  Grid,
  Paper,
  Button,
  Divider,
  CircularProgress,
  List,
  ListItem,
  ListItemAvatar,
  ListItemText,
  Avatar,
  Chip,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  IconButton,
  Alert,
} from "@mui/material";
import {
  CameraAlt,
  Check,
  Close,
  Edit,
  Refresh,
  Save,
  VerifiedUser,
  HourglassEmpty,
} from "@mui/icons-material";
import Webcam from "react-webcam";
import * as faceapi from "face-api.js";
import {
  getClassFaceFeatures,
  verifyAttendance,
  manualAttendance,
  setModelLoaded,
  setCameraReady,
  setDetectedFaces,
  setRecognizedStudents,
  clearRecognitionState,
} from "../../redux/slices/faceRecognitionSlice";
import axios from "axios";

const API_URL = process.env.REACT_APP_API_URL || "http://localhost:5000/api";

const AttendancePage = () => {
  const { classId, sessionId } = useParams();
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const { enqueueSnackbar } = useSnackbar();

  const webcamRef = useRef(null);
  const canvasRef = useRef(null);
  const videoRef = useRef(null);
  const [intervalId, setIntervalId] = useState(null);
  const [showLandmark, setShowLandmark] = useState(false);
  const [landmarkIntervalId, setLandmarkIntervalId] = useState(null);

  const { token } = useSelector((state) => state.auth);
  const {
    isModelLoaded,
    isCameraReady,
    detectedFaces,
    recognizedStudents,
    classStudents,
    isProcessing,
    error,
  } = useSelector((state) => state.faceRecognition);

  const [classInfo, setClassInfo] = useState(null);
  const [sessionInfo, setSessionInfo] = useState(null);
  const [attendanceLogs, setAttendanceLogs] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [isRecognizing, setIsRecognizing] = useState(false);
  const [isAutoMode, setIsAutoMode] = useState(false);
  const [selectedStudent, setSelectedStudent] = useState(null);
  const [manualDialogOpen, setManualDialogOpen] = useState(false);
  const [manualAttendanceData, setManualAttendanceData] = useState({
    status: "present",
    note: "",
  });

  // Các hằng số cấu hình
  const RECOGNITION_THRESHOLD = 0.5; // Ngưỡng nhận diện
  const DETECTION_THRESHOLD = 0.5; // Ngưỡng phát hiện khuôn mặt
  const CONFIDENCE_THRESHOLD = 0.7; // Ngưỡng độ tin cậy để điểm danh
  const AUTO_DETECT_INTERVAL = 1000; // Thời gian giữa các lần phát hiện (ms)
  const ENABLE_DEBUG_LOGS = true; // Bật/tắt log gỡ lỗi

  // Load class info, session info và models
  useEffect(() => {
    const loadInitialData = async () => {
      try {
        setIsLoading(true);

        // Bắt đầu tải dữ liệu và models song song
        const loadModelsPromise = !isModelLoaded
          ? loadFaceRecognitionModels()
          : Promise.resolve(true);

        // Lấy thông tin lớp học
        const classPromise = axios.get(
          `${API_URL}/classes/teaching/${classId}`,
          {
            headers: { Authorization: `Bearer ${token}` },
          }
        );

        // Lấy thông tin phiên điểm danh
        const sessionPromise = axios.get(
          `${API_URL}/attendance/sessions/${sessionId}`,
          {
            headers: { Authorization: `Bearer ${token}` },
          }
        );

        // Lấy danh sách điểm danh
        const logsPromise = axios.get(
          `${API_URL}/attendance/logs/${sessionId}`,
          {
            headers: { Authorization: `Bearer ${token}` },
          }
        );

        // Chờ tất cả API calls hoàn thành
        const [classResponse, sessionResponse, logsResponse] =
          await Promise.all([classPromise, sessionPromise, logsPromise]);

        setClassInfo(classResponse.data.data);
        setSessionInfo(sessionResponse.data.data);
        setAttendanceLogs(logsResponse.data.data);

        // Lấy đặc trưng khuôn mặt của sinh viên trong lớp
        console.log(
          "[DEBUG] Bắt đầu lấy đặc trưng khuôn mặt của sinh viên, classId:",
          classId
        );

        // Dispatch action để lấy feature và theo dõi kết quả
        const faceFeatureResult = await dispatch(
          getClassFaceFeatures(classId)
        ).unwrap();

        // Kiểm tra xem có dữ liệu trả về không
        if (faceFeatureResult && faceFeatureResult.students) {
          console.log(
            `[DEBUG] Nhận được dữ liệu khuôn mặt của ${faceFeatureResult.students.length} sinh viên`
          );

          // Kiểm tra chi tiết dữ liệu khuôn mặt
          let validDescriptorsCount = 0;
          let invalidDescriptorsCount = 0;

          faceFeatureResult.students.forEach((student) => {
            const hasDescriptors =
              student.faceDescriptors &&
              Array.isArray(student.faceDescriptors) &&
              student.faceDescriptors.length > 0;
            const validDescriptors = hasDescriptors
              ? student.faceDescriptors.filter(
                  (desc) => Array.isArray(desc) && desc.length === 128
                ).length
              : 0;

            if (validDescriptors > 0) {
              validDescriptorsCount++;
              console.log(
                `[DEBUG] Sinh viên ${
                  student.full_name
                } có ${validDescriptors}/${
                  hasDescriptors ? student.faceDescriptors.length : 0
                } descriptor hợp lệ`
              );
            } else {
              invalidDescriptorsCount++;
              console.log(
                `[DEBUG] Sinh viên ${student.full_name} không có descriptor hợp lệ`
              );
              if (hasDescriptors && student.faceDescriptors.length > 0) {
                console.log(
                  `[DEBUG] Chi tiết descriptor đầu tiên:`,
                  typeof student.faceDescriptors[0],
                  Array.isArray(student.faceDescriptors[0])
                    ? student.faceDescriptors[0].length
                    : "không phải mảng"
                );
              }
            }
          });

          console.log(
            `[DEBUG] Tổng hợp: ${validDescriptorsCount} sinh viên có descriptor hợp lệ, ${invalidDescriptorsCount} sinh viên không có`
          );

          if (validDescriptorsCount === 0) {
            enqueueSnackbar(
              "Không có sinh viên nào có dữ liệu khuôn mặt, vui lòng cập nhật dữ liệu khuôn mặt cho sinh viên",
              {
                variant: "warning",
                autoHideDuration: 8000,
              }
            );
          }
        } else {
          console.log("[DEBUG] Không nhận được dữ liệu khuôn mặt từ API");
          enqueueSnackbar("Không thể tải dữ liệu nhận diện khuôn mặt", {
            variant: "error",
          });
        }

        // Không chờ đợi model load xong mới hiển thị UI
        setIsLoading(false);

        // Model sẽ load song song
        await loadModelsPromise;
      } catch (error) {
        console.error("Lỗi khi tải dữ liệu:", error);
        enqueueSnackbar("Lỗi khi tải dữ liệu", { variant: "error" });
        setIsLoading(false);
      }
    };

    loadInitialData();

    // Clean up
    return () => {
      if (intervalId) {
        clearInterval(intervalId);
      }

      // Dừng landmark interval nếu đang chạy
      if (landmarkIntervalId) {
        clearInterval(landmarkIntervalId);
      }

      // Dừng camera stream nếu đang chạy
      if (
        webcamRef.current &&
        webcamRef.current.video &&
        webcamRef.current.video.srcObject
      ) {
        const tracks = webcamRef.current.video.srcObject.getTracks();
        tracks.forEach((track) => track.stop());
      }

      if (videoRef.current && videoRef.current.srcObject) {
        const tracks = videoRef.current.srcObject.getTracks();
        tracks.forEach((track) => track.stop());
      }

      dispatch(clearRecognitionState());
    };
  }, [
    classId,
    sessionId,
    token,
    dispatch,
    enqueueSnackbar,
    isModelLoaded,
    intervalId,
    landmarkIntervalId,
  ]);

  // Load các mô hình nhận diện khuôn mặt
  const loadFaceRecognitionModels = async () => {
    try {
      const MODEL_URL = "/models";
      console.log("Bắt đầu tải models từ:", MODEL_URL);

      // Thêm SsdMobilenetv1 vào danh sách models cần tải
      const modelPromises = [
        faceapi.nets.tinyFaceDetector.loadFromUri(MODEL_URL),
        faceapi.nets.faceLandmark68Net.loadFromUri(MODEL_URL),
        faceapi.nets.faceRecognitionNet.loadFromUri(MODEL_URL),
        faceapi.nets.ssdMobilenetv1.loadFromUri(MODEL_URL), // Thêm model SSD MobileNet
      ];

      // Kiểm tra xem các models đã được tải chưa
      const modelStatuses = [
        faceapi.nets.tinyFaceDetector.isLoaded,
        faceapi.nets.faceLandmark68Net.isLoaded,
        faceapi.nets.faceRecognitionNet.isLoaded,
        faceapi.nets.ssdMobilenetv1.isLoaded, // Kiểm tra SSD MobileNet đã tải chưa
      ];

      if (modelStatuses.every((status) => status)) {
        console.log("Các models đã được tải từ trước");
        dispatch(setModelLoaded(true));
        return true;
      }

      await Promise.all(modelPromises);

      console.log("Đã tải xong models thành công");
      dispatch(setModelLoaded(true));
      enqueueSnackbar("Tải mô hình nhận diện thành công", {
        variant: "success",
      });
      return true;
    } catch (error) {
      console.error("Lỗi khi tải mô hình:", error);
      enqueueSnackbar("Không thể tải mô hình nhận diện", { variant: "error" });
      return false;
    }
  };

  // Xử lý phát hiện khuôn mặt
  const handleDetectFaces = async () => {
    try {
      // Kiểm tra model đã tải chưa
      if (!isModelLoaded || !faceapi.nets.ssdMobilenetv1.isLoaded) {
        console.warn("Model SSD MobileNet chưa được tải, đang thử tải lại...");
        await loadFaceRecognitionModels();

        if (!faceapi.nets.ssdMobilenetv1.isLoaded) {
          enqueueSnackbar("Model nhận dạng chưa sẵn sàng, vui lòng thử lại", {
            variant: "warning",
          });
          return [];
        }
      }

      // Lấy dữ liệu video
      const videoElement = isAutoMode
        ? videoRef.current
        : webcamRef.current?.video;
      if (!videoElement || !canvasRef.current) return [];

      // Lấy kích thước của video
      const displaySize = {
        width: videoElement.videoWidth || videoElement.width || 640,
        height: videoElement.videoHeight || videoElement.height || 480,
      };

      // Phát hiện khuôn mặt trên video
      const detections = await faceapi
        .detectAllFaces(
          videoElement,
          new faceapi.SsdMobilenetv1Options({
            minConfidence: DETECTION_THRESHOLD,
          })
        )
        .withFaceLandmarks()
        .withFaceDescriptors();

      if (!detections || detections.length === 0) {
        if (ENABLE_DEBUG_LOGS) {
          console.log(
            "[DEBUG] Không phát hiện khuôn mặt",
            new Date().toLocaleTimeString()
          );
        }
        return [];
      }

      if (ENABLE_DEBUG_LOGS) {
        console.log(
          `[DEBUG] Phát hiện ${detections.length} khuôn mặt`,
          new Date().toLocaleTimeString()
        );
      }

      // Điều chỉnh kích thước phát hiện để phù hợp với kích thước hiển thị
      const resizedDetections = faceapi.resizeResults(detections, displaySize);

      // Cập nhật state
      dispatch(setDetectedFaces(resizedDetections));

      // Tạo FaceMatcher từ dữ liệu sinh viên
      const labeledDescriptors = classStudents
        .filter(
          (student) =>
            student.faceDescriptors &&
            student.faceDescriptors.length > 0 &&
            student.faceDescriptors[0].length === 128 // Đảm bảo descriptor có đúng 128 phần tử
        )
        .map((student) => {
          try {
            // Chuyển đổi mỗi descriptor thành Float32Array và xác minh độ dài
            const descriptors = student.faceDescriptors
              .filter((desc) => Array.isArray(desc) && desc.length === 128)
              .map((descriptor) => new Float32Array(descriptor));

            if (descriptors.length === 0) return null;

            return new faceapi.LabeledFaceDescriptors(student._id, descriptors);
          } catch (err) {
            console.error(
              `Lỗi khi xử lý descriptor của sinh viên ${student._id}:`,
              err
            );
            return null;
          }
        })
        .filter(Boolean); // Lọc bỏ các giá trị null

      // Tạo faceMatcher nếu có dữ liệu
      if (labeledDescriptors.length === 0) {
        console.warn("Không có dữ liệu khuôn mặt hợp lệ của sinh viên");
        return resizedDetections;
      }

      // Tạo faceMatcher với descriptor đã được xác thực
      const faceMatcher = new faceapi.FaceMatcher(
        labeledDescriptors,
        RECOGNITION_THRESHOLD
      );

      // Vẽ lên canvas nếu có khuôn mặt
      const canvas = canvasRef.current;
      canvas.width = displaySize.width;
      canvas.height = displaySize.height;

      const ctx = canvas.getContext("2d");
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      // Vẽ các phát hiện lên canvas
      resizedDetections.forEach((detection) => {
        try {
          if (detection.descriptor && detection.descriptor.length === 128) {
            const bestMatch = faceMatcher.findBestMatch(detection.descriptor);
            const { label, distance } = bestMatch;

            // Kiểm tra nếu match với một sinh viên
            if (label !== "unknown") {
              // Tìm sinh viên trong danh sách
              const student = classStudents.find((s) => s._id === label);
              if (student) {
                // Cập nhật thông tin nhận diện cho sinh viên
                const studentId = student._id;
                const confidence = 1 - distance;

                // Đánh dấu sinh viên đã điểm danh nếu đủ độ tin cậy
                if (confidence >= CONFIDENCE_THRESHOLD) {
                  handleMarkAttendance(studentId, confidence);
                }

                // Vẽ hộp và tên sinh viên
                const drawBox = new faceapi.draw.DrawBox(
                  detection.detection.box,
                  {
                    label: `${student.full_name} (${Math.round(
                      confidence * 100
                    )}%)`,
                    boxColor:
                      confidence >= CONFIDENCE_THRESHOLD ? "green" : "orange",
                  }
                );
                drawBox.draw(canvas);
              }
            } else {
              // Không nhận diện được sinh viên
              const drawBox = new faceapi.draw.DrawBox(
                detection.detection.box,
                {
                  label: "Unknown",
                  boxColor: "red",
                }
              );
              drawBox.draw(canvas);
            }
          } else {
            // Nếu descriptor không hợp lệ hoặc độ dài không đúng
            console.warn(
              "Phát hiện descriptor không hợp lệ",
              detection.descriptor
                ? detection.descriptor.length
                : "không có descriptor"
            );

            // Không có descriptor
            const drawBox = new faceapi.draw.DrawBox(detection.detection.box, {
              label: "Đang phân tích...",
              boxColor: "blue",
            });
            drawBox.draw(canvas);
          }
        } catch (err) {
          console.error("Lỗi khi xử lý detection:", err);
        }

        // Vẽ landmarks nếu đã bật
        if (showLandmark && detection.landmarks) {
          faceapi.draw.drawFaceLandmarks(canvas, detection);
        }
      });

      return resizedDetections;
    } catch (error) {
      console.error("Lỗi phát hiện khuôn mặt:", error);
      return [];
    }
  };

  // Điểm danh thủ công
  const openManualAttendanceDialog = (student) => {
    setSelectedStudent(student);
    setManualAttendanceData({
      status: "present",
      note: "",
    });
    setManualDialogOpen(true);
  };

  const handleManualDialogClose = () => {
    setManualDialogOpen(false);
  };

  const handleManualAttendanceChange = (e) => {
    setManualAttendanceData({
      ...manualAttendanceData,
      [e.target.name]: e.target.value,
    });
  };

  const handleManualAttendanceSubmit = async () => {
    if (!selectedStudent) return;

    try {
      const result = await dispatch(
        manualAttendance({
          sessionId,
          studentId: selectedStudent._id,
          status: manualAttendanceData.status,
          note: manualAttendanceData.note,
        })
      ).unwrap();

      // Cập nhật logs điểm danh
      if (result && result.data) {
        const updatedLogs = attendanceLogs.filter(
          (log) => log.student_id._id !== selectedStudent._id
        );

        updatedLogs.push({
          ...result.data,
          student_id: selectedStudent,
        });

        setAttendanceLogs(updatedLogs);
      }

      enqueueSnackbar("Điểm danh thủ công thành công", { variant: "success" });
      handleManualDialogClose();

      // Cập nhật lại thông tin phiên điểm danh
      const sessionResponse = await axios.get(
        `${API_URL}/attendance/sessions/${sessionId}`,
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      setSessionInfo(sessionResponse.data.data);
    } catch (error) {
      console.error("Lỗi khi điểm danh thủ công:", error);
      enqueueSnackbar("Lỗi khi điểm danh thủ công", { variant: "error" });
    }
  };

  // Đánh dấu điểm danh từ nhận diện khuôn mặt
  const markAttendance = async (
    studentId,
    faceDescriptor,
    confidence,
    imageBase64
  ) => {
    try {
      const result = await dispatch(
        verifyAttendance({
          sessionId,
          studentId,
          faceDescriptor,
          confidence,
          imageBase64,
        })
      ).unwrap();

      // Cập nhật logs điểm danh
      if (result && result.data) {
        const student = classStudents.find((s) => s._id === studentId);

        if (student) {
          const updatedLogs = attendanceLogs.filter(
            (log) => log.student_id._id !== studentId
          );

          updatedLogs.push({
            ...result.data,
            student_id: student,
          });

          setAttendanceLogs(updatedLogs);

          enqueueSnackbar(`Đã điểm danh cho ${student.full_name}`, {
            variant: "success",
          });
        }
      }

      // Cập nhật lại thông tin phiên điểm danh
      const sessionResponse = await axios.get(
        `${API_URL}/attendance/sessions/${sessionId}`,
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      setSessionInfo(sessionResponse.data.data);

      return true;
    } catch (error) {
      console.error("Lỗi khi xác nhận điểm danh:", error);
      enqueueSnackbar("Lỗi khi xác nhận điểm danh", { variant: "error" });
      return false;
    }
  };

  // Kết thúc phiên điểm danh
  const completeSession = async () => {
    try {
      await axios.put(
        `${API_URL}/attendance/sessions/${sessionId}`,
        { status: "completed" },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      enqueueSnackbar("Phiên điểm danh đã kết thúc", { variant: "success" });
      navigate(`/teacher/classes/${classId}`);
    } catch (error) {
      console.error("Lỗi khi kết thúc phiên điểm danh:", error);
      enqueueSnackbar("Lỗi khi kết thúc phiên điểm danh", { variant: "error" });
    }
  };

  // Làm mới danh sách điểm danh
  const refreshAttendanceLogs = async () => {
    try {
      const logsResponse = await axios.get(
        `${API_URL}/attendance/logs/${sessionId}`,
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      setAttendanceLogs(logsResponse.data.data);

      const sessionResponse = await axios.get(
        `${API_URL}/attendance/sessions/${sessionId}`,
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      setSessionInfo(sessionResponse.data.data);

      enqueueSnackbar("Đã làm mới dữ liệu điểm danh", { variant: "success" });
    } catch (error) {
      console.error("Lỗi khi làm mới dữ liệu:", error);
      enqueueSnackbar("Lỗi khi làm mới dữ liệu", { variant: "error" });
    }
  };

  // Chụp ảnh và nhận diện thủ công
  const captureAndRecognize = async () => {
    await handleDetectFaces();
  };

  // Hàm khởi tạo camera trực tiếp - nút duy nhất cho camera
  const initDirectCamera = async () => {
    try {
      // Tải model nhận diện và đảm bảo model đã sẵn sàng
      if (!isModelLoaded || !faceapi.nets.ssdMobilenetv1.isLoaded) {
        enqueueSnackbar("Đang tải các mô hình nhận diện...", {
          variant: "info",
        });
        const modelLoaded = await loadFaceRecognitionModels();

        if (!modelLoaded) {
          enqueueSnackbar("Không thể tải mô hình nhận diện, vui lòng thử lại", {
            variant: "error",
          });
          return;
        }
      }

      // Truy cập camera trực tiếp thông qua MediaDevices API
      try {
        const stream = await navigator.mediaDevices.getUserMedia({
          video: {
            width: { ideal: 1280 },
            height: { ideal: 720 },
            facingMode: "user",
          },
          audio: false,
        });

        console.log("Đã nhận được stream camera trực tiếp", stream.id);

        // Nếu đang sử dụng webcam component
        if (webcamRef.current && webcamRef.current.video) {
          webcamRef.current.video.srcObject = stream;
        }
        // Hoặc nếu đang sử dụng video element
        else if (videoRef.current) {
          videoRef.current.srcObject = stream;
          videoRef.current.onloadedmetadata = () => {
            videoRef.current
              .play()
              .catch((err) => console.error("Lỗi khi play video:", err));
          };
        }

        // Đánh dấu camera và model đã sẵn sàng
        dispatch(setModelLoaded(true));
        dispatch(setCameraReady(true));

        enqueueSnackbar("Đã kết nối camera thành công", {
          variant: "success",
        });

        // Bật hiển thị landmark sau khi camera khởi động
        setTimeout(() => {
          setShowLandmark(true);
        }, 1000);
      } catch (err) {
        console.error("Lỗi truy cập camera:", err);
        enqueueSnackbar(`Không thể truy cập camera: ${err.message}`, {
          variant: "error",
        });
      }
    } catch (err) {
      console.error("Lỗi khi khởi tạo camera trực tiếp:", err);
      enqueueSnackbar(`Lỗi: ${err.message}`, { variant: "error" });
    }
  };

  // Điểm danh thủ công trong chế độ đơn giản
  const showStudentList = () => {
    if (!classInfo) return;

    // Hiển thị danh sách sinh viên để điểm danh thủ công
    const absentStudents = getAbsentStudents();
    if (absentStudents.length === 0) {
      enqueueSnackbar("Tất cả sinh viên đã được điểm danh", {
        variant: "success",
      });
      return;
    }

    // Hiển thị dialog chọn sinh viên để điểm danh
    if (absentStudents.length > 0 && absentStudents[0]) {
      openManualAttendanceDialog(absentStudents[0]);
    }
  };

  // Bật/tắt hiển thị landmark trên khuôn mặt
  const toggleLandmarkDetection = () => {
    const newState = !showLandmark;
    setShowLandmark(newState);

    // Lấy canvas context
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext("2d");
    ctx.clearRect(0, 0, canvas.width, canvas.height);

    // Kiểm tra nếu không có khuôn mặt được phát hiện
    if (!detectedFaces || detectedFaces.length === 0) return;

    // Lấy video element
    const videoElement = isAutoMode
      ? videoRef.current
      : webcamRef.current?.video;
    if (!videoElement) return;

    // Tính toán tỷ lệ hiển thị
    const displaySize = {
      width: videoElement.videoWidth || videoElement.width,
      height: videoElement.videoHeight || videoElement.height,
    };

    // Đảm bảo canvas có kích thước phù hợp
    faceapi.matchDimensions(canvas, displaySize);

    try {
      // Tạo FaceMatcher từ dữ liệu sinh viên có độ dài hợp lệ
      const labeledDescriptors = classStudents
        .filter(
          (student) =>
            student.faceDescriptors &&
            student.faceDescriptors.length > 0 &&
            student.faceDescriptors[0].length === 128 // Đảm bảo descriptor có đúng 128 phần tử
        )
        .map((student) => {
          try {
            // Chuyển đổi mỗi descriptor thành Float32Array và xác minh độ dài
            const descriptors = student.faceDescriptors
              .filter((desc) => Array.isArray(desc) && desc.length === 128)
              .map((descriptor) => new Float32Array(descriptor));

            if (descriptors.length === 0) return null;

            return new faceapi.LabeledFaceDescriptors(student._id, descriptors);
          } catch (err) {
            if (ENABLE_DEBUG_LOGS) {
              console.error(
                `Lỗi khi xử lý descriptor của sinh viên ${student._id}:`,
                err
              );
            }
            return null;
          }
        })
        .filter(Boolean); // Lọc bỏ các giá trị null

      // Tạo faceMatcher nếu có dữ liệu
      let faceMatcher = null;
      if (labeledDescriptors.length > 0) {
        faceMatcher = new faceapi.FaceMatcher(
          labeledDescriptors,
          RECOGNITION_THRESHOLD
        );
      } else if (ENABLE_DEBUG_LOGS) {
        console.warn("Không có dữ liệu khuôn mặt hợp lệ của sinh viên");
      }

      // Sử dụng faceapi.resizeResults để điều chỉnh kích thước phát hiện phù hợp với canvas
      const resizedDetections = faceapi.resizeResults(
        detectedFaces,
        displaySize
      );

      // Vẽ các phát hiện lên canvas
      resizedDetections.forEach((detection) => {
        try {
          // Chỉ xử lý descriptor hợp lệ
          if (
            detection.descriptor &&
            detection.descriptor.length === 128 &&
            faceMatcher
          ) {
            const bestMatch = faceMatcher.findBestMatch(detection.descriptor);
            const { label, distance } = bestMatch;

            const drawBox = new faceapi.draw.DrawBox(detection.detection.box, {
              label:
                label !== "unknown"
                  ? `${
                      classStudents.find((s) => s._id === label)?.full_name ||
                      "Unknown"
                    } (${Math.round((1 - distance) * 100)}%)`
                  : "Unknown",
              boxColor: label !== "unknown" ? "green" : "red",
            });

            drawBox.draw(canvas);
          } else {
            // Nếu không có descriptor hoặc không thể nhận diện
            const drawBox = new faceapi.draw.DrawBox(detection.detection.box, {
              label: "Đang phân tích...",
              boxColor: "blue",
            });
            drawBox.draw(canvas);
          }

          // Vẽ landmarks nếu đã bật và detection có landmarks
          if (
            newState &&
            detection.landmarks &&
            detection.landmarks.positions &&
            detection.landmarks.positions.length > 0
          ) {
            try {
              // Vẽ landmark cho từng detection riêng biệt
              faceapi.draw.drawFaceLandmarks(canvas, detection);
            } catch (err) {
              console.error("Lỗi khi vẽ landmarks:", err);
            }
          }
        } catch (err) {
          console.error("Lỗi khi xử lý detection trong toggle landmark:", err);
        }
      });
    } catch (error) {
      console.error("Lỗi khi hiển thị landmarks:", error);
    }
  };

  // Xử lý đánh dấu sinh viên điểm danh
  const handleMarkAttendance = async (studentId, confidence) => {
    // Kiểm tra xem sinh viên đã được điểm danh chưa
    const isAlreadyAttended = attendanceLogs.some(
      (log) => log.student_id._id === studentId && log.status === "present"
    );

    if (isAlreadyAttended) {
      if (ENABLE_DEBUG_LOGS) {
        console.log(
          `[DEBUG] Sinh viên ${studentId} đã được điểm danh trước đó`
        );
      }
      return;
    }

    try {
      // Lấy dữ liệu video
      const videoElement = isAutoMode
        ? videoRef.current
        : webcamRef.current?.video;

      if (!videoElement) return;

      // Tạo ảnh chụp từ video element
      let imageBase64 = null;

      // Nếu sử dụng webcam component
      if (!isAutoMode && webcamRef.current) {
        imageBase64 = webcamRef.current.getScreenshot();
      }
      // Nếu sử dụng video element trực tiếp
      else if (isAutoMode) {
        const canvas = document.createElement("canvas");
        canvas.width = videoElement.videoWidth;
        canvas.height = videoElement.videoHeight;
        const ctx = canvas.getContext("2d");
        ctx.drawImage(videoElement, 0, 0);
        imageBase64 = canvas.toDataURL("image/jpeg");
      }

      // Tìm sinh viên đã được phát hiện
      const student = classStudents.find((s) => s._id === studentId);

      if (!student) {
        console.error(`Không tìm thấy sinh viên với ID ${studentId}`);
        return;
      }

      // Lấy descriptor mới nhất từ sinh viên
      const descriptor =
        student.faceDescriptors && student.faceDescriptors.length > 0
          ? student.faceDescriptors[0]
          : null;

      // Gọi API điểm danh
      if (ENABLE_DEBUG_LOGS) {
        console.log(
          `[DEBUG] Đánh dấu điểm danh cho ${
            student.full_name
          } với độ tin cậy ${confidence.toFixed(2)}`
        );
      }

      // Gọi hàm điểm danh
      await markAttendance(studentId, descriptor, confidence, imageBase64);
    } catch (error) {
      console.error("Lỗi khi đánh dấu điểm danh:", error);
    }
  };

  // Hàm để tạo dữ liệu điểm danh demo khi không có descriptor
  const generateDemoFaceData = () => {
    if (!classInfo || !classInfo.students || classInfo.students.length === 0) {
      console.warn("Không có dữ liệu sinh viên để tạo dữ liệu demo");
      return [];
    }

    // Tạo descriptor mẫu 128 phần tử
    const createDemoDescriptor = () => {
      // Tạo một mảng 128 phần tử với giá trị ngẫu nhiên
      const descriptor = new Array(128)
        .fill(0)
        .map(() => (Math.random() - 0.5) * 0.8);
      return descriptor;
    };

    // Tạo dữ liệu mẫu cho mỗi sinh viên
    const demoStudentsData = classInfo.students.map((student) => {
      // Mỗi sinh viên có 3 descriptor mẫu
      const descriptors = [
        createDemoDescriptor(),
        createDemoDescriptor(),
        createDemoDescriptor(),
      ];

      return {
        ...student,
        faceDescriptors: descriptors,
      };
    });

    console.log(
      `[DEMO] Đã tạo dữ liệu demo cho ${demoStudentsData.length} sinh viên`
    );
    enqueueSnackbar(
      `Đã tạo dữ liệu nhận diện demo cho ${demoStudentsData.length} sinh viên`,
      {
        variant: "info",
        autoHideDuration: 5000,
      }
    );

    return demoStudentsData;
  };

  // Hàm để cập nhật dữ liệu khuôn mặt khi không có dữ liệu thực
  const updateWithDemoFaceData = () => {
    if (
      classStudents.length === 0 ||
      !classStudents.some(
        (student) =>
          student.faceDescriptors &&
          student.faceDescriptors.length > 0 &&
          student.faceDescriptors[0].length === 128
      )
    ) {
      console.log(
        "[DEMO] Không tìm thấy dữ liệu khuôn mặt hợp lệ, tạo dữ liệu demo"
      );
      const demoData = generateDemoFaceData();
      if (demoData.length > 0) {
        dispatch({
          type: "faceRecognition/getClassFaceFeatures/fulfilled",
          payload: { data: demoData },
        });
        return true;
      }
    }
    return false;
  };

  if (isLoading) {
    return (
      <Box
        display="flex"
        justifyContent="center"
        alignItems="center"
        minHeight="60vh"
      >
        <CircularProgress />
      </Box>
    );
  }

  // Lấy danh sách sinh viên vắng mặt
  const getAbsentStudents = () => {
    if (!classInfo || !sessionInfo) return [];

    const presentStudentIds = attendanceLogs
      .filter((log) => log.status === "present")
      .map((log) => log.student_id._id);

    return classInfo.students.filter(
      (student) => !presentStudentIds.includes(student._id)
    );
  };

  const absentStudents = getAbsentStudents();

  return (
    <Box>
      <Typography variant="h5" gutterBottom>
        Điểm Danh - {classInfo?.class_name}
      </Typography>
      <Typography variant="subtitle1" color="textSecondary" gutterBottom>
        Buổi {sessionInfo?.session_number} -{" "}
        {new Date(sessionInfo?.date).toLocaleDateString("vi-VN")}
      </Typography>

      <Grid container spacing={3} mt={1}>
        <Grid item xs={12} md={8}>
          <Card>
            <CardContent>
              <Box
                display="flex"
                justifyContent="space-between"
                alignItems="center"
                mb={2}
              >
                <Typography variant="h6">Camera nhận diện</Typography>
                <Box>
                  <Chip
                    label={isAutoMode ? "Tự động đang bật" : "Tự động đang tắt"}
                    color={isAutoMode ? "success" : "default"}
                    sx={{ mr: 1 }}
                  />
                  <Button
                    variant="outlined"
                    color={isAutoMode ? "error" : "primary"}
                    onClick={
                      isAutoMode
                        ? () => {
                            // Dừng tự động
                            if (intervalId) {
                              clearInterval(intervalId);
                              setIntervalId(null);
                            }
                            setIsAutoMode(false);
                            enqueueSnackbar("Đã tắt chế độ tự động điểm danh", {
                              variant: "info",
                            });
                          }
                        : () => {
                            // Bắt đầu tự động
                            if (intervalId) {
                              clearInterval(intervalId);
                            }
                            const id = setInterval(() => {
                              handleDetectFaces();
                            }, AUTO_DETECT_INTERVAL);
                            setIntervalId(id);
                            setIsAutoMode(true);
                            enqueueSnackbar("Đã bật chế độ tự động điểm danh", {
                              variant: "info",
                            });
                          }
                    }
                  >
                    {isAutoMode ? "Tắt tự động" : "Bật tự động"}
                  </Button>
                </Box>
              </Box>
            </CardContent>

            <Paper
              elevation={3}
              sx={{
                position: "relative",
                width: "100%",
                height: "400px",
                display: "flex",
                justifyContent: "center",
                alignItems: "center",
                overflow: "hidden",
                mb: 2,
              }}
            >
              {isCameraReady ? (
                <>
                  {isAutoMode ? (
                    // Sử dụng video element trực tiếp thay vì Webcam component
                    <video
                      ref={videoRef}
                      style={{
                        position: "absolute",
                        width: "100%",
                        height: "100%",
                        objectFit: "cover",
                      }}
                      autoPlay
                      playsInline
                      muted
                    />
                  ) : (
                    <Webcam
                      audio={false}
                      ref={webcamRef}
                      screenshotFormat="image/jpeg"
                      width={640}
                      height={480}
                      videoConstraints={{
                        width: 640,
                        height: 480,
                        facingMode: "user",
                      }}
                      style={{
                        position: "absolute",
                        width: "100%",
                        height: "100%",
                        objectFit: "cover",
                      }}
                      onUserMedia={(stream) => {
                        console.log(
                          "Camera đã được cấp quyền và khởi tạo",
                          stream.id
                        );
                        // Đảm bảo readyState được kiểm tra khi stream đã sẵn sàng
                        setTimeout(() => {
                          if (webcamRef.current && webcamRef.current.video) {
                            console.log(
                              "Stream ready, readyState=",
                              webcamRef.current.video.readyState
                            );
                            dispatch(setCameraReady(true));
                          }
                        }, 500);
                      }}
                      onUserMediaError={(err) => {
                        console.error("Lỗi khi truy cập camera:", err);
                        enqueueSnackbar(
                          `Không thể truy cập camera: ${
                            err.name === "NotAllowedError"
                              ? "Bạn chưa cấp quyền truy cập camera"
                              : err.name === "NotFoundError"
                              ? "Không tìm thấy thiết bị camera"
                              : err.message || "Lỗi không xác định"
                          }`,
                          {
                            variant: "error",
                          }
                        );
                      }}
                    />
                  )}
                  {/* Canvas hiển thị ở cả hai chế độ */}
                  <canvas
                    ref={canvasRef}
                    style={{
                      position: "absolute",
                      top: 0,
                      left: 0,
                      width: "100%",
                      height: "100%",
                    }}
                  />
                </>
              ) : (
                <Box display="flex" flexDirection="column" alignItems="center">
                  <CircularProgress size={50} />
                  <Typography mt={2}>Đang khởi tạo camera...</Typography>
                  <Typography
                    variant="caption"
                    color="text.secondary"
                    sx={{ mt: 1 }}
                  >
                    {isModelLoaded
                      ? "Chờ kết nối camera - Hãy đảm bảo bạn đã cấp quyền camera"
                      : "Đang tải mô hình nhận diện khuôn mặt"}
                  </Typography>

                  <Box
                    sx={{
                      mt: 2,
                      display: "flex",
                      flexDirection: "column",
                      alignItems: "center",
                      gap: 1,
                    }}
                  >
                    <Button
                      variant="contained"
                      color="primary"
                      size="small"
                      onClick={initDirectCamera}
                    >
                      Khởi động camera trực tiếp
                    </Button>

                    <Button
                      variant="outlined"
                      color="warning"
                      size="small"
                      onClick={updateWithDemoFaceData}
                    >
                      Tạo dữ liệu nhận diện demo
                    </Button>
                  </Box>
                </Box>
              )}
            </Paper>

            <Box display="flex" justifyContent="space-between">
              <Button
                variant="outlined"
                onClick={captureAndRecognize}
                startIcon={<CameraAlt />}
                disabled={!isCameraReady || isRecognizing || isAutoMode}
              >
                {isRecognizing ? (
                  <CircularProgress size={24} color="inherit" />
                ) : (
                  "Chụp & Nhận diện"
                )}
              </Button>

              <Box>
                {isCameraReady && (
                  <>
                    <Button
                      variant="outlined"
                      color={showLandmark ? "success" : "info"}
                      onClick={toggleLandmarkDetection}
                      sx={{ mr: 1 }}
                    >
                      {showLandmark ? "Tắt Landmark" : "Hiện Landmark"}
                    </Button>

                    <Button
                      variant="outlined"
                      color={isAutoMode ? "error" : "primary"}
                      onClick={
                        isAutoMode
                          ? () => {
                              // Dừng tự động
                              if (intervalId) {
                                clearInterval(intervalId);
                                setIntervalId(null);
                              }
                              setIsAutoMode(false);
                              enqueueSnackbar(
                                "Đã tắt chế độ tự động điểm danh",
                                { variant: "info" }
                              );
                            }
                          : () => {
                              // Bắt đầu tự động
                              if (intervalId) {
                                clearInterval(intervalId);
                              }
                              const id = setInterval(() => {
                                handleDetectFaces();
                              }, AUTO_DETECT_INTERVAL);
                              setIntervalId(id);
                              setIsAutoMode(true);
                              enqueueSnackbar(
                                "Đã bật chế độ tự động điểm danh",
                                { variant: "info" }
                              );
                            }
                      }
                      sx={{ mr: 1 }}
                    >
                      {isAutoMode ? "Tắt tự động" : "Bật tự động"}
                    </Button>
                  </>
                )}

                <Button
                  variant="contained"
                  color="primary"
                  onClick={completeSession}
                  startIcon={<Save />}
                  disabled={sessionInfo?.status === "completed"}
                >
                  Kết thúc phiên
                </Button>
              </Box>
            </Box>

            {recognizedStudents.length > 0 && (
              <Box mt={2}>
                <Typography variant="subtitle1" gutterBottom>
                  Đã nhận diện ({recognizedStudents.length})
                </Typography>
                <Grid container spacing={1}>
                  {recognizedStudents.map((student, index) => (
                    <Grid item xs={6} sm={4} key={index}>
                      <Paper
                        elevation={2}
                        sx={{
                          p: 1,
                          display: "flex",
                          flexDirection: "column",
                          alignItems: "center",
                        }}
                      >
                        <Typography variant="body2" noWrap>
                          {student.name}
                        </Typography>
                        <Typography variant="caption" color="textSecondary">
                          {Math.round(student.confidence * 100)}% match
                        </Typography>
                        <Button
                          size="small"
                          variant="outlined"
                          color="primary"
                          sx={{ mt: 1 }}
                          onClick={() =>
                            handleMarkAttendance(
                              student.studentId,
                              student.confidence
                            )
                          }
                        >
                          <Check fontSize="small" sx={{ mr: 0.5 }} />
                          Điểm danh
                        </Button>
                      </Paper>
                    </Grid>
                  ))}
                </Grid>
              </Box>
            )}
          </Card>
        </Grid>

        <Grid item xs={12} md={4}>
          <Card sx={{ mb: 3 }}>
            <CardContent>
              <Box
                display="flex"
                justifyContent="space-between"
                alignItems="center"
                mb={2}
              >
                <Typography variant="h6">
                  Có mặt (
                  {
                    attendanceLogs.filter((log) => log.status === "present")
                      .length
                  }
                  /{classInfo?.students.length})
                </Typography>
                <IconButton size="small" onClick={refreshAttendanceLogs}>
                  <Refresh />
                </IconButton>
              </Box>

              <List dense>
                {attendanceLogs
                  .filter((log) => log.status === "present")
                  .sort((a, b) => new Date(b.timestamp) - new Date(a.timestamp))
                  .map((log) => (
                    <ListItem
                      key={log._id}
                      secondaryAction={
                        <Chip
                          size="small"
                          icon={
                            log.recognized ? (
                              <VerifiedUser fontSize="small" />
                            ) : null
                          }
                          label={log.recognized ? "Tự động" : "Thủ công"}
                          color={log.recognized ? "success" : "primary"}
                        />
                      }
                    >
                      <ListItemAvatar>
                        <Avatar
                          alt={log.student_id.full_name}
                          src={log.student_id.avatar_url}
                        />
                      </ListItemAvatar>
                      <ListItemText
                        primary={log.student_id.full_name}
                        secondary={`${new Date(
                          log.timestamp
                        ).toLocaleTimeString("vi-VN")}`}
                      />
                    </ListItem>
                  ))}

                {attendanceLogs.filter((log) => log.status === "present")
                  .length === 0 && (
                  <ListItem>
                    <ListItemText primary="Chưa có sinh viên nào điểm danh" />
                  </ListItem>
                )}
              </List>
            </CardContent>
          </Card>

          <Card>
            <CardContent>
              <Typography variant="h6" gutterBottom>
                Vắng mặt ({absentStudents.length}/{classInfo?.students.length})
              </Typography>

              <List dense>
                {absentStudents.map((student) => (
                  <ListItem
                    key={student._id}
                    secondaryAction={
                      <IconButton
                        edge="end"
                        onClick={() => openManualAttendanceDialog(student)}
                      >
                        <Edit fontSize="small" />
                      </IconButton>
                    }
                  >
                    <ListItemAvatar>
                      <Avatar
                        alt={student.full_name}
                        src={student.avatar_url}
                      />
                    </ListItemAvatar>
                    <ListItemText
                      primary={student.full_name}
                      secondary={student.student_code}
                    />
                  </ListItem>
                ))}

                {absentStudents.length === 0 && (
                  <ListItem>
                    <ListItemText primary="Không có sinh viên nào vắng mặt" />
                  </ListItem>
                )}
              </List>
            </CardContent>
          </Card>
        </Grid>
      </Grid>

      {/* Dialog điểm danh thủ công */}
      <Dialog open={manualDialogOpen} onClose={handleManualDialogClose}>
        <DialogTitle>Điểm danh thủ công</DialogTitle>
        <DialogContent>
          <Box sx={{ p: 1 }}>
            <Typography variant="subtitle1" gutterBottom>
              {selectedStudent?.full_name} ({selectedStudent?.student_code})
            </Typography>

            <FormControl fullWidth margin="normal">
              <InputLabel>Trạng thái</InputLabel>
              <Select
                name="status"
                value={manualAttendanceData.status}
                onChange={handleManualAttendanceChange}
                label="Trạng thái"
              >
                <MenuItem value="present">Có mặt</MenuItem>
                <MenuItem value="absent">Vắng mặt</MenuItem>
                <MenuItem value="late">Đi trễ</MenuItem>
                <MenuItem value="early_leave">Về sớm</MenuItem>
              </Select>
            </FormControl>

            <TextField
              fullWidth
              margin="normal"
              name="note"
              label="Ghi chú"
              value={manualAttendanceData.note}
              onChange={handleManualAttendanceChange}
              multiline
              rows={2}
            />
          </Box>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleManualDialogClose}>Hủy</Button>
          <Button onClick={handleManualAttendanceSubmit} variant="contained">
            Xác nhận
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default AttendancePage;
