import React from "react";
import { useSelector } from "react-redux";
import {
  Container,
  Box,
  Typography,
  Paper,
  Alert,
  Button,
} from "@mui/material";
import { HourglassEmpty, CheckCircle } from "@mui/icons-material";
import { useNavigate } from "react-router-dom";
import { selectUser } from "../redux/slices/authSlice";

const PendingApprovalPage = () => {
  const user = useSelector(selectUser);
  const navigate = useNavigate();

  if (!user) {
    navigate("/login");
    return null;
  }

  // Nếu người dùng đã được phê duyệt, chuyển hướng về trang chủ
  if (user.status === "approved") {
    navigate("/");
    return null;
  }

  return (
    <Container component="main" maxWidth="md">
      <Box
        sx={{
          marginTop: 8,
          marginBottom: 8,
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
        }}
      >
        <Paper
          elevation={3}
          sx={{
            padding: 4,
            width: "100%",
            borderRadius: 3,
            textAlign: "center",
          }}
        >
          {user.status === "rejected" ? (
            <>
              <Alert severity="error" sx={{ mb: 3 }}>
                Tài khoản của bạn đã bị từ chối
              </Alert>
              <Typography variant="h5" gutterBottom>
                Tài khoản không được phê duyệt
              </Typography>
              <Typography variant="body1" sx={{ mt: 2, mb: 3 }}>
                Rất tiếc, tài khoản của bạn đã bị từ chối. Vui lòng liên hệ với
                quản trị viên để biết thêm chi tiết.
              </Typography>
            </>
          ) : (
            <>
              <HourglassEmpty
                sx={{ fontSize: 60, color: "primary.main", mb: 2 }}
              />
              <Typography variant="h5" gutterBottom>
                Tài khoản đang chờ phê duyệt
              </Typography>
              <Typography variant="body1" sx={{ mt: 2, mb: 3 }}>
                {user.role === "student"
                  ? "Tài khoản của bạn đang đợi phê duyệt từ giáo viên cố vấn."
                  : "Tài khoản của bạn đang đợi phê duyệt từ quản trị viên."}
              </Typography>
              <Alert severity="info" sx={{ mb: 3, textAlign: "left" }}>
                <Typography variant="body2">
                  <strong>Lưu ý:</strong> Quá trình phê duyệt có thể mất một
                  chút thời gian. Bạn sẽ nhận được email thông báo khi tài khoản
                  được phê duyệt.
                </Typography>
              </Alert>
            </>
          )}

          <Box sx={{ mt: 3 }}>
            <Button
              variant="outlined"
              onClick={() => navigate("/login")}
              sx={{ mr: 2 }}
            >
              Quay lại đăng nhập
            </Button>
            {user.status === "rejected" && (
              <Button variant="contained" onClick={() => navigate("/contact")}>
                Liên hệ hỗ trợ
              </Button>
            )}
          </Box>
        </Paper>
      </Box>
    </Container>
  );
};

export default PendingApprovalPage;
