import React, { useState, useEffect, useRef } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { useSnackbar } from "notistack";
import {
  Box,
  Typography,
  Card,
  CardContent,
  Grid,
  Paper,
  Button,
  Divider,
  CircularProgress,
  List,
  ListItem,
  ListItemAvatar,
  ListItemText,
  Avatar,
  Chip,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  IconButton,
  Alert,
  AlertTitle,
  Tooltip,
} from "@mui/material";
import {
  CameraAlt,
  Check,
  Close,
  Edit,
  Refresh,
  Save,
  VerifiedUser,
  HourglassEmpty,
  ArrowBack,
  Info,
} from "@mui/icons-material";
import Webcam from "react-webcam";
import * as faceapi from "face-api.js";
import {
  getClassFaceFeatures,
  verifyAttendance,
  manualAttendance,
  setModelLoaded,
  setCameraReady,
  setDetectedFaces,
  setRecognizedStudents,
  clearRecognitionState,
} from "../../redux/slices/faceRecognitionSlice";
import axios from "axios";

const API_URL = process.env.REACT_APP_API_URL || "http://localhost:5000/api";

const AttendancePage = () => {
  const { classId, sessionId } = useParams();
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const { enqueueSnackbar } = useSnackbar();

  const webcamRef = useRef(null);
  const canvasRef = useRef(null);
  const videoRef = useRef(null);
  const [intervalId, setIntervalId] = useState(null);
  const [showLandmark, setShowLandmark] = useState(true);
  const [landmarkIntervalId, setLandmarkIntervalId] = useState(null);
  const [cameraMode, setCameraMode] = useState("face_recognition");

  const { token } = useSelector((state) => state.auth);
  const {
    isModelLoaded,
    isCameraReady,
    detectedFaces,
    recognizedStudents,
    classStudents,
    isProcessing,
    error,
  } = useSelector((state) => state.faceRecognition);

  const [classInfo, setClassInfo] = useState(null);
  const [sessionInfo, setSessionInfo] = useState(null);
  const [attendanceLogs, setAttendanceLogs] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [isRecognizing, setIsRecognizing] = useState(false);
  const [isAutoMode, setIsAutoMode] = useState(false);
  const [selectedStudent, setSelectedStudent] = useState(null);
  const [manualDialogOpen, setManualDialogOpen] = useState(false);
  const [manualAttendanceData, setManualAttendanceData] = useState({
    status: "present",
    note: "",
  });

  // Các hằng số cấu hình được tối ưu
  const RECOGNITION_THRESHOLD = 0.45; // Ngưỡng nhận diện được tối ưu
  const DETECTION_THRESHOLD = 0.5; // Ngưỡng phát hiện khuôn mặt
  const CONFIDENCE_THRESHOLD = 0.6; // Ngưỡng độ tin cậy để điểm danh
  const AUTO_DETECT_INTERVAL = 1000; // Thời gian giữa các lần phát hiện (ms)
  const ENABLE_DEBUG_LOGS = false; // Tắt log gỡ lỗi trong môi trường sản phẩm

  // Thêm state mới để theo dõi số lần thử lại camera
  const [cameraRetryCount, setCameraRetryCount] = useState(0);
  const [lastDetectionTime, setLastDetectionTime] = useState(null);

  // Thêm state theo dõi interval phát hiện landmarks
  const [landmarkDetectionInterval, setLandmarkDetectionInterval] =
    useState(null);

  // Load class info, session info và models
  useEffect(() => {
    const loadInitialData = async () => {
      try {
        setIsLoading(true);

        // Lấy thông tin lớp học
        const classPromise = axios.get(
          `${API_URL}/classes/teaching/${classId}`,
          {
            headers: { Authorization: `Bearer ${token}` },
          }
        );

        // Lấy thông tin phiên điểm danh
        const sessionPromise = axios.get(
          `${API_URL}/attendance/sessions/${sessionId}`,
          {
            headers: { Authorization: `Bearer ${token}` },
          }
        );

        // Lấy danh sách điểm danh
        const logsPromise = axios.get(
          `${API_URL}/attendance/logs/${sessionId}`,
          {
            headers: { Authorization: `Bearer ${token}` },
          }
        );

        // Chờ tất cả API calls hoàn thành
        const [classResponse, sessionResponse, logsResponse] =
          await Promise.all([classPromise, sessionPromise, logsPromise]);

        setClassInfo(classResponse.data.data);
        setSessionInfo(sessionResponse.data.data);
        setAttendanceLogs(logsResponse.data.data);

        // Đã tải xong dữ liệu cơ bản
        setIsLoading(false);

        // Tải model nhận diện nếu chưa tải
        if (!isModelLoaded) {
          const modelLoaded = await loadFaceRecognitionModels();
          if (modelLoaded) {
            dispatch(setModelLoaded(true));
          }
        }

        if (sessionResponse.data.data.status !== "completed") {
          // Lấy đặc trưng khuôn mặt của sinh viên trong lớp
          try {
            await dispatch(getClassFaceFeatures(classId)).unwrap();
          } catch (apiError) {
            console.error("Lỗi khi lấy dữ liệu khuôn mặt:", apiError);
            enqueueSnackbar(
              "Lỗi khi lấy dữ liệu khuôn mặt. Vui lòng kiểm tra lại.",
              { variant: "error" }
            );
          }
        }

        // Khởi tạo thời gian phát hiện ban đầu
        setLastDetectionTime(Date.now());
      } catch (error) {
        console.error("Lỗi khi tải dữ liệu:", error);
        enqueueSnackbar("Lỗi khi tải dữ liệu", { variant: "error" });
        setIsLoading(false);
      }
    };

    loadInitialData();

    // Clean up
    return () => {
      if (intervalId) {
        clearInterval(intervalId);
      }

      // Dừng landmark interval nếu đang chạy
      if (landmarkIntervalId) {
        clearInterval(landmarkIntervalId);
      }

      // Dừng camera stream nếu đang chạy
      if (
        webcamRef.current &&
        webcamRef.current.video &&
        webcamRef.current.video.srcObject
      ) {
        const tracks = webcamRef.current.video.srcObject.getTracks();
        tracks.forEach((track) => track.stop());
      }

      if (videoRef.current && videoRef.current.srcObject) {
        const tracks = videoRef.current.srcObject.getTracks();
        tracks.forEach((track) => track.stop());
      }

      dispatch(clearRecognitionState());

      // Reset các state theo dõi camera
      setCameraRetryCount(0);
      setLastDetectionTime(null);
    };
  }, [
    classId,
    sessionId,
    token,
    dispatch,
    enqueueSnackbar,
    isModelLoaded,
    intervalId,
    landmarkIntervalId,
  ]);

  // Kiểm tra tình trạng tải mô hình
  const loadFaceRecognitionModels = async () => {
    try {
      setLoading(true);
      setLoadingMessage("Đang tải mô hình nhận diện...");

      // Hiển thị tình trạng tải cho từng mô hình
      const modelLoadStates = {
        tinyFaceDetector: false,
        faceLandmark68Net: false,
        faceRecognitionNet: false,
        ssdMobilenetv1: false,
      };

      // Thực hiện tải mô hình với theo dõi trạng thái
      await Promise.all([
        // Tải mô hình TinyFaceDetector
        faceapi.nets.tinyFaceDetector.load(MODEL_URL).then(() => {
          console.log("Đã tải xong mô hình TinyFaceDetector");
          modelLoadStates.tinyFaceDetector = true;
          setLoadingMessage("Đã tải xong mô hình phát hiện khuôn mặt nhanh");
        }),

        // Tải mô hình FaceLandmark68Net
        faceapi.nets.faceLandmark68Net.load(MODEL_URL).then(() => {
          console.log("Đã tải xong mô hình FaceLandmark68Net");
          modelLoadStates.faceLandmark68Net = true;
          setLoadingMessage("Đã tải xong mô hình nhận diện đặc điểm khuôn mặt");
        }),

        // Tải mô hình FaceRecognitionNet
        faceapi.nets.faceRecognitionNet.load(MODEL_URL).then(() => {
          console.log("Đã tải xong mô hình FaceRecognitionNet");
          modelLoadStates.faceRecognitionNet = true;
          setLoadingMessage("Đã tải xong mô hình nhận diện người dùng");
        }),

        // Tải mô hình SsdMobilenetv1 để dự phòng
        faceapi.nets.ssdMobilenetv1.load(MODEL_URL).then(() => {
          console.log("Đã tải xong mô hình SsdMobilenetv1");
          modelLoadStates.ssdMobilenetv1 = true;
          setLoadingMessage("Đã tải xong mô hình phát hiện dự phòng");
        }),
      ]);

      // Kiểm tra lại trạng thái tải của các mô hình
      const allModelsLoaded = Object.values(modelLoadStates).every(
        (state) => state === true
      );

      if (!allModelsLoaded) {
        console.error("Không tải được tất cả các mô hình", modelLoadStates);
        throw new Error("Không tải được đầy đủ các mô hình cần thiết");
      }

      console.log("Tất cả các mô hình đã được tải thành công");
      setLoadingMessage("Tất cả mô hình đã được tải");
      setModelsLoaded(true);
      setLoading(false);
    } catch (error) {
      console.error("Lỗi khi tải mô hình nhận diện:", error);
      setLoadingMessage(
        "Lỗi khi tải mô hình nhận diện. Vui lòng làm mới trang và thử lại."
      );
      setModelsLoaded(false);
      setLoading(false);
    }
  };

  // Hàm khởi tạo camera đã tối ưu
  const initDirectCamera = async () => {
    try {
      // Hiển thị thông báo đang khởi tạo
      enqueueSnackbar("Đang khởi tạo camera...", { variant: "info" });

      // Tải model nếu chưa tải
      if (!isModelLoaded) {
        const modelLoaded = await loadFaceRecognitionModels();
        if (!modelLoaded) {
          enqueueSnackbar(
            "Không thể tải mô hình nhận diện. Vui lòng thử lại.",
            { variant: "error" }
          );
          return false;
        }
      }

      // Đảm bảo model đã được tải đầy đủ
      if (
        !faceapi.nets.tinyFaceDetector.isLoaded ||
        !faceapi.nets.faceLandmark68Net.isLoaded ||
        !faceapi.nets.faceRecognitionNet.isLoaded ||
        !faceapi.nets.ssdMobilenetv1.isLoaded
      ) {
        console.warn("Phát hiện model chưa được tải đầy đủ, thử tải lại");
        await loadFaceRecognitionModels();
      }

      // Dừng stream cũ nếu có
      const stopExistingStream = (videoElement) => {
        if (videoElement?.srcObject) {
          const tracks = videoElement.srcObject.getTracks();
          tracks.forEach((track) => track.stop());
        }
      };

      stopExistingStream(webcamRef.current?.video);
      stopExistingStream(videoRef.current);

      // Cấu hình camera tối ưu
      const constraints = {
        video: {
          width: { ideal: 640, min: 320 },
          height: { ideal: 480, min: 240 },
          facingMode: "user",
          frameRate: { ideal: 24, min: 15 },
        },
        audio: false,
      };

      // Truy cập camera
      try {
        const stream = await navigator.mediaDevices.getUserMedia(constraints);

        // Xử lý stream video
        const setupVideoStream = (videoElement) => {
          if (!videoElement) return false;

          videoElement.srcObject = stream;
          return true;
        };

        // Thiết lập stream cho webcam hoặc video element
        let streamSetupSuccess = false;
        if (webcamRef.current && webcamRef.current.video) {
          streamSetupSuccess = setupVideoStream(webcamRef.current.video);

          // Đảm bảo video đã được tải trước khi tiếp tục
          if (streamSetupSuccess) {
            await waitForVideoReady(webcamRef.current.video);
          }
        } else if (videoRef.current) {
          streamSetupSuccess = setupVideoStream(videoRef.current);

          // Đảm bảo video đã được tải trước khi tiếp tục
          if (streamSetupSuccess) {
            await waitForVideoReady(videoRef.current);
          }
        }

        if (!streamSetupSuccess) {
          throw new Error("Không thể thiết lập video stream");
        }

        // Đánh dấu camera đã sẵn sàng
        dispatch(setCameraReady(true));
        setLastDetectionTime(Date.now());
        setCameraRetryCount(0);

        // Đợi thêm 1 giây trước khi bật hiển thị landmark
        // để đảm bảo video đã ổn định
        await new Promise((resolve) => setTimeout(resolve, 1000));

        setShowLandmark(true);
        startLandmarkDetection();

        enqueueSnackbar("Đã kết nối camera thành công", { variant: "success" });
        return true;
      } catch (err) {
        console.error("Lỗi truy cập camera:", err);

        // Thử phương án thay thế nếu lỗi
        if (cameraRetryCount < 2) {
          try {
            enqueueSnackbar("Đang thử kết nối với camera thay thế...", {
              variant: "info",
            });
            const altStream = await navigator.mediaDevices.getUserMedia({
              video: true,
              audio: false,
            });

            if (webcamRef.current?.video) {
              webcamRef.current.video.srcObject = altStream;
              await waitForVideoReady(webcamRef.current.video);
            } else if (videoRef.current) {
              videoRef.current.srcObject = altStream;
              await waitForVideoReady(videoRef.current);
            }

            dispatch(setCameraReady(true));
            setLastDetectionTime(Date.now());
            setCameraRetryCount((prev) => prev + 1);
            enqueueSnackbar("Đã kết nối camera thay thế", {
              variant: "success",
            });
            return true;
          } catch (altErr) {
            console.error("Không thể sử dụng camera thay thế:", altErr);
          }
        }

        enqueueSnackbar(`Không thể truy cập camera: ${err.message}`, {
          variant: "error",
        });
        return false;
      }
    } catch (err) {
      console.error("Lỗi khi khởi tạo camera:", err);
      enqueueSnackbar(`Lỗi: ${err.message}`, { variant: "error" });
      return false;
    }
  };

  // Hàm đợi video sẵn sàng
  const waitForVideoReady = (videoElement) => {
    return new Promise((resolve) => {
      if (!videoElement) {
        resolve(false);
        return;
      }

      // Nếu video đã sẵn sàng thì resolve ngay
      if (videoElement.readyState >= 2) {
        videoElement
          .play()
          .catch((err) => console.error("Lỗi khi play video:", err));
        resolve(true);
        return;
      }

      const handleCanPlay = () => {
        videoElement
          .play()
          .catch((err) => console.error("Lỗi khi play video:", err));
        resolve(true);
      };

      const handleError = () => {
        console.error("Video không thể phát");
        resolve(false);
      };

      // Thiết lập các event listener
      videoElement.addEventListener("canplay", handleCanPlay, { once: true });
      videoElement.addEventListener("loadeddata", handleCanPlay, {
        once: true,
      });
      videoElement.addEventListener("error", handleError, { once: true });

      // Thiết lập timeout để tránh chờ vô hạn
      const timeoutId = setTimeout(() => {
        videoElement.removeEventListener("canplay", handleCanPlay);
        videoElement.removeEventListener("loadeddata", handleCanPlay);
        videoElement.removeEventListener("error", handleError);
        console.warn("Đã hết thời gian chờ video sẵn sàng");

        // Vẫn cố gắng phát video
        videoElement
          .play()
          .catch((err) =>
            console.error("Lỗi khi play video sau timeout:", err)
          );
        resolve(false);
      }, 5000); // 5 giây timeout

      // Cleanup function
      videoElement.addEventListener("canplay", () => clearTimeout(timeoutId), {
        once: true,
      });
      videoElement.addEventListener(
        "loadeddata",
        () => clearTimeout(timeoutId),
        { once: true }
      );
      videoElement.addEventListener("error", () => clearTimeout(timeoutId), {
        once: true,
      });
    });
  };

  // Bắt đầu phát hiện landmarks theo thời gian thực
  const startLandmarkDetection = () => {
    // Xóa interval cũ nếu có
    if (landmarkDetectionInterval) {
      clearInterval(landmarkDetectionInterval);
    }

    // Theo dõi frame trước đó để tránh xử lý các detection null liên tiếp
    let lastValidDetections = null;
    let invalidFrameCount = 0;
    const MAX_INVALID_FRAMES = 5; // Số lượng frame không hợp lệ tối đa trước khi xóa canvas

    // Tạo interval mới để cập nhật landmarks mỗi 200ms
    const intervalId = setInterval(async () => {
      if (cameraMode === "face_recognition" && showLandmark) {
        // Kiểm tra trạng thái camera trước khi thực hiện phát hiện
        const videoElement = isAutoMode
          ? videoRef.current
          : webcamRef.current?.video;

        // Kiểm tra kỹ video element
        if (
          !videoElement ||
          videoElement.readyState !== 4 ||
          videoElement.videoWidth === 0 ||
          videoElement.videoHeight === 0 ||
          !videoElement.srcObject ||
          videoElement.srcObject.active === false
        ) {
          console.log("Video chưa sẵn sàng, bỏ qua phát hiện landmark");
          return;
        }

        // Thực hiện phát hiện khuôn mặt
        try {
          const canvasElement = canvasRef.current;
          if (!canvasElement) return;

          const detections = await handleDetectFaces(true);

          // Kiểm tra kỹ detections
          if (
            !detections ||
            !Array.isArray(detections) ||
            detections.length === 0
          ) {
            invalidFrameCount++;

            // Nếu có quá nhiều frame không hợp lệ liên tiếp, xóa canvas
            if (invalidFrameCount > MAX_INVALID_FRAMES) {
              if (canvasElement && canvasElement.getContext) {
                const ctx = canvasElement.getContext("2d");
                if (ctx) {
                  ctx.clearRect(
                    0,
                    0,
                    canvasElement.width,
                    canvasElement.height
                  );
                }
              }

              // Có thể sử dụng kết quả hợp lệ cuối cùng
              if (
                lastValidDetections &&
                Array.isArray(lastValidDetections) &&
                lastValidDetections.length > 0
              ) {
                console.log("Sử dụng kết quả phát hiện hợp lệ cuối cùng");
                try {
                  // Hiển thị kết quả cũ an toàn
                  drawFaceLandmarks(
                    lastValidDetections,
                    canvasElement,
                    recognizedStudents
                  );
                } catch (error) {
                  console.error("Lỗi khi vẽ last valid detections:", error);
                }
              }
            }
            return;
          }

          // Reset bộ đếm khi có frame hợp lệ
          invalidFrameCount = 0;

          // Lọc kỹ một lần nữa
          const validDetections = detections.filter((detection) => {
            if (!detection || !detection.detection || !detection.detection.box)
              return false;

            const box = detection.detection.box;
            return (
              box &&
              typeof box.x === "number" &&
              !isNaN(box.x) &&
              typeof box.y === "number" &&
              !isNaN(box.y) &&
              typeof box.width === "number" &&
              !isNaN(box.width) &&
              typeof box.height === "number" &&
              !isNaN(box.height)
            );
          });

          if (validDetections.length > 0) {
            lastValidDetections = validDetections;
          }
        } catch (error) {
          console.error("Lỗi khi phát hiện landmarks:", error);
          // Tăng bộ đếm khi có lỗi
          invalidFrameCount++;
        }
      }
    }, 200); // Tăng lên 200ms để giảm tải CPU

    setLandmarkDetectionInterval(intervalId);
  };

  // Dừng phát hiện landmarks theo thời gian thực
  const stopLandmarkDetection = () => {
    if (landmarkDetectionInterval) {
      clearInterval(landmarkDetectionInterval);
      setLandmarkDetectionInterval(null);
    }
  };

  // Cập nhật useEffect để bắt đầu và dừng phát hiện landmarks khi camera hoặc chế độ thay đổi
  useEffect(() => {
    if (cameraMode === "face_recognition" && showLandmark) {
      startLandmarkDetection();
    } else {
      stopLandmarkDetection();
    }

    return () => {
      stopLandmarkDetection();
    };
  }, [cameraMode, showLandmark]);

  // Cập nhật useEffect cho việc toggle showLandmark
  useEffect(() => {
    if (showLandmark && cameraMode === "face_recognition") {
      startLandmarkDetection();
    } else {
      stopLandmarkDetection();
    }
  }, [showLandmark]);

  // Hàm kiểm tra và đảm bảo camera hoạt động
  const ensureCameraIsWorking = () => {
    // Kiểm tra nếu camera chưa hoạt động và đã thử khởi động
    if (!isCameraReady && cameraRetryCount === 0) {
      console.log("[DEBUG] Camera chưa sẵn sàng, thử khởi động");
      setCameraRetryCount(1);
      initDirectCamera();
      return false;
    }

    // Kiểm tra stream của camera
    const videoElement = isAutoMode
      ? videoRef.current
      : webcamRef.current?.video;

    if (
      !videoElement ||
      !videoElement.srcObject ||
      videoElement.readyState !== 4
    ) {
      console.log(
        "[DEBUG] Video stream không hoạt động, thử khởi động lại camera"
      );
      if (cameraRetryCount < 3) {
        setCameraRetryCount((prev) => prev + 1);
        initDirectCamera();
        return false;
      }
    }

    return true;
  };

  // Hàm vẽ landmarks được cập nhật
  const drawFaceLandmarks = (detections, canvas, recognizedStudents = []) => {
    if (!canvas) return;

    const ctx = canvas.getContext("2d");
    if (!ctx) return;

    ctx.clearRect(0, 0, canvas.width, canvas.height);

    // Nếu detections là null hoặc trống, không vẽ gì
    if (!detections || !Array.isArray(detections) || detections.length === 0) {
      return;
    }

    // Lọc các phát hiện không hợp lệ
    const validDetections = detections.filter((detection) => {
      if (!detection || !detection.detection || !detection.detection.box)
        return false;

      const box = detection.detection.box;
      return (
        box &&
        typeof box.x === "number" &&
        !isNaN(box.x) &&
        typeof box.y === "number" &&
        !isNaN(box.y) &&
        typeof box.width === "number" &&
        !isNaN(box.width) &&
        typeof box.height === "number" &&
        !isNaN(box.height)
      );
    });

    // Nếu không có detection hợp lệ sau khi lọc, thoát
    if (validDetections.length === 0) {
      return;
    }

    validDetections.forEach((detection, index) => {
      try {
        const box = detection.detection.box;
        const landmarks = detection.landmarks;

        if (
          !box ||
          !landmarks?.positions ||
          !Array.isArray(landmarks.positions) ||
          landmarks.positions.length === 0 ||
          box.x == null ||
          box.y == null ||
          box.width == null ||
          box.height == null ||
          isNaN(box.x) ||
          isNaN(box.y) ||
          isNaN(box.width) ||
          isNaN(box.height)
        ) {
          return;
        }

        // Vẽ hộp giới hạn khuôn mặt với viền gradient
        const boxGradient = ctx.createLinearGradient(
          box.x,
          box.y,
          box.x + box.width,
          box.y + box.height
        );
        boxGradient.addColorStop(0, "#4CAF50");
        boxGradient.addColorStop(1, "#2196F3");

        ctx.strokeStyle = boxGradient;
        ctx.lineWidth = 3;
        ctx.strokeRect(box.x, box.y, box.width, box.height);

        // Tìm sinh viên đã được nhận diện tương ứng với detection này
        const matchingStudent = recognizedStudents.find(
          (student, idx) => idx === index
        );

        // Tạo background cho text
        const labelY = box.y - 10;
        const labelText = matchingStudent
          ? `${matchingStudent.name} (${Math.round(
              matchingStudent.confidence * 100
            )}%)`
          : "Đã phát hiện khuôn mặt";

        const textMetrics = ctx.measureText(labelText);
        const textWidth = textMetrics.width + 10; // Thêm padding

        // Vẽ background cho label
        ctx.fillStyle = "rgba(0, 0, 0, 0.7)";
        ctx.fillRect(box.x, labelY - 20, textWidth, 24);

        // Vẽ text tên và % nhận diện
        ctx.fillStyle = "#FFFFFF";
        ctx.font = "14px Arial";
        ctx.fillText(labelText, box.x + 5, labelY - 4);

        const points = landmarks.positions;
        if (!points || !Array.isArray(points) || points.length < 68) return;

        // Vẽ các điểm landmarks với alpha thấp hơn để không quá nổi bật
        ctx.globalAlpha = 0.6;

        // Hàm vẽ nhóm điểm
        const drawPoints = (startIdx, endIdx, color) => {
          ctx.fillStyle = color;
          for (let i = startIdx; i <= endIdx; i++) {
            if (
              points[i] &&
              points[i].x != null &&
              points[i].y != null &&
              !isNaN(points[i].x) &&
              !isNaN(points[i].y)
            ) {
              ctx.beginPath();
              ctx.arc(points[i].x, points[i].y, 2, 0, 2 * Math.PI);
              ctx.fill();
            }
          }
        };

        // Hàm vẽ đường kết nối các điểm
        const drawLine = (points, indices) => {
          if (!points || indices.length < 2) return;

          for (let i = 1; i < indices.length; i++) {
            const idx1 = indices[i - 1];
            const idx2 = indices[i];

            if (
              points[idx1] &&
              points[idx2] &&
              points[idx1].x != null &&
              points[idx1].y != null &&
              points[idx2].x != null &&
              points[idx2].y != null &&
              !isNaN(points[idx1].x) &&
              !isNaN(points[idx1].y) &&
              !isNaN(points[idx2].x) &&
              !isNaN(points[idx2].y)
            ) {
              ctx.beginPath();
              ctx.moveTo(points[idx1].x, points[idx1].y);
              ctx.lineTo(points[idx2].x, points[idx2].y);
              ctx.stroke();
            }
          }
        };

        // Vẽ các nhóm điểm với màu khác nhau
        drawPoints(0, 16, "#E91E63"); // Đường viền khuôn mặt
        drawPoints(17, 21, "#03A9F4"); // Lông mày trái
        drawPoints(22, 26, "#03A9F4"); // Lông mày phải
        drawPoints(27, 35, "#FF9800"); // Mũi
        drawPoints(36, 41, "#4CAF50"); // Mắt trái
        drawPoints(42, 47, "#4CAF50"); // Mắt phải
        drawPoints(48, 59, "#FFC107"); // Miệng ngoài
        drawPoints(60, 67, "#FF5722"); // Miệng trong

        // Khôi phục alpha
        ctx.globalAlpha = 1.0;
      } catch (error) {
        console.error("Lỗi khi vẽ landmark cho detection:", error);
      }
    });
  };

  // Hàm kiểm tra an toàn cho detections trước khi sử dụng
  const safeResizeResults = (detections, size) => {
    // Kiểm tra đầu vào
    if (!detections) return [];

    if (!Array.isArray(detections)) {
      // Nếu là một detection đơn lẻ, bọc thành mảng để xử lý
      const singleDetection = detections;

      // Kiểm tra detection có hợp lệ không (có box và giá trị x, y, width, height đều là số)
      if (
        !singleDetection ||
        !singleDetection.detection ||
        !singleDetection.detection.box
      )
        return [];

      const box = singleDetection.detection.box;
      if (
        box.x === null ||
        box.y === null ||
        box.width === null ||
        box.height === null ||
        typeof box.x !== "number" ||
        typeof box.y !== "number" ||
        typeof box.width !== "number" ||
        typeof box.height !== "number" ||
        isNaN(box.x) ||
        isNaN(box.y) ||
        isNaN(box.width) ||
        isNaN(box.height)
      ) {
        console.error("Box không hợp lệ khi resize:", box);
        return [];
      }

      // Thực hiện resize an toàn
      try {
        return [faceapi.resizeResults(singleDetection, size)];
      } catch (error) {
        console.error("Lỗi khi resize detection đơn:", error);
        return [];
      }
    }

    // Xử lý mảng detections
    // Lọc bỏ các detection không hợp lệ
    const validDetections = detections.filter((detection) => {
      if (!detection || !detection.detection || !detection.detection.box)
        return false;

      const box = detection.detection.box;
      return (
        box &&
        box.x !== null &&
        box.y !== null &&
        box.width !== null &&
        box.height !== null &&
        typeof box.x === "number" &&
        typeof box.y === "number" &&
        typeof box.width === "number" &&
        typeof box.height === "number" &&
        !isNaN(box.x) &&
        !isNaN(box.y) &&
        !isNaN(box.width) &&
        !isNaN(box.height)
      );
    });

    if (validDetections.length === 0) return [];

    // Resize từng detection an toàn
    const resizedResults = [];
    for (const detection of validDetections) {
      try {
        const resized = faceapi.resizeResults(detection, size);
        resizedResults.push(resized);
      } catch (error) {
        console.error("Lỗi khi resize detection:", error);
        // Bỏ qua detection gây lỗi
      }
    }

    return resizedResults;
  };

  // Cải thiện hàm phát hiện khuôn mặt với tích hợp hiển thị thông tin sinh viên
  const handleDetectFaces = async (landmarkOnly = false) => {
    if (cameraMode !== "face_recognition" || !faceapi || !canvasRef.current)
      return [];

    // Xác định video element hiện tại
    const videoElement = isAutoMode
      ? videoRef.current
      : webcamRef.current?.video;

    // Kiểm tra video element kỹ hơn
    if (
      !videoElement ||
      videoElement.readyState !== 4 ||
      videoElement.videoWidth === 0 ||
      videoElement.videoHeight === 0 ||
      !videoElement.srcObject ||
      videoElement.srcObject.active === false
    ) {
      console.warn("Video chưa sẵn sàng hoặc không có kích thước hợp lệ");
      return [];
    }

    const canvasElement = canvasRef.current;

    try {
      // Kiểm tra xem canvas đã được khởi tạo chưa
      if (!canvasElement) {
        console.warn("Canvas chưa được khởi tạo");
        return [];
      }

      // Đặt kích thước canvas để khớp với video thực tế
      const displaySize = {
        width: videoElement.videoWidth,
        height: videoElement.videoHeight,
      };

      if (displaySize.width === 0 || displaySize.height === 0) {
        console.warn("Kích thước video không hợp lệ:", displaySize);
        return [];
      }

      // Đảm bảo canvas có kích thước đúng
      if (
        canvasElement.width !== displaySize.width ||
        canvasElement.height !== displaySize.height
      ) {
        canvasElement.width = displaySize.width;
        canvasElement.height = displaySize.height;
      }

      // Đảm bảo mô hình đã được tải trước khi phát hiện
      if (!faceapi.nets.tinyFaceDetector.isLoaded) {
        console.warn("Mô hình tinyFaceDetector chưa được tải");
        return [];
      }
      if (!faceapi.nets.faceLandmark68Net.isLoaded) {
        console.warn("Mô hình faceLandmark68Net chưa được tải");
        return [];
      }

      // Khớp kích thước an toàn - Chỉ áp dụng nếu canvas và displaySize đều hợp lệ
      if (
        canvasElement &&
        displaySize &&
        displaySize.width > 0 &&
        displaySize.height > 0
      ) {
        try {
          faceapi.matchDimensions(canvasElement, displaySize);
        } catch (error) {
          console.error("Lỗi khi matchDimensions:", error);
        }
      }

      // Sử dụng TinyFaceDetector nhanh hơn để phát hiện khuôn mặt với landmarks
      let detections = [];
      try {
        // Đảm bảo video đã sẵn sàng trước khi phát hiện
        if (videoElement.readyState < 4) {
          console.warn("Video chưa hoàn toàn tải xong, đợi thêm...");
          return [];
        }

        detections = await faceapi
          .detectAllFaces(
            videoElement,
            new faceapi.TinyFaceDetectorOptions({
              scoreThreshold: DETECTION_THRESHOLD,
              inputSize: 320,
            })
          )
          .withFaceLandmarks();
      } catch (detectionError) {
        console.error("Lỗi khi phát hiện khuôn mặt:", detectionError);
        return [];
      }

      // Kiểm tra xem detections có tồn tại và có phần tử không
      if (
        !detections ||
        !Array.isArray(detections) ||
        detections.length === 0
      ) {
        console.log("Không phát hiện khuôn mặt");
        if (canvasElement) {
          canvasElement
            .getContext("2d")
            .clearRect(0, 0, canvasElement.width, canvasElement.height);
        }
        return [];
      }

      // Lọc bỏ các phát hiện không hợp lệ một cách nghiêm ngặt hơn
      detections = detections.filter((detection) => {
        if (!detection || !detection.detection || !detection.detection.box)
          return false;

        const box = detection.detection.box;
        return (
          box &&
          typeof box.x === "number" &&
          !isNaN(box.x) &&
          typeof box.y === "number" &&
          !isNaN(box.y) &&
          typeof box.width === "number" &&
          !isNaN(box.width) &&
          typeof box.height === "number" &&
          !isNaN(box.height)
        );
      });

      if (detections.length === 0) {
        console.log("Không phát hiện khuôn mặt hợp lệ sau khi lọc");
        canvasElement
          .getContext("2d")
          .clearRect(0, 0, canvasElement.width, canvasElement.height);
        return [];
      }

      // Sử dụng hàm resize an toàn thay vì trực tiếp gọi faceapi.resizeResults
      const resizedDetections = safeResizeResults(detections, displaySize);

      if (resizedDetections.length === 0) {
        console.log("Không có detection hợp lệ sau khi resize an toàn");
        return [];
      }

      // Vẽ landmarks nếu có detection hợp lệ sau khi resize
      if (landmarkOnly && resizedDetections.length > 0) {
        // Vẽ landmarks với thông tin nhận diện nếu có
        drawFaceLandmarks(resizedDetections, canvasElement, recognizedStudents);
      }

      // Trả về detections gốc (chưa resize) để sử dụng cho nhận diện
      return detections;
    } catch (error) {
      console.error("Lỗi tổng thể khi phát hiện khuôn mặt:", error);
      return [];
    }
  };

  // Hàm trích xuất descriptor từ sinh viên, tối ưu hoá và chỉ phải làm một lần
  const extractStudentDescriptors = (student) => {
    if (!student) return [];

    const descriptors = [];

    // Kiểm tra cấu trúc mới
    if (student.faceFeatures?.descriptors) {
      const featuresData = student.faceFeatures.descriptors;

      // Duyệt qua tất cả cấu trúc lồng nhau để tìm mảng 128 phần tử
      const findDescriptorsInArray = (arr, maxDepth = 3, depth = 0) => {
        if (!Array.isArray(arr) || depth > maxDepth) return;

        if (arr.length === 128 && arr.every((val) => typeof val === "number")) {
          descriptors.push(arr);
          return;
        }

        for (const item of arr) {
          if (Array.isArray(item)) {
            findDescriptorsInArray(item, maxDepth, depth + 1);
          }
        }
      };

      findDescriptorsInArray(featuresData);
    }

    // Kiểm tra cấu trúc cũ
    if (descriptors.length === 0 && student.faceDescriptors) {
      const oldFeatures = student.faceDescriptors;

      if (Array.isArray(oldFeatures)) {
        // Trường hợp 1: mảng các descriptor
        for (const item of oldFeatures) {
          if (Array.isArray(item) && item.length === 128) {
            descriptors.push(item);
          }
          // Trường hợp 2: mảng các object chứa descriptor
          else if (typeof item === "object" && item !== null) {
            // Tìm trong các thuộc tính của object
            for (const key in item) {
              const value = item[key];

              if (
                Array.isArray(value) &&
                value.length === 128 &&
                value.every((v) => typeof v === "number")
              ) {
                descriptors.push(value);
              }
              // Tìm trong thuộc tính descriptors
              else if (key === "descriptors" && Array.isArray(value)) {
                value.forEach((desc) => {
                  if (Array.isArray(desc) && desc.length === 128) {
                    descriptors.push(desc);
                  }
                });
              }
            }
          }
        }
      }
    }

    return descriptors;
  };

  // Xử lý phát hiện khuôn mặt
  const handleFaceDetection = async (detections) => {
    // Nếu đang ở chế độ tự động và có tải mô hình
    if (isAutoMode && faceapi) {
      // Kiểm tra mảng detections
      if (
        !detections ||
        !Array.isArray(detections) ||
        detections.length === 0
      ) {
        console.log("Không có khuôn mặt nào được phát hiện để xử lý");
        return;
      }

      // Lọc các phát hiện có descriptor hợp lệ (bắt buộc phải có descriptor cho nhận diện)
      const validDetections = detections.filter((detection) => {
        // Kiểm tra box hợp lệ
        const boxValid =
          detection?.detection?.box &&
          typeof detection.detection.box.x === "number" &&
          !isNaN(detection.detection.box.x) &&
          typeof detection.detection.box.y === "number" &&
          !isNaN(detection.detection.box.y) &&
          typeof detection.detection.box.width === "number" &&
          !isNaN(detection.detection.box.width) &&
          typeof detection.detection.box.height === "number" &&
          !isNaN(detection.detection.box.height);

        // Kiểm tra descriptor hợp lệ (mảng 128 phần tử)
        const descriptorValid =
          detection?.descriptor &&
          Array.isArray(detection.descriptor) &&
          detection.descriptor.length === 128 &&
          detection.descriptor.every((v) => typeof v === "number" && !isNaN(v));

        return boxValid && descriptorValid;
      });

      // Nếu không có phát hiện hợp lệ
      if (validDetections.length === 0) {
        console.log("Không có detection hợp lệ nào có descriptor để nhận diện");
        return;
      }

      // Cập nhật state với dữ liệu hợp lệ - thay đổi tên hàm
      dispatch(setDetectedFaces(validDetections));

      // Xử lý tự động nhận diện nếu được bật
      if (autoAttendanceOn && validDetections.length > 0) {
        processAutoRecognition(validDetections);
      }
    }
  };

  // Điểm danh thủ công
  const openManualAttendanceDialog = (student) => {
    if (!student) {
      enqueueSnackbar("Không tìm thấy thông tin sinh viên", {
        variant: "error",
      });
      return;
    }

    setSelectedStudent(student);
    setManualAttendanceData({
      status: "present", // Mặc định là có mặt
      note: "",
    });
    setManualDialogOpen(true);
  };

  const handleManualDialogClose = () => {
    setManualDialogOpen(false);
  };

  const handleManualAttendanceChange = (e) => {
    setManualAttendanceData({
      ...manualAttendanceData,
      [e.target.name]: e.target.value,
    });
  };

  const handleManualAttendanceSubmit = async () => {
    if (!selectedStudent) return;

    try {
      // Hiển thị thông báo đang xử lý
      enqueueSnackbar("Đang xử lý điểm danh thủ công...", { variant: "info" });

      // Gọi API điểm danh thủ công
      const result = await dispatch(
        manualAttendance({
          sessionId,
          studentId: selectedStudent._id,
          status: manualAttendanceData.status,
          note: manualAttendanceData.note,
        })
      ).unwrap();

      // Nếu điểm danh thành công
      if (result?.data) {
        // Cập nhật logs điểm danh hiệu quả hơn
        setAttendanceLogs((prevLogs) => {
          // Lọc bỏ log cũ của sinh viên nếu có
          const filteredLogs = prevLogs.filter(
            (log) => log.student_id._id !== selectedStudent._id
          );
          // Thêm log mới
          return [
            ...filteredLogs,
            {
              ...result.data,
              student_id: selectedStudent,
            },
          ];
        });

        enqueueSnackbar(
          `Đã điểm danh ${
            manualAttendanceData.status === "present"
              ? "có mặt"
              : manualAttendanceData.status === "absent"
              ? "vắng mặt"
              : manualAttendanceData.status === "late"
              ? "đi trễ"
              : "về sớm"
          } cho ${selectedStudent.full_name}`,
          { variant: "success" }
        );

        // Đóng dialog
        handleManualDialogClose();

        // Cập nhật thông tin phiên
        fetchSessionInfo();
      }
    } catch (error) {
      console.error("Lỗi khi điểm danh thủ công:", error);
      enqueueSnackbar("Lỗi khi điểm danh thủ công", { variant: "error" });
    }
  };

  // Đánh dấu điểm danh từ nhận diện khuôn mặt
  const markAttendance = async (
    studentId,
    faceDescriptor,
    confidence,
    imageBase64
  ) => {
    try {
      // Hiển thị thông báo đang xử lý
      enqueueSnackbar("Đang xử lý điểm danh...", { variant: "info" });

      // Gọi API điểm danh qua Redux
      const result = await dispatch(
        verifyAttendance({
          sessionId,
          studentId,
          faceDescriptor,
          confidence,
          imageBase64,
        })
      ).unwrap();

      // Nếu điểm danh thành công
      if (result?.data) {
        // Tìm thông tin sinh viên
        const student = classStudents.find((s) => s._id === studentId);

        if (student) {
          // Cập nhật logs điểm danh hiệu quả hơn
          setAttendanceLogs((prevLogs) => {
            // Lọc bỏ log cũ của sinh viên nếu có
            const filteredLogs = prevLogs.filter(
              (log) => log.student_id._id !== studentId
            );
            // Thêm log mới
            return [
              ...filteredLogs,
              {
                ...result.data,
                student_id: student,
              },
            ];
          });

          enqueueSnackbar(`Đã điểm danh cho ${student.full_name}`, {
            variant: "success",
          });
        }

        // Cập nhật thông tin phiên một cách bất đồng bộ
        fetchSessionInfo();
        return true;
      }
      return false;
    } catch (error) {
      console.error("Lỗi khi xác nhận điểm danh:", error);
      enqueueSnackbar("Lỗi khi xác nhận điểm danh", { variant: "error" });
      return false;
    }
  };

  // Kết thúc phiên điểm danh
  const completeSession = async () => {
    try {
      // Hiển thị thông báo đang xử lý
      enqueueSnackbar("Đang kết thúc phiên điểm danh...", { variant: "info" });

      // Gọi API kết thúc phiên
      await axios.put(
        `${API_URL}/attendance/sessions/${sessionId}`,
        { status: "completed" },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      enqueueSnackbar("Phiên điểm danh đã kết thúc thành công", {
        variant: "success",
      });

      // Chuyển hướng về trang lớp học
      setTimeout(() => {
        navigate(`/teacher/classes/${classId}`);
      }, 1000);
    } catch (error) {
      console.error("Lỗi khi kết thúc phiên điểm danh:", error);
      enqueueSnackbar("Lỗi khi kết thúc phiên điểm danh", { variant: "error" });
    }
  };

  // Làm mới danh sách điểm danh
  const refreshAttendanceLogs = async () => {
    try {
      enqueueSnackbar("Đang làm mới dữ liệu điểm danh...", { variant: "info" });

      // Tải song song dữ liệu logs và thông tin phiên
      const [logsResponse, sessionResponse] = await Promise.all([
        axios.get(`${API_URL}/attendance/logs/${sessionId}`, {
          headers: { Authorization: `Bearer ${token}` },
        }),
        axios.get(`${API_URL}/attendance/sessions/${sessionId}`, {
          headers: { Authorization: `Bearer ${token}` },
        }),
      ]);

      // Cập nhật state
      setAttendanceLogs(logsResponse.data.data);
      setSessionInfo(sessionResponse.data.data);

      enqueueSnackbar("Đã làm mới dữ liệu điểm danh", { variant: "success" });
    } catch (error) {
      console.error("Lỗi khi làm mới dữ liệu:", error);
      enqueueSnackbar("Lỗi khi làm mới dữ liệu điểm danh", {
        variant: "error",
      });
    }
  };

  // Tối ưu hàm lấy thông tin phiên
  const fetchSessionInfo = async () => {
    try {
      const sessionResponse = await axios.get(
        `${API_URL}/attendance/sessions/${sessionId}`,
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      setSessionInfo(sessionResponse.data.data);
      return sessionResponse.data.data;
    } catch (error) {
      console.error("Lỗi khi lấy thông tin phiên:", error);
      return null;
    }
  };

  // Tối ưu hàm xử lý nhận diện khuôn mặt
  const captureAndRecognize = async () => {
    try {
      if (!modelsLoaded) {
        setFeedbackMessage({
          type: "error",
          message: "Mô hình chưa được tải. Vui lòng đợi.",
        });
        return;
      }

      // Đảm bảo camera đang hoạt động
      const cameraWorking = await ensureCameraIsWorking();
      if (!cameraWorking) {
        setFeedbackMessage({
          type: "error",
          message: "Camera không hoạt động, vui lòng kiểm tra lại.",
        });
        return;
      }

      // Xác định video element đang sử dụng
      const videoElement = webcamRef.current?.video;
      if (!videoElement || videoElement.readyState !== 4) {
        setFeedbackMessage({
          type: "error",
          message:
            "Webcam chưa sẵn sàng, vui lòng đợi hoặc kiểm tra quyền truy cập.",
        });
        return;
      }

      // Thông báo đang xử lý
      setFeedbackMessage({
        type: "info",
        message: "Đang phân tích khuôn mặt...",
      });

      try {
        // Phát hiện tất cả khuôn mặt với landmarks và descriptors
        const detections = await faceapi
          .detectAllFaces(
            videoElement,
            new faceapi.TinyFaceDetectorOptions({
              scoreThreshold: DETECTION_THRESHOLD,
              inputSize: 320,
            })
          )
          .withFaceLandmarks()
          .withFaceDescriptors();

        // Kiểm tra kết quả phát hiện
        if (
          !detections ||
          !Array.isArray(detections) ||
          detections.length === 0
        ) {
          setFeedbackMessage({
            type: "error",
            message: "Không phát hiện khuôn mặt nào trong khung hình.",
          });
          return;
        }

        // Lọc các phát hiện có descriptor hợp lệ
        const validDetections = detections.filter(
          (detection) =>
            detection?.descriptor &&
            Array.isArray(detection.descriptor) &&
            detection.descriptor.length === 128 &&
            detection.descriptor.every(
              (value) => typeof value === "number" && !isNaN(value)
            ) &&
            detection?.detection?._box &&
            typeof detection.detection._box._x === "number" &&
            !isNaN(detection.detection._box._x)
        );

        if (validDetections.length === 0) {
          setFeedbackMessage({
            type: "error",
            message: "Không thể trích xuất đặc trưng khuôn mặt. Hãy thử lại.",
          });
          return;
        }

        // Chỉ xử lý khuôn mặt đầu tiên phát hiện được
        const firstDetection = validDetections[0];
        const faceDescriptor = firstDetection.descriptor;

        // Lấy danh sách sinh viên và đặc trưng khuôn mặt
        const studentList = [...classStudents];
        if (!studentList || studentList.length === 0) {
          setFeedbackMessage({
            type: "error",
            message: "Không có dữ liệu sinh viên để so sánh.",
          });
          return;
        }

        // Tạo LabeledFaceDescriptors từ dữ liệu sinh viên
        const labeledDescriptors = [];
        for (const student of studentList) {
          const extractedDescriptors = extractStudentDescriptors(student);
          if (extractedDescriptors && extractedDescriptors.length > 0) {
            // Lọc và chuyển đổi descriptors
            const validExtractedDescriptors = extractedDescriptors
              .filter((d) => Array.isArray(d) && d.length === 128)
              .map((d) => new Float32Array(d));

            if (validExtractedDescriptors.length > 0) {
              labeledDescriptors.push(
                new faceapi.LabeledFaceDescriptors(
                  student._id,
                  validExtractedDescriptors
                )
              );
            }
          }
        }

        // Kiểm tra xem có đủ dữ liệu để so sánh không
        if (labeledDescriptors.length === 0) {
          setFeedbackMessage({
            type: "error",
            message: "Không có đủ dữ liệu sinh viên để so sánh.",
          });
          return;
        }

        // Tạo FaceMatcher với ngưỡng tin cậy
        const faceMatcher = new faceapi.FaceMatcher(
          labeledDescriptors,
          RECOGNITION_THRESHOLD
        );

        // Chuyển đổi descriptor thành Float32Array nếu cần
        const queryDescriptor =
          Array.isArray(faceDescriptor) && faceDescriptor.length === 128
            ? new Float32Array(faceDescriptor)
            : faceDescriptor;

        // So sánh khuôn mặt
        const bestMatch = faceMatcher.findBestMatch(queryDescriptor);
        const studentId = bestMatch.label;
        const confidence = 1 - bestMatch.distance;

        console.log(
          `Kết quả nhận diện: ${studentId} với độ tin cậy ${confidence.toFixed(
            2
          )}`
        );

        // Capture ảnh từ video để lưu minh chứng
        const canvas = document.createElement("canvas");
        canvas.width = videoElement.videoWidth;
        canvas.height = videoElement.videoHeight;
        const ctx = canvas.getContext("2d");

        if (!ctx) {
          throw new Error("Không thể tạo canvas context để chụp ảnh");
        }

        // Vẽ frame video vào canvas
        ctx.drawImage(videoElement, 0, 0, canvas.width, canvas.height);

        // Chuyển đổi thành base64
        const imageBase64 = canvas.toDataURL("image/jpeg");

        // Hiển thị kết quả nhận diện
        if (studentId !== "unknown" && confidence >= RECOGNITION_THRESHOLD) {
          // Tìm thông tin sinh viên từ ID
          const recognizedStudent = classStudents.find(
            (s) => s._id === studentId
          );
          if (recognizedStudent) {
            setFeedbackMessage({
              type: "success",
              message: `Đã nhận diện: ${recognizedStudent.name} (${(
                confidence * 100
              ).toFixed(0)}%)`,
            });

            // Đánh dấu sinh viên đã điểm danh
            await handleMarkAttendance(studentId, confidence, imageBase64);
          } else {
            setFeedbackMessage({
              type: "warning",
              message: `Đã nhận diện ID: ${studentId} nhưng không tìm thấy thông tin sinh viên.`,
            });
          }
        } else {
          setFeedbackMessage({
            type: "error",
            message: `Không nhận diện được sinh viên nào (Độ tin cậy: ${(
              confidence * 100
            ).toFixed(0)}%).`,
          });
        }
      } catch (detectionError) {
        console.error("Lỗi khi phát hiện khuôn mặt:", detectionError);
        setFeedbackMessage({
          type: "error",
          message: "Lỗi khi phân tích khuôn mặt, vui lòng thử lại.",
        });
      }
    } catch (error) {
      console.error("Lỗi khi chụp và nhận diện:", error);
      setFeedbackMessage({
        type: "error",
        message: "Đã xảy ra lỗi, vui lòng thử lại.",
      });
    }
  };

  // Tối ưu hàm đánh dấu điểm danh
  const handleMarkAttendance = async (studentId, confidence) => {
    // Kiểm tra xem sinh viên đã được điểm danh chưa
    const isAlreadyAttended = attendanceLogs.some(
      (log) => log.student_id._id === studentId && log.status === "present"
    );

    if (isAlreadyAttended) return;

    try {
      // Chụp ảnh từ video element
      const videoElement = isAutoMode
        ? videoRef.current
        : webcamRef.current?.video;
      if (!videoElement) return;

      let imageBase64 = null;

      // Tạo ảnh chụp
      if (!isAutoMode && webcamRef.current) {
        imageBase64 = webcamRef.current.getScreenshot();
      } else if (isAutoMode && videoElement) {
        // Tạo canvas để chụp từ video
        const canvas = document.createElement("canvas");
        canvas.width = videoElement.videoWidth;
        canvas.height = videoElement.videoHeight;
        const ctx = canvas.getContext("2d");
        ctx.drawImage(videoElement, 0, 0);
        imageBase64 = canvas.toDataURL("image/jpeg");
      }

      // Tìm sinh viên trong danh sách
      const student = classStudents.find((s) => s._id === studentId);
      if (!student) return;

      // Trích xuất descriptor
      const descriptors = extractStudentDescriptors(student);
      const descriptor = descriptors.length > 0 ? descriptors[0] : null;

      // Gọi API điểm danh
      await markAttendance(studentId, descriptor, confidence, imageBase64);
    } catch (error) {
      console.error("Lỗi khi đánh dấu điểm danh:", error);
      enqueueSnackbar("Lỗi khi đánh dấu điểm danh", { variant: "error" });
    }
  };

  // Bật/tắt hiển thị landmark trên khuôn mặt
  const toggleLandmarkDetection = () => {
    const newState = !showLandmark;
    setShowLandmark(newState);

    // Bắt đầu hoặc dừng interval landmark detection
    if (newState) {
      startLandmarkDetection();
    } else if (landmarkIntervalId) {
      clearInterval(landmarkIntervalId);
      setLandmarkIntervalId(null);

      // Xóa canvas khi tắt landmarks
      const canvasElement = canvasRef.current;
      if (canvasElement) {
        const ctx = canvasElement.getContext("2d");
        ctx.clearRect(0, 0, canvasElement.width, canvasElement.height);
      }
    }
  };

  // Thêm hàm debug để kiểm tra cấu trúc dữ liệu khuôn mặt
  const debugFaceFeatures = () => {
    if (!classStudents || classStudents.length === 0) {
      console.log(
        "[DEBUG] Không có sinh viên nào để kiểm tra dữ liệu khuôn mặt"
      );
      return;
    }

    classStudents.forEach((student, index) => {
      console.log(`[DEBUG] Sinh viên #${index + 1}: ${student.full_name}`);

      // Kiểm tra dữ liệu faceFeatures
      if (student.faceFeatures) {
        console.log(`  - Có dữ liệu faceFeatures`);

        // Kiểm tra descriptors
        if (student.faceFeatures.descriptors) {
          console.log(
            `  - Có descriptors: ${student.faceFeatures.descriptors.length} nhóm`
          );

          student.faceFeatures.descriptors.forEach((group, groupIndex) => {
            if (Array.isArray(group)) {
              console.log(
                `    - Nhóm #${groupIndex + 1}: ${group.length} descriptors`
              );

              group.forEach((descriptor, descIndex) => {
                if (Array.isArray(descriptor)) {
                  console.log(
                    `      - Descriptor #${descIndex + 1}: ${
                      descriptor.length
                    } phần tử`
                  );
                } else {
                  console.log(
                    `      - Descriptor #${descIndex + 1}: Không phải mảng`
                  );
                }
              });
            } else {
              console.log(`    - Nhóm #${groupIndex + 1}: Không phải mảng`);
            }
          });
        } else {
          console.log(`  - Không có dữ liệu descriptors`);
        }
      } else {
        console.log(`  - Không có dữ liệu faceFeatures`);
      }

      // Kiểm tra dữ liệu cũ (faceDescriptors)
      if (student.faceDescriptors) {
        console.log(
          `  - Có dữ liệu faceDescriptors (cấu trúc cũ): ${student.faceDescriptors.length} descriptors`
        );
      }
    });
  };

  // Tối ưu chế độ tự động điểm danh
  const startAutoAttendance = () => {
    // Dừng interval cũ nếu có
    if (intervalId) {
      clearInterval(intervalId);
    }

    // Hiển thị thông báo bắt đầu
    enqueueSnackbar("Đã bật chế độ tự động điểm danh", {
      variant: "success",
    });

    // Thiết lập thời gian phát hiện ban đầu
    setLastDetectionTime(Date.now());

    // Khởi tạo interval mới để điểm danh tự động
    const id = setInterval(async () => {
      // Chỉ xử lý nếu camera đã sẵn sàng
      if (isCameraReady) {
        try {
          // Phát hiện khuôn mặt
          const detections = await handleDetectFaces(false);

          // Nếu đã phát hiện khuôn mặt và có descriptor
          if (detections && detections.length > 0 && detections[0].descriptor) {
            // Nhận diện và điểm danh tự động
            await processAutoRecognition(detections);
          }
        } catch (error) {
          console.error("Lỗi trong quá trình điểm danh tự động:", error);
        }
      }
    }, AUTO_DETECT_INTERVAL);

    // Lưu interval ID để có thể dừng sau này
    setIntervalId(id);
    setIsAutoMode(true);
  };

  // Hàm dừng chế độ tự động điểm danh
  const stopAutoAttendance = () => {
    if (intervalId) {
      clearInterval(intervalId);
      setIntervalId(null);
    }
    setIsAutoMode(false);
    enqueueSnackbar("Đã tắt chế độ tự động điểm danh", {
      variant: "info",
    });
  };

  // Hàm xử lý nhận diện tự động
  const processAutoRecognition = async (detections) => {
    try {
      // Kiểm tra xem có detection hợp lệ không
      if (
        !detections ||
        !Array.isArray(detections) ||
        detections.length === 0
      ) {
        console.log(
          "Không có khuôn mặt nào được phát hiện cho nhận diện tự động"
        );
        return;
      }

      // Lọc ra các detections có descriptor hợp lệ
      const validDetections = detections.filter(
        (detection) =>
          detection?.descriptor &&
          Array.isArray(detection.descriptor) &&
          detection.descriptor.length === 128 &&
          detection.descriptor.every(
            (value) => typeof value === "number" && !isNaN(value)
          )
      );

      if (validDetections.length === 0) {
        console.log("Không có descriptor hợp lệ nào để nhận diện");
        return;
      }

      // Kiểm tra video đã sẵn sàng chưa
      const videoElement = videoRef.current;
      if (!videoElement || videoElement.readyState !== 4) {
        console.log("Video chưa sẵn sàng để chụp ảnh");
        return;
      }

      // Lấy danh sách sinh viên và họ đặc trưng khuôn mặt
      const studentList = [...classStudents];
      if (!studentList || studentList.length === 0) {
        console.log("Không có dữ liệu sinh viên để so sánh");
        return;
      }

      // Xử lý từng detection
      for (const detection of validDetections) {
        try {
          // Kiểm tra lại descriptor hợp lệ
          if (!detection.descriptor || detection.descriptor.length !== 128) {
            console.log("Bỏ qua detection với descriptor không hợp lệ");
            continue;
          }

          // Tạo LabeledFaceDescriptors từ dữ liệu sinh viên
          const labeledDescriptors = [];
          for (const student of studentList) {
            const extractedDescriptors = extractStudentDescriptors(student);
            if (extractedDescriptors && extractedDescriptors.length > 0) {
              labeledDescriptors.push(
                new faceapi.LabeledFaceDescriptors(
                  student._id,
                  extractedDescriptors
                    .map((d) =>
                      // Đảm bảo tất cả là Float32Array hợp lệ
                      Array.isArray(d) && d.length === 128
                        ? new Float32Array(d)
                        : null
                    )
                    .filter((d) => d !== null) // Lọc bỏ descriptor null
                )
              );
            }
          }

          // Kiểm tra xem có đủ dữ liệu để so sánh không
          if (labeledDescriptors.length === 0) {
            console.log("Không có đủ descriptor để tạo FaceMatcher");
            continue;
          }

          // Tạo FaceMatcher với ngưỡng tin cậy
          const faceMatcher = new faceapi.FaceMatcher(
            labeledDescriptors,
            RECOGNITION_THRESHOLD
          );

          // Chuyển đổi descriptor thành Float32Array nếu cần
          let queryDescriptor = detection.descriptor;
          if (
            Array.isArray(queryDescriptor) &&
            queryDescriptor.length === 128
          ) {
            queryDescriptor = new Float32Array(queryDescriptor);
          }

          // Kiểm tra lại trước khi nhận diện
          if (!queryDescriptor || queryDescriptor.length !== 128) {
            console.log("Descriptor không hợp lệ cho nhận diện");
            continue;
          }

          // So sánh khuôn mặt
          const bestMatch = faceMatcher.findBestMatch(queryDescriptor);
          const studentId = bestMatch.label;
          const confidence = 1 - bestMatch.distance;

          console.log(
            `Kết quả nhận diện: ${studentId} với độ tin cậy ${confidence.toFixed(
              2
            )}`
          );

          // Chỉ xử lý nếu không phải unknown và đạt ngưỡng
          if (
            studentId !== "unknown" &&
            confidence >= RECOGNITION_THRESHOLD &&
            !autoProcessedIds.includes(studentId)
          ) {
            // Capture ảnh từ video để lưu minh chứng
            try {
              // Tạo canvas để chụp ảnh
              const canvas = document.createElement("canvas");
              canvas.width = videoElement.videoWidth;
              canvas.height = videoElement.videoHeight;
              const ctx = canvas.getContext("2d");

              // Kiểm tra xem canvas được tạo thành công
              if (!ctx) {
                console.error("Không thể tạo canvas context để chụp ảnh");
                continue;
              }

              // Vẽ frame video vào canvas
              ctx.drawImage(videoElement, 0, 0, canvas.width, canvas.height);

              // Chuyển đổi thành base64
              const imageBase64 = canvas.toDataURL("image/jpeg");

              // Đánh dấu sinh viên đã điểm danh
              if (studentId && confidence && imageBase64) {
                await handleMarkAttendance(studentId, confidence, imageBase64);
                setAutoProcessedIds((prev) => [...prev, studentId]);
              }
            } catch (captureError) {
              console.error("Lỗi khi chụp ảnh từ video:", captureError);
            }
          }
        } catch (detectionError) {
          console.error("Lỗi khi xử lý detection:", detectionError);
        }
      }
    } catch (error) {
      console.error("Lỗi khi xử lý nhận diện tự động:", error);
    }
  };

  if (isLoading) {
    return (
      <Box
        display="flex"
        justifyContent="center"
        alignItems="center"
        minHeight="60vh"
      >
        <CircularProgress />
      </Box>
    );
  }

  // Lấy danh sách sinh viên vắng mặt
  const getAbsentStudents = () => {
    if (!classInfo || !classInfo.students || !attendanceLogs) return [];

    // Tạo Set các ID sinh viên đã điểm danh để tăng tốc độ tìm kiếm
    const presentStudentIds = new Set(
      attendanceLogs
        .filter((log) => log.status === "present")
        .map((log) => log.student_id._id)
    );

    // Lọc ra sinh viên chưa điểm danh
    return classInfo.students.filter(
      (student) => !presentStudentIds.has(student._id)
    );
  };

  const absentStudents = getAbsentStudents();

  // Điểm danh thủ công trong chế độ đơn giản
  const showStudentList = () => {
    if (!classInfo) {
      enqueueSnackbar("Không có thông tin lớp học", { variant: "error" });
      return;
    }

    // Lấy danh sách sinh viên vắng mặt
    const absentStudents = getAbsentStudents();

    if (absentStudents.length === 0) {
      enqueueSnackbar("Tất cả sinh viên đã được điểm danh", {
        variant: "success",
      });
      return;
    }

    // Mở dialog điểm danh thủ công với sinh viên đầu tiên trong danh sách
    if (absentStudents.length > 0 && absentStudents[0]) {
      openManualAttendanceDialog(absentStudents[0]);
    }
  };

  // Tối ưu phần Webcam và Camera
  const VideoDisplay = ({
    isCameraReady,
    isAutoMode,
    webcamRef,
    videoRef,
    canvasRef,
    onUserMedia,
    onUserMediaError,
  }) => {
    // Component con để xử lý hiển thị video
    return (
      <Paper
        elevation={3}
        sx={{
          position: "relative",
          width: "100%",
          height: "400px",
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          overflow: "hidden",
          mb: 2,
          borderRadius: "8px",
          border: "1px solid rgba(0, 0, 0, 0.12)",
        }}
      >
        {isCameraReady ? (
          <>
            {isAutoMode ? (
              // Video element cho chế độ tự động
              <video
                ref={videoRef}
                style={{
                  position: "absolute",
                  width: "100%",
                  height: "100%",
                  objectFit: "cover",
                }}
                autoPlay
                playsInline
                muted
              />
            ) : (
              // Webcam component cho chế độ thủ công
              <Webcam
                audio={false}
                ref={webcamRef}
                screenshotFormat="image/jpeg"
                width={640}
                height={480}
                videoConstraints={{
                  width: 640,
                  height: 480,
                  facingMode: "user",
                }}
                style={{
                  position: "absolute",
                  width: "100%",
                  height: "100%",
                  objectFit: "cover",
                }}
                onUserMedia={onUserMedia}
                onUserMediaError={onUserMediaError}
              />
            )}

            {/* Canvas cho hiển thị landmarks */}
            <canvas
              ref={canvasRef}
              style={{
                position: "absolute",
                top: 0,
                left: 0,
                width: "100%",
                height: "100%",
                objectFit: "cover",
                zIndex: 10,
              }}
            />

            {/* Chỉ báo chế độ */}
            <Box
              sx={{
                position: "absolute",
                bottom: 10,
                right: 10,
                backgroundColor: "rgba(0,0,0,0.6)",
                color: "white",
                padding: "4px 8px",
                borderRadius: "4px",
                fontSize: "12px",
                display: "flex",
                alignItems: "center",
                zIndex: 20,
              }}
            >
              {isAutoMode ? (
                <>
                  <span style={{ color: "#4CAF50", marginRight: "5px" }}>
                    ●
                  </span>
                  Đang tự động điểm danh
                </>
              ) : (
                <>
                  <span style={{ color: "#FFC107", marginRight: "5px" }}>
                    ●
                  </span>
                  Chế độ thủ công
                </>
              )}
            </Box>
          </>
        ) : (
          <CameraLoadingPlaceholder />
        )}
      </Paper>
    );
  };

  // Tối ưu giao diện hiển thị danh sách sinh viên đã điểm danh
  const PresentStudentsList = ({
    attendanceLogs,
    refreshAttendanceLogs,
    totalStudents,
  }) => (
    <Card
      sx={{
        mb: 3,
        borderRadius: "10px",
        boxShadow: "0 2px 8px rgba(0,0,0,0.08)",
      }}
    >
      <CardContent>
        <Box
          display="flex"
          justifyContent="space-between"
          alignItems="center"
          mb={2}
        >
          <Box display="flex" alignItems="center">
            <Box
              sx={{
                width: 10,
                height: 10,
                borderRadius: "50%",
                backgroundColor: "#4CAF50",
                mr: 1,
              }}
            />
            <Typography variant="h6">
              Có mặt (
              {attendanceLogs.filter((log) => log.status === "present").length}/
              {totalStudents})
            </Typography>
          </Box>
          <Tooltip title="Làm mới danh sách điểm danh">
            <IconButton
              size="small"
              onClick={refreshAttendanceLogs}
              sx={{
                backgroundColor: "#f4f4f4",
                "&:hover": { backgroundColor: "#e0e0e0" },
              }}
            >
              <Refresh />
            </IconButton>
          </Tooltip>
        </Box>

        <Box sx={{ maxHeight: "350px", overflow: "auto", pr: 1 }}>
          {attendanceLogs.filter((log) => log.status === "present").length ===
          0 ? (
            <Box
              sx={{
                display: "flex",
                flexDirection: "column",
                alignItems: "center",
                justifyContent: "center",
                py: 4,
                color: "text.secondary",
              }}
            >
              <HourglassEmpty sx={{ fontSize: 40, mb: 1, opacity: 0.5 }} />
              <Typography variant="body2">
                Chưa có sinh viên nào điểm danh
              </Typography>
            </Box>
          ) : (
            <List dense>
              {attendanceLogs
                .filter((log) => log.status === "present")
                .sort((a, b) => new Date(b.timestamp) - new Date(a.timestamp))
                .map((log) => (
                  <Paper
                    key={log._id}
                    elevation={0}
                    sx={{
                      mb: 1,
                      borderRadius: "8px",
                      backgroundColor: "#f9f9f9",
                      overflow: "hidden",
                    }}
                  >
                    <ListItem
                      secondaryAction={
                        <Chip
                          size="small"
                          icon={
                            log.recognized ? (
                              <VerifiedUser fontSize="small" />
                            ) : null
                          }
                          label={log.recognized ? "Tự động" : "Thủ công"}
                          color={log.recognized ? "success" : "primary"}
                          sx={{
                            borderRadius: "4px",
                            "& .MuiChip-label": { px: 1 },
                          }}
                        />
                      }
                    >
                      <ListItemAvatar>
                        <Avatar
                          alt={log.student_id.full_name}
                          src={log.student_id.avatar_url}
                          sx={{
                            border: "2px solid #f0f0f0",
                            bgcolor: log.recognized ? "#e8f5e9" : "#e3f2fd",
                          }}
                        />
                      </ListItemAvatar>
                      <ListItemText
                        primary={
                          <Typography variant="body2" fontWeight={500}>
                            {log.student_id.full_name}
                          </Typography>
                        }
                        secondary={
                          <Typography variant="caption" color="text.secondary">
                            {new Date(log.timestamp).toLocaleTimeString(
                              "vi-VN"
                            )}
                            {log.note && ` • ${log.note}`}
                          </Typography>
                        }
                      />
                    </ListItem>
                  </Paper>
                ))}
            </List>
          )}
        </Box>
      </CardContent>
    </Card>
  );

  // Tối ưu giao diện hiển thị danh sách sinh viên vắng mặt
  const AbsentStudentsList = ({
    absentStudents,
    onOpenManualAttendance,
    totalStudents,
  }) => (
    <Card
      sx={{ borderRadius: "10px", boxShadow: "0 2px 8px rgba(0,0,0,0.08)" }}
    >
      <CardContent>
        <Box display="flex" alignItems="center" mb={2}>
          <Box
            sx={{
              width: 10,
              height: 10,
              borderRadius: "50%",
              backgroundColor: "#f44336",
              mr: 1,
            }}
          />
          <Typography variant="h6">
            Vắng mặt ({absentStudents.length}/{totalStudents})
          </Typography>
        </Box>

        <Box sx={{ maxHeight: "350px", overflow: "auto", pr: 1 }}>
          {absentStudents.length === 0 ? (
            <Box
              sx={{
                display: "flex",
                flexDirection: "column",
                alignItems: "center",
                justifyContent: "center",
                py: 4,
                color: "text.secondary",
              }}
            >
              <Check sx={{ fontSize: 40, mb: 1, opacity: 0.5 }} />
              <Typography variant="body2">
                Tất cả sinh viên đã được điểm danh
              </Typography>
            </Box>
          ) : (
            <List dense>
              {absentStudents.map((student) => (
                <Paper
                  key={student._id}
                  elevation={0}
                  sx={{
                    mb: 1,
                    borderRadius: "8px",
                    backgroundColor: "#f9f9f9",
                    overflow: "hidden",
                    transition: "all 0.2s",
                    "&:hover": {
                      backgroundColor: "#f0f0f0",
                    },
                  }}
                >
                  <ListItem
                    secondaryAction={
                      <Tooltip title="Điểm danh thủ công">
                        <IconButton
                          edge="end"
                          onClick={() => onOpenManualAttendance(student)}
                          size="small"
                          sx={{
                            backgroundColor: "#e0e0e0",
                            "&:hover": { backgroundColor: "#bdbdbd" },
                          }}
                        >
                          <Edit fontSize="small" />
                        </IconButton>
                      </Tooltip>
                    }
                  >
                    <ListItemAvatar>
                      <Avatar
                        alt={student.full_name}
                        src={student.avatar_url}
                        sx={{
                          border: "2px solid #f0f0f0",
                          filter: "grayscale(0.5)",
                        }}
                      />
                    </ListItemAvatar>
                    <ListItemText
                      primary={
                        <Typography variant="body2" fontWeight={500}>
                          {student.full_name}
                        </Typography>
                      }
                      secondary={
                        <Typography variant="caption" color="text.secondary">
                          {student.student_code}
                        </Typography>
                      }
                    />
                  </ListItem>
                </Paper>
              ))}
            </List>
          )}
        </Box>
      </CardContent>
    </Card>
  );

  // Tối ưu dialog điểm danh thủ công
  const ManualAttendanceDialog = ({
    open,
    onClose,
    student,
    attendanceData,
    onAttendanceChange,
    onSubmit,
  }) => (
    <Dialog
      open={open}
      onClose={onClose}
      maxWidth="sm"
      fullWidth
      PaperProps={{
        sx: {
          borderRadius: "12px",
          overflow: "hidden",
        },
      }}
    >
      <DialogTitle sx={{ bgcolor: "#f5f5f5", py: 2 }}>
        <Typography variant="h6" fontWeight={500}>
          Điểm danh thủ công
        </Typography>
      </DialogTitle>
      <DialogContent dividers>
        <Box sx={{ p: 1 }}>
          {student && (
            <Box display="flex" alignItems="center" mb={3}>
              <Avatar
                src={student.avatar_url}
                alt={student.full_name}
                sx={{ width: 56, height: 56, mr: 2 }}
              />
              <Box>
                <Typography variant="h6" gutterBottom>
                  {student.full_name}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  MSSV: {student.student_code}
                </Typography>
              </Box>
            </Box>
          )}

          <FormControl fullWidth margin="normal">
            <InputLabel>Trạng thái</InputLabel>
            <Select
              name="status"
              value={attendanceData.status}
              onChange={onAttendanceChange}
              label="Trạng thái"
            >
              <MenuItem value="present">
                <Box sx={{ display: "flex", alignItems: "center" }}>
                  <Check sx={{ color: "#4CAF50", mr: 1 }} />
                  Có mặt
                </Box>
              </MenuItem>
              <MenuItem value="absent">
                <Box sx={{ display: "flex", alignItems: "center" }}>
                  <Close sx={{ color: "#F44336", mr: 1 }} />
                  Vắng mặt
                </Box>
              </MenuItem>
              <MenuItem value="late">
                <Box sx={{ display: "flex", alignItems: "center" }}>
                  <HourglassEmpty sx={{ color: "#FFC107", mr: 1 }} />
                  Đi trễ
                </Box>
              </MenuItem>
              <MenuItem value="early_leave">
                <Box sx={{ display: "flex", alignItems: "center" }}>
                  <HourglassEmpty sx={{ color: "#FF9800", mr: 1 }} />
                  Về sớm
                </Box>
              </MenuItem>
            </Select>
          </FormControl>

          <TextField
            fullWidth
            margin="normal"
            name="note"
            label="Ghi chú"
            value={attendanceData.note}
            onChange={onAttendanceChange}
            multiline
            rows={2}
            placeholder="Nhập ghi chú (nếu có)"
          />
        </Box>
      </DialogContent>
      <DialogActions sx={{ px: 3, py: 2 }}>
        <Button onClick={onClose} variant="outlined" startIcon={<Close />}>
          Hủy
        </Button>
        <Button
          onClick={onSubmit}
          variant="contained"
          color="primary"
          startIcon={<Save />}
        >
          Xác nhận
        </Button>
      </DialogActions>
    </Dialog>
  );

  // Tối ưu phần hiển thị camera placeholder với hướng dẫn rõ ràng hơn
  const CameraLoadingPlaceholder = () => (
    <Box display="flex" flexDirection="column" alignItems="center" py={4}>
      <Box
        sx={{
          width: 80,
          height: 80,
          borderRadius: "50%",
          backgroundColor: "#e3f2fd",
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          mb: 3,
        }}
      >
        <CameraAlt sx={{ fontSize: 40, color: "#2196f3" }} />
      </Box>

      <Typography variant="h6" gutterBottom align="center" fontWeight={500}>
        Sẵn sàng điểm danh
      </Typography>

      <Typography
        variant="body2"
        color="textSecondary"
        align="center"
        paragraph
        sx={{ maxWidth: 400 }}
      >
        Nhấn nút bên dưới để khởi động camera và bắt đầu quá trình điểm danh
      </Typography>

      <Button
        variant="contained"
        color="primary"
        size="large"
        onClick={initDirectCamera}
        startIcon={<CameraAlt />}
        sx={{ mt: 2, mb: 3, px: 3, borderRadius: "24px" }}
      >
        Bắt đầu điểm danh
      </Button>

      <Alert
        severity="info"
        variant="outlined"
        sx={{ maxWidth: 500 }}
        icon={<Info />}
      >
        <AlertTitle>Lưu ý khi sử dụng camera</AlertTitle>
        <Typography variant="body2" component="div">
          <ul>
            <li>Đảm bảo bạn đã cấp quyền truy cập camera cho trình duyệt</li>
            <li>Điều chỉnh ánh sáng đầy đủ để tăng độ chính xác nhận diện</li>
            <li>Camera sẽ tự động nhận diện khuôn mặt trong chế độ tự động</li>
            <li>Có thể điểm danh thủ công nếu gặp vấn đề với camera</li>
            <li>
              Nhấn "Chụp & Nhận diện" để điểm danh thủ công từng sinh viên
            </li>
          </ul>
        </Typography>
        <Box display="flex" justifyContent="flex-end" mt={1}>
          <Button
            variant="outlined"
            color="warning"
            size="small"
            onClick={showStudentList}
            startIcon={<Edit />}
          >
            Điểm danh thủ công
          </Button>
        </Box>
      </Alert>
    </Box>
  );

  // Component hiển thị danh sách sinh viên đã nhận diện
  const RecognizedStudentsList = ({ students, onMarkAttendance }) => {
    if (students.length === 0) return null;

    return (
      <Box mt={2}>
        <Box display="flex" alignItems="center" mb={1}>
          <Box
            sx={{
              width: 10,
              height: 10,
              borderRadius: "50%",
              backgroundColor: "#4CAF50",
              mr: 1,
            }}
          />
          <Typography variant="subtitle1" fontWeight="500">
            Đã nhận diện ({students.length})
          </Typography>
        </Box>

        <Grid container spacing={2}>
          {students.map((student, index) => (
            <Grid item xs={6} sm={4} md={3} key={index}>
              <Paper
                elevation={2}
                sx={{
                  p: 1.5,
                  display: "flex",
                  flexDirection: "column",
                  alignItems: "center",
                  borderRadius: "8px",
                  transition: "all 0.2s",
                  "&:hover": {
                    transform: "translateY(-3px)",
                    boxShadow: "0 4px 10px rgba(0,0,0,0.1)",
                  },
                }}
              >
                <Typography
                  variant="body2"
                  fontWeight="500"
                  align="center"
                  noWrap
                >
                  {student.name}
                </Typography>

                <Box
                  sx={{
                    width: "100%",
                    mt: 1,
                    mb: 1,
                    display: "flex",
                    alignItems: "center",
                    justifyContent: "center",
                  }}
                >
                  <Box
                    sx={{
                      width: "100%",
                      height: 6,
                      backgroundColor: "#eee",
                      borderRadius: 3,
                      position: "relative",
                    }}
                  >
                    <Box
                      sx={{
                        position: "absolute",
                        left: 0,
                        top: 0,
                        height: "100%",
                        width: `${Math.round(student.confidence * 100)}%`,
                        backgroundColor:
                          student.confidence > 0.7
                            ? "#4CAF50"
                            : student.confidence > 0.6
                            ? "#8BC34A"
                            : student.confidence > 0.5
                            ? "#FFC107"
                            : "#FF9800",
                        borderRadius: 3,
                        transition: "width 0.4s",
                      }}
                    />
                  </Box>
                  <Typography
                    variant="caption"
                    color="textSecondary"
                    sx={{ ml: 1, minWidth: "36px" }}
                  >
                    {Math.round(student.confidence * 100)}%
                  </Typography>
                </Box>

                <Button
                  size="small"
                  variant="contained"
                  color="primary"
                  fullWidth
                  onClick={() =>
                    onMarkAttendance(student.studentId, student.confidence)
                  }
                  startIcon={<Check />}
                  sx={{ mt: 0.5 }}
                >
                  Điểm danh
                </Button>
              </Paper>
            </Grid>
          ))}
        </Grid>
      </Box>
    );
  };

  return (
    <Box sx={{ position: "relative" }}>
      <Box
        display="flex"
        justifyContent="space-between"
        alignItems="center"
        mb={2.5}
        sx={{
          borderBottom: "1px solid #eaeaea",
          pb: 2,
        }}
      >
        <Box>
          <Typography variant="h5" fontWeight={500}>
            Điểm Danh - {classInfo?.class_name}
          </Typography>
          <Typography variant="subtitle1" color="textSecondary">
            Buổi {sessionInfo?.session_number} -{" "}
            {new Date(sessionInfo?.date).toLocaleDateString("vi-VN")}
          </Typography>
        </Box>
        <Button
          variant="outlined"
          startIcon={<ArrowBack />}
          onClick={() => navigate(`/teacher/classes/${classId}`)}
          sx={{ borderRadius: "8px" }}
        >
          Quay lại
        </Button>
      </Box>

      <Grid container spacing={3} mt={0.5}>
        <Grid item xs={12} md={8}>
          <Card
            sx={{
              borderRadius: "10px",
              boxShadow: "0 2px 8px rgba(0,0,0,0.08)",
              overflow: "hidden",
            }}
          >
            <CardContent
              sx={{
                bgcolor: "#f8f9fa",
                borderBottom: "1px solid #eaeaea",
                pb: 2,
              }}
            >
              <Box
                display="flex"
                justifyContent="space-between"
                alignItems="center"
              >
                <Box display="flex" alignItems="center">
                  <CameraAlt
                    fontSize="small"
                    sx={{ color: "#1976d2", mr: 1 }}
                  />
                  <Typography variant="h6" fontWeight={500}>
                    Camera nhận diện
                  </Typography>
                </Box>
                {isCameraReady && (
                  <Box>
                    <Chip
                      label={
                        isAutoMode ? "Tự động đang bật" : "Tự động đang tắt"
                      }
                      color={isAutoMode ? "success" : "default"}
                      sx={{ mr: 1, borderRadius: "6px" }}
                    />
                    <Button
                      variant={isAutoMode ? "outlined" : "contained"}
                      color={isAutoMode ? "error" : "primary"}
                      onClick={() => {
                        if (isAutoMode) {
                          stopAutoAttendance();
                        } else {
                          startAutoAttendance();
                        }
                      }}
                      sx={{ borderRadius: "8px" }}
                    >
                      {isAutoMode ? "Tắt tự động" : "Bật tự động"}
                    </Button>
                  </Box>
                )}
              </Box>
            </CardContent>

            {/* Sử dụng component VideoDisplay hoặc CameraLoadingPlaceholder */}
            {isCameraReady ? (
              <VideoDisplay
                isCameraReady={isCameraReady}
                isAutoMode={isAutoMode}
                webcamRef={webcamRef}
                videoRef={videoRef}
                canvasRef={canvasRef}
                onUserMedia={(stream) => {
                  console.log(
                    "Camera đã được cấp quyền và khởi tạo",
                    stream.id
                  );
                  setTimeout(() => {
                    if (webcamRef.current && webcamRef.current.video) {
                      dispatch(setCameraReady(true));
                      setLastDetectionTime(Date.now());
                    }
                  }, 500);
                }}
                onUserMediaError={(err) => {
                  console.error("Lỗi khi truy cập camera:", err);
                  enqueueSnackbar(
                    `Không thể truy cập camera: ${
                      err.name === "NotAllowedError"
                        ? "Bạn chưa cấp quyền truy cập camera"
                        : err.name === "NotFoundError"
                        ? "Không tìm thấy thiết bị camera"
                        : err.message || "Lỗi không xác định"
                    }`,
                    { variant: "error" }
                  );
                }}
              />
            ) : (
              <CameraLoadingPlaceholder />
            )}

            {isCameraReady && (
              <Box
                display="flex"
                justifyContent="space-between"
                px={3}
                py={2}
                bgcolor="#f8f9fa"
              >
                <Button
                  variant="outlined"
                  onClick={captureAndRecognize}
                  startIcon={
                    isRecognizing ? (
                      <CircularProgress size={20} color="inherit" />
                    ) : (
                      <CameraAlt />
                    )
                  }
                  disabled={!isCameraReady || isRecognizing || isAutoMode}
                  sx={{ borderRadius: "8px" }}
                >
                  {isRecognizing ? "Đang nhận diện..." : "Chụp & Nhận diện"}
                </Button>

                <Box>
                  <Button
                    variant="outlined"
                    color={showLandmark ? "success" : "info"}
                    onClick={toggleLandmarkDetection}
                    sx={{ mr: 1, borderRadius: "8px" }}
                  >
                    {showLandmark ? "Tắt Landmark" : "Hiện Landmark"}
                  </Button>

                  <Button
                    variant="contained"
                    color="primary"
                    onClick={completeSession}
                    startIcon={<Save />}
                    disabled={sessionInfo?.status === "completed"}
                    sx={{ borderRadius: "8px" }}
                  >
                    Kết thúc phiên
                  </Button>
                </Box>
              </Box>
            )}

            {/* Sử dụng component RecognizedStudentsList */}
            <Box px={3} pb={3}>
              <RecognizedStudentsList
                students={recognizedStudents}
                onMarkAttendance={handleMarkAttendance}
              />
            </Box>
          </Card>
        </Grid>

        <Grid item xs={12} md={4}>
          {/* Sử dụng component PresentStudentsList */}
          <PresentStudentsList
            attendanceLogs={attendanceLogs.filter(
              (log) => log.status === "present"
            )}
            refreshAttendanceLogs={refreshAttendanceLogs}
            totalStudents={classInfo?.students.length || 0}
          />

          {/* Sử dụng component AbsentStudentsList */}
          <AbsentStudentsList
            absentStudents={absentStudents}
            onOpenManualAttendance={openManualAttendanceDialog}
            totalStudents={classInfo?.students.length || 0}
          />
        </Grid>
      </Grid>

      {/* Sử dụng component ManualAttendanceDialog */}
      <ManualAttendanceDialog
        open={manualDialogOpen}
        onClose={handleManualDialogClose}
        student={selectedStudent}
        attendanceData={manualAttendanceData}
        onAttendanceChange={handleManualAttendanceChange}
        onSubmit={handleManualAttendanceSubmit}
      />
    </Box>
  );
};

export default AttendancePage;
