import React, { useState, useEffect } from "react";
import { useSelector } from "react-redux";
import axios from "axios";
import {
  Box,
  Typography,
  Paper,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Card,
  CardContent,
  Grid,
  Chip,
  CircularProgress,
  Alert,
  TextField,
  MenuItem,
  FormControl,
  InputLabel,
  Select,
  Divider,
  LinearProgress,
  IconButton,
  Tooltip,
  TablePagination,
  Button,
} from "@mui/material";
import {
  CheckCircle,
  Error,
  Info,
  Timeline,
  School,
  FilterList,
  CalendarToday,
  Sort,
  EventBusy,
  EventAvailable,
} from "@mui/icons-material";
import { useNavigate } from "react-router-dom";

const API_URL = process.env.REACT_APP_API_URL || "http://localhost:5000/api";

const ScoresPage = () => {
  const { user, token } = useSelector((state) => state.auth);
  const navigate = useNavigate();

  const [scores, setScores] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [page, setPage] = useState(0);
  const [rowsPerPage, setRowsPerPage] = useState(10);
  const [filters, setFilters] = useState({
    semester: "",
    searchTerm: "",
    status: "all",
  });
  const [semesters, setSemesters] = useState([]);
  const [stats, setStats] = useState({
    totalClasses: 0,
    avgAttendance: 0,
    failedClasses: 0,
    perfectClasses: 0,
  });

  useEffect(() => {
    const fetchScores = async () => {
      try {
        setLoading(true);
        setError(null);

        // Lấy danh sách tất cả lớp học của sinh viên
        const classesResponse = await axios.get(
          `${API_URL}/classes/teaching/student/${user._id}`,
          { headers: { Authorization: `Bearer ${token}` } }
        );

        // Lấy dữ liệu điểm danh của sinh viên
        const scoresResponse = await axios.get(
          `${API_URL}/attendance/student/${user._id}/scores`,
          { headers: { Authorization: `Bearer ${token}` } }
        );

        if (classesResponse.data.success && scoresResponse.data.success) {
          const classesData = classesResponse.data.data || [];
          const scoresData = scoresResponse.data.data || [];

          // Tạo map để tìm kiếm dễ dàng
          const scoresMap = new Map();
          scoresData.forEach((score) => {
            scoresMap.set(score.teaching_class_id, score);
          });

          // Kết hợp dữ liệu lớp học và điểm danh
          const combinedData = classesData.map((classItem) => {
            const existingScore = scoresMap.get(classItem._id);

            // Nếu lớp học chưa có điểm danh, tạo dữ liệu mặc định
            if (!existingScore) {
              return {
                _id: `${classItem._id}_new`,
                teaching_class_id: classItem._id,
                class_info: {
                  name: classItem.class_name,
                  subject: classItem.subject_id,
                  semester: classItem.semester_id,
                  main_class: classItem.main_class_id?.name,
                },
                attendance_percentage: 0,
                attendance_score: 0,
                total_sessions: 0,
                absent_sessions: 0,
                attended_sessions: 0,
                is_failed_due_to_absent: false,
                is_new_class: true, // Đánh dấu lớp mới chưa có điểm danh
              };
            }

            return existingScore;
          });

          setScores(combinedData);

          // Tính toán thống kê
          const totalClasses = combinedData.length;
          const classesWithSessions = combinedData.filter(
            (c) => c.total_sessions > 0
          ).length;
          const avgAttendance =
            combinedData.reduce(
              (sum, score) => sum + (score.attendance_percentage || 0),
              0
            ) / (classesWithSessions || 1);
          const failedClasses = combinedData.filter(
            (score) => score.is_failed_due_to_absent
          ).length;
          const perfectClasses = combinedData.filter(
            (score) => score.attendance_percentage >= 90
          ).length;

          setStats({
            totalClasses,
            avgAttendance,
            failedClasses,
            perfectClasses,
          });

          // Lấy danh sách học kỳ duy nhất
          const uniqueSemesters = Array.from(
            new Set(
              combinedData
                .map((score) => {
                  const semester = score.class_info?.semester;
                  return semester ? `${semester.name} ${semester.year}` : null;
                })
                .filter(Boolean)
            )
          );
          setSemesters(uniqueSemesters);
        }
      } catch (error) {
        console.error("Lỗi khi lấy dữ liệu điểm chuyên cần:", error);
        setError(
          "Không thể tải dữ liệu điểm chuyên cần. Vui lòng thử lại sau."
        );
      } finally {
        setLoading(false);
      }
    };

    fetchScores();
  }, [user._id, token]);

  // Lọc dữ liệu dựa trên bộ lọc
  const filteredScores = scores.filter((score) => {
    // Lọc theo học kỳ
    if (
      filters.semester &&
      `${score.class_info?.semester?.name} ${score.class_info?.semester?.year}` !==
        filters.semester
    ) {
      return false;
    }

    // Lọc theo tìm kiếm (tên lớp học hoặc tên môn học)
    if (filters.searchTerm) {
      const searchLower = filters.searchTerm.toLowerCase();
      const className = score.class_info?.name?.toLowerCase() || "";
      const subjectName = score.class_info?.subject?.name?.toLowerCase() || "";
      if (
        !className.includes(searchLower) &&
        !subjectName.includes(searchLower)
      ) {
        return false;
      }
    }

    // Lọc theo trạng thái điểm danh
    if (filters.status === "failed" && !score.is_failed_due_to_absent) {
      return false;
    }
    if (filters.status === "passed" && score.is_failed_due_to_absent) {
      return false;
    }
    if (filters.status === "perfect" && score.attendance_percentage < 90) {
      return false;
    }

    return true;
  });

  const handleChangePage = (event, newPage) => {
    setPage(newPage);
  };

  const handleChangeRowsPerPage = (event) => {
    setRowsPerPage(parseInt(event.target.value, 10));
    setPage(0);
  };

  const handleFilterChange = (event) => {
    setFilters({
      ...filters,
      [event.target.name]: event.target.value,
    });
    setPage(0);
  };

  const resetFilters = () => {
    setFilters({
      semester: "",
      searchTerm: "",
      status: "all",
    });
    setPage(0);
  };

  // Hiển thị trang loading
  if (loading) {
    return (
      <Box
        display="flex"
        flexDirection="column"
        alignItems="center"
        justifyContent="center"
        minHeight="60vh"
      >
        <CircularProgress size={60} />
        <Typography sx={{ mt: 2 }}>
          Đang tải dữ liệu điểm chuyên cần...
        </Typography>
      </Box>
    );
  }

  // Hiển thị trang lỗi
  if (error) {
    return (
      <Box sx={{ py: 3 }}>
        <Alert severity="error" sx={{ mb: 3 }}>
          {error}
        </Alert>
        <Button
          variant="contained"
          onClick={() => window.location.reload()}
          startIcon={<Timeline />}
        >
          Tải lại trang
        </Button>
      </Box>
    );
  }

  return (
    <Box>
      <Typography variant="h4" gutterBottom>
        Điểm Chuyên Cần
      </Typography>
      <Divider sx={{ mb: 3 }} />

      {/* Phần thống kê tổng quan */}
      <Grid container spacing={3} sx={{ mb: 4 }}>
        <Grid item xs={12} sm={6} md={3}>
          <Card sx={{ height: "100%" }}>
            <CardContent>
              <Typography color="textSecondary" gutterBottom variant="body2">
                Tổng số lớp học
              </Typography>
              <Box display="flex" alignItems="center">
                <School sx={{ mr: 1, color: "primary.main", fontSize: 32 }} />
                <Typography variant="h4">{stats.totalClasses}</Typography>
              </Box>
            </CardContent>
          </Card>
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <Card sx={{ height: "100%" }}>
            <CardContent>
              <Typography color="textSecondary" gutterBottom variant="body2">
                Tỷ lệ tham gia trung bình
              </Typography>
              <Box display="flex" alignItems="center">
                <Typography
                  variant="h4"
                  color={
                    stats.avgAttendance >= 80
                      ? "success.main"
                      : stats.avgAttendance >= 50
                      ? "warning.main"
                      : "error.main"
                  }
                >
                  {Math.round(stats.avgAttendance)}%
                </Typography>
              </Box>
              <LinearProgress
                variant="determinate"
                value={stats.avgAttendance}
                color={
                  stats.avgAttendance >= 80
                    ? "success"
                    : stats.avgAttendance >= 50
                    ? "warning"
                    : "error"
                }
                sx={{ mt: 1, height: 8, borderRadius: 4 }}
              />
            </CardContent>
          </Card>
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <Card sx={{ height: "100%" }}>
            <CardContent>
              <Typography color="textSecondary" gutterBottom variant="body2">
                Môn học đạt tỷ lệ cao
              </Typography>
              <Box display="flex" alignItems="center">
                <EventAvailable
                  sx={{ mr: 1, color: "success.main", fontSize: 32 }}
                />
                <Typography variant="h4">{stats.perfectClasses}</Typography>
              </Box>
              <Typography variant="body2" color="textSecondary" sx={{ mt: 1 }}>
                {stats.totalClasses > 0
                  ? `${Math.round(
                      (stats.perfectClasses / stats.totalClasses) * 100
                    )}% lớp học có tỷ lệ &gt;= 90%`
                  : "Chưa có dữ liệu"}
              </Typography>
            </CardContent>
          </Card>
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <Card
            sx={{
              height: "100%",
              bgcolor:
                stats.failedClasses > 0 ? "error.light" : "background.paper",
            }}
          >
            <CardContent>
              <Typography color="textSecondary" gutterBottom variant="body2">
                Môn học nguy cơ cấm thi
              </Typography>
              <Box display="flex" alignItems="center">
                <EventBusy
                  sx={{
                    mr: 1,
                    color:
                      stats.failedClasses > 0 ? "error.main" : "text.secondary",
                    fontSize: 32,
                  }}
                />
                <Typography
                  variant="h4"
                  color={
                    stats.failedClasses > 0 ? "error.main" : "text.primary"
                  }
                >
                  {stats.failedClasses}
                </Typography>
              </Box>
              {stats.failedClasses > 0 && (
                <Alert severity="warning" sx={{ mt: 1 }}>
                  Cần cải thiện ngay
                </Alert>
              )}
            </CardContent>
          </Card>
        </Grid>
      </Grid>

      {/* Bộ lọc */}
      <Paper sx={{ p: 2, mb: 3 }}>
        <Box
          display="flex"
          flexDirection={{ xs: "column", md: "row" }}
          alignItems={{ md: "center" }}
          justifyContent="space-between"
          mb={2}
        >
          <Typography variant="h6" sx={{ mb: { xs: 2, md: 0 } }}>
            <FilterList sx={{ mr: 1, verticalAlign: "middle" }} />
            Bộ lọc
          </Typography>
          <Box
            display="flex"
            flexDirection={{ xs: "column", sm: "row" }}
            gap={2}
            width={{ xs: "100%", md: "auto" }}
          >
            <TextField
              label="Tìm kiếm"
              name="searchTerm"
              size="small"
              value={filters.searchTerm}
              onChange={handleFilterChange}
              placeholder="Tên lớp hoặc môn học"
              sx={{ width: { xs: "100%", sm: 200 } }}
            />

            <FormControl size="small" sx={{ width: { xs: "100%", sm: 200 } }}>
              <InputLabel>Học kỳ</InputLabel>
              <Select
                label="Học kỳ"
                name="semester"
                value={filters.semester}
                onChange={handleFilterChange}
              >
                <MenuItem value="">Tất cả học kỳ</MenuItem>
                {semesters.map((semester) => (
                  <MenuItem key={semester} value={semester}>
                    {semester}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>

            <FormControl size="small" sx={{ width: { xs: "100%", sm: 200 } }}>
              <InputLabel>Trạng thái</InputLabel>
              <Select
                label="Trạng thái"
                name="status"
                value={filters.status}
                onChange={handleFilterChange}
              >
                <MenuItem value="all">Tất cả</MenuItem>
                <MenuItem value="passed">Đủ điều kiện</MenuItem>
                <MenuItem value="failed">Cấm thi</MenuItem>
                <MenuItem value="perfect">Xuất sắc (&gt;=90%)</MenuItem>
              </Select>
            </FormControl>

            <Button
              variant="outlined"
              size="small"
              onClick={resetFilters}
              sx={{ height: 40 }}
            >
              Đặt lại
            </Button>
          </Box>
        </Box>

        {/* Hiển thị kết quả bộ lọc */}
        <Box display="flex" alignItems="center" justifyContent="space-between">
          <Typography variant="body2" color="textSecondary">
            {`Hiển thị ${filteredScores.length} kết quả`}
          </Typography>
        </Box>
      </Paper>

      {/* Bảng điểm chuyên cần */}
      <TableContainer component={Paper}>
        <Table>
          <TableHead>
            <TableRow sx={{ bgcolor: "primary.light" }}>
              <TableCell>Lớp học</TableCell>
              <TableCell>Môn học</TableCell>
              <TableCell>Học kỳ</TableCell>
              <TableCell align="center">Số buổi tham gia</TableCell>
              <TableCell align="center">Tỷ lệ tham gia</TableCell>
              <TableCell align="center">Điểm chuyên cần</TableCell>
              <TableCell align="center">Trạng thái</TableCell>
              <TableCell align="right">Thao tác</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {filteredScores.length === 0 ? (
              <TableRow>
                <TableCell colSpan={8} align="center">
                  <Box py={3}>
                    <Info
                      sx={{
                        fontSize: 60,
                        color: "text.secondary",
                        opacity: 0.5,
                      }}
                    />
                    <Typography variant="h6" sx={{ mt: 1 }}>
                      Không có dữ liệu điểm chuyên cần
                    </Typography>
                    <Typography variant="body2" color="textSecondary">
                      {filters.searchTerm ||
                      filters.semester ||
                      filters.status !== "all"
                        ? "Hãy thử thay đổi bộ lọc để xem kết quả khác"
                        : "Bạn chưa có dữ liệu điểm chuyên cần"}
                    </Typography>
                  </Box>
                </TableCell>
              </TableRow>
            ) : (
              filteredScores
                .slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
                .map((score) => (
                  <TableRow
                    key={score._id}
                    sx={{
                      "&:hover": {
                        bgcolor: "action.hover",
                      },
                      borderLeft: `4px solid ${
                        score.is_failed_due_to_absent
                          ? "error.main"
                          : score.attendance_percentage >= 90
                          ? "success.main"
                          : score.attendance_percentage >= 70
                          ? "info.main"
                          : "warning.main"
                      }`,
                    }}
                  >
                    <TableCell>
                      <Typography variant="body2" fontWeight={500}>
                        {score.class_info?.name || "Không có dữ liệu"}
                      </Typography>
                      {score.class_info?.main_class && (
                        <Typography variant="caption" color="textSecondary">
                          Lớp: {score.class_info.main_class}
                        </Typography>
                      )}
                      {score.is_new_class && (
                        <Chip
                          size="small"
                          label="Chưa bắt đầu"
                          color="default"
                          sx={{ mt: 0.5, fontSize: "0.7rem" }}
                        />
                      )}
                    </TableCell>
                    <TableCell>
                      <Typography variant="body2">
                        {score.class_info?.subject?.name || "Không có dữ liệu"}
                      </Typography>
                      <Typography variant="caption" color="textSecondary">
                        {score.class_info?.subject?.code || ""}
                      </Typography>
                    </TableCell>
                    <TableCell>
                      <Chip
                        size="small"
                        icon={<CalendarToday fontSize="small" />}
                        label={
                          score.class_info?.semester
                            ? `${score.class_info.semester.name} ${score.class_info.semester.year}`
                            : "Không có dữ liệu"
                        }
                        sx={{ fontSize: "0.75rem" }}
                      />
                    </TableCell>
                    <TableCell align="center">
                      {score.is_new_class ? (
                        <Typography variant="body2" color="text.secondary">
                          Chưa có buổi học
                        </Typography>
                      ) : (
                        <Typography variant="body2">
                          {score.attended_sessions ||
                            score.total_sessions - score.absent_sessions ||
                            0}
                          /{score.total_sessions || 0}
                        </Typography>
                      )}
                    </TableCell>
                    <TableCell align="center">
                      {score.is_new_class ? (
                        <Typography variant="body2" color="text.secondary">
                          --
                        </Typography>
                      ) : (
                        <Box
                          display="flex"
                          alignItems="center"
                          justifyContent="center"
                        >
                          <Typography
                            variant="body2"
                            fontWeight={500}
                            color={
                              score.attendance_percentage >= 80
                                ? "success.main"
                                : score.attendance_percentage >= 50
                                ? "warning.main"
                                : "error.main"
                            }
                            sx={{ mr: 1 }}
                          >
                            {score.attendance_percentage || 0}%
                          </Typography>
                          <Box sx={{ width: 50 }}>
                            <LinearProgress
                              variant="determinate"
                              value={score.attendance_percentage || 0}
                              color={
                                score.attendance_percentage >= 80
                                  ? "success"
                                  : score.attendance_percentage >= 50
                                  ? "warning"
                                  : "error"
                              }
                              sx={{ height: 6, borderRadius: 3 }}
                            />
                          </Box>
                        </Box>
                      )}
                    </TableCell>
                    <TableCell align="center">
                      {score.is_new_class ? (
                        <Typography variant="body2" color="text.secondary">
                          --
                        </Typography>
                      ) : (
                        <Typography
                          variant="body2"
                          fontWeight={500}
                          fontSize="1rem"
                          color={
                            score.attendance_score >= 8
                              ? "success.main"
                              : score.attendance_score >= 5
                              ? "warning.main"
                              : "error.main"
                          }
                        >
                          {score.attendance_score || 0}
                        </Typography>
                      )}
                    </TableCell>
                    <TableCell align="center">
                      {score.is_new_class ? (
                        <Chip
                          size="small"
                          label="Chưa bắt đầu"
                          color="default"
                        />
                      ) : (
                        <Chip
                          size="small"
                          icon={
                            score.is_failed_due_to_absent ? (
                              <Error fontSize="small" />
                            ) : (
                              <CheckCircle fontSize="small" />
                            )
                          }
                          label={
                            score.is_failed_due_to_absent
                              ? "Cấm thi"
                              : "Đủ điều kiện"
                          }
                          color={
                            score.is_failed_due_to_absent ? "error" : "success"
                          }
                        />
                      )}
                    </TableCell>
                    <TableCell align="right">
                      <Tooltip title="Xem chi tiết điểm danh">
                        <IconButton
                          size="small"
                          onClick={() =>
                            navigate(
                              `/student/attendance/${score.teaching_class_id}`
                            )
                          }
                        >
                          <Info fontSize="small" />
                        </IconButton>
                      </Tooltip>
                    </TableCell>
                  </TableRow>
                ))
            )}
          </TableBody>
        </Table>
        <TablePagination
          rowsPerPageOptions={[5, 10, 25]}
          component="div"
          count={filteredScores.length}
          rowsPerPage={rowsPerPage}
          page={page}
          onPageChange={handleChangePage}
          onRowsPerPageChange={handleChangeRowsPerPage}
          labelRowsPerPage="Số hàng mỗi trang:"
          labelDisplayedRows={({ from, to, count }) =>
            `${from}-${to} trên ${count}`
          }
        />
      </TableContainer>
    </Box>
  );
};

export default ScoresPage;
