import React, { useEffect, useState } from "react";
import { useDispatch } from "react-redux";
import { useLocation, useNavigate } from "react-router-dom";
import {
  Container,
  Box,
  Typography,
  CircularProgress,
  Paper,
} from "@mui/material";
import { setCredentials } from "../redux/slices/authSlice";

const GoogleRedirectPage = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const location = useLocation();
  const [status, setStatus] = useState("loading");
  const [message, setMessage] = useState("Đang xử lý đăng nhập...");

  useEffect(() => {
    const handleGoogleCallback = async () => {
      try {
        setStatus("loading");

        // Kiểm tra URL
        if (location.pathname === "/login/success") {
          const params = new URLSearchParams(location.search);
          const token = params.get("token");
          const needsRegistration = params.get("needsRegistration");

          if (!token) {
            setStatus("error");
            setMessage("Không tìm thấy token đăng nhập. Vui lòng thử lại.");
            setTimeout(() => navigate("/login"), 2000);
            return;
          }

          // Nếu tham số needsRegistration được đặt, chuyển hướng đến trang đăng ký
          if (needsRegistration === "true") {
            const email = params.get("email") || "";
            const googleId = params.get("googleId") || "";
            const name = params.get("name") || "";
            const avatar = params.get("avatar") || "";

            setStatus("redirect");
            setMessage("Chuyển hướng đến trang hoàn tất đăng ký...");

            setTimeout(() => {
              navigate(
                `/complete-registration?email=${encodeURIComponent(
                  email
                )}&googleId=${encodeURIComponent(
                  googleId
                )}&name=${encodeURIComponent(name)}&avatar=${encodeURIComponent(
                  avatar
                )}`
              );
            }, 1000);

            return;
          }

          // Lấy thông tin người dùng từ API
          try {
            const response = await fetch(
              `${
                process.env.REACT_APP_API_URL || "http://localhost:5000/api"
              }/auth/me`,
              {
                headers: {
                  Authorization: `Bearer ${token}`,
                },
              }
            );

            const data = await response.json();

            if (!data.success) {
              throw new Error(
                data.message || "Không thể lấy thông tin người dùng"
              );
            }

            const user = data.data;

            // Xử lý theo trạng thái tài khoản
            if (user.status === "pending") {
              // Lưu thông tin đăng nhập nhưng chuyển hướng đến trang chờ duyệt
              dispatch(setCredentials({ token, user }));
              setStatus("pending");
              setMessage(
                `Tài khoản ${
                  user.role === "student" ? "sinh viên" : "giảng viên"
                } của bạn đang chờ được phê duyệt.`
              );
              setTimeout(() => navigate("/pending-approval"), 1500);
            } else if (user.status === "rejected") {
              // Lưu thông tin đăng nhập nhưng hiển thị thông báo bị từ chối
              dispatch(setCredentials({ token, user }));
              setStatus("rejected");
              setMessage(
                `Tài khoản ${
                  user.role === "student" ? "sinh viên" : "giảng viên"
                } của bạn đã bị từ chối. Đang chuyển hướng đến trang thông báo...`
              );
              setTimeout(() => navigate("/pending-approval"), 1500);
            } else if (user.status === "approved") {
              // Đăng nhập thành công
              dispatch(setCredentials({ token, user }));
              setStatus("success");
              setMessage(
                `Đăng nhập thành công với tài khoản ${
                  user.role === "student" ? "sinh viên" : "giảng viên"
                }! Đang chuyển hướng...`
              );
              setTimeout(() => navigate("/dashboard"), 1000);
            } else {
              throw new Error("Trạng thái tài khoản không hợp lệ");
            }
          } catch (error) {
            console.error("Error fetching user:", error);
            setStatus("error");
            setMessage(
              "Không thể lấy thông tin người dùng. Vui lòng thử lại sau."
            );
            setTimeout(() => navigate("/login"), 2000);
          }
        } else if (location.pathname === "/login/error") {
          setStatus("error");
          setMessage("Đăng nhập bằng Google thất bại. Vui lòng thử lại.");
          setTimeout(() => navigate("/login"), 2000);
        } else {
          setStatus("error");
          setMessage(
            "Đường dẫn không hợp lệ. Đang chuyển hướng về trang đăng nhập..."
          );
          setTimeout(() => navigate("/login"), 2000);
        }
      } catch (error) {
        console.error("Google redirect error:", error);
        setStatus("error");
        setMessage("Đã xảy ra lỗi. Vui lòng thử lại sau.");
        setTimeout(() => navigate("/login"), 2000);
      }
    };

    handleGoogleCallback();
  }, [dispatch, location, navigate]);

  // Hiển thị trạng thái khác nhau dựa vào status
  const getStatusColor = () => {
    switch (status) {
      case "success":
        return "#4caf50"; // Green
      case "error":
        return "#f44336"; // Red
      case "pending":
        return "#ff9800"; // Orange
      case "rejected":
        return "#f44336"; // Red
      case "redirect":
        return "#2196f3"; // Blue
      default:
        return "#2196f3"; // Blue for loading
    }
  };

  return (
    <Container component="main" maxWidth="sm">
      <Box
        sx={{
          marginTop: 8,
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
        }}
      >
        <Paper
          elevation={3}
          sx={{
            padding: 4,
            width: "100%",
            borderRadius: 3,
            textAlign: "center",
          }}
        >
          <Box
            sx={{
              display: "flex",
              flexDirection: "column",
              alignItems: "center",
              justifyContent: "center",
              minHeight: "200px",
            }}
          >
            <CircularProgress
              size={60}
              sx={{ mb: 3, color: getStatusColor() }}
            />
            <Typography variant="h5" gutterBottom>
              {status === "loading"
                ? "Đang xử lý"
                : status === "success"
                ? "Đăng nhập thành công"
                : status === "pending"
                ? "Tài khoản đang chờ duyệt"
                : status === "rejected"
                ? "Tài khoản bị từ chối"
                : status === "redirect"
                ? "Đang chuyển hướng"
                : "Có lỗi xảy ra"}
            </Typography>
            <Typography variant="body1" color="text.secondary">
              {message}
            </Typography>
          </Box>
        </Paper>
      </Box>
    </Container>
  );
};

export default GoogleRedirectPage;
