  );

  // Mở dialog chỉnh sửa lớp chính
  const handleOpenEditClassDialog = () => {
    if (!mainClass) return;

    // Tải dữ liệu tham chiếu
    fetchDepartments();

    // Thiết lập dữ liệu ban đầu
    setEditClassData({
      name: mainClass.name || "",
      code: mainClass.class_code || "",
      department_id: mainClass.department_id?._id || "",
      semester_id: mainClass.semester_id?._id || "",
      main_class_id: mainClass.main_class_id?._id || "",
      total_sessions: mainClass.total_sessions || 15,
      room_id: mainClass.room_id || "",
      course_start_date: mainClass.course_start_date || "",
      course_end_date: mainClass.course_end_date || "",
      auto_generate_sessions:
        mainClass.auto_generate_sessions !== undefined
          ? mainClass.auto_generate_sessions
          : true,
    });

    // Khởi tạo lịch học
    if (mainClass.schedule && mainClass.schedule.length > 0) {
      setEditScheduleDays(mainClass.schedule);
    } else {
      setEditScheduleDays([
        {
          day_of_week: 1,
          start_period: 1,
          end_period: 2,
          start_time: "07:00",
          end_time: "08:40",
          room_id: "",
          is_recurring: true,
          specific_dates: [],
          excluded_dates: [],
        },
      ]);
    }

    // Tải dữ liệu campus và building nếu có
    if (mainClass.campus_id) {
      setEditSelectedCampus(mainClass.campus_id);
      fetchEditBuildings(mainClass.campus_id);

      if (mainClass.building_id) {
        setEditSelectedBuilding(mainClass.building_id);
        fetchEditRooms(mainClass.building_id);
      }
    }

    setOpenEditClassDialog(true);
  };

  // Đóng dialog chỉnh sửa lớp chính
  const handleCloseEditClassDialog = () => {
    setOpenEditClassDialog(false);
    setEditClassData(null);
    setEditScheduleDays([]);
    setEditSelectedCampus("");
    setEditSelectedBuilding("");
    setEditRooms([]);
    setEditBuildings([]);
  };

  // Xử lý thay đổi trong form chỉnh sửa
  const handleEditClassChange = (e) => {
    const { name, value } = e.target;
    setEditClassData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  // Fetch buildings cho form edit
  const fetchEditBuildings = async (campusId) => {
    if (!campusId) {
      setEditBuildings([]);
      setEditSelectedBuilding("");
      return;
    }

    try {
      const response = await axios.get(
        `${API_URL}/facilities/buildings/campuses/${campusId}`,
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      setEditBuildings(response.data.data || []);
    } catch (error) {
      console.error("Lỗi khi tải danh sách tòa nhà:", error);
      enqueueSnackbar("Lỗi khi tải danh sách tòa nhà", { variant: "error" });
    }
  };

  // Fetch rooms cho form edit
  const fetchEditRooms = async (buildingId) => {
    if (!buildingId) {
      setEditRooms([]);
      return;
    }

    try {
      const response = await axios.get(
        `${API_URL}/facilities/rooms/building/${buildingId}`,
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      setEditRooms(response.data.data || []);
    } catch (error) {
      console.error("Lỗi khi tải danh sách phòng học:", error);
      enqueueSnackbar("Lỗi khi tải danh sách phòng học", { variant: "error" });
    }
  };

  // Xử lý thay đổi campus trong form edit
  const handleEditCampusChange = (e) => {
    const campusId = e.target.value;
    setEditSelectedCampus(campusId);
    fetchEditBuildings(campusId);
    setEditSelectedBuilding("");
    setEditClassData((prev) => ({
      ...prev,
      room_id: "",
    }));
  };

  // Xử lý thay đổi building trong form edit
  const handleEditBuildingChange = (e) => {
    const buildingId = e.target.value;
    setEditSelectedBuilding(buildingId);
    fetchEditRooms(buildingId);
    setEditClassData((prev) => ({
      ...prev,
      room_id: "",
    }));
  };

  // Xử lý thay đổi lịch học trong form edit
  const handleEditScheduleChange = (index, field, value) => {
    const updatedSchedule = [...editScheduleDays];
    updatedSchedule[index] = {
      ...updatedSchedule[index],
      [field]: value,
    };
    setEditScheduleDays(updatedSchedule);

    // Tính lại ngày kết thúc khi lịch học thay đổi
    setEditClassData((prev) => ({
      ...prev,
      course_end_date: calculateEndDate(
        prev.course_start_date,
        updatedSchedule,
        prev.total_sessions
      ),
    }));
  };

  // Thêm lịch học trong form edit
  const addEditScheduleDay = () => {
    // Lấy ngày bắt đầu
    const startDate = editClassData.course_start_date;
    if (!startDate) {
      enqueueSnackbar("Vui lòng chọn ngày bắt đầu trước", {
        variant: "warning",
      });
      return;
    }

    // Lấy thứ từ ngày bắt đầu
    const baseDayOfWeek = getDayOfWeekFromDate(startDate);

    // Tìm thứ tiếp theo chưa có trong lịch
    const existingDays = editScheduleDays.map((day) => day.day_of_week);
    let nextDay = (baseDayOfWeek + 1) % 7; // Thứ tiếp theo

    // Tìm một thứ chưa có trong lịch hiện tại
    for (let i = 0; i < 7; i++) {
      if (!existingDays.includes(nextDay)) {
        break;
      }
      nextDay = (nextDay + 1) % 7;
    }

    // Nếu đã có đủ 7 ngày trong tuần, thì dùng thứ 2
    if (existingDays.length >= 7) {
      nextDay = 1;
    }

    const updatedSchedule = [
      ...editScheduleDays,
      {
        day_of_week: nextDay,
        start_period: 1,
        end_period: 2,
        start_time: "07:00",
        end_time: "08:40",
        room_id: editClassData.room_id,
        is_recurring: true,
        specific_dates: [],
        excluded_dates: [],
      },
    ];

    setEditScheduleDays(updatedSchedule);

    // Tính lại ngày kết thúc sau khi thêm buổi học
    setEditClassData((prev) => ({
      ...prev,
      course_end_date: calculateEndDate(
        prev.course_start_date,
        updatedSchedule,
        prev.total_sessions
      ),
    }));
  };

  // Xóa lịch học trong form edit
  const removeEditScheduleDay = (index) => {
    if (editScheduleDays.length > 1) {
      const updatedSchedule = editScheduleDays.filter((_, i) => i !== index);
      setEditScheduleDays(updatedSchedule);

      // Tính lại ngày kết thúc sau khi xóa buổi học
      setEditClassData((prev) => ({
        ...prev,
        course_end_date: calculateEndDate(
          prev.course_start_date,
          updatedSchedule,
          prev.total_sessions
        ),
      }));
    }
  };

  // Xử lý thay đổi ngày bắt đầu trong form edit
  const handleEditStartDateChange = (e) => {
    const startDate = e.target.value;

    // Lấy thứ từ ngày bắt đầu
    const dayOfWeek = getDayOfWeekFromDate(startDate);

    // Cập nhật lịch học với thứ mới
    const updatedSchedule = [
      {
        day_of_week: dayOfWeek,
        start_period: 1,
        end_period: 2,
        start_time: "07:00",
        end_time: "08:40",
        room_id: editClassData.room_id,
        is_recurring: true,
        specific_dates: [],
        excluded_dates: [],
      },
    ];

    // Cập nhật lịch học
    setEditScheduleDays(updatedSchedule);

    // Cập nhật ngày bắt đầu và tính ngày kết thúc
    setEditClassData((prev) => ({
      ...prev,
      course_start_date: startDate,
      course_end_date: calculateEndDate(
        startDate,
        updatedSchedule,
        prev.total_sessions
      ),
    }));
  };

  // Xử lý thay đổi số buổi học trong form edit
  const handleEditTotalSessionsChange = (e) => {
    const totalSessions = e.target.value;

    setEditClassData((prev) => ({
      ...prev,
      total_sessions: totalSessions,
      // Tính lại ngày kết thúc khi thay đổi số buổi học
      course_end_date: calculateEndDate(
        prev.course_start_date,
        editScheduleDays,
        totalSessions
      ),
    }));
  };

  // Lấy thứ trong tuần từ ngày
  const getDayOfWeekFromDate = (dateString) => {
    if (!dateString) return 1; // Mặc định là thứ 2 nếu không có ngày

    const date = new Date(dateString);
    // getDay() trả về 0 cho Chủ Nhật, 1-6 cho Thứ 2 đến Thứ 7
    const day = date.getDay();

    // Trả về 0-6
    return day;
  };

  // Lấy tên của thứ
  const getDayOfWeekName = (dayOfWeek) => {
    const days = [
      "Chủ Nhật",
      "Thứ Hai",
      "Thứ Ba",
      "Thứ Tư",
      "Thứ Năm",
      "Thứ Sáu",
      "Thứ Bảy",
    ];
    return days[dayOfWeek];
  };

  // Tính ngày kết thúc dựa trên ngày bắt đầu và lịch học
  const calculateEndDate = (startDate, schedule, totalSessions) => {
    if (!startDate || !schedule || schedule.length === 0 || !totalSessions) {
      return "";
    }

    try {
      // Chuyển ngày bắt đầu thành đối tượng Date
      const start = new Date(startDate);
      if (isNaN(start.getTime())) {
        return "";
      }

      // Tổng số tuần cần thiết = tổng số buổi học / số buổi học mỗi tuần
      const sessionsPerWeek = schedule.length;
      const weeksNeeded = Math.ceil(totalSessions / sessionsPerWeek);

      // Tạo mảng chứa các ngày trong tuần từ lịch học (0-6, với 0 là Chủ Nhật)
      const daysOfWeek = schedule.map((day) => day.day_of_week);

      // Sắp xếp các ngày trong tuần
      daysOfWeek.sort((a, b) => a - b);

      // Tìm ngày đầu tiên trong lịch học sau ngày bắt đầu
      let currentDate = new Date(start);

      // Đếm số buổi học đã xếp lịch
      let sessionCount = 0;

      // Lặp qua từng tuần
      for (let week = 0; week < weeksNeeded * 2; week++) {
        // Nhân 2 để đảm bảo đủ số buổi
        // Lặp qua các ngày trong tuần từ lịch học
        for (let dayIndex = 0; dayIndex < daysOfWeek.length; dayIndex++) {
          const targetDayOfWeek = daysOfWeek[dayIndex];

          // Tìm ngày tiếp theo có thứ phù hợp
          while (currentDate.getDay() !== targetDayOfWeek) {
            currentDate.setDate(currentDate.getDate() + 1);
          }

          sessionCount++;

          // Nếu đã đủ số buổi học, trả về ngày hiện tại
          if (sessionCount >= totalSessions) {
            return currentDate.toISOString().split("T")[0];
          }

          // Di chuyển đến ngày tiếp theo
          currentDate.setDate(currentDate.getDate() + 1);
        }
      }

      // Nếu không tính được, trả về chuỗi rỗng
      return "";
    } catch (error) {
      console.error("Lỗi khi tính ngày kết thúc:", error);
      return "";
    }
  };

  // Cập nhật thông tin lớp chính
  const handleUpdateMainClass = async () => {
    try {
      setIsSubmittingEdit(true);

      // Kiểm tra các trường bắt buộc
      if (
        !editClassData.name ||
        !editClassData.code ||
        !editClassData.department_id
      ) {
        enqueueSnackbar("Vui lòng điền đầy đủ thông tin", {
          variant: "warning",
        });
        setIsSubmittingEdit(false);
        return;
      }

      // Cập nhật room_id cho mỗi lịch học nếu chưa có
      const updatedSchedule = editScheduleDays.map((schedule) => ({
        ...schedule,
        room_id: schedule.room_id || editClassData.room_id,
      }));

      const response = await axios.put(
        `${API_URL}/classes/main/${mainClass._id}`,
        {
          name: editClassData.name,
          code: editClassData.code,
          department_id: editClassData.department_id,
          semester_id: editClassData.semester_id,
          main_class_id: editClassData.main_class_id,
          total_sessions: editClassData.total_sessions,
          room_id: editClassData.room_id,
          course_start_date: editClassData.course_start_date,
          course_end_date: editClassData.course_end_date,
          auto_generate_sessions: editClassData.auto_generate_sessions,
          schedule: updatedSchedule,
        },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );

      enqueueSnackbar("Cập nhật lớp chính thành công", { variant: "success" });
      handleCloseEditClassDialog();

      // Cập nhật thông tin lớp chính trong state
      if (response.data.success && response.data.data) {
        const updatedClass = response.data.data;

        // Cập nhật lớp chính hiện tại
        setMainClass(updatedClass);

        // Cập nhật danh sách lớp chính
        setMainClasses(
          mainClasses.map((cls) =>
            cls._id === updatedClass._id ? updatedClass : cls
          )
        );
      }
    } catch (error) {
      console.error("Lỗi khi cập nhật lớp chính:", error);
      enqueueSnackbar(
        error.response?.data?.message || "Lỗi khi cập nhật lớp chính",
