                  onChange={handleEditStartDateChange}
                  variant="outlined"
                  InputLabelProps={{ shrink: true }}
                />
              </Grid>
              <Grid item xs={12} sm={6}>
                <TextField
                  fullWidth
                  type="date"
                  label="Ngày kết thúc khóa học (tự động tính)"
                  name="course_end_date"
                  value={editClassData.course_end_date}
                  disabled
                  variant="outlined"
                  InputLabelProps={{ shrink: true }}
                  helperText="Được tính tự động từ ngày bắt đầu và lịch học"
                />
              </Grid>
              <Grid item xs={12} sm={6}>
                <FormControl fullWidth>
                  <InputLabel>Tự động tạo buổi học</InputLabel>
                  <Select
                    label="Tự động tạo buổi học"
                    name="auto_generate_sessions"
                    value={editClassData.auto_generate_sessions}
                    onChange={handleEditClassChange}
                  >
                    <MenuItem value={true}>Có</MenuItem>
                    <MenuItem value={false}>Không</MenuItem>
                  </Select>
                  <FormHelperText>
                    Khi bật, hệ thống sẽ tự động tạo các buổi học dựa trên lịch
                  </FormHelperText>
                </FormControl>
              </Grid>

              {/* Chọn phòng học */}
              <Grid item xs={12}>
                <Typography
                  variant="subtitle1"
                  gutterBottom
                  sx={{ fontWeight: "bold", mt: 2 }}
                >
                  <RoomIcon sx={{ verticalAlign: "middle", mr: 1 }} />
                  Thông tin phòng học
                </Typography>
                <Divider sx={{ mb: 2 }} />
              </Grid>
              <Grid item xs={12} sm={4}>
                <FormControl fullWidth>
                  <InputLabel>Cơ sở</InputLabel>
                  <Select
                    label="Cơ sở"
                    value={editSelectedCampus}
                    onChange={handleEditCampusChange}
                  >
                    <MenuItem value="">Chọn cơ sở</MenuItem>
                    {departments.map((department) => (
                      <MenuItem key={department._id} value={department._id}>
                        {department.name}
                      </MenuItem>
                    ))}
                  </Select>
                </FormControl>
              </Grid>
              <Grid item xs={12} sm={4}>
                <FormControl fullWidth disabled={!editSelectedCampus}>
                  <InputLabel>Tòa nhà</InputLabel>
                  <Select
                    label="Tòa nhà"
                    value={editSelectedBuilding}
                    onChange={handleEditBuildingChange}
                  >
                    <MenuItem value="">Chọn tòa nhà</MenuItem>
                    {editBuildings.map((building) => (
                      <MenuItem key={building._id} value={building._id}>
                        {building.name}
                      </MenuItem>
                    ))}
                  </Select>
                </FormControl>
              </Grid>
              <Grid item xs={12} sm={4}>
                <FormControl fullWidth disabled={!editSelectedBuilding}>
                  <InputLabel>Phòng học</InputLabel>
                  <Select
                    label="Phòng học"
                    name="room_id"
                    value={editClassData.room_id}
                    onChange={handleEditClassChange}
                  >
                    <MenuItem value="">Chọn phòng học</MenuItem>
                    {editRooms.map((room) => (
                      <MenuItem key={room._id} value={room._id}>
                        {room.room_number} (Sức chứa: {room.capacity})
                      </MenuItem>
                    ))}
                  </Select>
                </FormControl>
              </Grid>

              {/* Lịch học */}
              <Grid item xs={12}>
                <Typography
                  variant="subtitle1"
                  gutterBottom
                  sx={{ fontWeight: "bold", mt: 2 }}
                >
                  <Event sx={{ verticalAlign: "middle", mr: 1 }} />
                  Lịch học
                </Typography>
                <Divider sx={{ mb: 2 }} />
              </Grid>

              {editScheduleDays.map((day, index) => (
                <Grid
                  container
                  spacing={2}
                  key={index}
                  sx={{
                    ml: 0,
                    mt: 1,
                    width: "100%",
                    pb: 2,
                    borderBottom:
                      index < editScheduleDays.length - 1
                        ? "1px dashed #ddd"
                        : "none",
                  }}
                >
                  <Grid item xs={12} sm={3}>
                    <TextField
                      fullWidth
                      label="Thứ trong tuần"
                      value={getDayOfWeekName(day.day_of_week)}
                      InputProps={{
                        readOnly: true,
                      }}
                      variant="outlined"
                      helperText="Được xác định từ ngày bắt đầu"
                    />
                  </Grid>
                  <Grid item xs={6} sm={2}>
                    <TextField
                      fullWidth
                      label="Tiết bắt đầu"
                      type="number"
                      value={day.start_period}
                      onChange={(e) =>
                        handleEditScheduleChange(
                          index,
                          "start_period",
                          parseInt(e.target.value)
                        )
                      }
                      InputProps={{ inputProps: { min: 1, max: 15 } }}
                    />
                  </Grid>
                  <Grid item xs={6} sm={2}>
                    <TextField
                      fullWidth
                      label="Tiết kết thúc"
                      type="number"
                      value={day.end_period}
                      onChange={(e) =>
                        handleEditScheduleChange(
                          index,
                          "end_period",
                          parseInt(e.target.value)
                        )
                      }
                      InputProps={{
                        inputProps: { min: day.start_period, max: 15 },
                      }}
                    />
                  </Grid>
                  <Grid item xs={6} sm={2}>
                    <TextField
                      fullWidth
                      label="Giờ bắt đầu"
                      type="time"
                      value={day.start_time}
                      onChange={(e) =>
                        handleEditScheduleChange(
                          index,
                          "start_time",
                          e.target.value
                        )
                      }
                      InputLabelProps={{ shrink: true }}
                    />
                  </Grid>
                  <Grid item xs={6} sm={2}>
                    <TextField
                      fullWidth
                      label="Giờ kết thúc"
                      type="time"
                      value={day.end_time}
                      onChange={(e) =>
                        handleEditScheduleChange(
                          index,
                          "end_time",
                          e.target.value
                        )
                      }
                      InputLabelProps={{ shrink: true }}
                    />
                  </Grid>
                  <Grid
                    item
                    xs={12}
                    sm={1}
                    sx={{ display: "flex", alignItems: "center" }}
                  >
                    <IconButton
                      color="error"
                      onClick={() => removeEditScheduleDay(index)}
                      disabled={editScheduleDays.length <= 1}
                    >
                      <Chip
                        label="Xóa"
                        color="error"
                        size="small"
                        variant="outlined"
                        disabled={editScheduleDays.length <= 1}
                      />
                    </IconButton>
                  </Grid>

                  <Grid item xs={12} sm={4}>
                    <FormControl fullWidth>
                      <InputLabel>Phòng học riêng</InputLabel>
                      <Select
                        label="Phòng học riêng"
                        value={day.room_id || ""}
                        onChange={(e) =>
                          handleEditScheduleChange(
                            index,
                            "room_id",
                            e.target.value
                          )
                        }
                      >
                        <MenuItem value="">Dùng phòng mặc định</MenuItem>
                        {editRooms.map((room) => (
                          <MenuItem key={room._id} value={room._id}>
                            {room.room_number} (Sức chứa: {room.capacity})
                          </MenuItem>
                        ))}
                      </Select>
                      <FormHelperText>
                        Phòng học riêng cho buổi này, để trống sẽ dùng phòng mặc
                        định của lớp
                      </FormHelperText>
                    </FormControl>
                  </Grid>

                  <Grid item xs={12} sm={4}>
                    <FormControl fullWidth>
                      <InputLabel>Loại lịch</InputLabel>
                      <Select
                        label="Loại lịch"
                        value={day.is_recurring}
                        onChange={(e) =>
                          handleEditScheduleChange(
                            index,
                            "is_recurring",
                            e.target.value
                          )
                        }
                      >
                        <MenuItem value={true}>Định kỳ hàng tuần</MenuItem>
                        <MenuItem value={false}>Các ngày cụ thể</MenuItem>
                      </Select>
                    </FormControl>
                  </Grid>
                </Grid>
              ))}

              <Grid item xs={12}>
                <Button
                  variant="outlined"
                  color="primary"
                  startIcon={<Add />}
                  onClick={addEditScheduleDay}
                  sx={{ mt: 1 }}
                >
                  Thêm lịch học
                </Button>
              </Grid>
            </Grid>
          )}
        </DialogContent>
        <DialogActions sx={{ px: 3, pb: 2 }}>
          <Button
            onClick={handleCloseEditClassDialog}
            disabled={isSubmittingEdit}
          >
            Hủy
          </Button>
          <Button
            onClick={handleUpdateMainClass}
            variant="contained"
            color="primary"
            disabled={isSubmittingEdit}
            startIcon={
              isSubmittingEdit ? <CircularProgress size={20} /> : <Edit />
            }
          >
            {isSubmittingEdit ? "Đang cập nhật..." : "Cập nhật lớp"}
          </Button>
        </DialogActions>
      </Dialog>

      {renderStudentDetailDialog()}
    </Box>
  );
};

export default TeacherMainClassPage;
