import { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import axios from "../utils/axios";
import { setCredentials } from "../redux/slices/authSlice";

const API_URL = process.env.REACT_APP_API_URL || "http://localhost:5000/api";

export const useUserProfile = () => {
  const dispatch = useDispatch();
  const { user, token, isAuthenticated } = useSelector((state) => state.auth);

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        // Nếu chưa đăng nhập hoặc không có token, dừng lại
        if (!isAuthenticated || !token || !user) return;

        // Chỉ gọi API nếu đã đăng nhập và không có thông tin sinh viên/giảng viên đầy đủ
        const needsProfileData =
          (user.role === "student" && !user.school_info) ||
          (user.role === "teacher" && !user.teacher_code);

        if (needsProfileData) {
          console.log("Đang lấy thông tin chi tiết người dùng...");
          const response = await axios.get(`${API_URL}/auth/me`, {
            headers: { Authorization: `Bearer ${token}` },
          });

          if (response.data.success) {
            console.log("Đã cập nhật thông tin người dùng");
            // Cập nhật thông tin user vào Redux store, giữ nguyên token
            dispatch(
              setCredentials({
                token: token,
                user: response.data.data,
              })
            );
          }
        }
      } catch (error) {
        console.error("Lỗi khi lấy thông tin người dùng:", error);
      }
    };

    fetchUserData();
  }, [isAuthenticated, token, user?._id, dispatch]); // Thêm dispatch để tránh warning

  return { user };
};
