import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useSnackbar } from "notistack";
import {
  Box,
  Typography,
  Tabs,
  Tab,
  Paper,
  Button,
  TextField,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  TablePagination,
  IconButton,
  Dialog,
  DialogActions,
  DialogContent,
  DialogContentText,
  DialogTitle,
  Grid,
  Card,
  CardContent,
  Chip,
  Tooltip,
  CircularProgress,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  InputAdornment,
  Divider,
} from "@mui/material";
import {
  Add,
  Delete,
  Edit,
  Search,
  Refresh,
  Room,
  Domain,
  ArrowBack,
} from "@mui/icons-material";
import {
  fetchAllBuildings,
  createBuilding,
  updateBuilding,
  deleteBuilding,
  fetchAllRooms,
  createRoom,
  updateRoom,
  deleteRoom,
  setSelectedBuilding,
} from "../../redux/slices/facilitySlice";

const FacilitiesPage = () => {
  const dispatch = useDispatch();
  const { enqueueSnackbar } = useSnackbar();

  // Lấy trạng thái từ Redux
  const {
    buildings,
    rooms,
    selectedBuilding,
    isLoading,
    error,
    buildingPagination,
    roomPagination,
  } = useSelector((state) => state.facility);

  // States cho tabs
  const [tabValue, setTabValue] = useState(0);

  // States cho tìm kiếm và phân trang tòa nhà
  const [buildingSearchTerm, setBuildingSearchTerm] = useState("");
  const [buildingPage, setBuildingPage] = useState(0);
  const [buildingRowsPerPage, setBuildingRowsPerPage] = useState(10);

  // States cho tìm kiếm và phân trang phòng học
  const [roomSearchTerm, setRoomSearchTerm] = useState("");
  const [roomPage, setRoomPage] = useState(0);
  const [roomRowsPerPage, setRoomRowsPerPage] = useState(10);

  // States cho dialogs tòa nhà
  const [buildingDialogOpen, setBuildingDialogOpen] = useState(false);
  const [buildingDialogMode, setBuildingDialogMode] = useState("create");
  const [buildingFormData, setBuildingFormData] = useState({
    name: "",
    code: "",
    campus_id: "",
    floors_count: 1,
    year_built: new Date().getFullYear(),
    status: "active",
    facilities: [],
    image_url: "",
  });

  // Thêm state lưu danh sách campus
  const [campuses, setCampuses] = useState([]);

  // States cho dialogs phòng học
  const [roomDialogOpen, setRoomDialogOpen] = useState(false);
  const [roomDialogMode, setRoomDialogMode] = useState("create");
  const [roomFormData, setRoomFormData] = useState({
    name: "",
    code: "",
    building_id: "",
    floor: 1,
    capacity: 30,
    equipment: "",
    type: "classroom",
  });

  // States cho dialogs xóa
  const [deleteDialogOpen, setDeleteDialogOpen] = useState(false);
  const [deleteDialogType, setDeleteDialogType] = useState("building");
  const [itemToDelete, setItemToDelete] = useState(null);

  // Load dữ liệu khi component được tạo
  useEffect(() => {
    if (tabValue === 0) {
      loadBuildings();
    } else {
      loadRooms();
    }
  }, [
    dispatch,
    tabValue,
    buildingPage,
    buildingRowsPerPage,
    roomPage,
    roomRowsPerPage,
  ]);

  // Hiển thị lỗi nếu có
  useEffect(() => {
    if (error) {
      enqueueSnackbar(error, { variant: "error" });
    }
  }, [error, enqueueSnackbar]);

  // Hàm load dữ liệu
  const loadBuildings = () => {
    dispatch(
      fetchAllBuildings({
        page: buildingPage + 1,
        limit: buildingRowsPerPage,
        search: buildingSearchTerm,
      })
    );
  };

  const loadRooms = () => {
    dispatch(
      fetchAllRooms({
        page: roomPage + 1,
        limit: roomRowsPerPage,
        search: roomSearchTerm,
        buildingId: selectedBuilding ? selectedBuilding._id : "",
      })
    );
  };

  // Handlers cho tabs
  const handleTabChange = (event, newValue) => {
    setTabValue(newValue);
  };

  // Handlers cho tìm kiếm
  const handleBuildingSearch = () => {
    setBuildingPage(0);
    loadBuildings();
  };

  const handleRoomSearch = () => {
    setRoomPage(0);
    loadRooms();
  };

  // Handlers cho phân trang tòa nhà
  const handleBuildingChangePage = (event, newPage) => {
    setBuildingPage(newPage);
  };

  const handleBuildingChangeRowsPerPage = (event) => {
    setBuildingRowsPerPage(parseInt(event.target.value, 10));
    setBuildingPage(0);
  };

  // Handlers cho phân trang phòng học
  const handleRoomChangePage = (event, newPage) => {
    setRoomPage(newPage);
  };

  const handleRoomChangeRowsPerPage = (event) => {
    setRoomRowsPerPage(parseInt(event.target.value, 10));
    setRoomPage(0);
  };

  // Handlers cho dialog tòa nhà
  const openBuildingDialog = (mode, building = null) => {
    setBuildingDialogMode(mode);
    if (mode === "edit" && building) {
      setBuildingFormData({
        name: building.name,
        code: building.code,
        campus_id: building.campus_id?._id || "",
        floors_count: building.floors_count || 1,
        year_built: building.year_built || new Date().getFullYear(),
        status: building.status || "active",
        facilities: building.facilities || [],
        image_url: building.image_url || "",
      });
    } else {
      setBuildingFormData({
        name: "",
        code: "",
        campus_id: "",
        floors_count: 1,
        year_built: new Date().getFullYear(),
        status: "active",
        facilities: [],
        image_url: "",
      });
    }
    setBuildingDialogOpen(true);
  };

  const handleBuildingFormChange = (e) => {
    const { name, value } = e.target;
    let processedValue = value;

    // Đảm bảo floors_count và year_built luôn là số hợp lệ
    if (name === "floors_count" || name === "year_built") {
      const numValue = parseInt(value, 10);
      processedValue = isNaN(numValue) ? "" : numValue;
    }

    // Xử lý facilities là một mảng
    if (name === "facilities") {
      // Chuyển đổi chuỗi thành mảng, phân tách bởi dấu phẩy
      processedValue = value
        .split(",")
        .map((item) => item.trim())
        .filter((item) => item !== "");
    }

    setBuildingFormData({
      ...buildingFormData,
      [name]: processedValue,
    });
  };

  const handleBuildingFormSubmit = async () => {
    try {
      if (buildingDialogMode === "create") {
        await dispatch(createBuilding(buildingFormData)).unwrap();
        enqueueSnackbar("Tạo tòa nhà mới thành công", { variant: "success" });
      } else {
        await dispatch(
          updateBuilding({
            buildingId: selectedBuilding._id,
            buildingData: buildingFormData,
          })
        ).unwrap();
        enqueueSnackbar("Cập nhật tòa nhà thành công", { variant: "success" });
      }
      setBuildingDialogOpen(false);
      loadBuildings();
    } catch (error) {
      enqueueSnackbar(error?.message || "Lỗi khi thao tác với tòa nhà", {
        variant: "error",
      });
    }
  };

  // Handlers cho dialog phòng học
  const openRoomDialog = (mode, room = null) => {
    setRoomDialogMode(mode);
    if (mode === "edit" && room) {
      setRoomFormData({
        name: room.name,
        code: room.code,
        building_id: room.building_id._id,
        floor: room.floor || 1,
        capacity: room.capacity || 30,
        equipment: room.equipment || "",
        type: room.type || "classroom",
      });
    } else {
      setRoomFormData({
        name: "",
        code: "",
        building_id: selectedBuilding ? selectedBuilding._id : "",
        floor: 1,
        capacity: 30,
        equipment: "",
        type: "classroom",
      });
    }
    setRoomDialogOpen(true);
  };

  const handleRoomFormChange = (e) => {
    const { name, value } = e.target;
    let processedValue = value;

    // Đảm bảo floor và capacity luôn là số hợp lệ
    if (name === "floor" || name === "capacity") {
      const numValue = parseInt(value, 10);
      processedValue = isNaN(numValue) ? "" : numValue;
    }

    setRoomFormData({
      ...roomFormData,
      [name]: processedValue,
    });
  };

  const handleRoomFormSubmit = async () => {
    try {
      if (roomDialogMode === "create") {
        await dispatch(createRoom(roomFormData)).unwrap();
        enqueueSnackbar("Tạo phòng học mới thành công", { variant: "success" });
      } else {
        await dispatch(
          updateRoom({
            roomId: itemToDelete._id,
            roomData: roomFormData,
          })
        ).unwrap();
        enqueueSnackbar("Cập nhật phòng học thành công", {
          variant: "success",
        });
      }
      setRoomDialogOpen(false);
      loadRooms();
    } catch (error) {
      enqueueSnackbar(error?.message || "Lỗi khi thao tác với phòng học", {
        variant: "error",
      });
    }
  };

  // Handlers cho xóa
  const openDeleteDialog = (type, item) => {
    setDeleteDialogType(type);
    setItemToDelete(item);
    setDeleteDialogOpen(true);
  };

  const handleDeleteItem = async () => {
    try {
      if (deleteDialogType === "building") {
        await dispatch(deleteBuilding(itemToDelete._id)).unwrap();
        enqueueSnackbar("Xóa tòa nhà thành công", { variant: "success" });
        loadBuildings();
      } else {
        await dispatch(deleteRoom(itemToDelete._id)).unwrap();
        enqueueSnackbar("Xóa phòng học thành công", { variant: "success" });
        loadRooms();
      }
      setDeleteDialogOpen(false);
    } catch (error) {
      enqueueSnackbar(
        error?.message ||
          `Lỗi khi xóa ${
            deleteDialogType === "building" ? "tòa nhà" : "phòng học"
          }`,
        { variant: "error" }
      );
    }
  };

  // Handler cho việc chọn tòa nhà
  const handleSelectBuilding = (building) => {
    dispatch(setSelectedBuilding(building));
    setTabValue(1); // Chuyển sang tab phòng học
  };

  const handleBackToBuildings = () => {
    dispatch(setSelectedBuilding(null));
    setRoomSearchTerm("");
    setRoomPage(0);
  };

  // Render tùy theo tab đang chọn
  const renderContent = () => {
    if (tabValue === 0) {
      return renderBuildingsTab();
    } else {
      return renderRoomsTab();
    }
  };

  // Render tab tòa nhà
  const renderBuildingsTab = () => (
    <Box>
      <Paper sx={{ p: 2, mb: 3 }}>
        <Box
          display="flex"
          flexDirection={{ xs: "column", sm: "row" }}
          alignItems={{ sm: "center" }}
          justifyContent="space-between"
          gap={2}
        >
          <Box display="flex" alignItems="center" gap={1} flexGrow={1}>
            <TextField
              label="Tìm kiếm tòa nhà"
              variant="outlined"
              size="small"
              fullWidth
              value={buildingSearchTerm}
              onChange={(e) => setBuildingSearchTerm(e.target.value)}
              onKeyPress={(e) => e.key === "Enter" && handleBuildingSearch()}
              InputProps={{
                startAdornment: (
                  <InputAdornment position="start">
                    <Search />
                  </InputAdornment>
                ),
              }}
            />
            <Button
              variant="contained"
              color="primary"
              onClick={handleBuildingSearch}
              startIcon={<Search />}
            >
              Tìm
            </Button>
          </Box>

          <Box display="flex" gap={2}>
            <Button
              variant="outlined"
              startIcon={<Refresh />}
              onClick={loadBuildings}
            >
              Làm mới
            </Button>
            <Button
              variant="contained"
              startIcon={<Add />}
              onClick={() => openBuildingDialog("create")}
            >
              Thêm tòa nhà
            </Button>
          </Box>
        </Box>
      </Paper>

      <TableContainer component={Paper}>
        <Table sx={{ minWidth: 650 }}>
          <TableHead>
            <TableRow>
              <TableCell>Tên tòa nhà</TableCell>
              <TableCell>Mã</TableCell>
              <TableCell>Địa chỉ</TableCell>
              <TableCell align="center">Số tầng</TableCell>
              <TableCell align="right">Hành động</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {isLoading && buildings.length === 0 ? (
              <TableRow>
                <TableCell colSpan={5} align="center">
                  <CircularProgress size={30} />
                </TableCell>
              </TableRow>
            ) : buildings.length === 0 ? (
              <TableRow>
                <TableCell colSpan={5} align="center">
                  Không tìm thấy tòa nhà nào
                </TableCell>
              </TableRow>
            ) : (
              buildings.map((building) => (
                <TableRow key={building._id}>
                  <TableCell>
                    <Box display="flex" alignItems="center">
                      <Domain sx={{ mr: 1, color: "primary.main" }} />
                      <Typography variant="body1">{building.name}</Typography>
                    </Box>
                  </TableCell>
                  <TableCell>{building.code}</TableCell>
                  <TableCell>{building.address}</TableCell>
                  <TableCell align="center">{building.floors}</TableCell>
                  <TableCell align="right">
                    <Box display="flex" justifyContent="flex-end">
                      <Tooltip title="Xem phòng học">
                        <IconButton
                          color="primary"
                          onClick={() => handleSelectBuilding(building)}
                        >
                          <Room />
                        </IconButton>
                      </Tooltip>
                      <Tooltip title="Sửa tòa nhà">
                        <IconButton
                          color="primary"
                          onClick={() => {
                            dispatch(setSelectedBuilding(building));
                            openBuildingDialog("edit", building);
                          }}
                        >
                          <Edit />
                        </IconButton>
                      </Tooltip>
                      <Tooltip title="Xóa tòa nhà">
                        <IconButton
                          color="error"
                          onClick={() => openDeleteDialog("building", building)}
                        >
                          <Delete />
                        </IconButton>
                      </Tooltip>
                    </Box>
                  </TableCell>
                </TableRow>
              ))
            )}
          </TableBody>
        </Table>
        <TablePagination
          rowsPerPageOptions={[5, 10, 25]}
          component="div"
          count={buildingPagination.totalCount}
          rowsPerPage={buildingRowsPerPage}
          page={buildingPage}
          onPageChange={handleBuildingChangePage}
          onRowsPerPageChange={handleBuildingChangeRowsPerPage}
          labelRowsPerPage="Dòng mỗi trang:"
          labelDisplayedRows={({ from, to, count }) =>
            `${from}-${to} trên ${count}`
          }
        />
      </TableContainer>
    </Box>
  );

  // Render tab phòng học
  const renderRoomsTab = () => (
    <Box>
      {selectedBuilding ? (
        <>
          <Box sx={{ mb: 3 }}>
            <Button
              variant="outlined"
              startIcon={<ArrowBack />}
              onClick={handleBackToBuildings}
              sx={{ mb: 2 }}
            >
              Quay lại danh sách tòa nhà
            </Button>
            <Paper sx={{ p: 2 }}>
              <Typography variant="h6" gutterBottom>
                {selectedBuilding.name} ({selectedBuilding.code})
              </Typography>
              <Typography variant="body2" color="textSecondary">
                Địa chỉ: {selectedBuilding.address}
              </Typography>
              <Typography variant="body2" color="textSecondary">
                Số tầng: {selectedBuilding.floors}
              </Typography>
              {selectedBuilding.description && (
                <Typography variant="body2" color="textSecondary">
                  Mô tả: {selectedBuilding.description}
                </Typography>
              )}
            </Paper>
          </Box>

          <Paper sx={{ p: 2, mb: 3 }}>
            <Box
              display="flex"
              flexDirection={{ xs: "column", sm: "row" }}
              alignItems={{ sm: "center" }}
              justifyContent="space-between"
              gap={2}
            >
              <Box display="flex" alignItems="center" gap={1} flexGrow={1}>
                <TextField
                  label="Tìm kiếm phòng học"
                  variant="outlined"
                  size="small"
                  fullWidth
                  value={roomSearchTerm}
                  onChange={(e) => setRoomSearchTerm(e.target.value)}
                  onKeyPress={(e) => e.key === "Enter" && handleRoomSearch()}
                  InputProps={{
                    startAdornment: (
                      <InputAdornment position="start">
                        <Search />
                      </InputAdornment>
                    ),
                  }}
                />
                <Button
                  variant="contained"
                  color="primary"
                  onClick={handleRoomSearch}
                  startIcon={<Search />}
                >
                  Tìm
                </Button>
              </Box>

              <Box display="flex" gap={2}>
                <Button
                  variant="outlined"
                  startIcon={<Refresh />}
                  onClick={loadRooms}
                >
                  Làm mới
                </Button>
                <Button
                  variant="contained"
                  startIcon={<Add />}
                  onClick={() => openRoomDialog("create")}
                >
                  Thêm phòng học
                </Button>
              </Box>
            </Box>
          </Paper>

          <TableContainer component={Paper}>
            <Table sx={{ minWidth: 650 }}>
              <TableHead>
                <TableRow>
                  <TableCell>Tên phòng</TableCell>
                  <TableCell>Mã phòng</TableCell>
                  <TableCell align="center">Tầng</TableCell>
                  <TableCell align="center">Sức chứa</TableCell>
                  <TableCell>Loại phòng</TableCell>
                  <TableCell align="right">Hành động</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {isLoading && rooms.length === 0 ? (
                  <TableRow>
                    <TableCell colSpan={6} align="center">
                      <CircularProgress size={30} />
                    </TableCell>
                  </TableRow>
                ) : rooms.length === 0 ? (
                  <TableRow>
                    <TableCell colSpan={6} align="center">
                      Không tìm thấy phòng học nào
                    </TableCell>
                  </TableRow>
                ) : (
                  rooms.map((room) => (
                    <TableRow key={room._id}>
                      <TableCell>
                        <Box display="flex" alignItems="center">
                          <Room sx={{ mr: 1, color: "primary.main" }} />
                          <Typography variant="body1">{room.name}</Typography>
                        </Box>
                      </TableCell>
                      <TableCell>{room.code}</TableCell>
                      <TableCell align="center">{room.floor}</TableCell>
                      <TableCell align="center">{room.capacity}</TableCell>
                      <TableCell>
                        <Chip
                          label={
                            room.type === "classroom"
                              ? "Phòng học"
                              : room.type === "lab"
                              ? "Phòng thực hành"
                              : room.type
                          }
                          color={
                            room.type === "classroom"
                              ? "primary"
                              : room.type === "lab"
                              ? "secondary"
                              : "default"
                          }
                          size="small"
                        />
                      </TableCell>
                      <TableCell align="right">
                        <Box display="flex" justifyContent="flex-end">
                          <Tooltip title="Sửa phòng học">
                            <IconButton
                              color="primary"
                              onClick={() => {
                                setItemToDelete(room);
                                openRoomDialog("edit", room);
                              }}
                            >
                              <Edit />
                            </IconButton>
                          </Tooltip>
                          <Tooltip title="Xóa phòng học">
                            <IconButton
                              color="error"
                              onClick={() => openDeleteDialog("room", room)}
                            >
                              <Delete />
                            </IconButton>
                          </Tooltip>
                        </Box>
                      </TableCell>
                    </TableRow>
                  ))
                )}
              </TableBody>
            </Table>
            <TablePagination
              rowsPerPageOptions={[5, 10, 25]}
              component="div"
              count={roomPagination.totalCount}
              rowsPerPage={roomRowsPerPage}
              page={roomPage}
              onPageChange={handleRoomChangePage}
              onRowsPerPageChange={handleRoomChangeRowsPerPage}
              labelRowsPerPage="Dòng mỗi trang:"
              labelDisplayedRows={({ from, to, count }) =>
                `${from}-${to} trên ${count}`
              }
            />
          </TableContainer>
        </>
      ) : (
        <Box
          display="flex"
          justifyContent="center"
          alignItems="center"
          minHeight="200px"
        >
          <Typography variant="h6" color="textSecondary">
            Vui lòng chọn một tòa nhà để xem danh sách phòng học
          </Typography>
        </Box>
      )}
    </Box>
  );

  return (
    <Box>
      <Typography variant="h5" gutterBottom>
        Quản lý cơ sở vật chất
      </Typography>

      <Paper sx={{ mb: 3 }}>
        <Tabs
          value={tabValue}
          onChange={handleTabChange}
          indicatorColor="primary"
          textColor="primary"
          variant="fullWidth"
        >
          <Tab label="Tòa nhà" />
          <Tab label="Phòng học" />
        </Tabs>
      </Paper>

      {renderContent()}

      {/* Dialog tạo/sửa tòa nhà */}
      <Dialog
        open={buildingDialogOpen}
        onClose={() => setBuildingDialogOpen(false)}
        maxWidth="sm"
        fullWidth
      >
        <DialogTitle>
          {buildingDialogMode === "create" ? "Thêm tòa nhà mới" : "Sửa tòa nhà"}
        </DialogTitle>
        <DialogContent>
          <Grid container spacing={2} sx={{ mt: 1 }}>
            <Grid item xs={12}>
              <TextField
                name="name"
                label="Tên tòa nhà"
                fullWidth
                required
                value={buildingFormData.name}
                onChange={handleBuildingFormChange}
              />
            </Grid>
            <Grid item xs={12}>
              <TextField
                name="code"
                label="Mã tòa nhà"
                fullWidth
                required
                value={buildingFormData.code}
                onChange={handleBuildingFormChange}
              />
            </Grid>
            <Grid item xs={12}>
              <TextField
                name="campus_id"
                label="Mã khu vực"
                fullWidth
                value={buildingFormData.campus_id}
                onChange={handleBuildingFormChange}
              />
            </Grid>
            <Grid item xs={12} sm={6}>
              <TextField
                name="floors_count"
                label="Số tầng"
                type="number"
                fullWidth
                required
                value={buildingFormData.floors_count}
                onChange={handleBuildingFormChange}
                InputProps={{ inputProps: { min: 1 } }}
              />
            </Grid>
            <Grid item xs={12} sm={6}>
              <TextField
                name="year_built"
                label="Năm xây dựng"
                type="number"
                fullWidth
                required
                value={buildingFormData.year_built}
                onChange={handleBuildingFormChange}
                InputProps={{
                  inputProps: { min: 1900, max: new Date().getFullYear() },
                }}
              />
            </Grid>
            <Grid item xs={12}>
              <TextField
                name="status"
                label="Trạng thái"
                fullWidth
                value={buildingFormData.status}
                onChange={handleBuildingFormChange}
              />
            </Grid>
            <Grid item xs={12}>
              <TextField
                name="facilities"
                label="Các cơ sở"
                fullWidth
                multiline
                rows={2}
                value={buildingFormData.facilities.join(", ")}
                onChange={handleBuildingFormChange}
                placeholder="Danh sách cơ sở, phân cách bằng dấu phẩy"
              />
            </Grid>
            <Grid item xs={12}>
              <TextField
                name="image_url"
                label="URL hình ảnh"
                fullWidth
                value={buildingFormData.image_url}
                onChange={handleBuildingFormChange}
              />
            </Grid>
          </Grid>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setBuildingDialogOpen(false)}>Hủy</Button>
          <Button
            onClick={handleBuildingFormSubmit}
            variant="contained"
            color="primary"
            disabled={!buildingFormData.name || !buildingFormData.code}
          >
            {buildingDialogMode === "create" ? "Tạo mới" : "Cập nhật"}
          </Button>
        </DialogActions>
      </Dialog>

      {/* Dialog tạo/sửa phòng học */}
      <Dialog
        open={roomDialogOpen}
        onClose={() => setRoomDialogOpen(false)}
        maxWidth="sm"
        fullWidth
      >
        <DialogTitle>
          {roomDialogMode === "create" ? "Thêm phòng học mới" : "Sửa phòng học"}
        </DialogTitle>
        <DialogContent>
          <Grid container spacing={2} sx={{ mt: 1 }}>
            <Grid item xs={12}>
              <TextField
                name="name"
                label="Tên phòng"
                fullWidth
                required
                value={roomFormData.name}
                onChange={handleRoomFormChange}
              />
            </Grid>
            <Grid item xs={12}>
              <TextField
                name="code"
                label="Mã phòng"
                fullWidth
                required
                value={roomFormData.code}
                onChange={handleRoomFormChange}
              />
            </Grid>
            <Grid item xs={12} sm={6}>
              <TextField
                name="floor"
                label="Tầng"
                type="number"
                fullWidth
                required
                value={roomFormData.floor}
                onChange={handleRoomFormChange}
                InputProps={{
                  inputProps: {
                    min: 1,
                    max: selectedBuilding ? selectedBuilding.floors : 100,
                  },
                }}
              />
            </Grid>
            <Grid item xs={12} sm={6}>
              <TextField
                name="capacity"
                label="Sức chứa"
                type="number"
                fullWidth
                required
                value={roomFormData.capacity}
                onChange={handleRoomFormChange}
                InputProps={{ inputProps: { min: 1 } }}
              />
            </Grid>
            <Grid item xs={12}>
              <FormControl fullWidth required>
                <InputLabel>Loại phòng</InputLabel>
                <Select
                  name="type"
                  value={roomFormData.type}
                  onChange={handleRoomFormChange}
                  label="Loại phòng"
                >
                  <MenuItem value="classroom">Phòng học</MenuItem>
                  <MenuItem value="lab">Phòng thực hành</MenuItem>
                  <MenuItem value="office">Phòng làm việc</MenuItem>
                  <MenuItem value="meeting">Phòng họp</MenuItem>
                </Select>
              </FormControl>
            </Grid>
            <Grid item xs={12}>
              <TextField
                name="equipment"
                label="Thiết bị"
                fullWidth
                multiline
                rows={2}
                value={roomFormData.equipment}
                onChange={handleRoomFormChange}
                placeholder="Danh sách thiết bị, phân cách bằng dấu phẩy"
              />
            </Grid>
          </Grid>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setRoomDialogOpen(false)}>Hủy</Button>
          <Button
            onClick={handleRoomFormSubmit}
            variant="contained"
            color="primary"
            disabled={!roomFormData.name || !roomFormData.code}
          >
            {roomDialogMode === "create" ? "Tạo mới" : "Cập nhật"}
          </Button>
        </DialogActions>
      </Dialog>

      {/* Dialog xác nhận xóa */}
      <Dialog
        open={deleteDialogOpen}
        onClose={() => setDeleteDialogOpen(false)}
      >
        <DialogTitle>
          Xác nhận xóa{" "}
          {deleteDialogType === "building" ? "tòa nhà" : "phòng học"}
        </DialogTitle>
        <DialogContent>
          <DialogContentText>
            Bạn có chắc chắn muốn xóa{" "}
            {deleteDialogType === "building" ? "tòa nhà" : "phòng học"} "
            {itemToDelete?.name}"? Hành động này không thể hoàn tác.
            {deleteDialogType === "building" &&
              " Tất cả phòng học trong tòa nhà này cũng sẽ bị xóa."}
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setDeleteDialogOpen(false)}>Hủy</Button>
          <Button onClick={handleDeleteItem} variant="contained" color="error">
            Xóa
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default FacilitiesPage;
