import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useLocation, useNavigate } from "react-router-dom";
import { useSnackbar } from "notistack";
import {
  Container,
  Box,
  Typography,
  TextField,
  Button,
  Paper,
  Grid,
  Divider,
  CircularProgress,
  IconButton,
  InputAdornment,
  Link,
  Alert,
} from "@mui/material";
import { Visibility, VisibilityOff, Google } from "@mui/icons-material";
import { login, clearError, setCredentials } from "../redux/slices/authSlice";

const LoginPage = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const location = useLocation();
  const { enqueueSnackbar } = useSnackbar();

  const { isAuthenticated, error, isLoading } = useSelector(
    (state) => state.auth
  );

  const [formData, setFormData] = useState({
    email: "",
    password: "",
  });

  const [showPassword, setShowPassword] = useState(false);
  const [googleLoginError, setGoogleLoginError] = useState(null);
  const [googleLoginSuccess, setGoogleLoginSuccess] = useState(false);
  const [googleLoading, setGoogleLoading] = useState(false);

  // Xử lý callback từ Google OAuth
  useEffect(() => {
    // Nếu URL có chứa token (đăng nhập thành công)
    if (location.pathname === "/login/success") {
      const params = new URLSearchParams(location.search);
      const token = params.get("token");

      if (token) {
        // Lấy thông tin người dùng từ token
        fetch(
          `${
            process.env.REACT_APP_API_URL || "http://localhost:5000/api"
          }/auth/me`,
          {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          }
        )
          .then((res) => res.json())
          .then((userData) => {
            if (userData.success) {
              const user = userData.data;

              // Xử lý dựa vào trạng thái tài khoản
              if (user.status === "pending") {
                // Chuyển hướng đến trang chờ phê duyệt
                navigate("/pending-approval");
              } else if (user.status === "rejected") {
                // Hiển thị thông báo tài khoản bị từ chối
                setGoogleLoginError(
                  "Tài khoản của bạn đã bị từ chối. Vui lòng liên hệ quản trị viên."
                );
              } else if (user.status === "approved") {
                // Đăng nhập thành công
                dispatch(
                  setCredentials({
                    token,
                    user,
                  })
                );
                setGoogleLoginSuccess(true);
                setTimeout(() => {
                  navigate("/dashboard");
                }, 1000);
              }
            } else {
              setGoogleLoginError(
                "Không thể lấy thông tin người dùng. Vui lòng thử lại."
              );
            }
          })
          .catch((err) => {
            console.error("Error fetching user data:", err);
            setGoogleLoginError(
              "Đã xảy ra lỗi khi lấy thông tin người dùng. Vui lòng thử lại."
            );
          })
          .finally(() => {
            setGoogleLoading(false);
          });
      }
    } else if (location.pathname === "/login/error" || error) {
      // Xử lý lỗi
      setGoogleLoginError(
        "Đăng nhập bằng Google thất bại. Vui lòng thử lại hoặc sử dụng phương thức đăng nhập khác."
      );
    }
  }, [location, dispatch, navigate]);

  useEffect(() => {
    // Nếu đã đăng nhập, chuyển hướng đến trang chủ
    if (isAuthenticated && !googleLoginSuccess) {
      navigate("/dashboard");
    }
  }, [isAuthenticated, navigate, googleLoginSuccess]);

  useEffect(() => {
    // Hiển thị lỗi nếu có
    if (error) {
      enqueueSnackbar(error, { variant: "error" });
      dispatch(clearError());
    }
  }, [error, enqueueSnackbar, dispatch]);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    dispatch(login(formData));
  };

  const handleGoogleLogin = () => {
    try {
      setGoogleLoading(true);
      const apiUrl =
        process.env.REACT_APP_API_URL || "http://localhost:5000/api";
      // Thêm timestamp để tránh cache
      const redirectUrl = `${apiUrl}/auth/google?t=${Date.now()}`;
      console.log("Redirecting to:", redirectUrl);
      window.location.href = redirectUrl;
    } catch (error) {
      console.error("Google login error:", error);
      setGoogleLoginError(
        "Không thể kết nối đến dịch vụ Google. Vui lòng thử lại sau."
      );
      setGoogleLoading(false);
    }
  };

  const toggleShowPassword = () => {
    setShowPassword(!showPassword);
  };

  return (
    <Container component="main" maxWidth="sm">
      <Box
        sx={{
          marginTop: 8,
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
        }}
      >
        <Paper
          elevation={3}
          sx={{
            padding: 4,
            width: "100%",
            borderRadius: 3,
          }}
        >
          <Typography component="h1" variant="h4" align="center" gutterBottom>
            Đăng Nhập
          </Typography>
          <Typography
            variant="body1"
            align="center"
            color="text.secondary"
            mb={3}
          >
            Hệ thống điểm danh bằng khuôn mặt
          </Typography>

          {googleLoginError && (
            <Alert severity="error" sx={{ mb: 2 }}>
              {googleLoginError}
            </Alert>
          )}

          {googleLoginSuccess && (
            <Alert severity="success" sx={{ mb: 2 }}>
              Đăng nhập thành công! Đang chuyển hướng...
            </Alert>
          )}

          <Box
            component="form"
            onSubmit={handleSubmit}
            noValidate
            sx={{ mt: 1 }}
          >
            <TextField
              margin="normal"
              required
              fullWidth
              id="email"
              label="Email"
              name="email"
              autoComplete="email"
              autoFocus
              value={formData.email}
              onChange={handleChange}
              variant="outlined"
            />
            <TextField
              margin="normal"
              required
              fullWidth
              name="password"
              label="Mật khẩu"
              type={showPassword ? "text" : "password"}
              id="password"
              autoComplete="current-password"
              value={formData.password}
              onChange={handleChange}
              variant="outlined"
              InputProps={{
                endAdornment: (
                  <InputAdornment position="end">
                    <IconButton
                      aria-label="toggle password visibility"
                      onClick={toggleShowPassword}
                      edge="end"
                    >
                      {showPassword ? <VisibilityOff /> : <Visibility />}
                    </IconButton>
                  </InputAdornment>
                ),
              }}
            />

            <Button
              type="submit"
              fullWidth
              variant="contained"
              sx={{ mt: 3, mb: 2, py: 1.5 }}
              disabled={isLoading}
              color="primary"
            >
              {isLoading ? (
                <CircularProgress size={24} color="inherit" />
              ) : (
                "Đăng Nhập"
              )}
            </Button>

            <Grid container>
              <Grid item xs>
                <Link href="#" variant="body2">
                  Quên mật khẩu?
                </Link>
              </Grid>
              <Grid item>
                <Link href="/register" variant="body2">
                  {"Chưa có tài khoản? Đăng ký"}
                </Link>
              </Grid>
            </Grid>

            <Divider sx={{ my: 3 }}>Hoặc</Divider>

            <Button
              fullWidth
              variant="contained"
              color="secondary"
              startIcon={
                googleLoading ? (
                  <CircularProgress size={20} color="inherit" />
                ) : (
                  <Google />
                )
              }
              onClick={handleGoogleLogin}
              disabled={googleLoading}
              sx={{ py: 1.5 }}
            >
              {googleLoading ? "Đang xử lý..." : "Đăng nhập bằng Google"}
            </Button>
          </Box>
        </Paper>
      </Box>
    </Container>
  );
};

export default LoginPage;
