                  <Grid item xs={12} sm={6}>
                    <TextField
                      fullWidth
                      label="Mã lớp"
                      name="class_code"
                      value={newClass.class_code}
                      onChange={handleNewClassChange}
                      required
                      variant="outlined"
                      placeholder="VD: WEB101-G1"
                    />
                  </Grid>
                  <Grid item xs={12} sm={6}>
                    <FormControl fullWidth required>
                      <InputLabel>Môn học</InputLabel>
                      <Select
