                          <FormControl
                            fullWidth
                            size="small"
                            disabled={!day.campusId}
                          >
                            <InputLabel>Tòa nhà</InputLabel>
                            <Select
                              label="Tòa nhà"
                              value={day.buildingId || ""}
                              onChange={(e) =>
                                handleScheduleChange(
                                  index,
                                  "buildingId",
                                  e.target.value
                                )
                              }
                            >
                              <MenuItem value="">
                                <em>Chọn tòa nhà</em>
                              </MenuItem>
                              {day.availableBuildings.map((building) => (
                                <MenuItem
                                  key={building._id}
                                  value={building._id}
                                >
                                  {building.name}
                                </MenuItem>
                              ))}
                            </Select>
                          </FormControl>
                        </Grid>
                        <Grid item xs={12} sm={4}>
                          <FormControl
                            fullWidth
                            required
                            error={!day.room_id && isSubmitting}
                            size="small"
                          >
                            <InputLabel>Phòng học</InputLabel>
                            <Select
                              label="Phòng học"
                              value={day.room_id || ""}
                              onChange={(e) =>
                                handleScheduleChange(
                                  index,
                                  "room_id",
                                  e.target.value
                                )
                              }
                              disabled={!day.buildingId}
                            >
                              <MenuItem value="">
                                <em>Vui lòng chọn phòng</em>
                              </MenuItem>
                              {day.availableRooms.map((room) => (
                                <MenuItem key={room._id} value={room._id}>
                                  {room.room_number} ({room.capacity})
                                </MenuItem>
                              ))}
                            </Select>
                            {!day.room_id && isSubmitting && (
                              <FormHelperText error>Bắt buộc</FormHelperText>
                            )}
                          </FormControl>
                        </Grid>
                      </Grid>
                    </Grid>

                    <Grid item xs={12} sm={4}>
                      <FormControl fullWidth size="small">
                        <InputLabel>Loại lịch</InputLabel>
                        <Select
                          label="Loại lịch"
                          value={day.is_recurring}
                          onChange={(e) => {
                            // Chuyển đổi giá trị nhận được (có thể là string "true"/"false") thành boolean
                            const booleanValue =
                              e.target.value === true ||
                              e.target.value === "true";
                            handleScheduleChange(
                              index,
                              "is_recurring",
                              booleanValue
                            );
                          }}
                        >
                          <MenuItem value={true}>Định kỳ hàng tuần</MenuItem>
                          <MenuItem value={false}>Ngày cụ thể</MenuItem>
                        </Select>
                      </FormControl>
                    </Grid>
                  </Grid>
                </Paper>
              </Grid>
            ))}

            <Grid item xs={12}>
              <Button
                variant="contained"
                color="secondary"
                startIcon={<Add />}
                onClick={addScheduleDay}
                sx={{ mt: 1, textTransform: "none" }}
              >
                Thêm buổi học trong tuần
              </Button>
            </Grid>
          </Grid>
        </DialogContent>
        <DialogActions sx={{ px: 3, py: 2, bgcolor: "grey.100" }}>
          <Button
            onClick={handleCloseAddClassDialog}
            disabled={isSubmitting}
            color="inherit"
          >
            Hủy
          </Button>
          <Button
            onClick={handleCreateClass}
            variant="contained"
            color="primary"
            disabled={isSubmitting}
            startIcon={isSubmitting ? <CircularProgress size={20} /> : <Add />}
            sx={{ fontWeight: "bold" }}
          >
            {isSubmitting ? "Đang tạo..." : "Tạo lớp học"}
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default TeacherClassesPage;
