import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useSnackbar } from "notistack";
import {
  Box,
  Typography,
  Card,
  CardContent,
  Button,
  IconButton,
  TextField,
  Paper,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  TablePagination,
  Chip,
  Dialog,
  DialogActions,
  DialogContent,
  DialogContentText,
  DialogTitle,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Grid,
  Tooltip,
  Avatar,
  CircularProgress,
  InputAdornment,
} from "@mui/material";
import {
  Add,
  Delete,
  Edit,
  Search,
  Check,
  Close,
  Refresh,
  VerifiedUser,
  Person,
  School,
  SupervisorAccount,
} from "@mui/icons-material";
import {
  fetchAllUsers,
  approveUser,
  updateUserRole,
  deleteUser,
} from "../../redux/slices/adminSlice";

const ROLES = [
  { value: "admin", label: "Admin", color: "error" },
  { value: "teacher", label: "Giáo viên", color: "primary" },
  { value: "student", label: "Sinh viên", color: "success" },
];

const UsersPage = () => {
  const dispatch = useDispatch();
  const { enqueueSnackbar } = useSnackbar();

  const { users, userStats, isLoading, error, pagination } = useSelector(
    (state) => state.admin
  );
  const { token } = useSelector((state) => state.auth);

  const [searchTerm, setSearchTerm] = useState("");
  const [roleFilter, setRoleFilter] = useState("");
  const [page, setPage] = useState(0);
  const [rowsPerPage, setRowsPerPage] = useState(10);

  // Dialog states
  const [deleteDialogOpen, setDeleteDialogOpen] = useState(false);
  const [selectedUser, setSelectedUser] = useState(null);
  const [roleDialogOpen, setRoleDialogOpen] = useState(false);
  const [selectedRole, setSelectedRole] = useState("");

  useEffect(() => {
    loadUsers();
  }, [dispatch, page, rowsPerPage, roleFilter]);

  useEffect(() => {
    if (error) {
      enqueueSnackbar(error, { variant: "error" });
    }
  }, [error, enqueueSnackbar]);

  const loadUsers = () => {
    dispatch(
      fetchAllUsers({
        page: page + 1,
        limit: rowsPerPage,
        search: searchTerm,
        role: roleFilter,
      })
    );
  };

  const handleSearch = () => {
    setPage(0);
    loadUsers();
  };

  const handleRoleFilterChange = (event) => {
    setRoleFilter(event.target.value);
    setPage(0);
  };

  const handleChangePage = (event, newPage) => {
    setPage(newPage);
  };

  const handleChangeRowsPerPage = (event) => {
    setRowsPerPage(parseInt(event.target.value, 10));
    setPage(0);
  };

  const handleApproveUser = async (userId) => {
    try {
      await dispatch(approveUser(userId)).unwrap();
      enqueueSnackbar("Phê duyệt người dùng thành công", {
        variant: "success",
      });
    } catch (error) {
      enqueueSnackbar(error?.message || "Lỗi khi phê duyệt người dùng", {
        variant: "error",
      });
    }
  };

  const openRoleDialog = (user) => {
    setSelectedUser(user);
    setSelectedRole(user.role);
    setRoleDialogOpen(true);
  };

  const handleRoleChange = async () => {
    if (!selectedUser || !selectedRole) return;

    try {
      await dispatch(
        updateUserRole({
          userId: selectedUser._id,
          role: selectedRole,
        })
      ).unwrap();
      enqueueSnackbar("Cập nhật vai trò thành công", { variant: "success" });
      setRoleDialogOpen(false);
    } catch (error) {
      enqueueSnackbar(error?.message || "Lỗi khi cập nhật vai trò", {
        variant: "error",
      });
    }
  };

  const openDeleteDialog = (user) => {
    setSelectedUser(user);
    setDeleteDialogOpen(true);
  };

  const handleDeleteUser = async () => {
    if (!selectedUser) return;

    try {
      await dispatch(deleteUser(selectedUser._id)).unwrap();
      enqueueSnackbar("Xóa người dùng thành công", { variant: "success" });
      setDeleteDialogOpen(false);
    } catch (error) {
      enqueueSnackbar(error?.message || "Lỗi khi xóa người dùng", {
        variant: "error",
      });
    }
  };

  return (
    <Box>
      <Typography variant="h5" gutterBottom>
        Quản lý người dùng
      </Typography>

      {/* Dashboard thống kê */}
      <Grid container spacing={2} sx={{ mb: 3 }}>
        <Grid item xs={12} sm={6} md={3}>
          <Card>
            <CardContent>
              <Typography variant="h6" color="textSecondary" gutterBottom>
                Tổng người dùng
              </Typography>
              <Box display="flex" alignItems="center">
                <Person sx={{ mr: 1, color: "primary.main", fontSize: 40 }} />
                <Typography variant="h4">{userStats.totalUsers}</Typography>
              </Box>
            </CardContent>
          </Card>
        </Grid>

        <Grid item xs={12} sm={6} md={3}>
          <Card>
            <CardContent>
              <Typography variant="h6" color="textSecondary" gutterBottom>
                Sinh viên
              </Typography>
              <Box display="flex" alignItems="center">
                <School sx={{ mr: 1, color: "success.main", fontSize: 40 }} />
                <Typography variant="h4">{userStats.students}</Typography>
              </Box>
            </CardContent>
          </Card>
        </Grid>

        <Grid item xs={12} sm={6} md={3}>
          <Card>
            <CardContent>
              <Typography variant="h6" color="textSecondary" gutterBottom>
                Giáo viên
              </Typography>
              <Box display="flex" alignItems="center">
                <SupervisorAccount
                  sx={{ mr: 1, color: "info.main", fontSize: 40 }}
                />
                <Typography variant="h4">{userStats.teachers}</Typography>
              </Box>
            </CardContent>
          </Card>
        </Grid>

        <Grid item xs={12} sm={6} md={3}>
          <Card>
            <CardContent>
              <Typography
                variant="h6"
                color="textSecondary"
                gutterBottom
                sx={{ fontSize: "1rem" }}
              >
                Cần phê duyệt
              </Typography>
              <Box display="flex" alignItems="center">
                <VerifiedUser
                  sx={{ mr: 1, color: "warning.main", fontSize: 40 }}
                />
                <Typography variant="h4">
                  {userStats.pendingTeachers}
                </Typography>
              </Box>
            </CardContent>
          </Card>
        </Grid>
      </Grid>

      {/* Khung tìm kiếm và lọc */}
      <Paper sx={{ p: 2, mb: 3 }}>
        <Box
          display="flex"
          flexDirection={{ xs: "column", sm: "row" }}
          alignItems={{ sm: "center" }}
          justifyContent="space-between"
          gap={2}
        >
          <Box display="flex" alignItems="center" gap={1} flexGrow={1}>
            <TextField
              label="Tìm kiếm"
              variant="outlined"
              size="small"
              fullWidth
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              onKeyPress={(e) => e.key === "Enter" && handleSearch()}
              InputProps={{
                startAdornment: (
                  <InputAdornment position="start">
                    <Search />
                  </InputAdornment>
                ),
              }}
            />
            <Button
              variant="contained"
              color="primary"
              onClick={handleSearch}
              startIcon={<Search />}
            >
              Tìm
            </Button>
          </Box>

          <Box display="flex" gap={2}>
            <FormControl variant="outlined" size="small" sx={{ minWidth: 150 }}>
              <InputLabel>Vai trò</InputLabel>
              <Select
                value={roleFilter}
                onChange={handleRoleFilterChange}
                label="Vai trò"
              >
                <MenuItem value="">Tất cả</MenuItem>
                {ROLES.map((role) => (
                  <MenuItem key={role.value} value={role.value}>
                    {role.label}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>

            <Button
              variant="outlined"
              startIcon={<Refresh />}
              onClick={loadUsers}
            >
              Làm mới
            </Button>
          </Box>
        </Box>
      </Paper>

      {/* Bảng dữ liệu */}
      <TableContainer component={Paper}>
        <Table sx={{ minWidth: 650 }}>
          <TableHead>
            <TableRow>
              <TableCell>Người dùng</TableCell>
              <TableCell>Email</TableCell>
              <TableCell>Vai trò</TableCell>
              <TableCell align="center">Trạng thái</TableCell>
              <TableCell align="right">Hành động</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {isLoading && users.length === 0 ? (
              <TableRow>
                <TableCell colSpan={5} align="center">
                  <CircularProgress size={30} />
                </TableCell>
              </TableRow>
            ) : users.length === 0 ? (
              <TableRow>
                <TableCell colSpan={5} align="center">
                  Không tìm thấy người dùng nào
                </TableCell>
              </TableRow>
            ) : (
              users.map((user) => (
                <TableRow key={user._id}>
                  <TableCell>
                    <Box display="flex" alignItems="center">
                      <Avatar
                        src={user.avatar_url}
                        sx={{ mr: 2 }}
                        alt={user.full_name}
                      />
                      <Box>
                        <Typography variant="body1">
                          {user.full_name}
                        </Typography>
                        {user.student_code && (
                          <Typography variant="caption" color="textSecondary">
                            MSSV: {user.student_code}
                          </Typography>
                        )}
                      </Box>
                    </Box>
                  </TableCell>
                  <TableCell>{user.email}</TableCell>
                  <TableCell>
                    <Chip
                      label={
                        ROLES.find((r) => r.value === user.role)?.label ||
                        user.role
                      }
                      color={
                        ROLES.find((r) => r.value === user.role)?.color ||
                        "default"
                      }
                      size="small"
                    />
                  </TableCell>
                  <TableCell align="center">
                    {user.role === "teacher" && !user.is_approved ? (
                      <Chip
                        label="Chờ phê duyệt"
                        color="warning"
                        size="small"
                      />
                    ) : (
                      <Chip label="Đã kích hoạt" color="success" size="small" />
                    )}
                  </TableCell>
                  <TableCell align="right">
                    <Box display="flex" justifyContent="flex-end">
                      {user.role === "teacher" && !user.is_approved && (
                        <Tooltip title="Phê duyệt">
                          <IconButton
                            color="success"
                            onClick={() => handleApproveUser(user._id)}
                          >
                            <Check />
                          </IconButton>
                        </Tooltip>
                      )}
                      <Tooltip title="Thay đổi vai trò">
                        <IconButton
                          color="primary"
                          onClick={() => openRoleDialog(user)}
                        >
                          <Edit />
                        </IconButton>
                      </Tooltip>
                      <Tooltip title="Xóa người dùng">
                        <IconButton
                          color="error"
                          onClick={() => openDeleteDialog(user)}
                        >
                          <Delete />
                        </IconButton>
                      </Tooltip>
                    </Box>
                  </TableCell>
                </TableRow>
              ))
            )}
          </TableBody>
        </Table>
        <TablePagination
          rowsPerPageOptions={[5, 10, 25]}
          component="div"
          count={pagination.totalCount}
          rowsPerPage={rowsPerPage}
          page={page}
          onPageChange={handleChangePage}
          onRowsPerPageChange={handleChangeRowsPerPage}
          labelRowsPerPage="Dòng mỗi trang:"
          labelDisplayedRows={({ from, to, count }) =>
            `${from}-${to} trên ${count}`
          }
        />
      </TableContainer>

      {/* Dialog thay đổi vai trò */}
      <Dialog open={roleDialogOpen} onClose={() => setRoleDialogOpen(false)}>
        <DialogTitle>Thay đổi vai trò người dùng</DialogTitle>
        <DialogContent>
          <DialogContentText>
            Thay đổi vai trò cho {selectedUser?.full_name}
          </DialogContentText>
          <FormControl fullWidth margin="normal">
            <InputLabel>Vai trò</InputLabel>
            <Select
              value={selectedRole}
              onChange={(e) => setSelectedRole(e.target.value)}
              label="Vai trò"
            >
              {ROLES.map((role) => (
                <MenuItem key={role.value} value={role.value}>
                  {role.label}
                </MenuItem>
              ))}
            </Select>
          </FormControl>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setRoleDialogOpen(false)}>Hủy</Button>
          <Button
            onClick={handleRoleChange}
            variant="contained"
            color="primary"
          >
            Lưu thay đổi
          </Button>
        </DialogActions>
      </Dialog>

      {/* Dialog xóa người dùng */}
      <Dialog
        open={deleteDialogOpen}
        onClose={() => setDeleteDialogOpen(false)}
      >
        <DialogTitle>Xác nhận xóa người dùng</DialogTitle>
        <DialogContent>
          <DialogContentText>
            Bạn có chắc chắn muốn xóa người dùng "{selectedUser?.full_name}"?
            Hành động này không thể hoàn tác.
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setDeleteDialogOpen(false)}>Hủy</Button>
          <Button onClick={handleDeleteUser} variant="contained" color="error">
            Xóa
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default UsersPage;
