import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { useSelector } from "react-redux";
import { useSnackbar } from "notistack";
import axios from "axios";
import {
  Box,
  Typography,
  Paper,
  Tabs,
  Tab,
  Button,
  TextField,
  CircularProgress,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  TablePagination,
  Chip,
  IconButton,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Grid,
  Card,
  CardContent,
  Alert,
  Divider,
  Avatar,
  List,
  ListItem,
  ListItemAvatar,
  ListItemText,
  ListItemSecondaryAction,
} from "@mui/material";
import {
  Check,
  Close,
  PersonAdd,
  PersonRemove,
  School,
  Info,
  Warning,
  AccessTime,
} from "@mui/icons-material";

const API_URL = process.env.REACT_APP_API_URL || "http://localhost:5000/api";

function TabPanel(props) {
  const { children, value, index, ...other } = props;

  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      id={`tabpanel-${index}`}
      aria-labelledby={`tab-${index}`}
      {...other}
    >
      {value === index && <Box sx={{ p: 3 }}>{children}</Box>}
    </div>
  );
}

const TeacherMainClassPage = () => {
  const navigate = useNavigate();
  const { enqueueSnackbar } = useSnackbar();
  const { user, token } = useSelector((state) => state.auth);

  // State cho dữ liệu
  const [mainClass, setMainClass] = useState(null);
  const [pendingStudents, setPendingStudents] = useState([]);
  const [approvedStudents, setApprovedStudents] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [tabValue, setTabValue] = useState(0);
  const [rejectDialog, setRejectDialog] = useState({
    open: false,
    studentId: null,
    reason: "",
  });

  // Phân trang
  const [page, setPage] = useState(0);
  const [rowsPerPage, setRowsPerPage] = useState(10);

  useEffect(() => {
    fetchMainClass();
  }, [token]);

  // Lấy thông tin lớp chính của giáo viên
  const fetchMainClass = async () => {
    try {
      setIsLoading(true);

      // Lấy danh sách lớp được phân công cố vấn
      const response = await axios.get(
        `${API_URL}/classes/main?advisor_id=${user._id}&all=true`,
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );

      if (response.data.data && response.data.data.length > 0) {
        // Lấy lớp đầu tiên làm mặc định
        const classData = response.data.data[0];
        setMainClass(classData);

        // Lấy danh sách sinh viên chờ duyệt và đã duyệt
        await fetchPendingStudents(classData._id);
        await fetchApprovedStudents(classData._id);
      } else {
        enqueueSnackbar("Bạn chưa được phân công làm cố vấn cho lớp nào", {
          variant: "info",
        });
      }
    } catch (error) {
      console.error("Lỗi khi tải dữ liệu lớp chính:", error);
      enqueueSnackbar("Lỗi khi tải dữ liệu lớp chính", { variant: "error" });
    } finally {
      setIsLoading(false);
    }
  };

  // Lấy sinh viên đang chờ duyệt
  const fetchPendingStudents = async (classId) => {
    try {
      const response = await axios.get(
        `${API_URL}/classes/main/${classId}/pending-students`,
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      setPendingStudents(response.data.data || []);
    } catch (error) {
      console.error("Lỗi khi tải danh sách sinh viên chờ duyệt:", error);
      enqueueSnackbar("Lỗi khi tải danh sách sinh viên chờ duyệt", {
        variant: "error",
      });
    }
  };

  // Lấy sinh viên đã được duyệt
  const fetchApprovedStudents = async (classId) => {
    try {
      const response = await axios.get(
        `${API_URL}/classes/main/${classId}/approved-students`,
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      setApprovedStudents(response.data.data || []);
    } catch (error) {
      console.error("Lỗi khi tải danh sách sinh viên đã duyệt:", error);
      enqueueSnackbar("Lỗi khi tải danh sách sinh viên đã duyệt", {
        variant: "error",
      });
    }
  };

  // Xử lý thay đổi tab
  const handleTabChange = (event, newValue) => {
    setTabValue(newValue);
  };

  // Xử lý thay đổi trang
  const handleChangePage = (event, newPage) => {
    setPage(newPage);
  };

  // Xử lý thay đổi số dòng mỗi trang
  const handleChangeRowsPerPage = (event) => {
    setRowsPerPage(parseInt(event.target.value, 10));
    setPage(0);
  };

  // Phê duyệt sinh viên
  const handleApproveStudent = async (studentId) => {
    try {
      await axios.put(
        `${API_URL}/classes/main/${mainClass._id}/approve-student/${studentId}`,
        {},
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );

      enqueueSnackbar("Phê duyệt sinh viên thành công", { variant: "success" });

      // Cập nhật lại danh sách
      await fetchPendingStudents(mainClass._id);
      await fetchApprovedStudents(mainClass._id);
    } catch (error) {
      console.error("Lỗi khi phê duyệt sinh viên:", error);
      enqueueSnackbar(
        error.response?.data?.message || "Lỗi khi phê duyệt sinh viên",
        { variant: "error" }
      );
    }
  };

  // Mở dialog từ chối sinh viên
  const openRejectDialog = (studentId) => {
    setRejectDialog({
      open: true,
      studentId,
      reason: "",
    });
  };

  // Đóng dialog từ chối sinh viên
  const closeRejectDialog = () => {
    setRejectDialog({
      open: false,
      studentId: null,
      reason: "",
    });
  };

  // Từ chối sinh viên
  const handleRejectStudent = async () => {
    try {
      await axios.put(
        `${API_URL}/classes/main/${mainClass._id}/reject-student/${rejectDialog.studentId}`,
        { reason: rejectDialog.reason },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );

      enqueueSnackbar("Đã từ chối sinh viên", { variant: "success" });
      closeRejectDialog();

      // Cập nhật lại danh sách
      await fetchPendingStudents(mainClass._id);
    } catch (error) {
      console.error("Lỗi khi từ chối sinh viên:", error);
      enqueueSnackbar(
        error.response?.data?.message || "Lỗi khi từ chối sinh viên",
        { variant: "error" }
      );
    }
  };

  // Render thông tin lớp
  const renderClassInfo = () => {
    if (!mainClass) return null;

    return (
      <Card sx={{ mb: 3 }}>
        <CardContent>
          <Grid container spacing={2}>
            <Grid item xs={12} md={6}>
              <Typography variant="h6" gutterBottom>
                Thông tin lớp chính
              </Typography>
              <Typography variant="body1">
                <strong>Tên lớp:</strong> {mainClass.name}
              </Typography>
              <Typography variant="body1">
                <strong>Mã lớp:</strong> {mainClass.class_code}
              </Typography>
              {mainClass.department_id && (
                <Typography variant="body1">
                  <strong>Khoa:</strong> {mainClass.department_id.name}
                </Typography>
              )}
              <Typography variant="body1">
                <strong>Tổng số sinh viên:</strong>{" "}
                {mainClass.students?.length || 0}
              </Typography>
            </Grid>
            <Grid item xs={12} md={6}>
              <Typography variant="h6" gutterBottom>
                Giáo viên cố vấn
              </Typography>
              {mainClass.advisor_id ? (
                <>
                  <Typography variant="body1">
                    <strong>Họ tên:</strong> {mainClass.advisor_id.full_name}
                  </Typography>
                  <Typography variant="body1">
                    <strong>Email:</strong> {mainClass.advisor_id.email}
                  </Typography>
                </>
              ) : (
                <Typography color="text.secondary">
                  Chưa có giáo viên cố vấn
                </Typography>
              )}
            </Grid>
          </Grid>
        </CardContent>
      </Card>
    );
  };

  // Xem chi tiết sinh viên
  const handleViewStudent = (studentId) => {
    navigate(`/profile/${studentId}`);
  };

  if (isLoading) {
    return (
      <Box
        sx={{
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          height: "80vh",
        }}
      >
        <CircularProgress />
      </Box>
    );
  }

  if (!mainClass) {
    return (
      <Box sx={{ p: 3 }}>
        <Alert severity="info" sx={{ mb: 2 }}>
          Bạn chưa được phân công làm cố vấn cho lớp nào.
        </Alert>
        <Button
          variant="contained"
          color="primary"
          onClick={() => navigate("/teacher/dashboard")}
        >
          Quay lại trang chủ
        </Button>
      </Box>
    );
  }

  return (
    <Box sx={{ padding: 3 }}>
      <Typography variant="h4" gutterBottom>
        Quản lý lớp chính
      </Typography>

      {renderClassInfo()}

      <Paper sx={{ width: "100%", mb: 2 }}>
        <Tabs
          value={tabValue}
          onChange={handleTabChange}
          indicatorColor="primary"
          textColor="primary"
          centered
        >
          <Tab
            label={
              <Box sx={{ display: "flex", alignItems: "center" }}>
                <AccessTime sx={{ mr: 1 }} />
                Sinh viên chờ duyệt{" "}
                {pendingStudents.length > 0 && (
                  <Chip
                    label={pendingStudents.length}
                    color="error"
                    size="small"
                    sx={{ ml: 1 }}
                  />
                )}
              </Box>
            }
          />
          <Tab
            label={
              <Box sx={{ display: "flex", alignItems: "center" }}>
                <School sx={{ mr: 1 }} />
                Sinh viên của lớp
              </Box>
            }
          />
        </Tabs>

        <TabPanel value={tabValue} index={0}>
          {pendingStudents.length === 0 ? (
            <Alert severity="info">
              Không có sinh viên nào đang chờ phê duyệt
            </Alert>
          ) : (
            <Box>
              <Typography variant="h6" gutterBottom>
                Danh sách sinh viên đang chờ phê duyệt
              </Typography>
              <List>
                {pendingStudents.map((student) => (
                  <ListItem
                    key={student._id}
                    sx={{
                      mb: 1,
                      border: "1px solid #e0e0e0",
                      borderRadius: 1,
                    }}
                  >
                    <ListItemAvatar>
                      <Avatar>
                        {student.full_name.charAt(0).toUpperCase()}
                      </Avatar>
                    </ListItemAvatar>
                    <ListItemText
                      primary={
                        <Box
                          sx={{
                            display: "flex",
                            alignItems: "center",
                          }}
                        >
                          {student.full_name}
                          <Chip
                            label="Chờ duyệt"
                            color="warning"
                            size="small"
                            sx={{ ml: 1 }}
                          />
                        </Box>
                      }
                      secondary={
                        <>
                          <Typography variant="body2">
                            Email: {student.email}
                          </Typography>
                          <Typography variant="body2">
                            MSSV:{" "}
                            {student.school_info?.student_code || "Chưa có"}
                          </Typography>
                          <Typography variant="body2">
                            Ngày đăng ký:{" "}
                            {new Date(student.created_at).toLocaleDateString()}
                          </Typography>
                        </>
                      }
                    />
                    <ListItemSecondaryAction>
                      <IconButton
                        edge="end"
                        color="success"
                        onClick={() => handleApproveStudent(student._id)}
                        title="Phê duyệt"
                      >
                        <Check />
                      </IconButton>
                      <IconButton
                        edge="end"
                        color="error"
                        onClick={() => openRejectDialog(student._id)}
                        title="Từ chối"
                        sx={{ ml: 1 }}
                      >
                        <Close />
                      </IconButton>
                      <IconButton
                        edge="end"
                        color="info"
                        onClick={() => handleViewStudent(student._id)}
                        title="Xem chi tiết"
                        sx={{ ml: 1 }}
                      >
                        <Info />
                      </IconButton>
                    </ListItemSecondaryAction>
                  </ListItem>
                ))}
              </List>
            </Box>
          )}
        </TabPanel>

        <TabPanel value={tabValue} index={1}>
          {approvedStudents.length === 0 ? (
            <Alert severity="info">Chưa có sinh viên nào trong lớp</Alert>
          ) : (
            <Box>
              <Typography variant="h6" gutterBottom>
                Danh sách sinh viên của lớp ({approvedStudents.length})
              </Typography>
              <TableContainer>
                <Table sx={{ minWidth: 650 }}>
                  <TableHead>
                    <TableRow>
                      <TableCell>Họ tên</TableCell>
                      <TableCell>MSSV</TableCell>
                      <TableCell>Email</TableCell>
                      <TableCell>Trạng thái</TableCell>
                      <TableCell align="center">Thao tác</TableCell>
                    </TableRow>
                  </TableHead>
                  <TableBody>
                    {approvedStudents
                      .slice(
                        page * rowsPerPage,
                        page * rowsPerPage + rowsPerPage
                      )
                      .map((student) => (
                        <TableRow key={student._id}>
                          <TableCell>{student.full_name}</TableCell>
                          <TableCell>
                            {student.school_info?.student_code || "Chưa có"}
                          </TableCell>
                          <TableCell>{student.email}</TableCell>
                          <TableCell>
                            <Chip
                              label="Đã phê duyệt"
                              color="success"
                              size="small"
                            />
                          </TableCell>
                          <TableCell align="center">
                            <IconButton
                              color="info"
                              onClick={() => handleViewStudent(student._id)}
                              title="Xem chi tiết"
                            >
                              <Info />
                            </IconButton>
                          </TableCell>
                        </TableRow>
                      ))}
                  </TableBody>
                </Table>
              </TableContainer>
              <TablePagination
                rowsPerPageOptions={[5, 10, 25, 50]}
                component="div"
                count={approvedStudents.length}
                rowsPerPage={rowsPerPage}
                page={page}
                onPageChange={handleChangePage}
                onRowsPerPageChange={handleChangeRowsPerPage}
                labelRowsPerPage="Dòng mỗi trang:"
                labelDisplayedRows={({ from, to, count }) =>
                  `${from}-${to} trên ${count}`
                }
              />
            </Box>
          )}
        </TabPanel>
      </Paper>

      {/* Dialog từ chối sinh viên */}
      <Dialog
        open={rejectDialog.open}
        onClose={closeRejectDialog}
        maxWidth="sm"
        fullWidth
      >
        <DialogTitle>Từ chối sinh viên</DialogTitle>
        <DialogContent>
          <Typography variant="body2" paragraph>
            Bạn có chắc muốn từ chối sinh viên này?
          </Typography>
          <TextField
            fullWidth
            multiline
            rows={3}
            label="Lý do từ chối"
            placeholder="Nhập lý do từ chối để thông báo cho sinh viên"
            value={rejectDialog.reason}
            onChange={(e) =>
              setRejectDialog({ ...rejectDialog, reason: e.target.value })
            }
            variant="outlined"
            sx={{ mt: 2 }}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={closeRejectDialog} color="inherit">
            Hủy
          </Button>
          <Button
            onClick={handleRejectStudent}
            color="error"
            variant="contained"
            startIcon={<Close />}
          >
            Từ chối
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default TeacherMainClassPage;
