import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useSnackbar } from "notistack";
import axios from "axios";
import {
  Box,
  Typography,
  Paper,
  Button,
  IconButton,
  TextField,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  TablePagination,
  Dialog,
  DialogActions,
  DialogContent,
  DialogContentText,
  DialogTitle,
  Grid,
  Tooltip,
  CircularProgress,
  InputAdornment,
  Tabs,
  Tab,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Chip,
  Autocomplete,
  Collapse,
} from "@mui/material";
import {
  Add,
  Delete,
  Edit,
  Search,
  Refresh,
  School,
  People,
} from "@mui/icons-material";

const API_URL = process.env.REACT_APP_API_URL || "http://localhost:5000/api";

const ClassesPage = () => {
  const dispatch = useDispatch();
  const { enqueueSnackbar } = useSnackbar();
  const { token } = useSelector((state) => state.auth);

  // State
  const [tabValue, setTabValue] = useState(0);
  const [classes, setClasses] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [page, setPage] = useState(0);
  const [rowsPerPage, setRowsPerPage] = useState(10);
  const [totalCount, setTotalCount] = useState(0);
  const [searchTerm, setSearchTerm] = useState("");

  // Lookup data for dropdowns
  const [teachers, setTeachers] = useState([]);
  const [departments, setDepartments] = useState([]);
  const [students, setStudents] = useState([]);
  const [courses, setCourses] = useState([]);

  // Dialog states
  const [dialogOpen, setDialogOpen] = useState(false);
  const [dialogMode, setDialogMode] = useState("create");
  const [selectedClass, setSelectedClass] = useState(null);
  const [deleteDialogOpen, setDeleteDialogOpen] = useState(false);

  // Main Class Form Data
  const [mainClassForm, setMainClassForm] = useState({
    name: "",
    code: "",
    department_id: "",
    advisor_id: "",
    description: "",
    students: [],
  });

  // Teaching Class Form Data
  const [teachingClassForm, setTeachingClassForm] = useState({
    class_name: "",
    course_id: "",
    semester: "",
    academic_year: "",
    teacher_id: "",
    main_class_id: "",
    selected_students: [],
    description: "",
  });

  // State nâng cao
  const [filterOptions, setFilterOptions] = useState({
    department: "",
    teacher: "",
    course: "",
    semester: "",
  });

  // Load data on mount and when pagination/search changes
  useEffect(() => {
    loadClasses();
  }, [page, rowsPerPage, tabValue]);

  useEffect(() => {
    loadLookupData();
  }, []);

  const loadLookupData = async () => {
    try {
      setIsLoading(true);
      // Load teachers với tham số tìm kiếm nâng cao
      const teachersResponse = await axios.get(
        `${API_URL}/users?role=teacher&status=approved&limit=200&sort=full_name`,
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      setTeachers(teachersResponse.data.data || []);

      // Load departments
      const departmentsResponse = await axios.get(
        `${API_URL}/departments?limit=100`,
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      setDepartments(departmentsResponse.data.data || []);

      // Load students
      const studentsResponse = await axios.get(
        `${API_URL}/users?role=student&status=approved&limit=200&sort=full_name`,
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      setStudents(studentsResponse.data.data || []);

      // Load courses
      const coursesResponse = await axios.get(
        `${API_URL}/courses?limit=100&sort=name`,
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      setCourses(coursesResponse.data.data || []);
      setIsLoading(false);
    } catch (error) {
      console.error("Lỗi khi tải dữ liệu tham chiếu:", error);
      enqueueSnackbar("Lỗi khi tải dữ liệu tham chiếu", { variant: "error" });
      setIsLoading(false);
    }
  };

  const loadClasses = async () => {
    try {
      setIsLoading(true);
      const endpoint = tabValue === 0 ? "classes/main" : "classes/teaching";

      // Xây dựng query params với bộ lọc
      let queryParams = `page=${
        page + 1
      }&limit=${rowsPerPage}&search=${searchTerm}`;

      if (tabValue === 0 && filterOptions.department) {
        queryParams += `&department_id=${filterOptions.department}`;
      }

      if (tabValue === 0 && filterOptions.teacher) {
        queryParams += `&advisor_id=${filterOptions.teacher}`;
      }

      if (tabValue === 1 && filterOptions.course) {
        queryParams += `&course_id=${filterOptions.course}`;
      }

      if (tabValue === 1 && filterOptions.teacher) {
        queryParams += `&teacher_id=${filterOptions.teacher}`;
      }

      if (tabValue === 1 && filterOptions.semester) {
        queryParams += `&semester=${filterOptions.semester}`;
      }

      const response = await axios.get(
        `${API_URL}/${endpoint}?${queryParams}`,
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );

      setClasses(response.data.data || []);
      setTotalCount(response.data.totalCount || 0);
      setIsLoading(false);
    } catch (error) {
      console.error("Lỗi khi tải danh sách lớp:", error);
      enqueueSnackbar("Lỗi khi tải danh sách lớp", { variant: "error" });
      setIsLoading(false);
    }
  };

  const handleSearch = () => {
    setPage(0);
    loadClasses();
  };

  const handleTabChange = (event, newValue) => {
    setTabValue(newValue);
    setPage(0);
    setSearchTerm("");
  };

  const handleChangePage = (event, newPage) => {
    setPage(newPage);
  };

  const handleChangeRowsPerPage = (event) => {
    setRowsPerPage(parseInt(event.target.value, 10));
    setPage(0);
  };

  // Thêm hàm xử lý lọc
  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilterOptions({
      ...filterOptions,
      [name]: value,
    });
  };

  // Thêm hàm áp dụng bộ lọc
  const applyFilters = () => {
    setPage(0);
    loadClasses();
  };

  // Thêm hàm xóa bộ lọc
  const clearFilters = () => {
    setFilterOptions({
      department: "",
      teacher: "",
      course: "",
      semester: "",
    });
    setSearchTerm("");
    setPage(0);
    loadClasses();
  };

  // Dialog handlers
  const openDialog = (mode, classItem = null) => {
    setDialogMode(mode);

    if (tabValue === 0) {
      // Main Class
      if (mode === "edit" && classItem) {
        setMainClassForm({
          name: classItem.name,
          code: classItem.code,
          department_id: classItem.department_id?._id || "",
          advisor_id: classItem.advisor_id?._id || "",
          description: classItem.description || "",
          students: classItem.students || [],
        });
        setSelectedClass(classItem);
      } else {
        setMainClassForm({
          name: "",
          code: "",
          department_id: "",
          advisor_id: "",
          description: "",
          students: [],
        });
      }
    } else {
      // Teaching Class
      if (mode === "edit" && classItem) {
        setTeachingClassForm({
          class_name: classItem.class_name,
          course_id: classItem.course_id?._id || "",
          semester: classItem.semester || "",
          academic_year: classItem.academic_year || "",
          teacher_id: classItem.teacher_id?._id || "",
          main_class_id: classItem.main_class_id?._id || "",
          selected_students: classItem.students || [],
          description: classItem.description || "",
        });
        setSelectedClass(classItem);
      } else {
        setTeachingClassForm({
          class_name: "",
          course_id: "",
          semester: "",
          academic_year: "",
          teacher_id: "",
          main_class_id: "",
          selected_students: [],
          description: "",
        });
      }
    }

    setDialogOpen(true);
  };

  const handleMainClassFormChange = (e) => {
    const { name, value } = e.target;
    setMainClassForm({
      ...mainClassForm,
      [name]: value,
    });
  };

  const handleTeachingClassFormChange = (e) => {
    const { name, value } = e.target;
    setTeachingClassForm({
      ...teachingClassForm,
      [name]: value,
    });
  };

  const handleStudentSelection = (event, newValue) => {
    if (tabValue === 0) {
      setMainClassForm({
        ...mainClassForm,
        students: newValue.map((student) => student._id),
      });
    } else {
      setTeachingClassForm({
        ...teachingClassForm,
        selected_students: newValue.map((student) => student._id),
      });
    }
  };

  const handleFormSubmit = async () => {
    try {
      if (tabValue === 0) {
        // Main class
        if (dialogMode === "create") {
          await axios.post(`${API_URL}/classes/main`, mainClassForm, {
            headers: { Authorization: `Bearer ${token}` },
          });
          enqueueSnackbar("Tạo lớp chính mới thành công", {
            variant: "success",
          });
        } else {
          await axios.put(
            `${API_URL}/classes/main/${selectedClass._id}`,
            mainClassForm,
            {
              headers: { Authorization: `Bearer ${token}` },
            }
          );
          enqueueSnackbar("Cập nhật lớp chính thành công", {
            variant: "success",
          });
        }
      } else {
        // Teaching class
        if (dialogMode === "create") {
          await axios.post(`${API_URL}/classes/teaching`, teachingClassForm, {
            headers: { Authorization: `Bearer ${token}` },
          });
          enqueueSnackbar("Tạo lớp giảng dạy mới thành công", {
            variant: "success",
          });
        } else {
          await axios.put(
            `${API_URL}/classes/teaching/${selectedClass._id}`,
            teachingClassForm,
            {
              headers: { Authorization: `Bearer ${token}` },
            }
          );
          enqueueSnackbar("Cập nhật lớp giảng dạy thành công", {
            variant: "success",
          });
        }
      }

      setDialogOpen(false);
      loadClasses();
    } catch (error) {
      console.error("Lỗi khi thao tác với lớp học:", error);
      enqueueSnackbar(
        error.response?.data?.message || "Lỗi khi thao tác với lớp học",
        { variant: "error" }
      );
    }
  };

  // Delete handlers
  const openDeleteDialog = (classItem) => {
    setSelectedClass(classItem);
    setDeleteDialogOpen(true);
  };

  const handleDeleteClass = async () => {
    try {
      const endpoint = tabValue === 0 ? "classes/main" : "classes/teaching";

      await axios.delete(`${API_URL}/${endpoint}/${selectedClass._id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });

      enqueueSnackbar(
        `Xóa ${tabValue === 0 ? "lớp chính" : "lớp giảng dạy"} thành công`,
        { variant: "success" }
      );
      setDeleteDialogOpen(false);
      loadClasses();
    } catch (error) {
      console.error(
        `Lỗi khi xóa ${tabValue === 0 ? "lớp chính" : "lớp giảng dạy"}:`,
        error
      );
      enqueueSnackbar(
        error.response?.data?.message ||
          `Lỗi khi xóa ${tabValue === 0 ? "lớp chính" : "lớp giảng dạy"}`,
        { variant: "error" }
      );
    }
  };

  // Render main class table
  const renderMainClassesTable = () => (
    <TableContainer component={Paper}>
      <Table sx={{ minWidth: 650 }}>
        <TableHead>
          <TableRow>
            <TableCell>Tên lớp</TableCell>
            <TableCell>Mã lớp</TableCell>
            <TableCell>Khoa</TableCell>
            <TableCell>Giáo viên chủ nhiệm</TableCell>
            <TableCell align="center">Số lượng SV</TableCell>
            <TableCell align="right">Hành động</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {isLoading ? (
            <TableRow>
              <TableCell colSpan={6} align="center">
                <CircularProgress size={30} />
              </TableCell>
            </TableRow>
          ) : classes.length === 0 ? (
            <TableRow>
              <TableCell colSpan={6} align="center">
                Không tìm thấy lớp học nào
              </TableCell>
            </TableRow>
          ) : (
            classes.map((classItem) => (
              <TableRow key={classItem._id}>
                <TableCell>{classItem.name}</TableCell>
                <TableCell>{classItem.code}</TableCell>
                <TableCell>
                  {classItem.department_id?.name || "Chưa phân khoa"}
                </TableCell>
                <TableCell>
                  {classItem.advisor_id?.full_name || "Chưa phân công"}
                </TableCell>
                <TableCell align="center">
                  {classItem.students?.length || 0}
                </TableCell>
                <TableCell align="right">
                  <Box display="flex" justifyContent="flex-end">
                    <Tooltip title="Sửa lớp">
                      <IconButton
                        color="primary"
                        onClick={() => openDialog("edit", classItem)}
                      >
                        <Edit />
                      </IconButton>
                    </Tooltip>
                    <Tooltip title="Xóa lớp">
                      <IconButton
                        color="error"
                        onClick={() => openDeleteDialog(classItem)}
                      >
                        <Delete />
                      </IconButton>
                    </Tooltip>
                  </Box>
                </TableCell>
              </TableRow>
            ))
          )}
        </TableBody>
      </Table>
      <TablePagination
        rowsPerPageOptions={[5, 10, 25]}
        component="div"
        count={totalCount}
        rowsPerPage={rowsPerPage}
        page={page}
        onPageChange={handleChangePage}
        onRowsPerPageChange={handleChangeRowsPerPage}
        labelRowsPerPage="Dòng mỗi trang:"
        labelDisplayedRows={({ from, to, count }) =>
          `${from}-${to} trên ${count}`
        }
      />
    </TableContainer>
  );

  // Render teaching class table
  const renderTeachingClassesTable = () => (
    <TableContainer component={Paper}>
      <Table sx={{ minWidth: 650 }}>
        <TableHead>
          <TableRow>
            <TableCell>Tên lớp</TableCell>
            <TableCell>Môn học</TableCell>
            <TableCell>Niên khóa</TableCell>
            <TableCell>Giáo viên</TableCell>
            <TableCell>Lớp chính</TableCell>
            <TableCell align="center">Số lượng SV</TableCell>
            <TableCell align="right">Hành động</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {isLoading ? (
            <TableRow>
              <TableCell colSpan={7} align="center">
                <CircularProgress size={30} />
              </TableCell>
            </TableRow>
          ) : classes.length === 0 ? (
            <TableRow>
              <TableCell colSpan={7} align="center">
                Không tìm thấy lớp học nào
              </TableCell>
            </TableRow>
          ) : (
            classes.map((classItem) => (
              <TableRow key={classItem._id}>
                <TableCell>{classItem.class_name}</TableCell>
                <TableCell>{classItem.course_id?.name || "N/A"}</TableCell>
                <TableCell>
                  {classItem.semester}/{classItem.academic_year}
                </TableCell>
                <TableCell>
                  {classItem.teacher_id?.full_name || "Chưa phân công"}
                </TableCell>
                <TableCell>
                  {classItem.main_class_id?.name || "Không có"}
                </TableCell>
                <TableCell align="center">
                  {classItem.students?.length || 0}
                </TableCell>
                <TableCell align="right">
                  <Box display="flex" justifyContent="flex-end">
                    <Tooltip title="Sửa lớp">
                      <IconButton
                        color="primary"
                        onClick={() => openDialog("edit", classItem)}
                      >
                        <Edit />
                      </IconButton>
                    </Tooltip>
                    <Tooltip title="Xóa lớp">
                      <IconButton
                        color="error"
                        onClick={() => openDeleteDialog(classItem)}
                      >
                        <Delete />
                      </IconButton>
                    </Tooltip>
                  </Box>
                </TableCell>
              </TableRow>
            ))
          )}
        </TableBody>
      </Table>
      <TablePagination
        rowsPerPageOptions={[5, 10, 25]}
        component="div"
        count={totalCount}
        rowsPerPage={rowsPerPage}
        page={page}
        onPageChange={handleChangePage}
        onRowsPerPageChange={handleChangeRowsPerPage}
        labelRowsPerPage="Dòng mỗi trang:"
        labelDisplayedRows={({ from, to, count }) =>
          `${from}-${to} trên ${count}`
        }
      />
    </TableContainer>
  );

  // Render main class form dialog
  const renderMainClassFormDialog = () => (
    <Dialog
      open={dialogOpen && tabValue === 0}
      onClose={() => setDialogOpen(false)}
      maxWidth="md"
      fullWidth
    >
      <DialogTitle>
        {dialogMode === "create" ? "Thêm lớp chính mới" : "Sửa lớp chính"}
      </DialogTitle>
      <DialogContent>
        <Grid container spacing={2} sx={{ mt: 1 }}>
          <Grid item xs={12} sm={6}>
            <TextField
              name="name"
              label="Tên lớp"
              fullWidth
              required
              value={mainClassForm.name}
              onChange={handleMainClassFormChange}
            />
          </Grid>
          <Grid item xs={12} sm={6}>
            <TextField
              name="code"
              label="Mã lớp"
              fullWidth
              required
              value={mainClassForm.code}
              onChange={handleMainClassFormChange}
            />
          </Grid>
          <Grid item xs={12} sm={6}>
            <FormControl fullWidth>
              <InputLabel>Khoa</InputLabel>
              <Select
                name="department_id"
                value={mainClassForm.department_id}
                onChange={handleMainClassFormChange}
                label="Khoa"
              >
                {departments.map((dept) => (
                  <MenuItem key={dept._id} value={dept._id}>
                    {dept.name}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
          </Grid>
          <Grid item xs={12} sm={6}>
            <Autocomplete
              id="advisor-select"
              options={teachers}
              getOptionLabel={(option) =>
                `${option.full_name} ${option.email ? `(${option.email})` : ""}`
              }
              value={
                teachers.find((t) => t._id === mainClassForm.advisor_id) || null
              }
              onChange={(event, newValue) => {
                setMainClassForm({
                  ...mainClassForm,
                  advisor_id: newValue?._id || "",
                });
              }}
              renderInput={(params) => (
                <TextField
                  {...params}
                  label="Giáo viên chủ nhiệm"
                  variant="outlined"
                  fullWidth
                />
              )}
              renderOption={(props, option) => (
                <li {...props}>
                  <Box>
                    <Typography variant="body1">{option.full_name}</Typography>
                    <Typography variant="body2" color="text.secondary">
                      {option.email}
                    </Typography>
                  </Box>
                </li>
              )}
            />
          </Grid>
          <Grid item xs={12}>
            <Autocomplete
              multiple
              options={students}
              getOptionLabel={(option) =>
                `${option.full_name} (${option.student_code || option.email})`
              }
              value={students.filter((student) =>
                mainClassForm.students.includes(student._id)
              )}
              onChange={handleStudentSelection}
              renderInput={(params) => (
                <TextField
                  {...params}
                  label="Chọn sinh viên"
                  placeholder="Tìm sinh viên"
                />
              )}
              renderTags={(value, getTagProps) =>
                value.map((option, index) => (
                  <Chip
                    label={`${option.full_name} (${
                      option.student_code || option.email
                    })`}
                    {...getTagProps({ index })}
                    key={option._id}
                  />
                ))
              }
            />
          </Grid>
          <Grid item xs={12}>
            <TextField
              name="description"
              label="Mô tả"
              fullWidth
              multiline
              rows={3}
              value={mainClassForm.description}
              onChange={handleMainClassFormChange}
            />
          </Grid>
        </Grid>
      </DialogContent>
      <DialogActions>
        <Button onClick={() => setDialogOpen(false)}>Hủy</Button>
        <Button
          onClick={handleFormSubmit}
          variant="contained"
          color="primary"
          disabled={!mainClassForm.name || !mainClassForm.code}
        >
          {dialogMode === "create" ? "Tạo mới" : "Cập nhật"}
        </Button>
      </DialogActions>
    </Dialog>
  );

  // Render teaching class form dialog
  const renderTeachingClassFormDialog = () => (
    <Dialog
      open={dialogOpen && tabValue === 1}
      onClose={() => setDialogOpen(false)}
      maxWidth="md"
      fullWidth
    >
      <DialogTitle>
        {dialogMode === "create"
          ? "Thêm lớp giảng dạy mới"
          : "Sửa lớp giảng dạy"}
      </DialogTitle>
      <DialogContent>
        <Grid container spacing={2} sx={{ mt: 1 }}>
          <Grid item xs={12}>
            <TextField
              name="class_name"
              label="Tên lớp"
              fullWidth
              required
              value={teachingClassForm.class_name}
              onChange={handleTeachingClassFormChange}
            />
          </Grid>
          <Grid item xs={12} sm={6}>
            <FormControl fullWidth required>
              <InputLabel>Môn học</InputLabel>
              <Select
                name="course_id"
                value={teachingClassForm.course_id}
                onChange={handleTeachingClassFormChange}
                label="Môn học"
              >
                {courses.map((course) => (
                  <MenuItem key={course._id} value={course._id}>
                    {course.name}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
          </Grid>
          <Grid item xs={12} sm={6}>
            <Autocomplete
              id="teacher-select"
              options={teachers}
              getOptionLabel={(option) =>
                `${option.full_name} ${option.email ? `(${option.email})` : ""}`
              }
              value={
                teachers.find((t) => t._id === teachingClassForm.teacher_id) ||
                null
              }
              onChange={(event, newValue) => {
                setTeachingClassForm({
                  ...teachingClassForm,
                  teacher_id: newValue?._id || "",
                });
              }}
              renderInput={(params) => (
                <TextField
                  {...params}
                  label="Giáo viên"
                  variant="outlined"
                  fullWidth
                />
              )}
              renderOption={(props, option) => (
                <li {...props}>
                  <Box>
                    <Typography variant="body1">{option.full_name}</Typography>
                    <Typography variant="body2" color="text.secondary">
                      {option.email}
                    </Typography>
                  </Box>
                </li>
              )}
            />
          </Grid>
          <Grid item xs={12} sm={6}>
            <TextField
              name="semester"
              label="Học kỳ"
              fullWidth
              required
              value={teachingClassForm.semester}
              onChange={handleTeachingClassFormChange}
              placeholder="VD: 1, 2, 3"
            />
          </Grid>
          <Grid item xs={12} sm={6}>
            <TextField
              name="academic_year"
              label="Năm học"
              fullWidth
              required
              value={teachingClassForm.academic_year}
              onChange={handleTeachingClassFormChange}
              placeholder="VD: 2023-2024"
            />
          </Grid>
          <Grid item xs={12} sm={12}>
            <FormControl fullWidth>
              <InputLabel>Lớp chính</InputLabel>
              <Select
                name="main_class_id"
                value={teachingClassForm.main_class_id}
                onChange={handleTeachingClassFormChange}
                label="Lớp chính"
              >
                <MenuItem value="">Không chọn</MenuItem>
                {/* Cần tải danh sách lớp chính từ API */}
                {classes.map((mainClass) => (
                  <MenuItem key={mainClass._id} value={mainClass._id}>
                    {mainClass.name}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
          </Grid>
          <Grid item xs={12}>
            <Autocomplete
              multiple
              options={students}
              getOptionLabel={(option) =>
                `${option.full_name} (${option.student_code || option.email})`
              }
              value={students.filter((student) =>
                teachingClassForm.selected_students.includes(student._id)
              )}
              onChange={handleStudentSelection}
              renderInput={(params) => (
                <TextField
                  {...params}
                  label="Chọn sinh viên"
                  placeholder="Tìm sinh viên"
                />
              )}
              renderTags={(value, getTagProps) =>
                value.map((option, index) => (
                  <Chip
                    label={`${option.full_name} (${
                      option.student_code || option.email
                    })`}
                    {...getTagProps({ index })}
                    key={option._id}
                  />
                ))
              }
            />
          </Grid>
          <Grid item xs={12}>
            <TextField
              name="description"
              label="Mô tả"
              fullWidth
              multiline
              rows={3}
              value={teachingClassForm.description}
              onChange={handleTeachingClassFormChange}
            />
          </Grid>
        </Grid>
      </DialogContent>
      <DialogActions>
        <Button onClick={() => setDialogOpen(false)}>Hủy</Button>
        <Button
          onClick={handleFormSubmit}
          variant="contained"
          color="primary"
          disabled={
            !teachingClassForm.class_name ||
            !teachingClassForm.course_id ||
            !teachingClassForm.semester ||
            !teachingClassForm.academic_year
          }
        >
          {dialogMode === "create" ? "Tạo mới" : "Cập nhật"}
        </Button>
      </DialogActions>
    </Dialog>
  );

  return (
    <Box>
      <Typography variant="h5" gutterBottom>
        Quản lý lớp học
      </Typography>

      <Paper sx={{ mb: 3 }}>
        <Tabs
          value={tabValue}
          onChange={handleTabChange}
          indicatorColor="primary"
          textColor="primary"
          variant="fullWidth"
        >
          <Tab label="Lớp chính" icon={<School />} iconPosition="start" />
          <Tab label="Lớp giảng dạy" icon={<People />} iconPosition="start" />
        </Tabs>
      </Paper>

      <Paper sx={{ p: 2, mb: 3 }}>
        <Box
          display="flex"
          flexDirection={{ xs: "column", sm: "row" }}
          alignItems={{ sm: "center" }}
          justifyContent="space-between"
          gap={2}
          mb={2}
        >
          <Box display="flex" alignItems="center" gap={1} flexGrow={1}>
            <TextField
              label={`Tìm kiếm ${
                tabValue === 0 ? "lớp chính" : "lớp giảng dạy"
              }`}
              variant="outlined"
              size="small"
              fullWidth
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              onKeyPress={(e) => e.key === "Enter" && handleSearch()}
              InputProps={{
                startAdornment: (
                  <InputAdornment position="start">
                    <Search />
                  </InputAdornment>
                ),
              }}
            />
            <Button
              variant="contained"
              color="primary"
              onClick={handleSearch}
              startIcon={<Search />}
            >
              Tìm
            </Button>
          </Box>

          <Box display="flex" gap={2}>
            <Button
              variant="outlined"
              startIcon={<Refresh />}
              onClick={loadClasses}
            >
              Làm mới
            </Button>
            <Button
              variant="contained"
              startIcon={<Add />}
              onClick={() => openDialog("create")}
            >
              Thêm {tabValue === 0 ? "lớp chính" : "lớp giảng dạy"}
            </Button>
          </Box>
        </Box>

        {/* Bộ lọc nâng cao */}
        <Collapse in={true}>
          <Box
            sx={{ p: 1, border: "1px solid #e0e0e0", borderRadius: 1, mt: 1 }}
          >
            <Typography variant="subtitle2" gutterBottom>
              Bộ lọc nâng cao
            </Typography>
            <Grid container spacing={2}>
              {tabValue === 0 ? (
                // Bộ lọc cho lớp chính
                <>
                  <Grid item xs={12} sm={6} md={3}>
                    <FormControl fullWidth size="small">
                      <InputLabel>Khoa/Bộ môn</InputLabel>
                      <Select
                        name="department"
                        value={filterOptions.department}
                        onChange={handleFilterChange}
                        label="Khoa/Bộ môn"
                      >
                        <MenuItem value="">Tất cả</MenuItem>
                        {departments.map((dept) => (
                          <MenuItem key={dept._id} value={dept._id}>
                            {dept.name}
                          </MenuItem>
                        ))}
                      </Select>
                    </FormControl>
                  </Grid>
                  <Grid item xs={12} sm={6} md={3}>
                    <FormControl fullWidth size="small">
                      <InputLabel>Giáo viên chủ nhiệm</InputLabel>
                      <Select
                        name="teacher"
                        value={filterOptions.teacher}
                        onChange={handleFilterChange}
                        label="Giáo viên chủ nhiệm"
                      >
                        <MenuItem value="">Tất cả</MenuItem>
                        {teachers.map((teacher) => (
                          <MenuItem key={teacher._id} value={teacher._id}>
                            {teacher.full_name}
                          </MenuItem>
                        ))}
                      </Select>
                    </FormControl>
                  </Grid>
                </>
              ) : (
                // Bộ lọc cho lớp giảng dạy
                <>
                  <Grid item xs={12} sm={6} md={3}>
                    <FormControl fullWidth size="small">
                      <InputLabel>Môn học</InputLabel>
                      <Select
                        name="course"
                        value={filterOptions.course}
                        onChange={handleFilterChange}
                        label="Môn học"
                      >
                        <MenuItem value="">Tất cả</MenuItem>
                        {courses.map((course) => (
                          <MenuItem key={course._id} value={course._id}>
                            {course.name}
                          </MenuItem>
                        ))}
                      </Select>
                    </FormControl>
                  </Grid>
                  <Grid item xs={12} sm={6} md={3}>
                    <FormControl fullWidth size="small">
                      <InputLabel>Giáo viên</InputLabel>
                      <Select
                        name="teacher"
                        value={filterOptions.teacher}
                        onChange={handleFilterChange}
                        label="Giáo viên"
                      >
                        <MenuItem value="">Tất cả</MenuItem>
                        {teachers.map((teacher) => (
                          <MenuItem key={teacher._id} value={teacher._id}>
                            {teacher.full_name}
                          </MenuItem>
                        ))}
                      </Select>
                    </FormControl>
                  </Grid>
                  <Grid item xs={12} sm={6} md={3}>
                    <TextField
                      fullWidth
                      size="small"
                      label="Học kỳ"
                      name="semester"
                      value={filterOptions.semester}
                      onChange={handleFilterChange}
                    />
                  </Grid>
                </>
              )}
              <Grid item xs={12} sm={6} md={3}>
                <Box display="flex" gap={1}>
                  <Button
                    variant="outlined"
                    color="primary"
                    fullWidth
                    onClick={applyFilters}
                  >
                    Áp dụng
                  </Button>
                  <Button
                    variant="outlined"
                    color="inherit"
                    onClick={clearFilters}
                  >
                    Xóa lọc
                  </Button>
                </Box>
              </Grid>
            </Grid>
          </Box>
        </Collapse>
      </Paper>

      {tabValue === 0 ? renderMainClassesTable() : renderTeachingClassesTable()}

      {/* Dialog forms */}
      {tabValue === 0
        ? renderMainClassFormDialog()
        : renderTeachingClassFormDialog()}

      {/* Delete confirmation dialog */}
      <Dialog
        open={deleteDialogOpen}
        onClose={() => setDeleteDialogOpen(false)}
      >
        <DialogTitle>
          Xác nhận xóa {tabValue === 0 ? "lớp chính" : "lớp giảng dạy"}
        </DialogTitle>
        <DialogContent>
          <DialogContentText>
            Bạn có chắc chắn muốn xóa{" "}
            {tabValue === 0
              ? `lớp chính "${selectedClass?.name}"`
              : `lớp giảng dạy "${selectedClass?.class_name}"`}
            ? Hành động này không thể hoàn tác.
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setDeleteDialogOpen(false)}>Hủy</Button>
          <Button onClick={handleDeleteClass} variant="contained" color="error">
            Xóa
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default ClassesPage;
