import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import axios from "axios";

const API_URL = process.env.REACT_APP_API_URL || "http://localhost:5000/api";

// Thunks cho quản lý cơ sở
export const fetchAllCampuses = createAsyncThunk(
  "facility/fetchAllCampuses",
  async (
    { page = 1, limit = 10, search = "" },
    { getState, rejectWithValue }
  ) => {
    try {
      const { token } = getState().auth;
      const response = await axios.get(
        `${API_URL}/facilities/campuses?page=${page}&limit=${limit}&search=${search}`,
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      return response.data;
    } catch (error) {
      return rejectWithValue(
        error.response?.data?.message || "Không thể lấy danh sách cơ sở"
      );
    }
  }
);

export const createCampus = createAsyncThunk(
  "facility/createCampus",
  async (campusData, { getState, rejectWithValue }) => {
    try {
      const { token } = getState().auth;
      const response = await axios.post(
        `${API_URL}/facilities/campuses`,
        campusData,
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      return response.data;
    } catch (error) {
      return rejectWithValue(
        error.response?.data?.message || "Không thể tạo cơ sở mới"
      );
    }
  }
);

export const updateCampus = createAsyncThunk(
  "facility/updateCampus",
  async ({ campusId, campusData }, { getState, rejectWithValue }) => {
    try {
      const { token } = getState().auth;
      const response = await axios.put(
        `${API_URL}/facilities/campuses/${campusId}`,
        campusData,
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      return response.data;
    } catch (error) {
      return rejectWithValue(
        error.response?.data?.message || "Không thể cập nhật cơ sở"
      );
    }
  }
);

export const deleteCampus = createAsyncThunk(
  "facility/deleteCampus",
  async (campusId, { getState, rejectWithValue }) => {
    try {
      const { token } = getState().auth;
      await axios.delete(`${API_URL}/facilities/campuses/${campusId}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      return campusId;
    } catch (error) {
      return rejectWithValue(
        error.response?.data?.message || "Không thể xóa cơ sở"
      );
    }
  }
);

// Thunks cho quản lý tòa nhà
export const fetchAllBuildings = createAsyncThunk(
  "facility/fetchAllBuildings",
  async (
    { page = 1, limit = 10, search = "" },
    { getState, rejectWithValue }
  ) => {
    try {
      const { token } = getState().auth;
      const response = await axios.get(
        `${API_URL}/facilities/buildings?page=${page}&limit=${limit}&search=${search}`,
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      return response.data;
    } catch (error) {
      return rejectWithValue(
        error.response?.data?.message || "Không thể lấy danh sách tòa nhà"
      );
    }
  }
);

export const createBuilding = createAsyncThunk(
  "facility/createBuilding",
  async (buildingData, { rejectWithValue }) => {
    try {
      // Kiểm tra dữ liệu đầu vào
      if (!buildingData.name || !buildingData.code) {
        return rejectWithValue("Tên và mã tòa nhà là bắt buộc");
      }

      if (
        !buildingData.campus_id ||
        !buildingData.campus_id.match(/^[0-9a-fA-F]{24}$/)
      ) {
        return rejectWithValue(
          "ID cơ sở không hợp lệ hoặc không được cung cấp"
        );
      }

      const response = await axios.post(
        `${API_URL}/facilities/buildings`,
        buildingData
      );
      return response.data;
    } catch (error) {
      console.error("Lỗi khi tạo tòa nhà:", error);
      // Kiểm tra lỗi token
      if (
        error.response?.data?.message?.includes("token") ||
        error.response?.data?.message?.includes("không được phép truy cập") ||
        error.response?.data?.message?.includes("unauthorized") ||
        error.message?.includes("jwt")
      ) {
        return rejectWithValue("Token không hợp lệ hoặc đã hết hạn");
      }
      // Kiểm tra lỗi ObjectId
      if (
        error.response?.data?.message?.includes("ObjectId") ||
        error.response?.status === 400
      ) {
        return rejectWithValue("ID cơ sở không hợp lệ");
      }
      // Kiểm tra lỗi server
      if (error.response?.status === 500) {
        return rejectWithValue("Lỗi server, vui lòng thử lại sau");
      }
      // Lỗi khác
      return rejectWithValue(
        error.response?.data?.message || error.message || "Lỗi khi tạo tòa nhà"
      );
    }
  }
);

