                  // Kết hợp dữ liệu từ API chi tiết sinh viên với dữ liệu cơ bản
                  const detailedInfo = studentDetailResponse.data.data;

                  // Kiểm tra dữ liệu khuôn mặt
                  const hasFaceData = !!(
