                          <TableCell>Thời gian</TableCell>
                          <TableCell>Trạng thái</TableCell>
                          <TableCell>Phòng học</TableCell>
                          <TableCell>Ghi chú</TableCell>
                          <TableCell align="right">Thao tác</TableCell>
                        </TableRow>
                      </TableHead>
                      <TableBody>
                        {attendanceSessions.map((session) => (
                          <TableRow key={session._id}>
                            <TableCell>
                              {new Date(session.date).toLocaleDateString(
                                "vi-VN"
                              )}
                            </TableCell>
                            <TableCell>
                              {session.start_time} - {session.end_time}
                            </TableCell>
                            <TableCell>
                              <Chip
                                label={
                                  session.status === "completed"
                                    ? "Đã hoàn thành"
                                    : session.status === "active"
                                    ? "Đang diễn ra"
                                    : "Chưa bắt đầu"
                                }
                                color={
                                  session.status === "completed"
                                    ? "success"
                                    : session.status === "active"
                                    ? "warning"
                                    : "primary"
                                }
                                size="small"
                                variant="outlined"
                              />
                            </TableCell>
                            <TableCell>
                              {getRoomDetailsString(
                                session.room?._id || session.room
                              )}
                            </TableCell>
                            <TableCell>
                              {session.notes && session.notes.length > 20
                                ? `${session.notes.substring(0, 20)}...`
                                : session.notes || ""}
                            </TableCell>
                            <TableCell align="right">
                              <Tooltip
                                title={
                                  session.notes
                                    ? "Xem/Sửa Ghi chú"
                                    : "Thêm Ghi chú"
                                }
                              >
                                <IconButton
                                  onClick={() => handleOpenNotesDialog(session)}
                                  size="small"
                                  sx={{ mr: 0.5 }}
                                >
                                  <AssignmentIcon fontSize="small" />
                                </IconButton>
                              </Tooltip>
                              <Button
                                variant="contained"
                                color="primary"
                                size="small"
                                onClick={() => handleStartSession(session._id)}
                                startIcon={
                                  session.status === "completed" ? (
                                    <CheckCircle />
                                  ) : (
                                    <PlayArrow />
                                  )
                                }
                              >
                                {session.status === "completed"
                                  ? "Xem"
                                  : "Bắt đầu"}
                              </Button>
                            </TableCell>
                          </TableRow>
                        ))}
                      </TableBody>
                    </Table>
                  </TableContainer>
                ) : (
                  <Alert severity="info">
                    Chưa có buổi điểm danh nào được tạo
                  </Alert>
