            <DialogActions sx={{ p: 2, justifyContent: "space-between" }}>
              <Button
                onClick={handleCloseAddStudentDialog}
                color="inherit"
                variant="outlined"
              >
                Hủy
              </Button>
              <Button
                onClick={handleAddStudents}
                color="primary"
                variant="contained"
                disabled={selectedStudents.length === 0}
              >
                Thêm {selectedStudents.length} sinh viên
              </Button>
            </DialogActions>
          </Dialog>

          {/* Dialog tạo buổi điểm danh */}
          <Dialog
            open={openSessionDialog}
            onClose={handleCloseSessionDialog}
            maxWidth="md"
            fullWidth
          >
            <DialogTitle>
              {sessionFormData._id
                ? "Chỉnh sửa buổi điểm danh"
                : "Tạo buổi điểm danh mới"}
            </DialogTitle>
            <DialogContent dividers>
              <Grid container spacing={2} sx={{ pt: 1 }}>
                <Grid item xs={12} sm={6}>
                  <TextField
                    fullWidth
                    label="Tên buổi học"
                    name="title"
                    value={sessionFormData.title}
                    onChange={handleSessionFormChange}
                    required
                  />
                </Grid>
                <Grid item xs={12} sm={6}>
                  <TextField
                    fullWidth
                    label="Số buổi (thứ tự)"
                    name="session_number"
                    type="number"
                    value={sessionFormData.session_number}
                    onChange={handleSessionFormChange}
                    required
                    InputProps={{
                      inputProps: {
                        min: 1,
                        max: classInfo?.total_sessions || 15,
                      },
                    }}
                    helperText={`Buổi học thứ (1-${
                      classInfo?.total_sessions || "?"
                    })`}
                  />
                </Grid>
                <Grid item xs={12} sm={6}>
                  <TextField
                    fullWidth
                    label="Ngày"
                    name="date"
                    type="date"
                    value={sessionFormData.date}
                    onChange={handleSessionFormChange}
                    required
                    InputLabelProps={{ shrink: true }}
                  />
                </Grid>
                <Grid item xs={12} sm={6}>
                  <FormControl fullWidth>
                    <InputLabel>Phòng học</InputLabel>
                    <Select
                      label="Phòng học"
                      name="room"
                      value={sessionFormData.room}
                      onChange={handleSessionFormChange}
                    >
                      <MenuItem value="">
                        <em>Không chọn phòng học</em>
                      </MenuItem>
                      {rooms.map((room) => (
                        <MenuItem key={room._id} value={room._id}>
                          {room.room_number} - Tòa{" "}
                          {room.building_id?.name || "N/A"}
                        </MenuItem>
                      ))}
                    </Select>
                    <FormHelperText>
                      Chọn phòng học cho buổi điểm danh
                    </FormHelperText>
                  </FormControl>
                </Grid>
                <Grid item xs={12} sm={6}>
                  <TextField
                    fullWidth
                    label="Giờ bắt đầu"
                    name="start_time"
                    type="time"
                    value={sessionFormData.start_time}
                    onChange={handleSessionFormChange}
                    required
                    InputLabelProps={{ shrink: true }}
                  />
                </Grid>
                <Grid item xs={12} sm={6}>
                  <TextField
                    fullWidth
                    label="Giờ kết thúc"
                    name="end_time"
                    type="time"
                    value={sessionFormData.end_time}
                    onChange={handleSessionFormChange}
                    required
                    InputLabelProps={{ shrink: true }}
                  />
                </Grid>
                <Grid item xs={12} sm={6}>
                  <TextField
                    fullWidth
                    label="Tiết bắt đầu"
                    name="start_period"
                    type="number"
                    value={sessionFormData.start_period}
                    onChange={handleSessionFormChange}
                    InputProps={{ inputProps: { min: 1, max: 15 } }}
                  />
                </Grid>
                <Grid item xs={12} sm={6}>
                  <TextField
                    fullWidth
                    label="Tiết kết thúc"
                    name="end_period"
                    type="number"
                    value={sessionFormData.end_period}
                    onChange={handleSessionFormChange}
                    InputProps={{ inputProps: { min: 1, max: 15 } }}
                  />
                </Grid>
                <Grid item xs={12}>
                  <TextField
                    fullWidth
                    label="Ghi chú"
                    name="notes"
                    multiline
                    rows={3}
                    value={sessionFormData.notes}
                    onChange={handleSessionFormChange}
                    placeholder="Nhập ghi chú về buổi học (nếu có)"
                  />
                </Grid>
                <Grid item xs={12}>
                  <Alert severity="info" sx={{ mt: 1 }}>
                    Lưu ý: Thời gian buổi học phải nằm trong khoảng thời gian
                    của học kỳ (
                    {classInfo?.semester_id?.start_date
                      ? new Date(
                          classInfo.semester_id.start_date
                        ).toLocaleDateString("vi-VN")
                      : "?"}{" "}
                    -
                    {classInfo?.semester_id?.end_date
                      ? new Date(
                          classInfo.semester_id.end_date
                        ).toLocaleDateString("vi-VN")
                      : "?"}
                    )
                  </Alert>
                </Grid>
              </Grid>
            </DialogContent>
            <DialogActions sx={{ p: 2, justifyContent: "space-between" }}>
              <Button
                onClick={handleCloseSessionDialog}
                color="inherit"
                variant="outlined"
              >
                Hủy
              </Button>
              <Button
                onClick={
                  sessionFormData._id
                    ? handleUpdateSession
                    : handleCreateSession
                }
                color="primary"
                variant="contained"
              >
                {sessionFormData._id ? "Cập nhật" : "Tạo buổi điểm danh"}
              </Button>
            </DialogActions>
          </Dialog>

          {/* Dialog chỉnh sửa lớp học */}
          <Dialog
            open={openEditClassDialog}
            onClose={handleCloseEditClassDialog}
            maxWidth="md"
            fullWidth
          >
            <DialogTitle>Chỉnh sửa thông tin lớp học</DialogTitle>
            <DialogContent dividers>
              {editClassData && (
                <Grid container spacing={2} sx={{ pt: 1 }}>
                  <Grid item xs={12} sm={6}>
                    <TextField
                      fullWidth
                      label="Tên lớp"
                      name="class_name"
                      value={editClassData.class_name}
                      onChange={handleEditClassChange}
                      required
                    />
                  </Grid>
                  <Grid item xs={12} sm={6}>
                    <TextField
                      fullWidth
                      label="Mã lớp"
                      name="class_code"
                      value={editClassData.class_code}
                      onChange={handleEditClassChange}
                      required
                    />
                  </Grid>
                  <Grid item xs={12} sm={6}>
                    <FormControl fullWidth>
                      <InputLabel>Môn học</InputLabel>
                      <Select
                        name="subject_id"
                        value={editClassData.subject_id}
                        onChange={handleEditClassChange}
                        label="Môn học"
                      >
                        {subjects.map((subject) => (
                          <MenuItem key={subject._id} value={subject._id}>
                            {subject.name} ({subject.code})
                          </MenuItem>
                        ))}
                      </Select>
                    </FormControl>
                  </Grid>
                  <Grid item xs={12} sm={6}>
                    <FormControl fullWidth>
                      <InputLabel>Học kỳ</InputLabel>
                      <Select
                        name="semester_id"
                        value={editClassData.semester_id}
                        onChange={handleEditClassChange}
                        label="Học kỳ"
                      >
                        {semesters.map((semester) => (
                          <MenuItem key={semester._id} value={semester._id}>
                            {semester.name} (
                            {semester.academic_year || semester.year})
                          </MenuItem>
                        ))}
                      </Select>
                    </FormControl>
                  </Grid>
                  <Grid item xs={12} sm={6}>
                    <FormControl fullWidth>
                      <InputLabel>Lớp chính</InputLabel>
                      <Select
                        name="main_class_id"
                        value={editClassData.main_class_id}
                        onChange={handleEditClassChange}
                        label="Lớp chính"
                      >
                        <MenuItem value="">Không chọn lớp chính</MenuItem>
                        {mainClasses.map((cls) => (
                          <MenuItem key={cls._id} value={cls._id}>
                            {cls.name} ({cls.class_code})
                          </MenuItem>
                        ))}
                      </Select>
                    </FormControl>
                  </Grid>
                  <Grid item xs={12} sm={6}>
                    <TextField
                      fullWidth
                      label="Số buổi học"
                      name="total_sessions"
                      type="number"
                      value={editClassData.total_sessions}
                      onChange={handleTotalSessionsChange}
                      InputProps={{ inputProps: { min: 1 } }}
                    />
                  </Grid>
                  <Grid item xs={12} sm={6}>
                    <TextField
                      fullWidth
                      label="Ngày bắt đầu"
                      name="course_start_date"
                      type="date"
                      value={editClassData.course_start_date}
                      onChange={handleStartDateChange}
                      InputLabelProps={{ shrink: true }}
                    />
                  </Grid>
                  <Grid item xs={12} sm={6}>
                    <TextField
                      fullWidth
                      label="Ngày kết thúc (tự động tính)"
                      name="course_end_date"
                      type="date"
                      value={editClassData.course_end_date}
                      disabled
                      InputLabelProps={{ shrink: true }}
                    />
                  </Grid>

                  {/* Phần cấu hình lịch học */}
                  <Grid item xs={12}>
                    <Typography variant="h6" sx={{ mt: 2, mb: 1 }}>
                      Cấu hình lịch học
                    </Typography>
                    <Divider sx={{ mb: 2 }} />

                    {scheduleDays.map((day, index) => (
                      <Paper
                        key={index}
                        sx={{ p: 2, mb: 2, position: "relative" }}
                        variant="outlined"
                      >
                        <Grid container spacing={2}>
                          <Grid item xs={12} sm={3}>
                            <FormControl fullWidth size="small">
                              <InputLabel>Thứ</InputLabel>
                              <Select
                                label="Thứ"
                                value={day.day_of_week}
                                onChange={(e) =>
                                  handleScheduleChange(
                                    index,
                                    "day_of_week",
                                    e.target.value
                                  )
                                }
                              >
                                {[0, 1, 2, 3, 4, 5, 6].map((d) => (
                                  <MenuItem key={d} value={d}>
                                    {getDayOfWeekName(d)}
                                  </MenuItem>
                                ))}
                              </Select>
                            </FormControl>
                          </Grid>
                          <Grid item xs={6} sm={2}>
                            <TextField
                              fullWidth
                              label="Tiết BĐ"
                              type="number"
                              size="small"
                              value={day.start_period}
                              onChange={(e) =>
                                handleScheduleChange(
                                  index,
                                  "start_period",
                                  e.target.value
                                )
                              }
                              InputProps={{ inputProps: { min: 1, max: 15 } }}
                            />
                          </Grid>
                          <Grid item xs={6} sm={2}>
                            <TextField
                              fullWidth
                              label="Tiết KT"
                              type="number"
                              size="small"
                              value={day.end_period}
                              onChange={(e) =>
                                handleScheduleChange(
                                  index,
                                  "end_period",
                                  e.target.value
                                )
                              }
                              InputProps={{
                                inputProps: {
                                  min: day.start_period || 1,
                                  max: 15,
                                },
                              }}
                            />
                          </Grid>
                          <Grid item xs={12} sm={5}>
                            <FormControl fullWidth size="small">
                              <InputLabel>Phòng học</InputLabel>
                              <Select
                                label="Phòng học"
                                value={day.room_id || ""}
                                onChange={(e) =>
                                  handleScheduleChange(
                                    index,
                                    "room_id",
                                    e.target.value
                                  )
                                }
                              >
                                <MenuItem value="">
                                  <em>Chọn phòng học</em>
                                </MenuItem>
                                {rooms.map((room) => (
                                  <MenuItem key={room._id} value={room._id}>
                                    {room.room_number} -{" "}
                                    {room.building_id?.name || "N/A"}
                                  </MenuItem>
                                ))}
                              </Select>
                            </FormControl>
                          </Grid>
                        </Grid>

                        {scheduleDays.length > 1 && (
                          <IconButton
                            color="error"
                            size="small"
                            onClick={() => removeScheduleDay(index)}
                            sx={{ position: "absolute", top: 8, right: 8 }}
                          >
                            <Delete fontSize="small" />
                          </IconButton>
                        )}
                      </Paper>
                    ))}

                    <Button
                      variant="outlined"
                      startIcon={<Add />}
                      onClick={addScheduleDay}
                      sx={{ mt: 1 }}
                    >
                      Thêm buổi học
                    </Button>
                  </Grid>
                </Grid>
              )}
            </DialogContent>
            <DialogActions
              sx={{ p: 2, justifyContent: "space-between", bgcolor: "grey.50" }}
            >
              <Button
                onClick={handleCloseEditClassDialog}
                color="inherit"
                variant="outlined"
              >
                Hủy
              </Button>
              <Button
                onClick={handleUpdateClass}
                color="primary"
                variant="contained"
                disabled={isSubmittingEdit || !editClassData}
              >
                Cập nhật lớp học
              </Button>
            </DialogActions>
          </Dialog>

          {/* Dialog Ghi chú */}
          <Dialog
            open={openNotesDialog}
            onClose={handleCloseNotesDialog}
            maxWidth="sm"
            fullWidth
          >
            <DialogTitle sx={{ pb: 1 }}>
              {currentSessionForNotes?.notes
                ? "Xem/Sửa ghi chú"
                : "Thêm ghi chú"}
            </DialogTitle>
            <DialogContent dividers sx={{ pt: "16px !important" }}>
              <TextField
                fullWidth
                multiline
                rows={5}
                label="Nội dung ghi chú"
                value={notesText}
                onChange={(e) => setNotesText(e.target.value)}
                placeholder="Nhập nội dung ghi chú cho buổi học..."
                variant="outlined"
              />
            </DialogContent>
            <DialogActions sx={{ p: 2, justifyContent: "space-between" }}>
              <Button
                onClick={handleCloseNotesDialog}
                color="inherit"
                variant="outlined"
              >
                Hủy
              </Button>
              <Button
                onClick={handleSaveNotes}
                color="primary"
                variant="contained"
                disabled={isSavingNotes}
              >
                {isSavingNotes ? <CircularProgress size={20} /> : "Lưu ghi chú"}
              </Button>
            </DialogActions>
          </Dialog>
        </>
      )}
    </Box>
  );
};

export default TeacherClassDetailPage;
