import React, { useEffect } from "react";
import { Routes, Route, Navigate } from "react-router-dom";
import { useSelector } from "react-redux";

// Pages
import LoginPage from "./pages/LoginPage";
import RegisterPage from "./pages/RegisterPage";
import RoleSelectionPage from "./pages/RoleSelectionPage";
import DashboardPage from "./pages/DashboardPage";
import FaceRegistrationPage from "./pages/FaceRegistrationPage";

// Student Pages
import StudentClassesPage from "./pages/student/ClassesPage";
import StudentAttendancePage from "./pages/student/AttendancePage";

// Layouts
import MainLayout from "./layouts/MainLayout";
import MinimalLayout from "./layouts/MinimalLayout";

// Auth check
import ProtectedRoute from "./components/ProtectedRoute";

const App = () => {
  const { isAuthenticated, user } = useSelector((state) => state.auth);

  // Hàm kiểm tra vai trò
  const hasRole = (role) => {
    if (!user) return false;
    return user.role === role;
  };

  return (
    <Routes>
      {/* Public Routes */}
      <Route path="/" element={<MinimalLayout />}>
        <Route index element={<Navigate to="/login" replace />} />
        <Route path="login" element={<LoginPage />} />
        <Route path="register" element={<RegisterPage />} />
        <Route path="role-selection" element={<RoleSelectionPage />} />
        {/* Routes cho đăng nhập Google */}
        <Route path="login/success" element={<LoginPage />} />
        <Route path="login/error" element={<LoginPage />} />
      </Route>

      {/* Protected Routes */}
      <Route
        path="/"
        element={
          <ProtectedRoute>
            <MainLayout />
          </ProtectedRoute>
        }
      >
        <Route path="dashboard" element={<DashboardPage />} />
        <Route path="face-registration" element={<FaceRegistrationPage />} />

        {/* Student Routes */}
        <Route path="student">
          <Route
            path="classes"
            element={
              <ProtectedRoute roles={["student"]}>
                <StudentClassesPage />
              </ProtectedRoute>
            }
          />
          <Route
            path="classes/:classId"
            element={
              <ProtectedRoute roles={["student"]}>
                <StudentAttendancePage />
              </ProtectedRoute>
            }
          />
        </Route>
      </Route>

      {/* Not Found Route */}
      <Route path="*" element={<Navigate to="/dashboard" replace />} />
    </Routes>
  );
};

export default App;
