import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { useSelector } from "react-redux";
import { useSnackbar } from "notistack";
import axios from "axios";
import {
  Box,
  Typography,
  Card,
  CardContent,
  Grid,
  Paper,
  Button,
  TextField,
  CircularProgress,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  TablePagination,
  InputAdornment,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Chip,
  Divider,
  Stack,
  LinearProgress,
  Tooltip,
  IconButton,
  CardActions,
  CardMedia,
  CardHeader,
  Avatar,
} from "@mui/material";
import {
  Search,
  School,
  CalendarToday,
  AccessTime,
  VerifiedUser,
  Warning,
  Visibility,
  Person,
  Face,
  Book,
  Class,
  Info,
  BarChart,
} from "@mui/icons-material";

// Import studentService
import studentService from "../../services/studentService";

const API_URL = process.env.REACT_APP_API_URL || "http://localhost:5000/api";

const StudentClassesPage = () => {
  const navigate = useNavigate();
  const { enqueueSnackbar } = useSnackbar();
  const { user, token } = useSelector((state) => state.auth);

  // State cho dữ liệu
  const [classes, setClasses] = useState([]);
  const [semesters, setSemesters] = useState([]);
  const [attendanceStats, setAttendanceStats] = useState({});
  const [isLoading, setIsLoading] = useState(true);
  const [totalClasses, setTotalClasses] = useState(0);

  // State cho tìm kiếm và phân trang
  const [search, setSearch] = useState("");
  const [semester, setSemester] = useState("");
  const [academicYear, setAcademicYear] = useState("");
  const [page, setPage] = useState(0);
  const [rowsPerPage, setRowsPerPage] = useState(8);

  // State cho thông tin đăng ký khuôn mặt
  const [faceRegistrationStatus, setFaceRegistrationStatus] = useState(false);

  // Load dữ liệu ban đầu
  useEffect(() => {
    const fetchInitialData = async () => {
      try {
        setIsLoading(true);

        // Kiểm tra trạng thái đăng ký khuôn mặt
        const userResponse = await axios.get(`${API_URL}/auth/me`, {
          headers: { Authorization: `Bearer ${token}` },
        });

        setFaceRegistrationStatus(
          userResponse.data.data.faceFeatures?.descriptors?.length > 0 || false
        );

        // Load các học kỳ
        const semestersResponse = await axios.get(`${API_URL}/semesters`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setSemesters(semestersResponse.data.data || []);

        // Load lớp học theo mặc định
        await fetchClasses();
      } catch (error) {
        console.error("Lỗi khi tải dữ liệu:", error);
        enqueueSnackbar("Lỗi khi tải dữ liệu", { variant: "error" });
      } finally {
        setIsLoading(false);
      }
    };

    fetchInitialData();
  }, [token, enqueueSnackbar, user._id]);

  // Tải danh sách lớp học sử dụng service
  const fetchClasses = async () => {
    try {
      setIsLoading(true);

      const options = {
        page: page + 1,
        limit: rowsPerPage,
        search,
        semester,
        academicYear,
      };

      const response = await studentService.getStudentClasses(
        user._id,
        token,
        options
      );

      if (!response.success) {
        enqueueSnackbar(response.error, { variant: "error" });
        return;
      }

      const classesData = response.data;
      setClasses(classesData);
      setTotalClasses(response.total);

      // Lấy thống kê điểm danh cho từng lớp
      if (classesData.length > 0) {
        const statsPromises = classesData.map((classItem) =>
          studentService.getAttendanceStats(user._id, classItem._id, token)
        );

        const statsResults = await Promise.all(statsPromises);

        const statsMap = {};
        statsResults.forEach((result, index) => {
          if (result.success && classesData[index]) {
            statsMap[classesData[index]._id] = result.data.stats;
          }
        });

        setAttendanceStats(statsMap);
      }
    } catch (error) {
      console.error("Lỗi khi tải danh sách lớp học:", error);
      enqueueSnackbar("Lỗi khi tải danh sách lớp học", { variant: "error" });
    } finally {
      setIsLoading(false);
    }
  };

  // Thực hiện tìm kiếm
  const handleSearch = () => {
    setPage(0);
    fetchClasses();
  };

  // Thay đổi trang
  const handleChangePage = (event, newPage) => {
    setPage(newPage);
    fetchClasses();
  };

  // Thay đổi số hàng trên trang
  const handleChangeRowsPerPage = (event) => {
    setRowsPerPage(parseInt(event.target.value, 10));
    setPage(0);
    fetchClasses();
  };

  // Xử lý phím Enter trong ô tìm kiếm
  const handleSearchKeyDown = (e) => {
    if (e.key === "Enter") {
      handleSearch();
    }
  };

  // Xử lý thay đổi bộ lọc
  const handleFilterChange = () => {
    setPage(0);
    fetchClasses();
  };

  // Xem chi tiết điểm danh của lớp học
  const handleViewAttendance = (classId) => {
    navigate(`/student/attendance/${classId}`);
  };

  // Đăng ký khuôn mặt
  const handleRegisterFace = () => {
    navigate("/register-face");
  };

  // Lấy danh sách năm học từ danh sách học kỳ
  const getAcademicYears = () => {
    const years = new Set();
    semesters.forEach((semester) => {
      if (semester.academic_year) {
        years.add(semester.academic_year);
      }
    });
    return Array.from(years).sort().reverse();
  };

  // Hiển thị trạng thái lớp học
  const renderClassStatus = (classItem) => {
    const currentDate = new Date();
    const startDate = classItem.course_start_date
      ? new Date(classItem.course_start_date)
      : null;
    const endDate = classItem.course_end_date
      ? new Date(classItem.course_end_date)
      : null;

    if (!startDate || !endDate) {
      return <Chip label="Không xác định" color="default" size="small" />;
    }

    if (currentDate < startDate) {
      return <Chip label="Chưa bắt đầu" color="info" size="small" />;
    } else if (currentDate > endDate) {
      return <Chip label="Đã kết thúc" color="secondary" size="small" />;
    } else {
      return <Chip label="Đang học" color="success" size="small" />;
    }
  };

  // Render Card cho một lớp học
  const renderClassCard = (classItem) => {
    const stats = attendanceStats[classItem._id] || {
      total: 0,
      present: 0,
      absent: 0,
      late: 0,
      attendanceRate: 0,
      totalPlannedSessions: classItem.total_sessions || 0,
      message: "",
    };

    // Sử dụng service để xác định trạng thái điểm danh
    const attendanceStatus = studentService.getAttendanceStatus(stats);

    const attendanceScore =
      stats.total > 0
        ? studentService.calculateAttendanceScore(stats.absent)
        : "N/A";

    return (
      <Card
        elevation={2}
        sx={{
          height: "100%",
          display: "flex",
          flexDirection: "column",
          transition: "transform 0.2s, box-shadow 0.2s",
          "&:hover": {
            transform: "translateY(-5px)",
            boxShadow: 6,
          },
        }}
      >
        <CardHeader
          avatar={
            <Avatar sx={{ bgcolor: "primary.main" }}>
              <School />
            </Avatar>
          }
          title={classItem.class_name}
          subheader={`${classItem.subject_id?.name || "Không có thông tin"} (${
            classItem.subject_id?.code || "N/A"
          })`}
          action={renderClassStatus(classItem)}
        />

        <Divider />

        <CardContent sx={{ flexGrow: 1 }}>
          <Stack spacing={1.5}>
            <Box display="flex" alignItems="center">
              <Book sx={{ mr: 1, fontSize: 18, color: "primary.main" }} />
              <Typography variant="body2">
                {classItem.semester_id?.name || "Không có thông tin học kỳ"}
              </Typography>
            </Box>

            <Box display="flex" alignItems="center">
              <Person sx={{ mr: 1, fontSize: 18, color: "primary.main" }} />
              <Typography variant="body2">
                GV: {classItem.teacher_id?.full_name || "Không có thông tin"}
              </Typography>
            </Box>

            <Divider />
            <Typography variant="subtitle2" gutterBottom>
              Thông tin điểm danh:
            </Typography>

            <Stack spacing={1}>
              <Box>
                <Typography
                  variant="body2"
                  display="flex"
                  justifyContent="space-between"
                >
                  <span>Buổi đã học:</span>
                  <span>
                    {stats.total} /{" "}
                    {stats.totalPlannedSessions ||
                      classItem.total_sessions ||
                      "?"}
                  </span>
                </Typography>
              </Box>

              {stats.message ? (
                <Box sx={{ py: 1 }}>
                  <Typography
                    variant="body2"
                    color="text.secondary"
                    fontStyle="italic"
                  >
                    {stats.message}
                  </Typography>
                </Box>
              ) : stats.total > 0 ? (
                <>
                  <Box>
                    <Typography
                      variant="body2"
                      display="flex"
                      justifyContent="space-between"
                    >
                      <span>Tham gia:</span>
                      <span>
                        {stats.present} buổi (
                        {Math.round(stats.presentPercentage || 0)}%)
                      </span>
                    </Typography>
                  </Box>

                  <Box>
                    <Typography
                      variant="body2"
                      display="flex"
                      justifyContent="space-between"
                      color={stats.absent > 0 ? "error.main" : "text.secondary"}
                    >
                      <span>Vắng mặt:</span>
                      <span>{stats.absent} buổi</span>
                    </Typography>
                  </Box>

                  <Box>
                    <Typography variant="body2" gutterBottom>
                      Tỷ lệ tham gia:
                    </Typography>
                    <LinearProgress
                      variant="determinate"
                      value={stats.attendanceRate}
                      color={attendanceStatus.status}
                      sx={{ height: 8, borderRadius: 1 }}
                    />
                    <Box display="flex" justifyContent="space-between" mt={0.5}>
                      <Typography variant="caption" color="text.secondary">
                        {attendanceStatus.message}
                      </Typography>
                      <Typography variant="caption" fontWeight="bold">
                        {Math.round(stats.attendanceRate)}%
                      </Typography>
                    </Box>
                  </Box>
                </>
              ) : (
                <Box sx={{ py: 1 }}>
                  <Typography
                    variant="body2"
                    color="text.secondary"
                    fontStyle="italic"
                  >
                    Chưa có dữ liệu điểm danh
                  </Typography>
                </Box>
              )}
            </Stack>
          </Stack>
        </CardContent>

        <CardActions>
          <Box display="flex" justifyContent="space-between" width="100%">
            <Button
              size="small"
              color="primary"
              startIcon={<Visibility />}
              onClick={() => handleViewAttendance(classItem._id)}
            >
              Xem điểm danh
            </Button>

            {stats.total > 0 && (
              <Chip
                label={`Điểm: ${attendanceScore}`}
                color={
                  attendanceScore === "N/A"
                    ? "default"
                    : attendanceScore >= 8
                    ? "success"
                    : attendanceScore >= 5
                    ? "warning"
                    : "error"
                }
                size="small"
              />
            )}
          </Box>
        </CardActions>
      </Card>
    );
  };

  return (
    <Box sx={{ p: 3 }}>
      {/* Tiêu đề trang */}
      <Box sx={{ mb: 3 }}>
        <Typography variant="h4" gutterBottom>
          Danh sách lớp học
        </Typography>
        <Divider />
      </Box>

      {/* Phần cảnh báo nếu chưa đăng ký khuôn mặt */}
      {!faceRegistrationStatus && (
        <Paper
          sx={{
            p: 2,
            bgcolor: "warning.light",
            mb: 3,
            display: "flex",
            alignItems: "center",
            justifyContent: "space-between",
          }}
        >
          <Box sx={{ display: "flex", alignItems: "center" }}>
            <Warning color="warning" sx={{ mr: 2 }} />
            <Typography variant="body1">
              Bạn chưa đăng ký khuôn mặt cho việc điểm danh tự động. Hãy đăng ký
              để có thể sử dụng đầy đủ tính năng của hệ thống.
            </Typography>
          </Box>
          <Button
            variant="contained"
            color="warning"
            startIcon={<Face />}
            onClick={handleRegisterFace}
          >
            Đăng ký ngay
          </Button>
        </Paper>
      )}

      {/* Bộ lọc tìm kiếm */}
      <Paper sx={{ p: 2, mb: 3 }}>
        <Grid container spacing={2} alignItems="center">
          <Grid item xs={12} sm={6} md={3}>
            <TextField
              label="Tìm kiếm"
              variant="outlined"
              size="small"
              fullWidth
              value={search}
              onChange={(e) => setSearch(e.target.value)}
              onKeyDown={handleSearchKeyDown}
              InputProps={{
                startAdornment: (
                  <InputAdornment position="start">
                    <Search />
                  </InputAdornment>
                ),
              }}
            />
          </Grid>
          <Grid item xs={12} sm={6} md={3}>
            <FormControl variant="outlined" size="small" fullWidth>
              <InputLabel>Học kỳ</InputLabel>
              <Select
                value={semester}
                onChange={(e) => {
                  setSemester(e.target.value);
                  handleFilterChange();
                }}
                label="Học kỳ"
              >
                <MenuItem value="">Tất cả</MenuItem>
                {semesters.map((sem) => (
                  <MenuItem key={sem._id} value={sem._id}>
                    {sem.name} - {sem.year}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
          </Grid>
          <Grid item xs={12} sm={6} md={3}>
            <FormControl variant="outlined" size="small" fullWidth>
              <InputLabel>Năm học</InputLabel>
              <Select
                value={academicYear}
                onChange={(e) => {
                  setAcademicYear(e.target.value);
                  handleFilterChange();
                }}
                label="Năm học"
              >
                <MenuItem value="">Tất cả</MenuItem>
                {getAcademicYears().map((year) => (
                  <MenuItem key={year} value={year}>
                    {year}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
          </Grid>
          <Grid item xs={12} sm={6} md={3}>
            <Button
              variant="contained"
              color="primary"
              fullWidth
              onClick={handleSearch}
            >
              Tìm kiếm
            </Button>
          </Grid>
        </Grid>
      </Paper>

      {/* Hiển thị danh sách lớp học */}
      {isLoading ? (
        <Box sx={{ display: "flex", justifyContent: "center", py: 4 }}>
          <CircularProgress />
        </Box>
      ) : classes.length === 0 ? (
        <Paper sx={{ p: 3, textAlign: "center" }}>
          <School sx={{ fontSize: 60, color: "text.secondary", mb: 2 }} />
          <Typography variant="h6" gutterBottom>
            Không tìm thấy lớp học nào
          </Typography>
          <Typography variant="body2" color="textSecondary">
            Thay đổi tiêu chí tìm kiếm hoặc liên hệ với giáo vụ để biết thêm
            thông tin
          </Typography>
        </Paper>
      ) : (
        <>
          <Grid container spacing={3}>
            {classes.map((classItem) => (
              <Grid item xs={12} sm={6} md={4} lg={3} key={classItem._id}>
                {renderClassCard(classItem)}
              </Grid>
            ))}
          </Grid>
        </>
