                            <ListItem
                              key={student._id}
                              sx={{
                                mb: 1,
                                border: "1px solid #e0e0e0",
                                borderRadius: 1,
                              }}
                            >
                              <ListItemAvatar>
                                <Avatar src={student.avatar_url}>
                                  {student.full_name.charAt(0).toUpperCase()}
                                </Avatar>
                              </ListItemAvatar>
                              <ListItemText
                                primary={
                                  <Box
                                    sx={{
                                      display: "flex",
                                      alignItems: "center",
                                    }}
                                  >
                                    {student.full_name}
                                    <Chip
                                      label="Chờ duyệt"
                                      color="warning"
                                      size="small"
                                      sx={{ ml: 1 }}
                                    />
                                  </Box>
                                }
                                secondary={
                                  <>
                                    <Typography variant="body2">
                                      Email: {student.email}
                                    </Typography>
                                    <Typography variant="body2">
                                      MSSV:{" "}
                                      {student.school_info?.student_id ||
                                        "Chưa có mã SV"}
                                    </Typography>
                                    <Typography variant="body2">
                                      Ngày đăng ký:{" "}
                                      {new Date(
                                        student.created_at
                                      ).toLocaleDateString("vi-VN")}
                                    </Typography>
                                  </>
                                }
                              />
                              <ListItemSecondaryAction>
                                <Tooltip title="Phê duyệt">
                                  <IconButton
                                    edge="end"
                                    color="success"
                                    onClick={() =>
                                      handleApproveStudent(student._id)
                                    }
                                  >
                                    <Check />
                                  </IconButton>
                                </Tooltip>
                                <Tooltip title="Từ chối">
                                  <IconButton
                                    edge="end"
                                    color="error"
                                    onClick={() =>
                                      openRejectDialog(student._id)
                                    }
                                    sx={{ ml: 0.5 }} // Reduced margin
                                  >
                                    <Close />
                                  </IconButton>
                                </Tooltip>
                              </ListItemSecondaryAction>
                            </ListItem>
                          )
                        )}
                      </List>
                    )}
                  </>
                )}

                {studentApprovalDialog.tabValue === 1 && ( // Approved Students Tab
                  <>
                    {studentApprovalDialog.approvedStudents.length === 0 ? (
                      <Alert severity="info">
                        Chưa có sinh viên nào trong lớp
                      </Alert>
                    ) : (
                      <TableContainer component={Paper} sx={{ mt: 1 }}>
                        <Table stickyHeader size="small">
                          <TableHead>
                            <TableRow>
                              <TableCell sx={{ width: "5%" }}>STT</TableCell>
                              <TableCell sx={{ width: "30%" }}>
                                Họ tên
                              </TableCell>
                              <TableCell sx={{ width: "20%" }}>MSSV</TableCell>
                              <TableCell sx={{ width: "30%" }}>Email</TableCell>
                              <TableCell align="center" sx={{ width: "15%" }}>
                                Hành động
                              </TableCell>
                            </TableRow>
                          </TableHead>
                          <TableBody>
                            {studentApprovalDialog.approvedStudents.map(
                              (student, index) => (
                                <TableRow key={student._id} hover>
                                  <TableCell>{index + 1}</TableCell>
                                  <TableCell>{student.full_name}</TableCell>
                                  <TableCell>
                                    {student.school_info?.student_id || "N/A"}
                                  </TableCell>
                                  <TableCell>{student.email}</TableCell>
                                  <TableCell align="center">
                                    <Tooltip title="Xóa sinh viên này khỏi lớp chính">
                                      <IconButton
                                        size="small"
                                        color="error"
                                        onClick={() =>
                                          handleOpenConfirmDeleteMainClassStudentDialog(
                                            student
                                          )
                                        }
                                      >
                                        <DeleteForever fontSize="small" />
                                      </IconButton>
                                    </Tooltip>
                                  </TableCell>
                                </TableRow>
                              )
                            )}
                          </TableBody>
                        </Table>
                      </TableContainer>
                    )}
                  </>
                )}
              </Box>
            </Box>
          )}
        </DialogContent>
        <DialogActions>
          <Button onClick={closeStudentApproval} color="primary">
            Đóng
          </Button>
        </DialogActions>
      </Dialog>

      {/* Confirmation Dialog for deleting student from main class */}
      <Dialog
        open={studentApprovalDialog.confirmDeleteMainClassStudentDialogOpen}
        onClose={handleCloseConfirmDeleteMainClassStudentDialog}
        maxWidth="xs"
      >
        <DialogTitle>Xác nhận xóa sinh viên khỏi lớp chính</DialogTitle>
        <DialogContent>
          <DialogContentText>
            {`Bạn có chắc chắn muốn xóa sinh viên `}
            <strong>
              {studentApprovalDialog.studentToDeleteFromMainClass?.full_name}
            </strong>
            {` (MSSV: `}
            <strong>
              {studentApprovalDialog.studentToDeleteFromMainClass?.school_info
                ?.student_id || "N/A"}
            </strong>
            {`) khỏi lớp chính này? Sinh viên sẽ bị gỡ khỏi lớp và các thông báo liên quan đến việc duyệt vào lớp này có thể bị xóa.`}
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button
            onClick={handleCloseConfirmDeleteMainClassStudentDialog}
            color="inherit"
          >
            Hủy
          </Button>
          <Button
            onClick={handleDeleteStudentFromMainClass}
            color="error"
            autoFocus
          >
            Xóa
          </Button>
        </DialogActions>
      </Dialog>

      {/* Dialog từ chối sinh viên */}
      <Dialog
        open={studentApprovalDialog.rejectDialog.open}
        onClose={closeRejectDialog}
        maxWidth="xs"
      >
        <DialogTitle>Từ chối sinh viên</DialogTitle>
        <DialogContent>
          <DialogContentText>
            {`Bạn có chắc chắn muốn từ chối sinh viên `}
            <strong>{studentApprovalDialog.rejectDialog.studentId}</strong>
            {` không?`}
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button onClick={closeRejectDialog} color="primary">
            Hủy
          </Button>
          <Button onClick={handleRejectStudent} color="error" autoFocus>
            Xóa
          </Button>
        </DialogActions>
      </Dialog>
    </>
  );

  const closeDeleteDialog = () => {
    setDeleteDialog({ open: false, item: null, type: "teaching" }); // Default back to teaching or handle accordingly
  };

  // Mở dialog xác nhận xóa (chung cho cả main và teaching class)
  const openDeleteConfirmationDialog = (item, type) => {
    setDeleteDialog({ open: true, item: item, type: type });
  };

  const handleDelete = async () => {
    try {
      const endpoint = tabValue === 0 ? "classes/main" : "classes/teaching";
      const classId = selectedClass._id;

      setIsLoading(true); // Bắt đầu loading
      if (deleteDialog.type === "teaching") {
        await axios.delete(`${API_URL}/${endpoint}/${classId}`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        enqueueSnackbar("Xóa lớp giảng dạy thành công!", {
          variant: "success",
        });
      } else if (deleteDialog.type === "main") {
        await axios.delete(`${API_URL}/${endpoint}/${classId}`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        enqueueSnackbar("Xóa lớp chính và các dữ liệu liên quan thành công!", {
          variant: "success",
        });
      }
      closeDeleteDialog();
      loadClasses();
      loadStats(); // Cập nhật lại thống kê sau khi thao tác
    } catch (error) {
      console.error("Lỗi khi xóa lớp:", error);
      enqueueSnackbar("Lỗi khi xóa lớp", { variant: "error" });
      setIsLoading(false);
    }
  };

  return (
    <Box>
      <Typography variant="h5" gutterBottom>
        Quản lý lớp học
      </Typography>

      {/* Thống kê */}
      {renderStatCards()}

      <Paper sx={{ mb: 3 }}>
        <Tabs
          value={tabValue}
          onChange={handleTabChange}
          indicatorColor="primary"
          textColor="primary"
          variant="fullWidth"
        >
          <Tab label="Lớp chính" icon={<School />} iconPosition="start" />
          <Tab label="Lớp giảng dạy" icon={<People />} iconPosition="start" />
        </Tabs>
      </Paper>

      <Paper sx={{ p: 2, mb: 3 }}>
        <Box
          display="flex"
          flexDirection={{ xs: "column", sm: "row" }}
          alignItems={{ sm: "center" }}
          justifyContent="space-between"
          gap={2}
          mb={2}
        >
          <Box display="flex" alignItems="center" gap={1} flexGrow={1}>
            <TextField
              label={`Tìm kiếm ${
                tabValue === 0 ? "lớp chính" : "lớp giảng dạy"
              }`}
              variant="outlined"
              size="small"
              fullWidth
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              onKeyPress={(e) => e.key === "Enter" && handleSearch()}
              InputProps={{
                startAdornment: (
                  <InputAdornment position="start">
                    <Search />
                  </InputAdornment>
                ),
              }}
            />
            <Button
              variant="contained"
              color="primary"
              onClick={handleSearch}
              startIcon={<Search />}
            >
              Tìm
            </Button>
          </Box>

          <Box display="flex" gap={2}>
            <Button
              variant="outlined"
              startIcon={<Refresh />}
              onClick={loadClasses}
            >
              Làm mới
            </Button>
            <Button
              variant="contained"
              startIcon={<Add />}
              onClick={() => openDialog("create")}
            >
              Thêm {tabValue === 0 ? "lớp chính" : "lớp giảng dạy"}
            </Button>
          </Box>
        </Box>

        {/* Bộ lọc nâng cao */}
        <Collapse in={true}>
          <Box
            sx={{ p: 1, border: "1px solid #e0e0e0", borderRadius: 1, mt: 1 }}
          >
            <Typography variant="subtitle2" gutterBottom>
              Bộ lọc nâng cao
            </Typography>
            <Grid container spacing={2}>
              {tabValue === 0 ? (
                // Bộ lọc cho lớp chính
                <>
                  <Grid item xs={12} sm={6} md={3}>
                    <FormControl fullWidth size="small">
                      <InputLabel>Khoa/Bộ môn</InputLabel>
                      <Select
                        name="department"
                        value={filterOptions.department}
                        onChange={handleFilterChange}
                        label="Khoa/Bộ môn"
                      >
                        <MenuItem value="">Tất cả</MenuItem>
                        {departments.map((dept) => (
                          <MenuItem key={dept._id} value={dept._id}>
                            {dept.name}
                          </MenuItem>
                        ))}
                      </Select>
                    </FormControl>
                  </Grid>
                  <Grid item xs={12} sm={6} md={3}>
                    <FormControl fullWidth size="small">
                      <InputLabel>Giáo viên chủ nhiệm</InputLabel>
                      <Select
                        name="teacher"
                        value={filterOptions.teacher}
                        onChange={handleFilterChange}
                        label="Giáo viên chủ nhiệm"
                      >
                        <MenuItem value="">Tất cả</MenuItem>
                        {teachers.map((teacher) => (
                          <MenuItem key={teacher._id} value={teacher._id}>
                            {teacher.full_name}
                          </MenuItem>
                        ))}
                      </Select>
                    </FormControl>
                  </Grid>
                </>
              ) : (
                // Bộ lọc cho lớp giảng dạy
                <>
                  <Grid item xs={12} sm={6} md={3}>
                    <FormControl fullWidth size="small">
                      <InputLabel>Môn học</InputLabel>
                      <Select
                        name="course"
                        value={filterOptions.course}
                        onChange={handleFilterChange}
                        label="Môn học"
                      >
                        <MenuItem value="">Tất cả</MenuItem>
                        {subjects.map((subject) => (
                          <MenuItem key={subject._id} value={subject._id}>
                            {subject.name}
                          </MenuItem>
                        ))}
                      </Select>
                    </FormControl>
                  </Grid>
                  <Grid item xs={12} sm={6} md={3}>
                    <FormControl fullWidth size="small">
                      <InputLabel>Giáo viên</InputLabel>
                      <Select
                        name="teacher"
                        value={filterOptions.teacher}
                        onChange={handleFilterChange}
                        label="Giáo viên"
                      >
                        <MenuItem value="">Tất cả</MenuItem>
                        {teachers.map((teacher) => (
                          <MenuItem key={teacher._id} value={teacher._id}>
                            {teacher.full_name}
                          </MenuItem>
                        ))}
                      </Select>
                    </FormControl>
                  </Grid>
                  <Grid item xs={12} sm={6} md={3}>
                    <FormControl fullWidth size="small">
                      <InputLabel>Học kỳ</InputLabel>
                      <Select
                        name="semester"
                        value={filterOptions.semester}
                        onChange={handleFilterChange}
                        label="Học kỳ"
                      >
                        <MenuItem value="">Tất cả</MenuItem>
                        {semesters.map((semester) => (
                          <MenuItem key={semester._id} value={semester._id}>
                            {semester.name} ({semester.year})
                          </MenuItem>
                        ))}
                      </Select>
                    </FormControl>
                  </Grid>
                </>
              )}
            </Grid>
          </Box>
        </Collapse>
      </Paper>

      {/* Hiển thị kết quả hoặc trạng thái trống */}
      {isLoading ? (
        <Box sx={{ display: "flex", justifyContent: "center", py: 8 }}>
          <CircularProgress />
        </Box>
      ) : classes.length === 0 &&
        searchTerm === "" &&
        !filterOptions.department &&
        !filterOptions.teacher &&
        !filterOptions.course &&
        !filterOptions.semester ? (
        renderEmptyState()
      ) : tabValue === 0 ? (
        renderMainClassesTable()
      ) : (
        renderTeachingClassesTable()
      )}

      {/* Dialog forms */}
      {tabValue === 0
        ? renderMainClassFormDialog()
        : renderTeachingClassFormDialog()}

      {/* Dialog xem sinh viên lớp giảng dạy */}
      {renderViewTeachingClassStudentsDialog()}

      {/* Dialog xem sinh viên lớp chính */}
      {renderViewMainClassStudentsDialog()}

      {/* Dialog xác nhận xóa */}
      <Dialog
        open={deleteDialog.open}
        onClose={closeDeleteDialog}
        aria-labelledby="delete-dialog-title"
      >
        <DialogTitle id="delete-dialog-title">
          {deleteDialog.type === "main"
            ? "Xác nhận xóa Lớp Chính"
            : "Xác nhận xóa Lớp Giảng dạy"}
        </DialogTitle>
        <DialogContent>
          <DialogContentText>
            Bạn có chắc chắn muốn xóa mục này không?
            {deleteDialog.type === "main" && (
              <>
                <br />
                <strong>Lưu ý:</strong> Hành động này sẽ xóa Lớp Chính và tất cả
                dữ liệu liên quan bao gồm việc gỡ bỏ sinh viên khỏi lớp chính
                này, cập nhật các lớp giảng dạy liên kết và xóa các thông báo
                liên quan. Hành động này không thể hoàn tác.
              </>
            )}
            {deleteDialog.type === "teaching" && (
              <>
                <br />
                <strong>Lưu ý:</strong> Hành động này sẽ xóa Lớp Giảng dạy và
                tất cả dữ liệu liên quan như buổi học, điểm danh, điểm số của
                sinh viên trong lớp này. Hành động này không thể hoàn tác.
              </>
            )}
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button onClick={closeDeleteDialog} color="inherit">
            Hủy
          </Button>
          <Button onClick={handleDelete} color="error" autoFocus>
            Xóa
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default ClassesPage;
