export const fetchAllBuildings = createAsyncThunk(
  "facility/fetchAllBuildings",
  async (searchParams = {}, { rejectWithValue, getState }) => {
    try {
      // Tạo tham số truy vấn từ searchParams
      const queryParams = new URLSearchParams();
      if (searchParams.name) queryParams.append("name", searchParams.name);
      if (searchParams.code) queryParams.append("code", searchParams.code);
      if (searchParams.campus_id)
        queryParams.append("campus_id", searchParams.campus_id);
      if (searchParams.status)
        queryParams.append("status", searchParams.status);
      if (searchParams.page) queryParams.append("page", searchParams.page);
      if (searchParams.limit) queryParams.append("limit", searchParams.limit);

      const queryString = queryParams.toString();
      const endpoint = `/facilities/buildings${
        queryString ? `?${queryString}` : ""
      }`;

      // Sử dụng axiosInstance thay vì axios
      const response = await axiosInstance.get(endpoint);

      return response.data;
    } catch (error) {
      // Xử lý các loại lỗi
      if (error.response) {
        const { status, data } = error.response;
        switch (status) {
          case 401:
            return rejectWithValue(
              "Không được phép truy cập, token không hợp lệ"
            );
          case 403:
            return rejectWithValue("Bạn không có quyền truy cập dữ liệu này");
          case 404:
            return rejectWithValue("Không tìm thấy dữ liệu");
          case 500:
