          default:
            return rejectWithValue(
              data.message || "Không thể lấy danh sách tòa nhà"
            );
        }
      } else if (error.request) {
        return rejectWithValue("Không thể kết nối đến máy chủ");
      } else {
        return rejectWithValue(
          error.message || "Đã xảy ra lỗi khi gửi yêu cầu"
        );
      }
    }
  }
);

