import React, { useState, useEffect, useCallback } from "react";
import {
  Box,
  Card,
  Container,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TablePagination,
  TableRow,
  Paper,
  Typography,
  Button,
  IconButton,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  Grid,
  CircularProgress,
  Tooltip,
  Alert,
  InputAdornment,
  CardContent,
  Collapse,
  Chip,
} from "@mui/material";
import { DatePicker } from "@mui/x-date-pickers/DatePicker";
import { AdapterDateFns } from "@mui/x-date-pickers/AdapterDateFns";
import { LocalizationProvider } from "@mui/x-date-pickers/LocalizationProvider";
import { useSnackbar } from "notistack";
import axios from "axios";
import AddIcon from "@mui/icons-material/Add";
import EditIcon from "@mui/icons-material/Edit";
import DeleteIcon from "@mui/icons-material/Delete";
import SearchIcon from "@mui/icons-material/Search";
import RefreshIcon from "@mui/icons-material/Refresh";
import CalendarTodayIcon from "@mui/icons-material/CalendarToday";
import EventNoteIcon from "@mui/icons-material/EventNote";
import PeopleIcon from "@mui/icons-material/People";
import MainLayout from "../../layouts/MainLayout";
import { API_URL } from "../../config";
import ConfirmDialog from "../../components/ConfirmDialog";
import viLocale from "date-fns/locale/vi";

const SemestersPage = () => {
  // State
  const [semesters, setSemesters] = useState([]);
  const [page, setPage] = useState(0);
  const [rowsPerPage, setRowsPerPage] = useState(10);
  const [dialogOpen, setDialogOpen] = useState(false);
  const [deleteDialogOpen, setDeleteDialogOpen] = useState(false);
  const [selectedSemester, setSelectedSemester] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const [semesterForm, setSemesterForm] = useState({
    name: "",
    start_date: new Date(),
    end_date: new Date(new Date().setMonth(new Date().getMonth() + 3)),
  });
  const [dialogMode, setDialogMode] = useState("create");
  const [total, setTotal] = useState(0);
  const [searchTerm, setSearchTerm] = useState("");
  const [advancedFilters, setAdvancedFilters] = useState({
    status: "", // "active", "upcoming", "past"
  });
  const [stats, setStats] = useState({
    total: 0,
    active: 0,
    upcoming: 0,
    past: 0,
  });

  const { enqueueSnackbar } = useSnackbar();
  const token = localStorage.getItem("token");

  // Load semesters - sử dụng useCallback để tránh warning về dependency
  const loadSemesters = useCallback(async () => {
    try {
      setIsLoading(true);
      setError(null);

      let url = `${API_URL}/semesters?page=${page + 1}&limit=${rowsPerPage}`;

      // Thêm tham số tìm kiếm nếu có
      if (searchTerm) {
        url += `&search=${encodeURIComponent(searchTerm)}`;
      }

      // Thêm bộ lọc nâng cao nếu có
      if (advancedFilters.status) {
        url += `&status=${advancedFilters.status}`;
      }

      const response = await axios.get(url, {
        headers: { Authorization: `Bearer ${token}` },
      });

      if (response.data && response.data.data) {
        setSemesters(response.data.data);
        setTotal(response.data.total || 0);
      } else {
        setSemesters([]);
        setTotal(0);
        console.warn("Dữ liệu API không đúng định dạng:", response.data);
      }
    } catch (error) {
      console.error("Lỗi khi tải danh sách kỳ học:", error);
      setError("Không thể tải danh sách kỳ học. Vui lòng thử lại sau.");
      enqueueSnackbar("Lỗi khi tải danh sách kỳ học", { variant: "error" });
    } finally {
      setIsLoading(false);
    }
  }, [page, rowsPerPage, token, enqueueSnackbar, searchTerm, advancedFilters]);

  // Load semesters on page load và khi page/rowsPerPage thay đổi
  useEffect(() => {
    loadSemesters();
  }, [loadSemesters]);

  // Load thống kê
  const loadStats = useCallback(async () => {
    try {
      const response = await axios.get(`${API_URL}/semesters/statistics`, {
        headers: { Authorization: `Bearer ${token}` },
      });

      if (response.data && response.data.data) {
        setStats({
          total: response.data.data.total || 0,
          active: response.data.data.active || 0,
          upcoming: response.data.data.upcoming || 0,
          past: response.data.data.past || 0,
        });
      }
    } catch (error) {
      console.error("Lỗi khi tải thống kê kỳ học:", error);
    }
  }, [token]);

  // Load thống kê khi trang được tải
  useEffect(() => {
    loadStats();
  }, [loadStats]);

  // Handle page change
  const handlePageChange = (event, newPage) => {
    setPage(newPage);
  };

  // Handle rows per page change
  const handleRowsPerPageChange = (event) => {
    setRowsPerPage(parseInt(event.target.value, 10));
    setPage(0);
  };

  // Handle search
  const handleSearch = () => {
    setPage(0);
    loadSemesters();
  };

  // Handle filter change
  const handleFilterChange = (status) => {
    setAdvancedFilters({
      ...advancedFilters,
      status,
    });
    setPage(0);
  };

  // Handle clear filters
  const clearFilters = () => {
    setSearchTerm("");
    setAdvancedFilters({ status: "" });
    setPage(0);
  };

  // Handle open dialog to create
  const handleOpenCreateDialog = () => {
    setSemesterForm({
      name: "",
      start_date: new Date(),
      end_date: new Date(new Date().setMonth(new Date().getMonth() + 3)),
    });
    setDialogMode("create");
    setDialogOpen(true);
  };

  // Handle open dialog to edit
  const handleOpenEditDialog = (semester) => {
    setSelectedSemester(semester);
    setSemesterForm({
      name: semester.name,
      start_date: new Date(semester.start_date),
      end_date: new Date(semester.end_date),
    });
    setDialogMode("edit");
    setDialogOpen(true);
  };

  // Handle open dialog to delete
  const handleOpenDeleteDialog = (semester) => {
    setSelectedSemester(semester);
    setDeleteDialogOpen(true);
  };

  // Handle form change
  const handleFormChange = (event) => {
    const { name, value } = event.target;
    setSemesterForm({
      ...semesterForm,
      [name]: value,
    });
  };

  // Handle date change
  const handleDateChange = (name, date) => {
    setSemesterForm({
      ...semesterForm,
      [name]: date,
    });
  };

  // Validate form
  const validateForm = () => {
    if (!semesterForm.name.trim()) {
      enqueueSnackbar("Vui lòng nhập tên kỳ học", { variant: "error" });
      return false;
    }

    if (!semesterForm.start_date) {
      enqueueSnackbar("Vui lòng chọn ngày bắt đầu", { variant: "error" });
      return false;
    }

    if (!semesterForm.end_date) {
      enqueueSnackbar("Vui lòng chọn ngày kết thúc", { variant: "error" });
      return false;
    }

    if (semesterForm.start_date >= semesterForm.end_date) {
      enqueueSnackbar("Ngày kết thúc phải sau ngày bắt đầu", {
        variant: "error",
      });
      return false;
    }

    return true;
  };

  // Handle form submit
  const handleFormSubmit = async () => {
    try {
      if (!validateForm()) {
        return;
      }

      setIsLoading(true);

      if (dialogMode === "create") {
        await axios.post(`${API_URL}/semesters`, semesterForm, {
          headers: { Authorization: `Bearer ${token}` },
        });
        enqueueSnackbar("Tạo kỳ học mới thành công", { variant: "success" });
      } else {
        await axios.put(
          `${API_URL}/semesters/${selectedSemester._id}`,
          semesterForm,
          {
            headers: { Authorization: `Bearer ${token}` },
          }
        );
        enqueueSnackbar("Cập nhật kỳ học thành công", { variant: "success" });
      }

      setDialogOpen(false);
      loadSemesters();
      loadStats();
    } catch (error) {
      console.error("Lỗi khi lưu kỳ học:", error);
      enqueueSnackbar(
        `Lỗi khi ${dialogMode === "create" ? "tạo" : "cập nhật"} kỳ học: ${
          error.response?.data?.message || error.message
        }`,
        { variant: "error" }
      );
    } finally {
      setIsLoading(false);
    }
  };

  // Handle delete semester
  const handleDeleteSemester = async () => {
    try {
      setIsLoading(true);
      await axios.delete(`${API_URL}/semesters/${selectedSemester._id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      enqueueSnackbar("Xóa kỳ học thành công", { variant: "success" });
      setDeleteDialogOpen(false);
      loadSemesters();
      loadStats();
    } catch (error) {
      console.error("Lỗi khi xóa kỳ học:", error);
      enqueueSnackbar(
        `Lỗi khi xóa kỳ học: ${error.response?.data?.message || error.message}`,
        { variant: "error" }
      );
    } finally {
      setIsLoading(false);
    }
  };

  // Format date
  const formatDate = (dateString) => {
    if (!dateString) return "";
    const date = new Date(dateString);
    return date.toLocaleDateString("vi-VN");
  };

  // Xác định trạng thái của kỳ học
  const getSemesterStatus = (semester) => {
    const now = new Date();
    const startDate = new Date(semester.start_date);
    const endDate = new Date(semester.end_date);

    if (now >= startDate && now <= endDate) {
      return { label: "Đang diễn ra", color: "success" };
    } else if (now < startDate) {
      return { label: "Sắp tới", color: "info" };
    } else {
      return { label: "Đã kết thúc", color: "default" };
    }
  };

  // Render statistics cards
  const renderStatCards = () => (
    <Grid container spacing={2} sx={{ mb: 3 }}>
      <Grid item xs={12} sm={6} md={3}>
        <Card
          sx={{
            borderLeft: "4px solid #3f51b5",
            boxShadow: 2,
            height: "100%",
          }}
        >
          <CardContent>
            <Typography variant="h6" color="textSecondary" gutterBottom>
              Tổng số kỳ học
            </Typography>
            <Typography variant="h4">{stats.total}</Typography>
            <Box sx={{ mt: 1, display: "flex", alignItems: "center" }}>
              <CalendarTodayIcon
                fontSize="small"
                sx={{ mr: 1, color: "text.secondary" }}
              />
              <Typography variant="body2" color="textSecondary">
                Tất cả các kỳ học trong hệ thống
              </Typography>
            </Box>
          </CardContent>
        </Card>
      </Grid>

      <Grid item xs={12} sm={6} md={3}>
        <Card
          sx={{
            borderLeft: "4px solid #4caf50",
            boxShadow: 2,
            height: "100%",
          }}
        >
          <CardContent>
            <Typography variant="h6" color="textSecondary" gutterBottom>
              Kỳ học đang diễn ra
            </Typography>
            <Typography variant="h4">{stats.active}</Typography>
            <Box sx={{ mt: 1, display: "flex", alignItems: "center" }}>
              <EventNoteIcon
                fontSize="small"
                sx={{ mr: 1, color: "text.secondary" }}
              />
              <Typography variant="body2" color="textSecondary">
                Các kỳ học đang diễn ra
              </Typography>
            </Box>
          </CardContent>
        </Card>
      </Grid>

      <Grid item xs={12} sm={6} md={3}>
        <Card
          sx={{
            borderLeft: "4px solid #2196f3",
            boxShadow: 2,
            height: "100%",
          }}
        >
          <CardContent>
            <Typography variant="h6" color="textSecondary" gutterBottom>
              Kỳ học sắp tới
            </Typography>
            <Typography variant="h4">{stats.upcoming}</Typography>
            <Box sx={{ mt: 1, display: "flex", alignItems: "center" }}>
              <CalendarTodayIcon
                fontSize="small"
                sx={{ mr: 1, color: "text.secondary" }}
              />
              <Typography variant="body2" color="textSecondary">
                Các kỳ học chưa bắt đầu
              </Typography>
            </Box>
          </CardContent>
        </Card>
      </Grid>

      <Grid item xs={12} sm={6} md={3}>
        <Card
          sx={{
            borderLeft: "4px solid #757575",
            boxShadow: 2,
            height: "100%",
          }}
        >
          <CardContent>
            <Typography variant="h6" color="textSecondary" gutterBottom>
              Kỳ học đã kết thúc
            </Typography>
            <Typography variant="h4">{stats.past}</Typography>
            <Box sx={{ mt: 1, display: "flex", alignItems: "center" }}>
              <PeopleIcon
                fontSize="small"
                sx={{ mr: 1, color: "text.secondary" }}
              />
              <Typography variant="body2" color="textSecondary">
                Các kỳ học đã hoàn thành
              </Typography>
            </Box>
          </CardContent>
        </Card>
      </Grid>
    </Grid>
  );

  // Empty state
  const renderEmptyState = () => (
    <Box
      sx={{
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
        py: 5,
        px: 2,
        bgcolor: "#f5f5f5",
        borderRadius: 2,
      }}
    >
      <Box sx={{ mb: 2 }}>
        <CalendarTodayIcon sx={{ fontSize: 60, color: "#3f51b5" }} />
      </Box>
      <Typography variant="h6" gutterBottom>
        Chưa có kỳ học nào
      </Typography>
      <Typography
        variant="body2"
        color="textSecondary"
        align="center"
        sx={{ mb: 3 }}
      >
        Kỳ học được sử dụng để quản lý thời gian học tập trong trường. Mỗi kỳ
        học có thời gian bắt đầu và kết thúc riêng.
      </Typography>
      <Button
        variant="contained"
        color="primary"
        startIcon={<AddIcon />}
        onClick={handleOpenCreateDialog}
      >
        Tạo kỳ học mới
      </Button>
    </Box>
  );

  return (
    <MainLayout>
      <Container maxWidth="xl">
        <Box sx={{ mb: 4 }}>
          <Typography variant="h4" gutterBottom>
            Quản lý kỳ học
          </Typography>
          <Typography variant="body1" color="textSecondary" paragraph>
            Quản lý các kỳ học trong hệ thống điểm danh
          </Typography>
        </Box>

        {renderStatCards()}

        {error && (
          <Alert severity="error" sx={{ mb: 3 }}>
            {error}
          </Alert>
        )}

        <Paper sx={{ p: 2, mb: 3 }}>
          <Box
            sx={{
              display: "flex",
              justifyContent: "space-between",
              alignItems: "center",
              flexWrap: "wrap",
              gap: 2,
              mb: 2,
            }}
          >
            <Box sx={{ flexGrow: 1, maxWidth: "500px" }}>
              <TextField
                fullWidth
                placeholder="Tìm kiếm kỳ học..."
                size="small"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                onKeyPress={(e) => e.key === "Enter" && handleSearch()}
                InputProps={{
                  startAdornment: (
                    <InputAdornment position="start">
                      <SearchIcon />
                    </InputAdornment>
                  ),
                }}
              />
            </Box>

            <Box display="flex" gap={2}>
              <Button
                variant="outlined"
                startIcon={<RefreshIcon />}
                onClick={() => {
                  clearFilters();
                  loadSemesters();
                }}
              >
                Làm mới
              </Button>
              <Button
                variant="contained"
                startIcon={<AddIcon />}
                onClick={handleOpenCreateDialog}
              >
                Thêm kỳ học
              </Button>
            </Box>
          </Box>

          <Collapse in={true}>
            <Box sx={{ mb: 2 }}>
              <Typography variant="subtitle2" gutterBottom>
                Lọc theo trạng thái
              </Typography>
              <Box sx={{ display: "flex", gap: 1, flexWrap: "wrap" }}>
                <Chip
                  label="Tất cả"
                  variant={
                    advancedFilters.status === "" ? "filled" : "outlined"
                  }
                  color={advancedFilters.status === "" ? "primary" : "default"}
                  onClick={() => handleFilterChange("")}
                />
                <Chip
                  label="Đang diễn ra"
                  variant={
                    advancedFilters.status === "active" ? "filled" : "outlined"
                  }
                  color={
                    advancedFilters.status === "active" ? "success" : "default"
                  }
                  onClick={() => handleFilterChange("active")}
                />
                <Chip
                  label="Sắp tới"
                  variant={
                    advancedFilters.status === "upcoming"
                      ? "filled"
                      : "outlined"
                  }
                  color={
                    advancedFilters.status === "upcoming" ? "info" : "default"
                  }
                  onClick={() => handleFilterChange("upcoming")}
                />
                <Chip
                  label="Đã kết thúc"
                  variant={
                    advancedFilters.status === "past" ? "filled" : "outlined"
                  }
                  color={
                    advancedFilters.status === "past" ? "default" : "default"
                  }
                  onClick={() => handleFilterChange("past")}
                />
              </Box>
            </Box>
          </Collapse>
        </Paper>

        {/* Hiển thị kết quả hoặc trạng thái trống */}
        {isLoading ? (
          <Box sx={{ display: "flex", justifyContent: "center", py: 8 }}>
            <CircularProgress />
          </Box>
        ) : semesters.length === 0 &&
          searchTerm === "" &&
          !advancedFilters.status ? (
          renderEmptyState()
        ) : (
          <Card>
            <TableContainer component={Paper}>
              <Table sx={{ minWidth: 650 }}>
                <TableHead>
                  <TableRow sx={{ backgroundColor: "#f5f5f5" }}>
                    <TableCell>#</TableCell>
                    <TableCell>Tên kỳ học</TableCell>
                    <TableCell>Ngày bắt đầu</TableCell>
                    <TableCell>Ngày kết thúc</TableCell>
                    <TableCell>Trạng thái</TableCell>
                    <TableCell align="right">Thao tác</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {semesters.length === 0 ? (
                    <TableRow>
                      <TableCell colSpan={6} align="center">
                        Không tìm thấy kỳ học nào
                      </TableCell>
                    </TableRow>
                  ) : (
                    semesters.map((semester, index) => {
                      const status = getSemesterStatus(semester);
                      return (
                        <TableRow key={semester._id} hover>
                          <TableCell>
                            {page * rowsPerPage + index + 1}
                          </TableCell>
                          <TableCell>
                            <Typography variant="body1" fontWeight="500">
                              {semester.name}
                            </Typography>
                          </TableCell>
                          <TableCell>
                            {formatDate(semester.start_date)}
                          </TableCell>
                          <TableCell>{formatDate(semester.end_date)}</TableCell>
                          <TableCell>
                            <Chip
                              label={status.label}
                              color={status.color}
                              size="small"
                              variant="outlined"
                            />
                          </TableCell>
                          <TableCell align="right">
                            <Tooltip title="Chỉnh sửa">
                              <IconButton
                                color="primary"
                                onClick={() => handleOpenEditDialog(semester)}
                                size="small"
                                sx={{ mr: 1 }}
                              >
                                <EditIcon fontSize="small" />
                              </IconButton>
                            </Tooltip>
                            <Tooltip title="Xóa">
                              <IconButton
                                color="error"
                                onClick={() => handleOpenDeleteDialog(semester)}
                                size="small"
                              >
                                <DeleteIcon fontSize="small" />
                              </IconButton>
                            </Tooltip>
                          </TableCell>
                        </TableRow>
                      );
                    })
                  )}
                </TableBody>
              </Table>
            </TableContainer>
            <TablePagination
              rowsPerPageOptions={[5, 10, 25, 50]}
              component="div"
              count={total}
              rowsPerPage={rowsPerPage}
              page={page}
              onPageChange={handlePageChange}
              onRowsPerPageChange={handleRowsPerPageChange}
              labelRowsPerPage="Số hàng mỗi trang:"
              labelDisplayedRows={({ from, to, count }) =>
                `${from}-${to} của ${count !== -1 ? count : `hơn ${to}`}`
              }
            />
          </Card>
        )}

        {/* Dialog for creating/editing semester */}
        <Dialog
          open={dialogOpen}
          onClose={() => setDialogOpen(false)}
          maxWidth="md"
          fullWidth
        >
          <DialogTitle>
            {dialogMode === "create" ? "Thêm kỳ học mới" : "Chỉnh sửa kỳ học"}
          </DialogTitle>
          <DialogContent>
            <Grid container spacing={2} sx={{ mt: 1 }}>
              <Grid item xs={12}>
                <TextField
                  name="name"
                  label="Tên kỳ học"
                  value={semesterForm.name}
                  onChange={handleFormChange}
                  fullWidth
                  required
                  helperText="Ví dụ: Học kỳ 1 năm 2023-2024"
                />
              </Grid>
              <LocalizationProvider
                dateAdapter={AdapterDateFns}
                adapterLocale={viLocale}
              >
                <Grid item xs={12} sm={6}>
                  <DatePicker
                    label="Ngày bắt đầu"
                    value={semesterForm.start_date}
                    onChange={(date) => handleDateChange("start_date", date)}
                    slotProps={{
                      textField: {
                        fullWidth: true,
                        required: true,
                        helperText: "Ngày bắt đầu kỳ học",
                      },
                    }}
                    format="dd/MM/yyyy"
                  />
                </Grid>
                <Grid item xs={12} sm={6}>
                  <DatePicker
                    label="Ngày kết thúc"
                    value={semesterForm.end_date}
                    onChange={(date) => handleDateChange("end_date", date)}
                    slotProps={{
                      textField: {
                        fullWidth: true,
                        required: true,
                        helperText: "Ngày kết thúc kỳ học",
                      },
                    }}
                    format="dd/MM/yyyy"
                  />
                </Grid>
              </LocalizationProvider>
            </Grid>
          </DialogContent>
          <DialogActions sx={{ px: 3, pb: 2 }}>
            <Button onClick={() => setDialogOpen(false)}>Hủy</Button>
            <Button
              variant="contained"
              onClick={handleFormSubmit}
              disabled={isLoading}
            >
              {isLoading ? (
                <CircularProgress size={24} />
              ) : dialogMode === "create" ? (
                "Thêm kỳ học"
              ) : (
                "Lưu thay đổi"
              )}
            </Button>
          </DialogActions>
        </Dialog>

        {/* Confirmation dialog for deleting semester */}
        <ConfirmDialog
          open={deleteDialogOpen}
          title="Xác nhận xóa kỳ học"
          content={`Bạn có chắc chắn muốn xóa kỳ học "${
            selectedSemester?.name || ""
          }"? Hành động này không thể hoàn tác và có thể ảnh hưởng đến các lớp học liên quan.`}
          onClose={() => setDeleteDialogOpen(false)}
          onConfirm={handleDeleteSemester}
          isLoading={isLoading}
          danger={true}
        />
      </Container>
    </MainLayout>
  );
};

export default SemestersPage;
